INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 1, 'Andriananahary masina indrindra!\nNy anjelinao izay mitoetra Aminao\nMifamaly hoe : Masina indrindra\nAndriananahary, Telo Izay Iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 2, 'Andriananahary masina indrindra!\nNa tsy hita aza izao ny voninahitrao!\nMasina indrindra Hianao irery.\nAndriananahary, Telo Izay Iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 3, 'Zava-manana aina samy mankalaza\nSady manambara Anao Izay Tompony izao;\nHianao irery no mitahy azy,\nAndriananahary, Telo Izay Iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 4, 'Andriananahary feno hatsarana!\nHe ny fitahianao izay mpanomponao.\nTsara dia tsara ny omenao azy\nAndriananahary, Telo Izay Iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 5, 'Andriananahary masina indrindra!\nIzahay mpanomponao ta-hankalaza Anao;\nFeno fahendrena, feno fiantrana,\nAndriananahary, Telo Izay Iray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 1, 'Misaora an''i Zanahary\n''Zao olona tontolo izao;\nDia mankalazà Azy,\nFa anton''izao rehetra izao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 2, 'Izay zavatra nomeny\nMahafaly, mahasoa;\nToy ny andro fararano,\nToy ny andro mipoaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 3, 'Dia ampitomboy ny sainay\nHahalala ny teninao,\nHahafantatra marina\nIzay sitraky ny fonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 1, 'Isaorana anie Andriamanitsika\nFa fitahiam-be Omeny ho antsika\nHatreo ambohoka\nKa mandraka antitra,\nIsika henika\nNy soan''ny lanitra', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 2, 'Tsy mety tapitra na lany ny hareny,\nTsy hita faritra ny sisa izay homeny;\nFanahy sambatra\nSy fo miadana\nNo efa santatra\nIzay ananana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 3, 'Ny Ray sy Zanaka sy ny Fanahy koa,\nDerain''ny masina ho Tompo iray tokoa;\nAndriamanitray\nBe fahasoavana\nNo anateranay\nIzay fisaorana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 1, 'Mamy ny fitianao,\nRy Jehovah Tomponay!\nFoinao ny Zanakao,\nHo Mpanavotra anay.\nTsy mba misy tokinay\nAfatsy ny Zanakao.\nKa misaotra Anao izahay\nNoho ny fitianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 2, 'Mamy ny fitianao,\nMahafaly fo tokoa;\nIzahay avelanao\nMba hibebaka avokoa.\nLasa lavitra izahay,\nNefa notadiavinao;\nEfa ratsy izahay,\nNefa tsy nafoinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 3, 'Mamy ny fitianao,\nIzay andraisanao anay\nHOnina ao an-dapanao\nRaha ho faty izahay\nNoho ny rastsy izay natao,\nLoza no anjaranay,\nNefa namboaranao\nParadisa izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 4, 'Mamy re, ry Tomponay!\nIzao fitianao izao,\nKa indreto izahay\nAvy mba hisaotra Anao.\nRaiso re izao saotra izao,\nFa fanati-tsitrapo;\nTsy mba mendrika ho Anao,\nNefa vokatry ny fo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 1, 'Jehovah, Ray an-danitra,\nMpamelona anay\nTsinjovy ny mpanomponao,\nTsarovy izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 2, 'Jehovah, Ray mamindra fo!\nHenoy ny hatakay;\nEkeo sy raso ho Anao\nNy dera entinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 3, 'Jehovah, Ray mahari-po!\nVonoy ny helokay;\nDiovy ny fanahinay,\nVelomy izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 4, 'Jehovah Ray! Ny antranao\nNo itokianay;\nAmbara-pahafatinay\nHisaoranay Hianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 1, 'Avia, miondreha,\nRy vazan-tany ô!\nFa lehibe Jehovah,\nSy masina tokoa;\nMiankohofa tsara,\nManatona Azy ''zao\nHaneho fanajana\nILay Mpanao anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 2, 'Andriananahary,\nManaiky izahay\nFa Aminao ny hery\nTsy hay toherina.\nTsy misy maharara\nNy tianao hatao,\nFa Hianao Mpitondra\nIzao rehetra izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 3, 'Re any lavitra any\nIzao ny herinao,\nKa isam-bazan-tany\nHiondrika Aminao;\nMpanjaka sy Mpitsara\nMahery Hianao;\nMiadana ny tany\nMiankina aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 1, 'Misaora an''Andriamanitra\nIzay nanao ny lanitra.\nNamorona ny tany koa\nSy ''zay rehetra mahasoa', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 2, 'Derao ny voninahiny,\nAnkalazao ny heriny,\nJereo ny fitiavany\nTsy tambonao tononina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 3, 'Dia avy izahay izao,\nRy Tompo be fitia ô!\nMiondrika eo imasonao\nMidera ny Anaranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 4, 'Isaorana mandrakizay\nHianao, Andriamanitray!\nNy haja be indrindra izao\nAtolotray mpanomponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (8, 1, 'Dera, Laza, Hery, Haja,\nHo an''Andriamanitra\nTompon''aina, Zanahary,\nRain''ny voninahitra,\nIzy no mpanao antsika,\nSady ivelomana;\nFototr''aina, foto-tsoa,\nIzy ivavahana.\nHihira isika hoe : Haleloia!\nDera ho an''Andriamanitra\n:,:Dia Haleloia! Haleloia! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (8, 2, 'Ray Tsitoha, tsy miova,\nTia sy mamindra fo,\nMahasoa isan''andro,\nIzy no isaorana;\nVatolampy tsy mifindra,\nTokin''olombelona;\nFiarova-mahavonjy,\nIzy itokiana\nHihira isika hoe : Haleloia!\nDera ho an''Andriamanitra\n:,:Dia Haleloia! Haleloia! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (9, 1, 'Ry Tompo Zanaharinay!\nManati-tsaotra izahay,\nF''efa novelominao,\nKa tratr''izao maraina izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (9, 2, 'Ny aizina efa lasa izao,\nmazava no naposakao;\nLevony koa ny otanay,\nKa hazavao ny hevitray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (9, 3, 'Ny asanay hatao anio\nAtrehonao ka mba tahio,\nHatao ho voninahitrao,\nHandrasanay ny antranao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (9, 4, 'Derainay Hianao, ry Ray!\nDerainay Jeso Tomponay,\nDerainay ny Fanahy koa,\nDerainay fa mpanisy soa', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 1, 'Ry Tomponay Tsitoha ô!\nMpanao izao tontolo izao,\nAnkalazainay Hianao\nFa lehibe ny herinao,', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 2, 'Ny hiran''ny anjelinao\nManerana ny lanitra,\nManehoeho fatratra\nNy hobin''ny voaavotra;', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 3, 'Fa masina, fa masina,\nFa masina i Jehovah!\nKa heniky ny lazanao\nNy tany sy ny lanitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 4, 'Ireo mpitory irakao\nMihoby eo anilanao,\nNahefa fanompoana,\nMihira ny fisaorana', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 5, 'Izao olonao rehetra izao\nDia mino sy manaiky Anao\nHo Ray Tsy miova sady to,\nMahery ny mpamindra fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 6, 'Ny Zanakao nirahinao\nSy ny Fanahy Masinao\nIsaoranay Anao, ry Ray,\nFa foto-pamonjena anay!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 7, 'Ry Kristy ô, Mpanjakanay\nMitovy hery amin-dRay', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 1, 'Endrey ny voninahitrao\nRy Ray Tsitoha ô !\nEndrey ny fiandriananao\nHajainay eram-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 2, 'Ny herinao, ny andronao,\nDia mandrakizay !\nNy lanitra mitsaoka Anao\nRy Andriamanitray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 3, 'Ny tavanao mamiratra\nNy fahamasinanao\nSy hery tapitr’ohatra\nNy fahendrenao soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 4, 'Endrey, matahotra aho izao,\nRy Tompo Masina ô !\nMangovitra eo imasonao\nSao dia lavinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 5, 'Na dia ambony Hianao,\nDia nidina amiko\nNitondra fihavanana,\nKa dia mba tiako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 6, 'Koa aiza re no misy ray\nMitovy Aminao :\nMikarakara tsara anay\nIzay natsanganao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 7, 'O, mahagaga Hianao,\nRy Tompo tia anay,\nNy fonay dia hatoky Anao\nAmbara-podinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 1, 'Jehovah ô, derainay  Hianao !\nAnkalazainay ny Anaranao !\n:,: Izao rehetra izao manaja\nAnao\nFa Rainay ho mandrakizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 2, 'Mpanjakanay sy Tompo\nMasina\nOmenay eram-po ny hasina\n:,: Izao rehetra izao mihoby\nAnao\nFa Rainay ho mandrakizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 3, 'Ry Masina manerinerina,\nJehovah be fitia sy marina !\n:,: Izao rehetra izao misaotra\nAnao\nFa Rainay ho mandrakizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 4, 'Manerana izao tontolo izao\nNy laza sy ny voninahitrao,\n:,: Izao rehetra izao hanoa\nAnao\nFa Rainay ho mandrakizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 5, 'Andriananahary Masina ô !\nTanteraka ny fiandriananao,\n:,: Izao rehetra izao mitsaoka\nAnao\nFa Rainay ho mandrakizay :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 1, 'Misaora ny Mpahary,\nTsarovinao ny asany\nMisaora ny Mpahary\nDerao ny soa vitany !\nNy helokao rehetra\nDia voavelany,\nFa tsy mba misy fetra\nNy fitiavany :\nNy Zanany nafoiny\nMba ho mpamonjinao,\nKa hianao nantsoiny\nHandray ny aim-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 2, 'Ny toky izay nomeny,\nSy ny fahasoavana,\nMaharitra ireny\nTsy manam-pahataperana,\nAvelany tokoa\nNy hatezerany,\nKa tsy mba hahavoa\nAntsika olony,\nFa Izy mahalala\nNy toetsika atý,\nKa tena Ray mandala\nAntsika zanany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 3, 'He, toy ny voninkazo\nNy ain’ny olombelona,\nTsy ela dia malazo\nKa tena mora levona,\nFa Jeso manan-kery\nHamoha fasana,\nTsy hisy izay ho very\nNy olo-masina ;\nIsika vovotany\nNo namboariny\nHiombom-panjakana\nSy laza aminy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 4, 'Misaora ny Mpahary,\nRy tany sy ny lanitra !\nRy zava-boahary,\nDerao Andriamanitra !\nRy irany mahery\nManao ny sitrany,\nRy zanany izay very,\nKanefa hitany,\nMisaora Azy àry,\nFa heniky ny soa,\nMisaora ny Mpahary,\nO, ry fanahiko !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 1, 'O, ry tany rehetra, avia izao\nHidera ny Tompo\nMpanavotra anao\nNy Tompo miantso\nantsika hankao\nAnatrehany, mba\nhohasoaviny ao :\nDera, laza anie\nVoninahi-dehibe,\nHo Anao, ry Tomponay\nTsitoha ô !\nAmen.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 2, 'He, Jehovah miantra \nantsika izao,\nKa dia manolotra\nantsika indray\nNy endriny mbamin’ny\naina vaovao ;\nEny, Izy Manaiky\ntokoa ho Ray\nDera, laza, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 3, 'Asandrato ny feo hihoby\nhoe :\nIsaorana mandrakizay\nHianao,\nRy Tompo Mahery,\nIzay manome\nHo anay olom-bery\nny aina vaovao ;\nDera, laza, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 1, 'Avia hankalaza\nNy Tompo lehibe,\nFa feno hery, haja,\nJehovah Masina e\nAndeha mitambara,\nRy vazan-tany ô !\nMiankohofa tsara\nHanaja Azy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 2, 'Ry Tomponay Tsitoha\nIndreto izahay,\nManondrika ny loha,\nMidera Anao, ry Ray\nNy fonay sy ny hery,\nFanahy, saina koa,\nOmena Anao irery,\nKa anjakao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 3, 'Mpanjaka tena hendry\nHianao, ry Tomponay,\nKa tena mahakendry\nIzay hahasoa anay ;\nNa misy toa mangidy\nNy fitondranao soa\nNy saina sy safidy\nManaiky Anao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 4, 'Manerana ny tany\nIzao ny lazanao,\nKa be tsy hita lany\nNy voninahitrao ;\nKoa hira fiderana\nVentesinay izao\nHaleloia, Hosana !\nMba raiso ho Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 1, 'Avia ry vazan-tany ô !\nSy mponina ao an-danitra \nMiraisa feo ka mihirà\nNy fanavotan-dehibe,\n:,: Izay amonjena marobe,\nDia vita re ka miderà :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 2, 'Avia ry  fiangonana  ô !\nMba asandrato re ny feo\nHidera ny Mpanavotra ;\nNa atý an-efitra aza izao,\n:,: Hirao am-pandehanana\nNy hira soan’i Ziona :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 3, 'Avia ry voavonjy ô !\nKa mifalia avokoa,\nFa afa-doza hianareo ;\nMiraisa feo, ka mihirà,\n:,: Derao, derao, fa mendrika\nIlay nanavotra anareo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 4, 'Avia ry olom-bery ô !\nIzay mandre ny teny soa\nHoe : « Manatòna Ahy izao »\nAnio ny Tompo mba fidio ;\n:,: Ny fitiavany valio ;\nNy fiderany koa hirao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 2, 'Haja sy voninahitra\nOmena an’Andriamanitra,\nFa Izy ihany no mahefa\n:,: Izay sitrakin’ny fony :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 3, 'Masina, tsy azo haratsina,\nMahery, tsy azo resena ;\nMarina, tsy azo laingaina ;\n:,: Hendry, tsy azo fitahina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 4, 'Ny am-po toy ny am-bava;\nNy maizina toy ny mazava\nNy takona toy ny miseho ;\n:,: Tsy misy àry tsy hitany :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 5, 'Tsy miova ny fombany ;\nLehibe ka tsy oharina ;\nTia, tsy azo arovana ;\n:,: Miantra, tsy azo ferana :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (17, 6, 'Saotra amam-bavaka atao\nDera sy laza ho Azy ;\nIzy no ivavahana\n:,: Fa anton’izao tontolo\nizao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 1, 'Ny feonay rehetra izao\nDia entinay midera Anao,\nRy Andriananaharinay !\nNa ambany aza izahay,\n:,: Dia tianao :,:\nRy Andriananaharinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 2, 'Ny andronay rehetra izao\nDia entinay manoa Anao,\nRy Tompo sy Mpanjakanay !\nSatria izao dia fantatray\n:,: Fa tianao :,:\nRy Tompo sy Mpanjakanay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 3, 'Ny hevitray rehetra izao\nDia enti-mankalaza Anao,\nRy Tompo Mpanasoa anay !\nMpitahy ny fanahinay,\n:,: Fa tianao :,:\nRy Tompo Mpanasoa anay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 4, 'Ny herinay rehetra izao\nDia iasanay ho Anao,\nRy Aronay sy Tanjakay !\nMiezaka tokoa izahay,\n:,: Fa tianao :,:\nRy Aronay sy Tanjakay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 5, 'Rehefa alainao izahay,\nKa tapitra ny andronay,\nHamantana any Aminao\nIzahay arý an-danitrao,\n:,: Fa tianao :,:\nHamantana any Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 1, 'Misaotra Anao izahay izao,\nJehovah Ray !\nFa efa notohizanao\nNy andronay,\nKa tonga atý an-tranonao\nIndray izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 2, 'Misaotra Anao izahay izao,\nRy Mpanome !\nNy soa avy Aminao\nTsy lany re,\nFa isan-andro misy izao\nKa maro be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 3, 'Misaotra Anao ‘zahay izao,\nRy Rainay soa !\nNy helokay navelanao\nNefa iza moa\nNo mahalaza izay natao ?\nFa be tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 4, 'Misaotra Anao ‘zahay izao,\nRy mora fo !\nNy mafy tsy sakananao\nFa mba hanjo,\nFa be ny fanirianao\nAnay hadio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 5, 'Misaotra Anao ‘zahay izao,\nRy Tomponay !\nAo aminao an-danitrao\nHonenanay,\nFa misy fitsaharana ao\nMandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 1, 'Tsaroanay tokoa izao\nNy soa be noraisainay\nTsy tapaka avy taminao\nRy Andriananaharinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 2, 'Ny lasa itodihana\nDia mampiseho avokao\nNy tanam-pitiavana\nMiahy sady manasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 3, 'He ! koa ity andronay ity,\nIzay diavinay izao,\nDia voahodidina erý\nNy fitahiana aminao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 4, 'Dia tokinay tokoa izao\nFa mbola ho arovanao\nAnio, ampitso, rahatrizay,\nIzay miankina Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 5, 'Ka dia mba velomy re\nAtý anatinay ny fo\nHahay misaotra Anao anie\nRy Rainay be famindram-po !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (21, 1, 'Derao Andriamanitra\nRy vazan-tany efitra,\nFa izy no mamindra fo,\nKa mety manome ny zo,\nNaniraka ny Zanany\nHanavotra ny olony !\nNy sandriny tsy toha re,\nFa manan-kery lehibe.\nMalaza ny Anarany,\nFa lehibe ny asany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (21, 2, 'Ny zavatra rehetra e\nNy ranomasin-dehibe,\nIreo tendrombohitra,\nNy saha feno ahitra,\nNy hazo sy ny vorona\nNy vato sy ny zavona,\nNy voninkazo manitra,\nNy endriky ny lanitra\nDia samy tsy mijanona\nMidera ny mpamorona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (21, 3, 'Ry olona navotana\nMba mitsangàna faingana,\nKa miderà ny Tomponao,\nIzay nanavotra anao,\nFa he, ny voninahiny\nManerana ny taniny\nTendreo ny valihanao\nKa miventesa hira vao :\nRy voavonjy ô, derao ny Ray\nMpamorona anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 1, 'Ry Rainay Izay namorona\nIzao rehetra izao,\nNy hita sy ny takona\n:,: Dia asan-tananao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 2, 'Ny volana aman-kitana,\nNy biby marobe,\nNy ala maitso mavana\n:,: Hitsaoka Anao anie :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 3, 'Ny tany sy ny lanitra,\nNy ranomasina,\nIreo tendrombohitra\n:,: Manolo-kasina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 4, 'Ny ory sy ny mpanana,\nDia fototra iray,\nFa samy asan-tanana\n:,: Nataonao Tomponay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 5, 'Dia saotra no atolotray,\nAnao, ka raisonao,\nFa fanomezan-ko anay\n:,: Ny asan-tananao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 1, 'Tsy misy zava-mahasoa,\nAndriamanitra ô !\nIzay tsy nataonao ho anay\nMba hifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 2, 'Tsy misy singan’ahitra,\nNa ravin-kazo koa,\nIzay tsy amantaranay\nFa hendry Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 3, 'Ny kintana mazava erý\nManeho avokoa\nFa sitrakao hanafaka\nNy aizina aminay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 4, 'Izay rehetra tazanay\nDia voninahitrao ;\nIzay rehetra takona\nMilaza izany koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 5, 'Ny tany sy ny lanitra\nNataon’ny herinao\nDia azo ianarana\nNy famindramponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 1, 'Ny lanitra ao ambony ao,\n:,: Andriamanitra ô !\nIzay rehetra vitanao\nDia mahagaga anay. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 2, 'Ny volana aman-kitana,\n:,: Ny masoandro koa,\nNy tany aman-danitra,\nMitory avokoa. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 3, 'Ny andro amana alina,\n:,: Milaza aminay\nFa hendry Andriamanitra,\nJehovah Tomponay. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 4, 'Ny tenanay, fanahy koa,\n:,: Dia avy taminao\nKa mampiseho tsara koa\nNy voninahitrao. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 5, 'Izay rehetra vitanao,\n:,: Atý sy any koa\nMiara mankalaza Anao\nFa Tompony tokoa. (in-3)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 2, 'Andriamanitra hendry,\n:,: Loharanon-tsaina  :,:\n:,: Ka manolo-pahendrena\nIzay manaiky Azy :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 3, 'Andriamanitra mahay,\n:,: Izao rehetra izao :,:\n:,: Ka Izy no akana saina\nHahahendry antsika :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 4, 'Ary isika mila saina,\n:,: Hanitsy antsika :,:\n:,: Amin’ny fianarana\nHanarahana Azy :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 5, 'Izy no ihavian’\n:,: Ny soa rehetra :,:\n:,: Hiainana mandrakizay,\nKa Izy no omen-tsaotra :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 1, 'Jehovah tsy mba manadino\nNy mpanompony manoa,\nFa hotsarovany ireo mino\nNofidiny ho Azy koa.\nManerinerina ambony\nNy fiandrianany ;\nKanefa kosa re ny fony\nMitsinjo izay ambany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 2, 'Izaho dia ravoravo\nKa midera Azy izao ;\nFahasoavanao, Ry Avo,\nNo namonjy hatrizao,\nFahavokisan’ny fanahy\nAry fisandratana\nNo hotantaninao ho ahy :\nNy foko mankalaza.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 3, 'O, mifalia ry Israely,\nFa vonjen’ny sandriny ;\nMba matokia dia hahery\nAntsoy ny Anarany :\nMpanomponao ity mitalaho,\nKa henoy ny hatako,\nO, ry Mahery, inty aho !\nNy sitrakao ekeko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 1, 'Fitiavana tokoa re,\nny Ray an-danitra\nFa manolo-java-tsoa\nTena mahasambatra\nHo antsika izay mpanota,\nOry, osa, rera-po,\nIzy masina sy to\nTa-hanaisotra ny ota\nNoho ny fanavotana\nVitan’i Jesosy e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 2, 'Tsy ho very, ry mahantra\nSy miferin’aina izao,\nFa ny Ray Izay feno antra,\nAoka mba hinoanao.\nSambatra ny voavela\nHeloka tokoa re,\nFa Jesosy manome\nFanavaozam-po sy lela,\nHira fideram-baovao\nNo ho re an-dalalanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 3, 'O, ry Rainay be fitia,\nMba ampio izahay,\nTsy ho kivy, tsy hania\nAo am-pandehananay,\nFa handroso mba hankany\nAminao an-danitra.\nRy Andriamanitra,\nBe ny loza atý an-tany\nFa ny ranomasonay\nFaohinao rahatrizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 1, 'Zanahary tsy mba tia\nNy fahaverezanao\nIzy, Rainao be fitia,\nTsy mba mahafoy anao.\nNy famoizam-po ario,\nFa Jesosy itokio,\nMba hatoninao anio\nIlay be fahasoavana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 2, 'Izy dia nitady ela,\nVao nahita anao izao ;\nMifalia, tsy ho ela\nDia hody hianao.\nHe, ry ondry saiky very,\nOry, osa, feno fery,\nNy Mpamonjy manan-kery\nHahasitrana anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 3, 'Ny ao an-danitra mifaly,\nRaha misy miova fo,\nAry any ny anjely\nMiramirana avokoa.\nFo marary dia tsaboiny,\nAhiahim-po vonoiny,\nFiadanana lazainy ;\nSambatra isika re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 1, '( Hira Paska )', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 2, 'Ry foko, mitsangàna izao\nKa miderà ny Tomponao,\nAndriamanitsika.\nNasehony ny heriny.\nNy fasana levoniny\nTsy hahatàna antsika.\nDerao ! Hobio !\nHaleloia sy hosana,\nFanjakana :\nAzy Tompom-pahefana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 3, 'He ! vitanao Mpanavotra\nNy lova mahasambatra :\nNy fasana resenao.\nMahazo toky fatratra,\nNy mitomany, sasatra :\nNy lanitra vohanao.\nJeso Kristy\nIzay nitsangana maraina\nTompon’aina :\nAndro Paska, arahabaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 4, 'Derao ny voninahiny !\nAnkalazao ny heriny,\nFa resy re satana.\nRy tany sy ry lanitra,\nDerao Andriamanitra\nAkaro ny hosana.\nRaiso Tompo,\nRaisonao ny voninahitra\nAman-dera :\nHo anao, ry Zanakondry.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 1, 'Paradisako ry Jeso,\nFifaliako Hianao ;\nLanitry ny foko eto\nSy Edenako vaovao !\nParadisa soan’ny foko\nEto an-tany Hianao ;\nEny, fanaperan-tsoako\nAo an-danitra vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 2, 'Volafotsy, volamena,\nVatosoako Hianao.\nZava-tsoako sy harena\nMiafina ao an-danitra ao.\nParadisa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 3, 'Zo sy hajako tokoa\nHianao Mpanavotro.\nHaingo sarobidy koa,\nSatro-boninahitro.\nParadisa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 4, 'Zavatra rehetra ho ahy\nHianao, ry Jeso tia ;\nFianinan’ny fanahy,\nFiadanako doria.\nParadisa ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 1, 'Misaotra Anao izahay, ry Ray,\nFa sitrakao homena ho anay\nNy Zanakao malalanao ;\nIlay tokanao no he natolotrao.\nAtolotray ny saotranay,\nO ! Raisonao anie Jehovah\nRay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 2, 'Misaotra Anao, Jesosinay,\nNy fitiavanao tsarovanay :\nNalatsakao ny rànao soa\nHanavotra aina ny\nmpanota voa ;\nAtolotray ny saotranay\nKa raisonao e, ry Jesosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 3, 'Misaotra Anao, Fanahy koa,\nMamelona sy monina\nao am-po.\nHavaozy ao anaty ao\nHo tonga lapanao madio\nsy vao\nAtolotray ny saotranay\nKa raiso, ry Fanahy tokinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 1, 'Misaotra Anao izahay\nJehovah Tompo ô !\nFa efa naharay\nNy soa taminao.\nKa lalandava no\nIsaoranay Anao\nHo eo am-bavanay tokoa\nNy teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 2, 'Fa reharehanay\nSy famonjena koa\nNy Tompo tia anay,\nTsy manam-paharoa.\nNy fandresena soa,\nNy fiononam-po\nDia avy taminao,\nKa hery manavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 3, 'Ny olo-mino Anao\nSy ireo mpanomponao\nNa velona na maty,\nDia misaotra Anao.\nKa sambatra tokoa\nFa manana Anao\nIzahay, ry reharehan’\nNy Fanahy ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 1, 'Tompo ô, tsy takatray\nNy hasoan’ny antranao ;\nZava-mahagaga anay\nNy fitiavanao.\nHe, mpanota izahay,\nNefa hasoavinao ;\nAvotr’aina azonay,\nMisaotra no atao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 2, 'Tompo ô, Mpanjakanay,\nFatratra ny herinao :\nTs’isy zavatra izay\nMahasakana Anao !\nNefa mora fo Hianao\nFa mandefitra aminay,\nKoa dia misaotra Anao\nFa afaka izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 3, 'Tompo Andriamanitray,\nAvy izahay izao :\nRaiso ny fiainanay ;\nAnao, ka anjakao.\nTompo ô, tsy takatray\nNy hasoan’ny antranao ;\nZava-mahagaga  anay\nNy fitiavanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 1, 'Tompo ô, Tsitoha\nZanaharinay,\nAsan-tànanao re\nIzao rehetra izao.\nMahagaga indrindra\nNy fahaizanao\nEntinao mitondra\nSy miasa ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 2, 'Hianao, Jehovah\nAo mandrakizay\nNa miovaova\nIzao rehetra izao.\nMahagaga indrindra\nNy fitiavanao\nTsy mba mety miova\nNy mandao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 3, 'Ray niandohana\nfony fahizay ;\nMbola hiafarana \nkoa rahatrizay.\nRaiso moramora\nre ny Zanakao.\nRaha tonga ny ora\nhiantsoanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 1, 'Isaoranay, ry Ray,\nNy fitiavanao ;\nFa indro azonay\n:,: Ny taombaovao :,:\nRy Tompo ô, ambeno,\nIzao fifaliana izao\nKa aoka re ho feno\nNy hasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 2, 'Ny taona vao lasa\nNipaoka olon-tsoa\nIreny mba nikasa\n:,: Mba ho tafita koa :,:\nNy fitondràna anefa\nAfeninao, ry Ray !\nKa nony miantefa\nVao sina izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 3, 'Fanahy ô, avia\nHitari-dàlana e !\nMandroso izao ny dia\n:,: Hamaky rano be :,:\nNy herinao tsy lefy\nNo tanjaky ny fo\nKa ampy mba ho fefy,\nRaha avy ny manjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 4, 'Itoero, Jeso tia\nNy sambonay re !\nNa tony na maria,\n:,: ‘Ty ranomasim-be :,:\nRaha Hianao no hita\nTsy fefika izahay\nAmbara-piampita\nArý an-tratran-dRay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 1, 'Moa ho haiko tantaraina\nNy hasoavanao ?\nMifamatra tamin’aina\nNy fitiavanao.\nNy vahoaka sesehena.\nIzay nahazo famonjena\nNo hidera Ilay Mpamonjy soa.\nJeso no derao, Jeso no derao :\nIzay nataony ambarao :\nJeso no derao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 2, 'Fiadanana nafoin''i Jeso Tomponay,\nF''efa tonga ny fotoana,\nSitraky ny Ray\nOlom-bery nozahany\nNy fatorany vahany\nHajambana koa nalany\nTsy ho ao\nJeso no derao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 3, 'Hoderaina lalandava\nNy Anaranao,\nFa ny aizina nisava\nEo imasonao :\nNa tsy tian’ny sasany aza\nNy Anaranao malaza,\nIzaho kosa hankalaza Azy re.\nJeso no derao, Jeso no derao :\nIzay nataony ambarao :\nJeso no derao.\n\n\nJESOSY KRISTY\nTOMPO\n1. NY FIHAVIANY\nSY NY FIVERENANY', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 1, 'Mihainoa, ry Ziona ô !\nAvy ny Mpanjakanao !\nRe hatrany lavitra\nFa hobin’ny olona.\nMifalia hianao\nIndro Izy, ka tsenao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 2, 'Indro Ilay miakatra\nMila tsy ho fantatra\nHo Mpanjaka marina,\nFa mietry lalina ;\nIzy no Mpanjakanao,\nRy Ziona ka derao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 3, 'Mandrosoa, Tompo ô !\nMba hanjaka amiko !\nFaly re ny olonao\nRaha tonga Hianao,\nO, hosana, Tomponay !\nRy Ilay nandrasanay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (38, 1, 'He, hosana !\nIsaorana anie ny Mesia,\nIzay nalefan’ny Tompo,\nKa hosana no asandratray ;\n:,: Ilay nirahinao, ry Tompo :,:\n:,: No derainay :,:\n:,: Ka hosana \nno asandratray :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 1, 'Faingàna, ry Mpanjaka !\nHandray ny lovanao ;\nFaingàna re mba haka\nNy tany ho Anao ;\nAvia hampifaly\nNy malahelo fo ;\nAfaho ny mijaly\nSy azon’ny manjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 2, 'Avia, fa misento\nAtý ny olonao ;\nNy fanjakana ento,\nFa Tompo Hianao.\nTsy hisy hitomany\nEo anatrehanao.\nHiadana ny tany\nIzay alehanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 3, 'Ny tendrombohitra avo,\nNy lohasaha koa,\nHo tonga ravoravo\nSy tretrika avokoa ;\nNy be tsy haka an-kery,\nNy kely afa-po ;\nTsy hisy izay ho very\nTsy hisy izay hanjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 4, 'Mpanjaka sy Mpiaro\nMahery Hianao ;\nNy firenena maro\nHitoky Aminao ;\nHo avy lalandava\nIzao rehetra izao\nHitondra harem-bevava\nHo eo an-tongotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 5, 'Faingàna, ry Mpamonjy,\nRy Tompo be fitia ,\nNy olonao tra-bonjy\nMiantso hoe : Avia !\nAnao ny fiderana,\nAnao ny laza be,\nAnao ny fanjakana\nSy arahaba e !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 1, 'Tonga ny fahazavan’i Ziona,\nRavo ny tany izay maizina teo\nTapitra koa ny andro misaona,\nAry ny tany miadana izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 2, 'Tonga ny fahazavan’i Ziona,\nEfa nandrasana izay ela izay,\nIzao dia tapitra ny\nfanompoana,\nKa ampodina ny babo indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 3, 'He misy rano izay miboiboika\nAny an-efitra maina sy lao,\nFeno ny hobiny ny lohasaha,\nManakoako ny hira vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 4, 'Indro ny tany sy nosy rehetra\nDia mankalaza ny Tompo\nizao,\nNy fanjakany dia tsy\nmanam-petra,\nMiravoravo ny tany vaovao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 1, 'Indro Jeso mandondona\nEo am-baravaram-po,\nInjao ny feony mifona\nMba haneho indrafo,\nAoka izy hovohàna,\nFa mitondra famelàna\nHampahery sy hitana\nNy fanahinao sy fo', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 2, 'Efa novidiny lafo\nMba ho afaka hianao ;\nIzy hiara-misakafo\nSy hifaly aminao ;\nFahasoavana zaraina,\nLova ampanantenaina,\nIray ny vatsy, iray ny aina\n‘Ndrey, ny hasambaranao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 1, 'Vohay ny varavaram-po !\nIlay Mpanjaka manan-jo,\nMpamonjy izao tontolo izao\nNo tonga hanasoa anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 2, 'Ny teny fanasana atao\nMitondra tena aim-baovao,\nNy fahoriana manjo\nAfahan’ny famindrampo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 3, 'Mba mihainoa, maneke,\nFa sambatra izay mandre,\nKa anjakan’ny Tompo to,\nIzay feno antra, mora fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 4, 'Ny andron’aina mitatao\nNo mahafaly fo izao,\nMamiratra sy manome\nFanantenana lehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 5, 'Ny vavahady mba vohay !\nFinaritra izay mandray,\nFa tonga ilay Mpanjaka soa\nHanao ny asany tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 6, 'Sokafany ny fo izao,\nRy Jeso, mba hidiranao,\nNy fahasoavano anie\nHananay harena be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 7, 'Atolotray, ry Tompo ô !\nMba hahatamana Anao ny fo,\nNy famonjena vitanao\nNo iankinanay izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 2, 'Manenoa, ry farara !\nHorohoroy, ry tany !\nKa miantsoa ny maty,\nFa akaiky ny Mpitsara;\n:,: Indro ! Kristy :,:\nAvy amin-drahona !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 3, 'Ny feony no hampifoha\nNy any am-pasana ;\nNy teniny no hamory\nNy velona rehetra ;\n:,: Samy handray :,:\nNy valin’ny nataony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 4, 'Ny tsy mino hilazany :\n“Mialà voaozona,\nMandehana, mitoera\nAo amin’ny maizina”\n:,: Ry mpanota :,:\nAny ny anjaranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 5, 'Fa ny mino hilazany :\n“Avia, notahina,\nMandrosoa, mankanesa\nHo amin’ny mazava”\n:,: Io, ry mpino :,:\nIndro ! ny anjaranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 1, 'Indro Jeso, mba vohay ;\nMba vohay !\nAvy ho Sakaizanao ;\nMba vohay !\nIlay Malala fahizay,\nSady sitraky ny Ray\nTonga ho anao indray\nKa vohay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 2, 'Indro Jeso, mba vohay ;\nMba vohay !\nTompo tia ka mba hajao ;\nMba vohay !\nHonina aminao etý,\nHo sakaiza hatrerý\nHo anao mandrakizay\nKa vohay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 3, 'Indro Jeso, mba vohay ;\nMba vohay !\nMbola tia ka mba vohay ;\nMba vohay !\nTena tia ka tsy mandao,\nRaha ela sao dia lao ;\nMoa ho lavinao izao\nKa vohay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 4, 'Eo am-baravaranao ;\nMba vohay !\nTsy manoa an-ditra izao;\nMba vohay !\nHe! mandona mora re,\nNa dia Tompo lehibe ;\nEfa niandry ela be\nKa vohay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 1, 'He, tonga ho antsika\nNy Tompo mora fo,\nMpamonjy, Havantsika,\nIlay mamindra fo ;\nAndeha mba hifaly\nIsika, ry zareo !\nNahazo Mpampifaly,\nKa manandrata feo', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 2, 'Hajao ny Tompontsika,\nMpanjaka lehibe,\nAndriamanitsika,\nMpanala rofy be ;\nAvia mba hidera,\nRy mino tsara fo !\nAvia manatera\nNy saotra ao am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 3, 'Ry Tompo Zanahary !\nMihira izahay,\nFa teny mahafaly\nNo tonga ho anay ;\nNy andro famonjena\nNo andro sitrakay ;\nFanahy manantena\nNo ifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 4, 'Ry Tomponay !  mandraisa\nNy haja entinay ;\nTsy volamena anefa,\nFa saotra aterinay ;\nRy Jeso Tompo soa !\nIsaoranay Hianao,\nFa lehibe tokoa\nNy fitiavanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 1, 'O, ry Mpanefa ny toky rehetra,\nMoa ho ela vao tonga ny\nfetra ?\nTena andrasanay ny\nfihavianao ;\n:,: Aza ela, re :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 2, 'He ! izahay fa vahiny mandala,\nIndro manenjika ny fahavalo ;\nKoa henoy re ny senton’ny\nolonao ;\n:,: Aza ela, re :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 3, 'He ! ny voavotrao dia miandry,\nNa izahay na ireo nodimandry ;\nTompo, Tsinjovy ny fasan’ny\nolonao ;\n:,: Aza ela, re :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 4, 'Ary ny zavatra eto an-tany\nSamy miara-misento ihany,\nKa manantena ny mba \nhihavianao ;\n:,: Aza ela, re :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 5, 'Mbola ho ela ve Ilay \nantenaina ?\nTsia, fa akaiky izao ny\nmaraina ;\nKoa andrandrao sy antsoy\nny Tompo hoe :\n:,: Aza ela, re :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 6, '«Tsy mbola ho ela, fa faingana\nAho,\nAry hanafaka ny mitalaho».\n« Eny, avia, faingàna,\nry Tompo ô ! »\n:,: Aza ela, re :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 1, 'Ry Ziona, mihainoa !\nInjay feo mafy mampifoha\nNy ampakarina izao :\nMbola alim-be ihany,\nFa ny fotoana no lany,\nKa avy ny malalanao;\nAntsoiny hianao\nAlao ny jironao\nKa velomy ;\nDia miaingà hivoaka,\nHitsena ny mpampakatra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 2, 'He, Ziona ravoravo,\nFa reny izao ny antso avo\nKa velona ny hobiny :\nIndro, avy Kristy Tompo,\nHanafaka anay mpanompo,\nHalainy hampakariny.\nHe, faly re ny fo !\nAvia Tompo ô !\nO, hosana !\nMihaingo izao hitsena Anao,\nRy Kristy, ny malalanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 3, 'Hira, hoby, fiderana\nAsandratray mba hitsenana\nAnao, Jesosy Tompo ô !\nTsy mba misy mitomany,\nFa mirana izay mankany\nAm-pampakaram-badinao ;\nMahafinaritra ny hiran-danitra\nHaleloia !\nIlaozanay ny trano lay,\nMiakatra ny dianay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 1, 'Hevero, ry mpanota ô,\nNy andro fitsarana !\nHiverina ny Tomponao\nHamaly fahotana ;\nNy lanitra hahorona,\nNy tany dia hodorana,\nFa avy Ilay Mahery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 2, 'Trompetra no ho levona\nHotsofin’ny anjely ;\nHisokatra ny fasana,\nHivory izay niely ;\nNy maty dia hatsangana,\nIsika koa hakarina\nHanatrika ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 3, 'Ny boky hovelarina,\nFa io no manambara\nNy asa izay miafina,\nHandraisana anjara ;\nKa aza mba mihevitra\nHahazo mihodivitra\nFa fantatry ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 4, 'Ry Tompo ô ! hazoninay\nNy tenim-panekena,\nKa mba arovy izahay\nHitana famonjena ;\nNy ranao sy Fanahinao\nNo tokinay raha avy izao\nNy andro fitsarana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 1, 'Mba jereo ny Tompontsika\nAvy ao an-danitra,\nIndro, avy amin-kery\nAo amin’ny rahona\n:,: Haleloia :,:\nAmbarao ny heriny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 2, 'Izay rehetra fahavalo\nEfa resiny tokoa ;\nMifalia ry anjely !\nMihobia, ry olona !\n:,: Haleloia :,:\nAmbarao ny lazany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 3, 'Fony mbola teto Izy\nSatro-tsilo nentiny ;\nIzao ny satro-bolamena\nMendrika ny lohany\n:,: Haleloia :,:\nAmbarao ny derany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 4, 'Voaeso mafy Izy ,\nBe ny fahoriany ;\nFa izao, asandratara Izy,\nAmbarao ny hajany !\n:,: Haleloia :,:\nHe, ny voninahiny !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 1, 'Mifohaza, ry matory ,\nFa re ny feon’ny Mpamory\nMiantso izao tontolo izao !\nMiangà, ry vovontany,\nFa he ! Jesosy avy any\nAn-danitra hitsara anao.\nRy mbola velona !\nRy ao am-pasana !\nMitsangàna !\nMiandrandra ny lanitra,\nFa ny atý dia tapitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 2, 'Kristy Tompo avy any,\nKa dia mangovitra ny tany\nFa be ny voninahiny.\nNa ny ratsy na ny tsara\nHanatrika ilay Mpitsara,\nMiandry ny anjarany\nZarainy roa izao\nNy tafavory ao ;\nKa ny tsara\nHoraisiny ao Aminy,\nNy ratsy dia ariany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 3, 'Jeso ô ! izahay mifaly,\nNa dia be ny mampijaly\nSy mahaketraka atý ;\nHianao mahay manova\nNy fahorianay ho lova\nMahafinaritra erý.\nRy fasa-maizina ô !\nRy fahavaloko !\nAiza moa ny herinao,\nny sabatrao ?\nJesosy no Mpandresy izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 1, 'Raha tonga anio ny Tompo,\nAmin-kery lehibe,\nKa arahin’ny mpanompo \nSy anjely marobe ;\nMoa ho faly hianao\nKa hidera ny Mpamonjy\nIzay nanavotra aina anao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 2, 'He ! ho simba sy ho rava\nIzao rehetra àry izao,\nKa hifankahita tava\nNy natao sy ny Mpanao,\nMoa ho faly ….sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 3, 'Maso maro no mijery\nIlay nijaly fahizay,\nFa ny mino Azy  ihany\nNo ho faly rahatrizay :\nMoa ho faly ….sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 4, 'Rehefa tonga re ny Tompo\nMba hitsara izay natao,\nHitomany ny tsy mino,\nFa hanatrika Azy ao :\nMoa ho faly ….sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 5, 'O ! avia re, ry Tompo !\nMba hanjaka aminay,\nHahatonga anay mpanompo\nHiara-paly Aminao :\nO! avia, ô ! avia,\nFa ny olonao rehetra\nDia maniry indrindra Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 1, 'Efa akaiky Jeso Tompo\nIlay Mpamonjinao,\nIndro, tamy Kristy Tompo,\nAvy ho anao ;\nDia tano mafy tsara\nIzay anananao ;\nLova soa, tsara fara,\nIndro, ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 2, 'Efa akaiky ny Mpamonjy\nTa-handray anao ;\nJeso Kristy mahavonjy\nNo sakaizanao ;\nDia tano ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 3, 'Efa akaiky ny Mpitsara,\nIzay hitsara anao ;\nJeso Kristy raiso tsara\nMba ho havanao ;\nDia tano ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 4, 'Aza mba mangataka andro\nHibebahanao ;\nHe, mazava anio ny andro\nMba hidiranao :\nDia tano ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (53, 1, 'Miposaka ny vonjy,\nEndrey ny hafaliana !\nJesosy no Mpamonjy,\nAndriam-piadanana\nIlay Zanaky ny Avo\nDia nidina tatý\nHahatonga antsika lavo\nHo Zanaka arý,\nManangana olom-bery\nHandova lanitra,\nKa dera, laza, hery\nHo an’Andriamanitra !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (54, 1, 'Jesosy Mpamonjy,\nIlay Tompon’ny aina, no hita\nNidina teto an-tany\nhamonjy antsika :\nO, mba derao !\nIzy Mpanavotra anao,\nKa mifalia, ry mino !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (54, 2, 'Indro ny tenin’anjely \nmilaza ny soa !\nTeraka Jeso Mpamonjy\nantsika tokoa :\nO, mba derao !\nTonga ny andro vaovao,\nAndron’ny Filazantsara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (54, 3, 'Dia iventesonao hira\nvaovao re, izany !\nTonga Jesosy hamonjy\nizay mitomany :\nO, mba derao !\nAfaka isika izao !\nIzy Mpisolo antsika !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (54, 4, 'Tonga ny Tompon’ny tompo\nhisolo ny ory,\nKa mifohaza, ry olona\nmbola matory :\nO, mba derao !\nAmin’ny feo vaovao,\nJeso, izay Tompo mahery !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (55, 1, 'Ny andron’ny fahasoavana\nMiposaka ho antsika\nManerana izao tontolo izao,\nNy tarany efa hita,\nKa ravo ny olombelona,\nFa lasa ny alintsika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (55, 2, 'Niova terý ny alina,\nFa teraka ny Mpamonjy,\nMamiratra re ny lanitra,\nMiseho izao ny vonjy,\nKa lasa tokoa ny aizina,\nMihoby ny voavonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (55, 3, 'Na dia ho nahay nihira re\nNy hazo an-ala rehetra,\nKa afa-mikalo ny ravina\nHidera ny Avo indrindra,\nTsy ampy ho fanandratana\nNy Tompon’ny ain-drehetra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (55, 4, 'Ny feon’ny olonao izao\nMisandratra, ry Jehovah,\nHidera ny fitiavanao\nSy ny indrafonao soa\nNiaro ny fiangonanao\nHatramin’ny fahagola.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (55, 5, 'Ho an’ny tanindrazanay\nIzahay re, ry Ray Tsitoha,\nKa hiditra ao an-tranon-dRay\nNomanin’Ilay Mpialoha ;\nNy olonao dia ho tafaray\nHidera Anao tokoa !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 8, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 1, 'Manatona Anao izao,\nRy Jeso, ny madinikao,\nAmpandrosoy ny dianay,\nFa ta-hahita Anao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 2, 'Hidera ny Anaranao\nNo zava-tianay hatao ;\nMiravoravo izahay\nFa teraka ny Tomponay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 3, 'Ny lanitra nilaozanao,\nHanavotra ny olonao,\nHo zanak’Andriamanitra\nHifaly ao an-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 4, 'Tsy takatray heverina\nNy fitiavan-dalina,\nIzay nitiavanao anay,\nKa dia misaotra Anao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 5, 'Tariho, Tompo, izahay,\nAmbinonao ny dianay,\nFa samy ta-handray Anao\nIzahay mivory eto izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 6, 'O, ry Sakaiza maminay,\nMba te-ho olonao izahay\nHipetraka eo anilanao,\nFa mamy ny fitianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 7, 'Raha manana Anao izahay,\nTsy hisy hatahoranay,\nFa zaza voamarikao\nHo isan’ny arovanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (56, 8, 'Raha ao an-danitra vaovao,\nIzahay sy ny anjelinao\nHisaotra sy hidera Anao\nHanatrika ny tavanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (57, 1, 'Izao Noely mamy izao\nNo ihobiantsika,\nFa noresen’ny aim-baovao\nNy fahafatesantsika :\nNy Zanak’Andriamanitra,\nIzay Tompom-boninahitra,\nNandao ny lapan-dRainy,\nNateraka ho olona\nHamonjy sy hamelona\nAntsika sombin’ainy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (57, 2, 'Velomy ny fisaorana,\nNa kely monja aza,\nKa aoka izay navotana\nHiara-hankalaza\nNy Zanak’Andriamanitra,\nNiala tao an-danitra\nHanafaka alahelo ;\nRy voavonjy ô ! derao Jesosy\nIlay Mpanjakanao ;\nHe, gaina izao ny helo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (57, 3, 'Izao Andriamanitra\nDia faly amintsika :\nJesosy, Tompon-danitra,\nNo tonga namantsika ;\nHo re hatrany lavitra\nFa foin’Andriamanitra\nIlay sitraky ny fony\nHitondra fahoriana,\nHiari-pijaliana\nHo solon’ny mpikomy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (57, 4, 'Na dia tratry ny manjo,\nIzay mampiferin’aina,\nIzaho tsy hamoy fo,\nFa manna-kantenaina ;\nJesosy dia Zokiko,\nKa tsy hohadinoiko,\nFa Izy tia ahy ;\nNy tenim-piadanany\nNo hery anohanany\nNy foko, izay manahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (57, 5, 'Haleloia, fa tapitra\nNy fahoriantsika !\nDerao Andriamanitra,\nIzay efa tia antsika !\nO, ry fanahiko, derao\nJesosy, Izay Mpamonjinao,\nHarena tsy ho lany,\nHaleloia, Haleloia !\nFa azo ny Mpanavotra,\nAnao ny fanjakany !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (58, 1, 'Alina masina\nTonga ny Mpanavotra ;\nIndro teraka ao Betlehema\nNy Mpanjakan’ny Jerosalema,\n:,: Izay Mpamonjy anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (58, 2, 'Alina masina\nNy anjely midina\nKa miara-mihoby hoe :\nIndro ny fiadanam-be,\n:,: Jeso teraka izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (58, 3, 'Alina masina\nNisehoan’ny Zanaka ;\nNy mazava niposaka,\nKa ny tahotra afaka ;\n:,: Mifalia izao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (59, 1, 'O! mihobia, fa teraka\nJesosy Tompo, Zanaka,\nMesia nantenaina,\nMpamonjy sitraky ny Ray\nHo avotra mandrakizay,\nKa mendri-koderaina !\nRenao izao\nNy anjely mampiely\nTeny soa :\nKristy teraka tokoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (59, 2, 'He ! tonga ny Mpanavotra\nHanafaka ny tahotra\nMamatotra ny tany,\nMitondra fiadanana,\nKa tena mampihavana\nNy lanitra aman-tany.\nIndro tonga\nNy Mpamonjy mahavonjy\nNy mpanota,\nKa manaisotra ny ota.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (59, 3, 'Omeo voninahitra\nNy Ray, Andriamanitra,\nIzay Tompom-pamonjena !\nNy Zanaka no raisonao,\nFa fananganana anao\nNy lova dia homena,\nAmen, Amen !\nTeny soa mamy koa,\nIsika lavo koa,\nTonga zanaky ny Avo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (60, 1, 'He, faly ny fanahiko,\nFa avy Ilay Malalako,\nDia Jeso tonga zaza,\nKa mety ho Mpamonjiko,\nNa dia tsy mendrika aza\nIzaho, ry Jesosy ô !\n:,: Raiso ho Anao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (60, 2, 'Ry Jeso Zazakely ô,\nIriko mafy Hianao,\nAvia re, ry Zaza,\nNy foko itoeranao\nNa dia ratsy aza\nKa tsy mba mendrika Anao,\n:,: Ry Jesosy ô :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (60, 3, 'Endrey ny fitiavanao,\nRy Ray, namoy ny Zanakao ;\nIzahay, izay fahavalo\nKa ivesaran-keloka,\nAvelanao hivalo\nKa hiditra ao an-danitra.\n:,: Enga ka ho ao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (60, 4, 'Endrey ny fifaliana\nTsy tratry ny fisainana\nAtaon’ireo anjely\nManerana ny lanitra !\nFa any ny Noely\nHitohy tsy ho tapitra,\n:,: Enga ka ho ao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (61, 1, 'Tany Betlehema fahiny\nNisy Zaza teraka ;\nTonga toy ny mpivahiny,\nNefa tena Zanaka,\nIzy no Jesosinay,\nKa mba raiso ho anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (61, 2, 'Ao ny lapa feno haja\nNisy Azy fahizay ;\nSaotra, dera aman-daza\nTeo anatrehan-dRay,\nIzany no nilaozany\nHamonjena ny atý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (61, 3, 'Zaza tsy nanota izy,\nNefa Zaza toa anao ;\nKoa raisinao rankizy,\nMba ho tena havanao ;\nAry mba arahonao\nIzay fanaony hitanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (61, 4, 'Raha hianao tomany,\nIzy hampangina anao,\nIzy koa hampahamamy\nIzay fifalianao ;\nMatokia Hianao,\nIzy no Sakaizanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (61, 5, 'Nefa mba henoy, ry zaza !\nAo an-danitra Izy izao,\nFa ho avy  indray hitsara\nAn’izao tontolo izao ;\nKoa mba fidionao\nIzy ho Mpamonjinao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 9, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 1, 'He, teraka any Betlehema,\nAo Betlehema,\nNy Tompon’i Jerosalema.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 2, 'Ny vilona :,: nandriany :,:\nNihoby ny anjeliny.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 3, 'Ny magy dia :,: nanatitra :,:\nNy miora sy ny hanitra\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 4, 'Voaisotra :,: ny helokao :,:\nFa tonga ny Mpanavotrao.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 5, 'Isika dia :,: noraisiny :,:\nHiombon-dova aminy.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 6, 'Isika koa :,: hianatra :,:\nNy fideran’ny lanitra.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 7, 'Isika ao :,: an-danitra :,:\nHo velona finaritra.\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 8, 'Ho naman’ny :,: anjelinao :,:\nIzahay, ry Jeso Tompo ô !\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (62, 9, 'Hidera ny :,: Anaranao :,:\nManatrika ny tavanao.\n:,: Haleloia ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (63, 1, 'Tany finaritra,\nTarafin-danitra,\nIty tanin’ny mpivahiny ity\nKa fihobiana\nNo andehanana\nMankany Paradisa arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (63, 2, 'Andro mihelina\nIlay sasakalina,\nNefa tsarovan’ny olona\nSatria ny lanitra\nTsy lefy hanitra ;\nMamelon-kira velona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (63, 3, 'Anjely marobe\nNo indray mihira hoe :\n« O ! mifalia ry reraka !\nFa fiadanana\nNo andehanana ;\nKristy Mpamonjy teraka ! »', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (64, 1, 'Injany hiran-danitra\nMisasak’alina ;\nLokanga volamena e,\nTendren’ny masina !\nNy tany dia nangina re,\nNandre ny hira vao :\nHo an’ny olona anie\nNy fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (64, 2, 'Ny hiran’ny anjely re\nTsy mba mitsahatra.\nAry ambony rahona\nIreo masina\nMitendry ny lokangany\nAm-pihobiana !\nHo an’ny olona anie \nNy fiadanana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (64, 3, 'O ! ry mivesatra entana,\nRy malahelo ô !\nMba andrandrao ny lanitra,\nHenoy ny hira vao !\nNy hiran’ny anjely re\nDia mampiadam-po.\nO ! mitsahara hianao\nHihaino azy izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (64, 4, 'Ny andron’olombelona,\nHe fandalovana !\nKanefa hisy tany vao\nIzay haharitra.\nEndrey ny fiadanana\nArý rahatrizay !\nNy hiran’ny anjely\nHiredona indray !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (65, 1, 'Ry Betlehema kely ô !\nSondrian-tory ve ?\nKanefa injay miseho ao\nNy hazavana be.\nNy toky sy ny tahotra\nMihaona ankehitrio,\nFa Kristy Ilay Mpanavotra\nNo teraka anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (65, 2, 'Ry kintana maraina ô !\nFitarik’andro soa.\nTorio fa tonga aty izao\nIlay Mpanjaka soa !\nKa he ! ny voninahitra\nHo an’Andriamanitra !\nFiadanana tsy tapitra\nHo an’ny olona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (65, 3, 'Endrey, mangina fatratra\nNy fampidinana\nNy fitahian-danitra\nAm-po ny olona !\nTsy misy tabataba re,\nFa horakoraka.\nKanefa Kristy tonga anie\nAm-po mangoraka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (65, 4, 'Rankizy hendry sy madio\nTsarovy Kristy izao\nFa izy tonga atý anio,\nMba ho Mpamonjinao.\nNa misy aza ny manjo.\nIzao Noely izao\nNo saino fatratra ao am-po\nHampionona anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (65, 5, 'Ry Zaza Masina sy soa,\nRy Zanak’i Maria,\nRy Tomponay, Mpamonjy\nkoa,\nMidina re atý.\nNy feon’ny lanitra no re,\nIndray Noely izay,\nNitondra teny soa hoe :\nHianao no avotray !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (66, 1, 'Ry olona ô, avia !\nRy tany, mifalia !\nMba henoy ny teninay ;\nTeraka ny Tomponay ;\nAo an-tranon’omby ao,\nHandeha, mitsaoka Azy izao\nTeraka ny Tompo !\nTeraka Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (66, 2, 'Ry olona ô, avia !\nRy tany mifalia !\nMba henoy, ry havanay !\nMisy zaza hitanay\nZanak’Andriamanitra\nHamoha indray ny lanitra.\nTeraka ny Tompo !\nTeraka Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (66, 3, 'Ry kristiana ô, hobio,\nFa afaka hianao anio;\nResy izao ny rafinao,\nAfaka ota hianao,\nFahasoavan-dehibe\nNo ho anjaranao anie ;\nTeraka ny Tompo !\nTeraka Jeso.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (67, 1, 'Avia ry mino !\nHifaly sy hihoby,\nAvia, avia hankany\nBetlehema :\nTeraka Jeso,\nTompon’ny anjely ;\nAvia hitsaoka Azy (in-3)\nIzay Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (67, 2, 'Tompon’ny tompo,\nAndriananahary,\nNefa mba zana-behivavy koa ;\nZanak’i Maria,\nNefa Zanahary ;\nAvia hitsaoka Azy (in-3)\nIzay Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (67, 3, 'Derao, ry anjely,\nMihobia mafy,\nRy mponina an-danitra ao\nAmbony ao !\nDera sy laza\nAtolory Azy ;\nAvia hitsaoka Azy (in-3)\nIzay Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (67, 4, 'He, arahaba,\nTonga aty an-tany\nNy Tompo, Mpamonjy sy\nMpanjaka koa,\nTenin’ny Rainy\nVoatafy nofo,\nAvia hitsaoka Azy (in-3)\nIzay Tomponao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (68, 1, 'Re tokoa ny feon’anjely,\nIrak’Andriamanitra ;\nHe, ny teny mahafaly\nAvy ao an-danitra.\nEntiny ny teny tsara,\nHo ety an-tany izao ;\nVoninahitra aman-dera,\nTompo, no omena Anao !\n(in-3)\nVoninahitra aman-dera,\nTompo no omena Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (68, 2, 'Teraka any Betlehema\nIlay Mpanjaka manan-jo\nKa misatro-diadema,\nVoninahi-tsy ho lo,\nMisy ‘zao ety an-tany\nFihavanana tokoa,\nHafalian-tsy holany\nFitahia-mahasoa. (in-3)\nHafalian-tsy ho lany,\nFitahia-mahasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (68, 3, 'Be ny lazanao, ry Tompo !\nTsara ny Anaranao,\nZovy re no tsy hanompo ?\nIza no handà Anao ?\nHasina sy arahaba\nNo aterina aminao ;\nFanjakana tsy ho rava,\nRy Mpanjaka ! no Anao (in-3)\nFanjakana tsy ho rava\nRy Mpanjaka ! no Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (69, 1, 'Zaza masina sy soa,\nNefa ory Hianao,\nKa nanetry tena koa\nSy nandao ny lapanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (69, 2, 'Teraka ory sy nahantra,\nTrano tsy nandrianao,\nFa tsy nisy izay niantra,\nBe ny fahorianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (69, 3, 'Fihinanam-bilon’omby\nNo natao fandrianao,\nFa ny trano be tsy omby,\nNisy olo-maro tao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (69, 4, 'Nofonosin-damban-jaza,\nTonga zazabodo re,\nNefa Tompo manan-daza\nSy Mpanjaka lehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (69, 5, 'Efa halan’olo-maro,\nNefa kosa tiako ;\nDia avia, ry Mpamonjy !\nHonina ato amiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (70, 1, 'Iza moa io Zazakely\n:,: Ao Betlehema :,:\nAnkalazain’ny anjely\nMisasaka alim-be ?\nInjao ! Mihira avokoa\nRy mponina ao an-danitra,\nKa iza re io zaza soa ?\nMoa zanak’Andriamanitra\nIo no Mpanjaka voatendry,\nSady mahery no hendry\nZanak’Andriamanitsika,\nKoa hobio sy ifalio !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (70, 2, 'Izy no Jeso Mpanjaka,\n:,: Ka mihobia ! :,:\nIzy mamory sy maka,\nAvia, mihobia !\nOmeo voninahitra,\nKa mitehafa tanana ;\nHobio ry tendrombohitra\nSy lohasaha mavana !\nIo no Mpanjaka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (70, 3, 'Izy no Tompo nahary,\n:,: Ka maneke ! :,:\nZanaky ny Zanahary,\nKa dia maneke !\nRy hazo ao an-ala ô !\nSamia re manandra-peo ;\nRy voninkazo tsara ô !\nAtero fofo-manitra !\nIo no Mpanjaka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (70, 4, 'Efitra sy tany foana !\n:,: O ! mihirà ! :,:\nLemaka ary havoana !\nFalia, mihirà ;\nRy ranomasin-dehibe,\nRy voronkely tsara feo,\nIzao rehetra marobe,\nAvia mankalaza re !\nIo Mpanjaka ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (71, 1, 'Mihainoa! Mihainoa!\nNy feon''anjely sambatra,\nMilaza fa vao teraka\nNy Zanak''Andriamanitra,\nMihainoa izao!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (71, 2, 'Mifalia! Mifalia!\nRy zanak''olombelona,\nFa tonga Ilay Mpanavotra\nAntsika izay nifatotra,\nMifalia izao!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (71, 3, 'Mba omeo! Mba omeo!\nNy fonao fa angatahiny;\nNy tenany natolony\nAnao izay malalany;\nMba omeo izao!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (71, 4, 'Matokia! Matokia!\nKa manaraha Azy izao;\nIlay Mpamonjy be fitia\nNanetry tena ho anao;\nMatokia izao!', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (72, 1, 'Teraka Jesosy Tomponay,\nTaitra ny olona fahizay,\nNisy anjely nihira teo,\nMahafinaritra, tsara feo,\nSady milaza soa aminay,\nDia hihira anio izahay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (72, 2, 'Taona fahasambaranay,\nVolana fifalianay,\nAndro firavoravoana Anao,\nJesosy, Zanaka Masina ô!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (72, 3, 'Teraka Jesosy be fitia,\nIndro kintana tonga teo,\nAvy hanoro ny Zaza soa:\nFaly ny Magy, fa Io tokoa,\nIo no Mpanjaka mandrakizay\nDia nanaja androtr''izay:\nTaona fahasambaranay, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (72, 4, 'Teraka Jesosy Zaza soa,\nZaza Malalan''ny Ray tokoa;\nTonga hamonjy io Zaza io,\nTeraka ao Betlehema anio;\nEnto ''zahay, Fanahy, hankao.\nAndronay mahafaly izao:\nTaona fahasambaranay, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (72, 5, 'Sambatra manana Anao ho Ray,\nFaly sy ravo tokoa izahay;\nJesosy Tompo nirahinao,\nTonga ho nofo ny Zanakao;\nTeraka ho Mpanavotra anay\nAvy hifaly eto ''zahay:\nTaona fahasambaranay, sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (73, 1, 'Indro Zazakely\nZanak''i Maria,\nTompon''ny anjely,\nNefa nankaty,\nIo no Zazalahy,\nZanaky ny Ray.\nTonga mba hitahy:\nIzy no anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (73, 2, 'Mihirà, ry tany,\nSy ny mponina eo.\nHafalian-tsy lany,\nManandrata feo!\nJeso no deraina,\nFa Mpanjaka soa;\nHe! Ny fanjakany\nLehibe tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (73, 3, 'Zaza nantenaina\nFony ela be,\nSady Tompon''aina\nManan-kery be,\nMihirà, ry tany!\nEny, mifalia:\nHoy ny mpaminany,\nIzy no Mesia:\nMihirà, ry tany, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (73, 4, 'Jeso Zaza hendry,\nMasina sy to,\nTompo voatendry,\nMpanadio ny fo,\nAoka mba hijery\nZao tontolo izao,\nZaza manan-kery,\nIndro, mba hajao:\nMihirà, ry tany, sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (74, 1, 'Tany Betlehema fahiny,\nTao an-tranon''omby re;\nNisy Zazakely soa,\nTampofoan-dreniny,\nJeso Kristy Zaza io,\nZanak''i Maria madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (74, 2, 'Nahafoy ny Rainy Izy\nSy nandao ny lanitra,\nTsy nandà ny trano ratsy,\nNefa Andriamanitra:\nToe-pahantrana re\nAn''ny Tompo lehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (74, 3, 'Fony Izy mbola Zaza\nDia nanaiky sy nanoa,\nAry tia reny Izy,\nSy nanetry tena koa,\nZaza Kristiana e!\nManaraha Azy re!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (74, 4, 'Ka ho hitantsika Izy\nNoho ny fitiavany:\nFa ''Lay Zazakely tsara\nNo mba Tompon-danitra,\nKa isika zanany\nDia hankany aminy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (74, 5, 'Nefa tsy mba tranon''omby.\nItoerany izao,\nFa nankao an-danitra Izy.\nSy mitafy laza ao;\nKa ho sambatra any koa\nTahaka Azy ny zaza soa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (75, 1, 'Andro malaza sady soa tokoa\n''Lay andro ''zay nisehoan''i Jeso:\n''Zay nantenain''ny mpaminany koa\nMba hampiseho fihavanana,', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (75, 2, 'Mifalia sy mihobia mafy,\nEfa tonga ny Mpanavotrao;\nAvia, ka miderà,\nDerao io Zaza io,\n;,: Hobio, hobio,\nFa Tompontsika io.:,;', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (75, 3, 'Anjely maro no nihoby tao;\nMpiandry ondry nitsaoko Azy koa.\nNy olon-kendry koa nanasina Azy,\nAndeha mba hankalaza Azy izao:\nMifalia, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (75, 4, 'Dera sy haja no atolotray\nAnao, ry Jeso ''zay teraka anie:\nKa mba mandraisa ny fanatitray\nHo zava-mani-pofona tokoa:\nMifalia, sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (76, 1, 'Miderà anio, ry mino,\nNy Mpanavotra anareo !\nManandrata feo hidera\nAn’i Kristy, Tompo soa ;\nZanaky ny vehivavy,\nNefa Andriananahary ;\nTeraka ho olona,\nMba hanavotra anareo ;\nTeraka any Betlehema\n‘Lay Mpanavotra anareo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (76, 2, 'Taitra ny mpiandry ondry,\nAry koa nivadi-po,\nTamin’ny anjely maro\nTonga tany aminy.\nFa isika tsy ho taitra,\nFa ho faly sy ho ravo ;\nSitraky ny Tompo soa\nNidina nankatý ;\nTeraka any Betlehema\nJeso Kristy ,Tompo soa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (76, 3, 'Aza taitra, matokia,\nManatòna, mihainoa,\nMazotoa, ka avia,\nManeke ny Tompo soa,\nArahaba, Tomponay ô !\nTompom-pihavananay,\nTeraka ho Tomponay ;\nTompon’ny fiainanay ;\nArahaba, Tomponay !\nRy Mpanavo-dehibe !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (77, 1, 'Tonga indray re ny Noely,\nAndro tsara sady soa ;\nJeso Tompon’ny anjely,\nNefa nietry tena koa.\nIndro tonga Zazakely,\nHo sakaizanay tokoa,\nSy hamory  ‘zay miely,\nHody amin-dRainy koa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (77, 2, 'He, ny fonay ravoravo,\nRy Noely maminay,\nFa ny Zanaky ny Avo,\nNo nomeny ho anay,\nMba hanarina ny lavo,\nSady ho fiainanay ;\nFeno sy mitafotafo\n‘Zany hasambaranay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (77, 3, 'O ! ry androm-pamonjena,\nArahaba, hianao !\nOlo-maro sesehena\nNo mifaly aminao,\nAvy koa mba hitsena\n‘Lay Mpamonjy soa ’zahay,\nKa ny fonay no homena\nAn’i Kristy Havanay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (78, 1, 'Be ny voninahitrao,\nJeso Kristy Tompo !\nLanitra nonenanao,\nMaro no nanompo,\nBe ny mahasambatra,\nFaly re ny fonao ;\nNanam-boninahitra\nTao an-dapan-dRainao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (78, 2, 'Fa ny voninahitra\nEfa nialanao ;\nNy haren’ny lanitra\nTsy mba nojerenao,\nTeraka ho olona\nHianao nahery,\nKa avy mba hanavotra\nNy mpanota very.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (78, 3, 'Eny, tonga Hianao\nHonina amin-tany,\nHahazoan ‘ny olonao\nSoa tsy ho lany.\nSarotra ny lalanao,\nBe ny zava-mafy,\nLoza no nanjo Anao,\nMaro no nandrafy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (78, 4, 'Saotra no aterinay,\nVavaka aman-dera,\nRavo ny fanahinay,\nKa avy mba hihira,\nVitanao ny asanao\nNamonjenao aina,\nKa indro ny Anaranao\nVelona ao an-tsaina !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (79, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (79, 2, 'Sambasamba, Zanahary\nTamy ny nampidinanao\nNy Zanakao, malalanao,\nMba hisolo ny helokay.\n:,: Tamy ny nampidinanao\nNy Zanakao malalanao,\nMba hisolo ny helokay :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (80, 1, 'He, ny hoby ao an-danitra,\nFa Mpamonjy re no teraka,\nIo no Zanak’Andriamanitra,\n‘Lay Tanteraka.\nMifalia, mihobia,\nFa tonga ho anao ny Tompo\ntia\nKa mba hirao sy lazao\nNy famonjena azonao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (80, 2, 'Zava-mahagaga fatratra\nNy fitiavan’Andriamanitra,\nNy mpanota tafasaraka\nTonga sambatra !\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (80, 3, 'O ! ry tany izay asandratra,\nKa vangian’ny Tompon-\ndanitra,\nInona ary no atolotra\nNy Mpanavotra ?\nMifalia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (81, 1, 'Tao an-tsahan’i Betlehema,\n‘Reo mpiandry ny ondry re,\nNo vangian’ny Serafima\nSy anjely maro be.\nVoninahitra ho an’\nAndriamanitra,\nHo an’Andriamanitra !\nVoninahitra ho an’\nAndriamanitra ,\nAmin’ny avo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (81, 2, '‘Njany ny hiran’ny anjely,\n‘Zay niredona tokoa\nIndro tonga Imanoely\nZaza masina sy soa.\nVoninahitra, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (81, 3, 'Nofo notafin’ny Teny,\nLanitra nilaozany ;\n‘Ndao isika izao hankany,\nBe tokoa ny lazany.\nVoninahitra, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (81, 4, 'Voninahitra ao ambony\nHo an’Andriamanitra,\nKa ny hira no redony\nSolon-java-manitra.\nVoninahitra, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (82, 1, 'He, Zaza mahasambatra\nNo omena ho antsika !\nVirjina no niteraka\nIlay Andriantsika\nIzay Zanak’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (82, 2, 'Nandao ny voninahitra\nHanavotra ny very ;\nKa saotra no aterinay,\nRy Jeso Kristy Tomponay,\nMpamonjy manan-kery !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (83, 1, 'Ry Jeso ô ! Hianao no\nmahavonjy\nIzay rehetra hiankina aminao ;\nTonga tatý hitady sy hamonjy\nIzay mpanota very Hianao.\nHe ! ry mpanota very atý !\nÔ ! mankanesa ho any aminy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (83, 2, 'Na dia Zana-Janahary aza,\nNa dia Tompo aza Hianao,\nTsy mba nitandro hery\naman-daza,\nTonga olona ory Hianao\nHe ! ry mpanota ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (83, 3, 'Tamin’ny dianao nanaovan-\ntsoa,\nTsy nahalala sasatra Hianao ;\nFa Hianao tsy nanana tokoa\n‘Zay nametrahanao ny lohanao\nHe ! ry mpanota ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (83, 4, 'Dia nolatsaina sy nalam-\nbaraka\nSy novonoin’olon-dozabe,\nKa dia maty Hianao Mpanjaka\nHo fanavotana olo-maro-be\nHe ! ry mpanota ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (83, 5, 'Ny fasanao nambenan’ny\nmahery,\nNefa tsy ela tao ny\nTomponay,\nVelona indray hamonjy\nolom-bery,\nVelon-kanao fifonan-ko anay.\nHe ! ry mpanota ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (84, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (84, 2, 'He ! be ny hasoavana,\nFa tonga ny fotoana,\nKa Kristy Tompo teraka\nHamonjy sy hanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (84, 3, 'Ny fahotan’ny olona\nDia lova tamin-drazana :\nIsika trany avokoa,\nKa very re ny aina soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (84, 4, 'Inty ny zaza masina e,\nIzay neken’ny Rainy hoe :\n“Ny Zanako Malalako,\n‘Zay mamiko sy sitrakao”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (84, 5, 'Ny dera eran-danitra\nOmeo an’Andriamanitra\n‘Zay nahafoy ny Zanany\nHanavotra ny olony.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (85, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (85, 2, 'Avelao isika\nIzay te ho tia\nNy Mpanavotra antsika\nHanaraka Azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (85, 3, 'Aoka ho fantatsika\nNy fitiavany\nSy ny anton’ny asany\nHiderantsika azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (85, 4, 'Be ny fiantrany,\nNijery ny ory,\nTratran-dRainy nialany\nHo teraka olona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (85, 5, 'Be ny fiantrany,\nNijaly ka maty\nHamonjeny ny mpanota,\nKa Izy hisaorana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (86, 1, 'Jereo ny Mpamonjy\nIzay miakatra\nHo ao Jerosalema\nHanao ny avotra ;\nNa dia be ny ratsy\nMikendry Azy ao,\nNy mba hamonjy azy\nNo tiany hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (86, 2, 'Tsy biby manan-daza\nNo nitaingenany\nTsy ny hitady laza\nNo nihaviany,\nKanefa manan-kery\nHandresy ho anao.\nNy ratrany irery\nNo aina ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (86, 3, 'Ilay voasatro-tsilo\nNoho ny helokao\nDia mety sy maniry\nMba ho Mpanjakanao\nNy rany sarobidy\nOmeny ho anao;\nNy lanitra nihidy\nMisokatra aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (86, 4, 'Ampio mba ho haiko\nHatolotra Anao,\nJesosy ô ny aiko\nHo voninahitrao ;\nVelariko ny foko\nEo anoloanao,\nTsy misy tsy ho foiko\nMba hodiovinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (86, 5, 'Hosana ! ry Mpamonjy,\nHiraiko ho Anao\nFa mamin’ny tra-bonjy\nNy fiderana Anao.\nHosana, Tompo tsara !\nSoava dia izao !\nHandresy ny devoly\nNo anton-dianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 1, 'Nitomany ny Mpamonjy\nFa tsy nety ho tra-bonjy\nIsraely olony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 2, 'Izy dia nanambara\nTaminy ny teny tsara\nFeno fitiavana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 3, 'Ka ny jamba, kilemaina\nMoana sy very saina,\nDia nositraniny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 4, 'He ! ny soa efa vita\nNefa nody tsy nahita\nNy ankamaroany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 5, 'Ary lotika ny fony,\nKoa halany ‘lay ony\nFeno rano velona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 6, 'Ry Mpamonjiko malala !\nAoka aho hahalala\nNy fitomanianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (87, 7, 'Ary hahalala koa\nFa ny soa mahavoa\nRaha tsy ho raisina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (88, 1, '‘Lay ora tao Getsemane\nTsaroako tsara izao,\nFa Tompo tia sy lehibe\nNo ory loatra tao :\nTsaroako ‘zao Getsemane,\n‘Zay niaretanao,\nTsy haiko hadinoina re\n‘Lay fahoriana tao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (88, 2, 'Ny mpianatrao malalanao\nNatory avokoa ;\nNafoin’izao rehetra izao\nJesosy Tompo soa.\nTsaroako ‘zao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (88, 3, 'Nanjary ra ny dinitrao\nNoho ny helokay ;\nFadiranovana Hianao\nNisolo voina anay.\nTsaroako ‘zao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (88, 4, 'Soraty ao am-poko re,\nHo sary velona ao,\nNy sarin’i Getsemane\n‘Zay niaretanao.\nTsaroako ‘zao ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 1, 'Ilay ora tao Getsemane\nTsy mba hadino re,\nRaha Hianao no tsembo-dra,\nRy Tompon’avotra\n:,: Tsahiviko :,:\nNy fahorianao,\nRaha tsinjoko ny ady be,\nIlay tao Getsemane.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 2, 'Nafoin-dRay irery tao,\nNilaozan-kavanao\nHandraisana anay indray\nHo zanaky ny Ray.\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 3, 'Nandroso mba hijaly tao,\nRy Tompo, Hianao ;\nFadiranovana sy may\nHanavotra anay.\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 4, 'Fa vinitra Jehovah Ray\nNoho ny ditranay ;\nNinianay nolavina\nNy didy masina.\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 5, 'Ka Hianao nasiana\nHo solo-voina ;\nNy dinitrao, ry Tomponay,\nDia valin’otanay.\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 6, 'Fa sitrakao ho afaka\nNy olo-meloka;\nEndrey ny fitiavanao\nAnay navotanao !\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (89, 7, 'Raha mila hangatsia-po\nIzaho, Tompo ô,\nMba ampisaino aho re\nNy tao Getsemane !\n:,: Tsahiviko ..sns :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (90, 1, 'He, mijaly ny Mpamonjy\nAo Getsemane izao !\nO, ry te-ho voavonjy,\nManaraha Azy ao ;\nIndro ny Mpanavotrao,\nFa mitady ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (90, 2, 'Ory ao ny Tompon’aina\nMafy re ny adiny\nKa misento mitaraina,\nToy ny ra ny dininy.\nRy mpanota mijere,\nMihevera ka hendre !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (90, 3, 'Indro tonga ny anjely,\nMba hampahatanjaka\nAn’i Jeso, Izay nijaly\nAmim-panaintainana.\nRy Mpamonjy tia ô !\nMamindrà fo amiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (91, 1, 'Tsy haiko ny milaza re,\n‘Lay fahorianao\nIrery tao Getsemane\nRy Tompo masina ô !\nTarihonao ny saiko e\nHo ao Getsemane.\nHahita ny fitiavanao\nIzao tontolo ‘zao.\nFitiavana ahy re,\nRy Tompo masina ô !\nNo niaretanao\nIzao mangidy ‘zao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (91, 2, 'Nangorohoro mafy re,\nNy fonao, Jeso tia !\nNy tsembokao’zay nitete,\nNanjary toy ny ra !\nEndrey, ny fahorianao\nNangotraka tokoa !\nTsy maintsy nosotroinao\nIlay kapoaka tao !\nFitiavana ahy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (91, 3, 'Ho ahy re, ho ahy re,\nNo niaretanao\nIrery tao Getsemane\nJeso malala ô !\nSintoninao ny foko ê\nHo ao Getsemane.\nHahita izay niaretanao.\nHo tena tia Anao !\nFitiavana ahy ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 9, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 2, 'O, fitiavan-dalina,\nNasehonao ilay alina\nIzay namadihana Anao,\nRy Jeso, fototr’aim-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 3, 'Mangoraka erý ny fo,\nSy ory ny fanahiko ,\nRaha mba tsaroako izao\nNy loza izay nanjo Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 4, 'Fadiranovana Hianao,\nNangotraka ny adinao;\nNy dinitrao nanjary ra\nNoho ny fahoriana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 5, 'Izany niaretanao\nNoho ny fitiavanao,\nKa nentinao ny heloko,\nIzay tokony ho nentiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 6, 'Namita fanekem-baovao\nHo famonjena Hianao ,\nNasianao kase izay\nMaharitra mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 7, 'Misaotra Anao ry Tompo ô !\nFa ravo ny fanahiko\nHandray ny fanomezanao\nEo amin’ny latabatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 8, 'Ny teninao inoako,\nKa raisiko ho ao am-po ;\nNy zava tsoa atolotrao\nDia ranao sy ny tenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (92, 9, 'Ry Jeso, amboaronao\nNy fonay ho fonenanao,\nKa Hianao ho tianay\nIzao sy ho mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (93, 1, 'Ry voasatro-tsilo,\nRy voakapoka !\nIndrisy be ny loza\nManafotra Anao !\nNitsaohan’ny anjely\nFahiny Hianao.\nFa henatra sy latsa\nAnjaranao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (93, 2, 'Atolotro, ry Jeso,\nNy foko ho Anao,\nHiaraka hitondra\nNy fahorianao.\nRaha henatra sy loza\nNo ho anjarako,\nDia Hianao Mpamonjy,\nNo ifaliako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (93, 3, 'Tsarovy aho, Tompo,\nRaha tratry ny manjo !\nTsy mendrika aho, nefa\nMba amindraonao fo !\nNy ratranao no misy\nFanasitranana.\nNy ranao ahitana\nNy fiadanana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (94, 1, 'Nampalahelo mafy re\nNy fahorian-dehibe\n‘Zay niaretanao.\nNalaim-baraka Hianao,\nNiaritra ka tsy nandao\n‘Lay asa masinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (94, 2, 'Ny henatra aman-tahotra\nMangeja anay voavotra,\nFa diso izahay.\nAvia, Tompo , mamonje\nMeteza izao ka maneke\nHanavotra indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (94, 3, 'Ampio ny olonao, Jeso\nHahay handray Anao am-po\nFa mila vonjy izao.\nMonena hanatanjaka\nNy ory sy ny reraka,\nIzay manantena Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (95, 1, 'Gologota no jereo,\nAo ny hazo fijaliana;\nIndro re, mihantona eo\nJeso, sady harabiana.\nMoa mba fantatrao, ry fo,\nNy fitiavan’i Jesosy,\n‘Zay nandefitra tokoa,\nAry koa ninia ho resy ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (95, 2, 'Famonjena ho anao\nNo netezany hihafy,\nKa hijaly toa izao\nSy hiharitra ny mafy.\nHe, ny rany nitete\nVokatry ny fanomboana;\nVetivety foana re\nDia ho faty ao ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (95, 3, 'Mitsangana, miaingà,\nNdeha mankany Gologota ;\nAo no latsaka ny ra\nHamonjena ny mpanota ;\nIo no hany mahadio,\nAntoka tsy hay ravàna\nEny, samy afak’io\n‘Zay rehetra fahotàna !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (96, 1, 'Ny hazo nijalianao,\nRy Jeso Tomponay !\nManeho ny fitianao.\nDia iankinanay, (in-3) | *\nManeho ny fitiavanao.  |', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (96, 2, 'Mitazana ny masonay,\nMangoraka ny fo ;\nNa meloka aza izahay.\nNy teninao ho to. (in-3) | *\nNa meloka aza izahay.     |', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (96, 3, 'Ny avotr’aina vitanao\nNo itokianay ;\nNy entanay voaesotrao.\nKa afaka izahay. (in-3) | *\nNy entanay voaesotrao. |', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (96, 4, 'Ny hazo nihomboanao\nDia voninahitray;\nNy nanalam-baraka Anao\nNo isandratanay ! (in-3)   | *\nNy nanalam-baraka Anao. |\n\n*) Ho an’ny feony (1) ihany', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (97, 1, 'Ilay havoana\nNatao hoe Kalvary,\nNo tany namonoana,\nNy Tompo Jesosy;\nO ! tsarovy re !\nMaty ory tao\nJeso Tompo lehibe\nHanavotra anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (97, 2, 'Jereo ny Tomponao,\nTazanonao irý,\nKa andrandrao ny masonao,\nFa io no Kalvary !\nO ! tsarovy re ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (97, 3, 'Hevero Kalvary,\nFa tao ny Tomponao ;\nTsarovinao ny nentiny\nMba hamonjena anao!\nO ! tsarovy re ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (97, 4, 'Tsinjovy Kalvary,\nFa Jeso Tomponao,\nNijaly tany raha tetý,\nKa maty Izy tao !\nO ! tsarovy re ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (98, 1, '‘Lay fonao tia tao Gologota\nNo velona ao an-tsaiko izao.\nNy fonao ‘zay mitady\nmpanota,\nNo voaratra indrindra tao.\nRy fo malala, nihafy loatra,\nLavitra ny mpangoraka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (98, 2, 'Indro ny lefona koa mamely\nKa notrobarana Hianao!\nHe ! malahelo ireo anjely,\nFa naharary re ny fonao !\nIo no fitia tsy misy toa azy,\nZaran’izao rehetra izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (98, 3, 'Efa hisoka ny antoandro,\nMenatra izao tontolo izao ;\nTsy ela intsony dia maizina\nandro\nKalvary ‘zay nihantonanao !\nFa izaho tsy mba hangataka\nandro\nHampiantrano ny tenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (98, 4, 'Tsy sasatra na valaka koa,\nNy fonao tia, ry Tompo ô ;\nMenatra izao ity foko tsy soa.\nTsy hahavaly tokoa Anao ;\nNefa ampio aho hanaiky,\nHanatontosa ‘zay sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (99, 1, 'Indro ny Tompo Jesosy,\njereo,\nTena nihafy tokoa !\nHe ! fa nivesatra eso, jereo,\nMba hamonjena anao !\nIndro Jeso, jereo Jeso.\nIzy izao dia mihevitra anao,\nIndro Jeso, jereo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (99, 2, 'Indro ny Tompo Jesosy,\njereo,\nEfa voahombo tokoa !\nHe ! fa nijaly tsy meloka teo,\nMba hamonjena anao !\nIndro Jeso, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (99, 3, 'Indry ny lefo-mandoza\njereo,\nNenti-namely ny Tompo !\nHe ! fa ny rany nirotsaka teo,\nMba hamonjena anao !\nIndro Jeso, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (100, 1, 'Ry fitiavan’i Jesosy !\n‘Zay voahombo ho anay,\nOny be sy mahavonjy,\nAoka ho lasanao ‘zahay !\nTompo, Zanakondry tsara,\nAmpy re ilay ranao,\nTsy helohin’ny Mpitsara\n‘Zay mpanota afakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (100, 2, 'Fa misento ny fanahy,\n‘Zay etý an-tany ‘zao ;\nMalahelo sy manahy,\nSao ho voan’izay natao.\nTompo, Zanakondry ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (100, 3, 'Ny mpanota dia ho very,\nRaha tsy mba miova izao.\nFa hahazo tena hery,\nRaha mino tsara Anao !\nTompo, Zanakondry ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (100, 4, 'Enga anie ka mba hiely\nHo an’izao tontolo izao\nIlehy Ony mahafaly,\nManadio sy manavao !\nTompo, Zanakondry ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (101, 1, 'Moa mba efa misy va\nFahoriana toy izao !\nJeso ‘Lay Mpanisy soa,\nTompo sy Mpanjaka koa\nEntina tsy meloka,\nEntina any Kalvary ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (101, 2, 'He, ny lanivoany\nVoatsindron-defona !\nTongotra aman-tanany\nVoafantsika avokoa,\nEny, fa ny ainy koa,\nFoiny tany Kalvary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (101, 3, 'Mafy ‘zay ry Jeso ô !\nMafy nefa zakanao,\nNy fitiavanao anay !\nNo niaretanao izay\nMenatra aho, Jeso ô\nRaha hadino Kalvary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (101, 4, 'Taomy aho Jeso ô !\nMba ho any Kalvary,\nKa ny foko tsy madio\nMba diovinao anio !\nEnto aho, Jeso ô !\nEnto any Kalvary.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (102, 1, 'Ny hazo fijaliana,\nRy Jeso Tomponay,\nDia tena fitoriana\nFa tianao ’zahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (102, 2, 'Ny hazo fijaliana\nDia manambara izao\nFa be ny fahoriana\nNihatra taminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (102, 3, 'Ny hazo fijaliana\nNo mampiseho koa\nFa tokony hariana\nNy ota mahavoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (102, 4, 'Isaoranay dia isaoranay\nHianao, ry Tomponay,\nFa be ny fitiavana\nNataonao taminay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (103, 1, 'Reharehako ny hazo\nNijalian’i Kristy ;\nManda fiarovana avo\nIalofako atý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (103, 2, 'Azon-doza maro aho\nTery koa ny làlana ;\nTsy ilaozan’i Jesosy\nMpanome fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (103, 3, 'Raha lany re ny andro\nMampiadana indray,\nTsy hadinoko ny hazo\nNentin’ny Mpamonjinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (103, 4, 'Ny anjara atý an-tany\nTsy mba mahavesatra\nNoho ny hazo fijalianao\nHazo mahasambatra.\nAmen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 15, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 1, 'Ny hazo-fijaliana\nTsy maintsy hotsarovana ;\nHenoy, ry fanahiko,\nNy tenin’ny Mpamonjinao !\n\nVakiteny 1', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 2, 'Ny teny voalohany\nMihevitra ny rafiny :\n« Mba mamelà ny helony,\nFa jamba ny fanahiny ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 3, 'Izaho koa Jeso ô !\nDia jamba sady ratsy fo ;\nKa mba jereo sy iantrao,\nFa te ho tonga olonao.\n\nVakiteny 2', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 4, 'Ilay meloka tanilany\nNaniry mba ho raisiny,\nNomeny toky tsara io :\n« Ho any Paradisa anio ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 5, 'Raha avy ny anjarako,\nKa tapitra ny androko,\nMba raiso aho Tompo ô,\nHo any Paradisanao !\n\nVakiteny 3', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 6, 'Dia hitany ny reniny\nTomany teo akaikiny,\nToy ny tsindronin-defona\nKa feno fisentoana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 7, 'Dia Jaona no natolony\nHo zanany ho solony :\n« Mba raisonao, ry Jaona ô\nHo reninao ny reniko ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 8, 'Tsarovy aho, Tompo ô !\nRaha ao am-pahoriako,\nIraho ny mpianatrao\nHanampy ny mpanomponao !\n\nVakiteny 4', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 9, 'Ny teniny fahefatra\nNo tena mampangovitra ;\nNiantso Izy, hoe : « Ry Ray,\nNahoana Aho no afoy ? »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 10, 'Izao no dia nandaozana\nAnao tam-pahoriana ;\nHahafaka ny tahotro,\nRaha tonga ny fotoako.\n\nVakiteny 5', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 11, 'Ny otako izay nentiny\nNahory ny fanahiny,\nKa nitarainany hoe :\n« Mangetaheta Aho re ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 12, 'Mangidy, ry Jesosy ô,\nNy loza niaretanao,\nKa aoka mba ho mamiko\nNy avotra, izay vitanao !\n\nVakiteny 6', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 13, 'Hoy  Izy indray :\n« Tanteraka ! »\nHe ! teny mahasambatra.\nTanteraka ny asany,\nVoavotra ny olony.\n\nVakiteny 7', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 14, 'Ny teny farany indray\nNilaza ny rahatrizay :\n« Atolotro ny aiko izao,\nRy Ray, ho eo an-tànanao ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (104, 15, 'Izany dia mamiko\nHiorina ao anatiko,\nKa teny hotononiko\nEo amim-pahafatesako !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 2, 'Raha mijery ny hazo\nNijalian’i Jesosy,\nTsy misy izay malaza\nNa izay mirehareha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 3, 'Aza miavonavona,\nFa indro ny Mpanavotra ;\nNy dian’i Jesosy Kristy\nNo arahina isan-andro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 4, 'Indro ! ny rany mandeha,\nVoahombo ny tànany,\nNijaly, ka dia maty\nNisolo olo-meloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 5, 'Raha ahy ‘zao tany izao\nSy ny aminy rehetra,\nTsy ho azoko havaly\nNy fitiavan’i Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (105, 6, 'Ny foko omeko Azy,\nFanahiko hatolotro ;\nTsy mba mendrika Azy ‘zany\nFa Jeso no mety mandray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (106, 1, 'Tsinjovy   ilay havoana\nArý Jerosalema,\nFa io Golgota !\nMba tadidio ange !\nFa tao, nivesatra ny ota\nNy Tompo Masina\nNijaly sy nolavina\nKa novonoina !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (106, 2, 'Tsy takatra heverina\nNy entan-dehibe,\nIzay nentin’ny Mpisolo\nKa mba diniho re !\nFa nentiny daholo\nNy heloka natao\nFa tonga Izy hanavao\nIzao tontolo izao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (106, 3, 'Ny helotsika koa izao\nDia nivesarany,\nKa manaraha Azy,\nMba ho mpianany !\nRaha tao Golgota Izy,\nNy rany nitete,\nKa vita ho antsika re\nNy vonjy lehibe !\nNy Ho resy sy ho faty.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (107, 1, 'Indro, ny Fitiavana nijaly\nTeo amin’ny hazo-fijaliana ;\nAry ny ratsy nivonona hifaly,\nHandresy ny Zanaka-lahy\nTokana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (107, 2, 'Indro, ny Fitiavana mahery,\nNitondra ny heloky ny olony ;\nIzy niady hanafaka ny very,\nNy ainy no avotra ka natolony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (107, 3, 'Izy nijaly mafy tao Golgota,\nVoahombo ny tongotra\naman-tanany ;\nIzy nisolo antsika izay\nmpanota\nKa fadiranovana ny fanahiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (107, 4, 'O, ry Mpamonjy, mba hazony\naho\nHijery ny hazo-fijaliana !\nOlona meloka aho ka afaho,\nHialoka amin’ny fitiavanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (108, 1, 'Maniry mafy aho ry Jeso\nTompo ô,\nHialoka eo ambany hazo-\nfijalianao ;\nTsy misy toeran-kafa re izay\ntena mamiko,\nNy hazo-fijalianao no\nfiarovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (108, 2, 'Ny hazo-fijalianao no\nitazanako\nNy tenanao Izay nijaly mba\nho avotro ;\nKa dia kotsan-dranomasom-\npitiavana\nNy tavako, ry Tompo ô, fa\nafa-keloka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (108, 3, 'Am-pototry ny hazo izay\nnijalianao\nNo itoerako hohazavain’ny\ntavanao ;\nAtaoko fatiantoka ny zava-\nmora lo,\nNy hazo-fijalianao no\ntombon-dahiko !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 1, 'Ny hazo-fijalianao,\nRy Jeso Tompo maminay,\nDia an’izao tontolo izao\nKa famonjena ho anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 2, 'Mivelatra eo ka hitanay\nIzany fitiavana ;\nKa ampianaro izahay\nHo be fitia toa   Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 3, 'He ! latsaka eo ho avotray\nNy ranao masina sy soa ;\nKa mba sasao re izahay\nHo olonao madio tokoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 4, 'Voatolotra eo mba ho anay\nNy famonjena vitanao ;\nKa mba tariho izahay\nHandray ny fahasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 5, 'Mamiratra eo ka tazanay\nNy lanitra fonenanao ;\nKa mba tantano izahay\nHo mendrika ho mponina ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (109, 6, 'Ndrey, tena mahafinaritra\nNy miara-monina   aminao ;\nKa ampio izahay hiaritra\nNy hazo-fijalianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (110, 1, 'Nahafoy ny aiko Aho,\nHanavotako anao,\nKa nalatsako ny rako,\nNisoloako anao ;\nHe ! ny aiko no nomeko,\nRy mpanota very e !\nAiza   izay afoinao re,\nMba ho Ahy Tomponao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (110, 2, 'Lapan-dRaiko ao ambony,\nVoninahi-dehibe,\nNo nilaozako hamonjy\nSy hanafa-doza anao,\n‘Zay voafitak’i Satana\nSy nozoin-doza be !\nAiza   izay afoinao re,\nMba ho Ahy Tomponao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (110, 3, 'Soa be sy mahagaga\nNo atolotro anao ;\nFamonjena tsy vidina,\nFamelana otanao.\nFitiavan-tsy voafetra\nLova ao an-danitra ao !\nAiza   izay afoinao re,\nMba ho Ahy Tomponao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (110, 4, 'Indro, Tompo ! fa omeko\nHo Anao ny tenako,\nKa tsy misy ‘zay tsy foiko\n‘Zay rehetra tianao !\nEso, latsa, tsy ahoako\nFa ny ho mpanomponao\nNo ho zavatra kendreko,\nRaiso re ny zanakao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 1, 'Nomeko ho anao\nNy rako masina,\nHanavotra anao,\nIzay tsy marina !\n:,: Ka dia mba inona izao\nNo Ahy taminao ? :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 2, 'Nafoiko ho anao\nNy zava-tsoa be,\nMba hanatonanao\nNy Raiko lehibe !\n:,: Ka dia mba inona ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 3, 'Izaho Tomponao\nNijaly mafy re,\nHanafaka anao,\nMpanota lehibe !\n:,: Ka dia mba inona ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 4, 'Ny vonjy lehibe\nNataoko ho anao,\nNy rako nitete\nHandio ny otanao !\n:,: Ka dia mba inona ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 5, 'Jesosy Tompo ô !\nIzao no teniko :\nAtolotro Anao\nNy aiko sy ny fo !\n:,: Izany no isaorako\nAnao Mpamonjiko ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (111, 6, 'Ny foko raisonao,\nOneno hatretý,\nAtaovy lapanao\nNa eto na arý !\n:,: Dia tano aho, Tompo ô,\nMba ho fanananao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 2, 'Indro ny Mpamonjy,\nMpanavotra antsika,\nIzy no manontany hoe :\n“ Tianao va Aho ? ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 3, 'Izaho no Mpamonjy\nSy mpisolo anao ;\nNy rako manontany hoe :\n“ Tianao va Aho ? ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 4, 'Ny fitiavako\nTsy mety miova\nKa mba manontany hoe :\n“ Tianao va Aho ? ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 5, 'Ry Jeso Tompoko !\nMalahelo ‘zahay,\nNy fitiavanay Anao\nMbola tsy toy inona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (112, 6, 'Aelezo ao am-po\nNy fitiavanao,\nHahazoanay hamaly :\n“ Tianay Hianao ”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 1, 'Tsy dia ny hahazo soa\nArý an-danitra\nNo itiavako Anao,\nRy Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 2, 'Tsy noho ny atahorako\nNy fijaliana,\nFa noho ny fitiavanao,\nNo itiavako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 3, 'Voahombo, voalefona,\nVoalatsa Hianao ;\nNahinjitra ny tànanao\nTambony hazo teo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 4, 'Izaho no niaretanao\nNy fijaliana ;\nNy ranao nanavotana ahy\nIzay fahavalonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 5, 'Ka moa tsy fitiavana\nHavaliko Anao ?\nNy foko no atolotro\nAnao, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (113, 6, 'Ry Jeso Kristy masina  ô !\nDeraiko Hianao,\nFa Hianao Mpanjakako\nSy Andriamanitro !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 1, 'Jeso, Zanahary !\nMasina sy to,\nNanome ny ainao\nNoho ny olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 2, 'Tsy niaro loza\nTamin-tenanao,\nFa mba tonga ory\nNoho ny olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 3, 'Nentinao ny ratsy,\nSoa nolavinao,\nLapa nialanao,\nNoho ny olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 4, 'Jeso Tomponay ô !\nTompo masina,\nManomezà soa\nNy mpanomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 5, 'Mamelà ny ota,\nManovà ny fo,\nDiovy ny maloto ;\nAoka ho olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (114, 6, 'Tompo ô, Tsitoha\nVatolampinay !\nSaotra aman-dera\nRaiso ho Anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (115, 1, 'Jeso no niala aina\nHanavotany anao,\n:,: Iza re no tsy hanaiky\nAzy izao ? :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (115, 2, 'Fa ny lapany ambony\nNialany fahizay,\n:,: Hamonjeny olom-bery,\nVery tokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (115, 3, 'Fa izay manatona  Azy,\nKa mibebaka ao am-po\n:,: Tsy hariany akory,\nKa matokia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (115, 4, 'Mba avia, ry havanay ô !\nManeke ny Tomponao,\n:,: Maneke ny Tompon’aina\nHo Tomponao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (115, 5, 'Iantrao, ry Tomponay ô !\nNy mialoka aminao,\n:,: Raisonao ‘zahay mpanota\nHo olonao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (116, 1, 'Jeso Tompo lehibe,\nIraky ny Rainy,\nNolatsain’ny maro be,\nTsy mba nohajainy !\nJeso tia, Jeso tia,\nMaty voahombo ;\nMaty tany Kalvary,\nMaty re ny Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (116, 2, 'Ory loatra fahizay,\nZanak’ondry mora,\nMaty mba ho solonay,\nSady voakora !\nJeso tia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (116, 3, 'Nalahelo, be nanjo,\nNefa tsy nanota,\nFa nijaly sitrapo\nTany Gologota !\nJeso tia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (116, 4, 'Jeso no Mpamonjy tia,\nNahafoy ny ainy,\nMba hamory ‘zay mania,\nHody amin-dRainy !\nJeso tia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (117, 1, 'Miaingà, ry fo sy saina,\nMba ho any Golgota,\nMaty tao ny Tompon’aina,\nVita tao ny avotra.\nRy mahantra, ry mania,\nSady raiki-tahotra,\nNy Mpamonjy be fitia\nHahitanao avotra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (117, 2, 'Fa ny ainao ry Mpamonjy,\nNisoloanao anay\nHahazoanay ny vonjy\nSy hahafa-trosa anay !\nVatofantsiky ny aiko,\nHianao, Mpamonjy ô !\nRavoravo izao ny saiko\nAfakao ny heloko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (117, 3, 'Ny manjo atý an-tany,\nTsy mba hatahorako,\nFa izaho dia ho any\nAmin’ny Mpanavotro,\nTombon-dahiko tokoa\nNy fahafatesana,\nHahatrarako ny soa,\nDia ny fiainana !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (118, 1, '(Hiraina in-3 no fanao ity)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (118, 2, 'O, ry Zanakondry,\nHe ! Hianao nijaly ;\nOry, voahombo,\nAnefa tsy namaly ;\nNy helokay nalainao,\nKa maty re ny ainao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (118, 3, 'Mba iantrao izahay, ry Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (118, 4, 'Ampiadanonao izahay re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (119, 1, 'Mba jereo ny Zanakondry\nVoahombo sady ory,\nNefa Tompo masina.\nHe ! ny Rainay tsy namaly,\nRaha ny Zanany nijaly\nTamim-panaintainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (119, 2, 'He ! ny Marina nijaly !\nMba hahaizako mifaly,\nIzay no niaretany.\nO ! ry foko, mangovita,\nManatòna mba hahita\nNy fahafatesany !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (119, 3, 'Eny, ry Mpamonjy tsara\nMba meteza hanambara\nNy harem-pitiavanao ;\nDia hafanao ny foko.\nMba hahaizako hanompo\nSy hanaraka Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (119, 4, 'Aoka aho hitomany,\nRaha mihevitra izany\nFahoriana nentinao.\nAzoko ny famonjena ;\nO ! ry foko, manenena\nNoho ny ratsy vitanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (119, 5, 'Tompo, manampia ahy,\nMba harisika am-panahy,\nHanompoako Anao\nRaha mbola atý an-tany\nAza afoy fa mba tohany\nTsy hisaraka aminao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (120, 1, 'Hianao, ry Tompon’aiko,\nMaty noho ny otako,\nTsy mba takatry ny saiko\nIzany fijalianao\nNefa sitrakao nijaly\nHahazoako mifaly ;\nRy Jesosy, Hianao\nNo isaorako izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (120, 2, 'Safotry ny fandatsana\nHianao, Mpanavotra\nHandovako fanjakana\nFeno voninahitra.\nVoaratra, voaeso,\nAry voasatro-tsilo\nNoho ny hadisoako,\nKoa dia isaorako !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (120, 3, 'Tianao izahay mpanota,\nHelokay no nentinao ;\nResinao ny tambin’ota\nIzahay nafahanao ;\nVoaloanao ny trosa.\nIzahay mpanota kosa,\nTonga olom-potsinao,\nKa derainay Hianao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 1, 'Ry mino ô, avia re !\nHiara-mankalaza\nIlay Mpanjaka lehibe,\nFa tena mahagaga\nSy sarobidy anie\nNy fahasoavan-dehibe\nNomeny ho antsika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 2, 'Tsy asa soa vitako\nNo tokiko anefa,\nFa ny Andriamanitro\nNo tsy nisalasala\nHanafaka ny otako,\nNomeny mba ho avotro\nNy Zanany malala !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 3, 'Jesosy faly eram-po\nHamonjy ahy very,\nKa azoko ny indrafo.\nIzay vitan’Izy irery\nIzaho tena afa-po.\nTsy misy atahorako,\nIzaho tsy ho very.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 4, 'Jesosy Kristy Tompo ô !\nIzaho ta-hamaly\nNy teny fiantsoanao :\n« Mba manatòna Ahy !\nIzaho dia ho anao,\nHo Ahy kosa hianao »\nKa manampia ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 5, 'Ny ranao soa nitete\nNo nanadio ahy\nNy ainao sarobidy re\nNo nanavotana ahy\nMba hitokiako anie\nNy famonjena lehibe\nNatolotrao ho ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (121, 6, 'Isaorana  eram-po anie\nNy Tompo Zanahary,\nFa Izy efa nanome\nNy Zanany nijaly,\nKa ny devoly lozabe,\nNy Ho resy sy ho faty.\n\n\nota sy ny heloka', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (122, 1, 'Ry Jesosy, ampombay\nFiadanana maharo\nFo sy saina izahay,\nKa mba ampahafantaro,\nRaha ny holatrao jerena,\nFa Anao ny fandresena !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (122, 2, 'Ry Mpandresy, ambarao\nAminay ny vokatr’ady :\nFamonjena, aim-baovao\nNo andramany sahady :\nRaha ny tafika miseho,\nFiadanam-po omeo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (123, 1, 'Hitako izao, sakaiza,\nNy fahasambarana,\nKa ny foko faly, ravo,\nManam-piadanana.\nTeo am-pototry ny hazo\nNijalian’ny Tompoko,\nTeo aho no nahazo\nFamelana heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (123, 2, 'He ! ny indrafo niriko,\nEfa azoko tokoa,\nKa Jesosy no fidiko,\nFa  Izy fototry ny soa,\nHe ! ny rany sarobidy,\nNanadio ny otako\nIzaho efa voavidy.\nLanitra no lovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (123, 3, 'Tena soa mahagaga\nIzao nomenao ahy izao,\nFa izaho izay mpanota,\nDia nohavaninao ;\nKa izaho dia hidera\nIzato fitiavanao\nAry koa hanambara,\nJeso ô ! ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (123, 4, 'Ry Mpamonjy ô ! mba raiso\nIzao fisaorako izao ;\nKa izay rehetra ahy\nDia atolotro Anao,\nRaha tonga re ny fetra\nHialako sasatra,\nEnto aho hitoetra,\nEo anilanao arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (124, 1, 'Velona Jesosy,\nNitsangana tokoa ;\nNy kristiana marobe,\nMihoby sy mihira hoe !\n:,: Dera ho Anao,\nry Mpandresy ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (124, 2, 'Velona Jesosy,\nNy helo dia resy,\nTanteraka ny avotra,\nKa afaka ny heloka !\n:,: Dera ho Anao,\nry Mpandresy ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (124, 3, 'Velona Jesosy,\nKa velona isika,\nHiara-monina  Aminy,\nHohazavain’ny tavany !\n:,: Dera ho Anao,\nry Mpandresy ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (125, 1, 'Nitsangana ny Tompo,\nNeken’ny Ray ny avotra,\nKa ravo ny mpanompo :\nMisokatra ny lanitra !\nIlay Tompom-pandresena\nNandrava fasana,\nAndriam-pamonjena,\nTolory hasina !\nJesosy no Mpanjaka\nAm-boninahitra,\nHiarahako mizaka\nNy zon’ny lanitra !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (126, 1, 'Indro, Jeso velona,\nAfaka ny olom-bery,\nRava re ny fasana,\nDia nivoaka Ilay Mahery ;\nKa ny horohorontany\nDia nahato izany !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (126, 2, 'Resinao ny rafinay,\nRy Jesosy manan-kery,\nKa mpandresy izahay,\nVoavonjinao ny very,\nMatinao ny helok’ota,\nVelona izahay mpanota !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (127, 1, 'Endrey, izato fahasambarana :\nJesosy dia  velona  indray !\nNy foko mandre fitsaharana\nNoho Izy natsangan’ny Ray,\nTsy any am-pasana  intsony\nFa hitako velona koa\n:,: Ilay Tompon’ny hery\nambony\nMamiratra sy manan-jo ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (127, 2, 'He, velona Izy ! Hafarany\nIzaho izao hampandre\nIreo izay tia  ny Anarany\nNy fampiononana be,\nAnjely no mendri-kilaza ;\nIzaho, mpanompo tsy soa,\n:,: Nirahin’ny Tompo malaza,\nKa ravo sy tretrika koa ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (127, 3, 'Endrey, izato fahasambarana,\nFa velona mandrakizay\nNy Tompo Izay nifankazarana,\nKa faly ny foko indray !\nNy fasana izay nampisaona\nNo fampiononana koa,\n:,: Fa tao izahay tafahaona\nKa ravo dia  ravo ny fo ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (128, 1, 'Tahaka ny masoandro miseho\nToy ny heriny izay mahaleo,\nNa dia takon’ny rahona tao ;\nTahaka izany Jesosy\nMpamonjy,\nIzy nitsangana velona indray\nRy olombelona, izay voavonjy\nMba miderà Azy \nmandrakizay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (128, 2, 'Saotra aman-dera sy laza sy\nhery,\nAoka ho Azy hatramin’izao\nIzay nanafaka ny olom-bery,\nIndro ny fasana vaky sy lao.\nIzy nanafaka ny vavahady :\nAiza, ry fahafatesana ô !\nAiza ny herinao sahy miady\nAmin’ny mino sy amin’ny to ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (128, 3, 'Jeso Mpamonjy ô ! mba\nhasoavy\nKa mba arovy ny mino Anao,\nKa ny Fanahinao aoka ho avy,\nMba hanazava ny fonay izao ;\nAry tantano izahay tsy hania,\nFa Hianao no Mpiaro anay !\nO ! ry Mpamonjy anay, mba\navia,\nKa mba vonjeo, fa miandry\nizahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (128, 4, 'Jeso, izahay te-hisaotra tokoa\nNoho ny soa nomenao anay ;\nTeraka teto nitondra ny soa,\nDia  ny fiainana  mandrakizay\nSaotra omenay Anao izay\nnijaly,\nNefa natsangan’ny Rainao\nindray ;\nRahatrizay izahay dia  hifaly\nAny an-danitra mandrakizay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (129, 1, 'Efa velona ny Tompo\nFa nitsangana tokoa ;\nResiny ny fahavalo\nIlay mpihaza aina soa ;\nVelona ny Tomponao,\nResiny ny rafinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (129, 2, 'Matokia, ry mpanota,\nAn’i Jeso Avotrao,\nFa ny ota nahadiso\nAfaka tokoa izao ;\nVelona ny Tomponao,\nAfany ny helokao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (129, 3, 'Eny, fa nitsangana ela\nJeso Kristy Tomponao,\nKa mamoha vavahady\nAny mba hidiranao,\nVelona ny Tomponao,\nKa  efa nialoha anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (130, 1, 'Any naraina\n:,: Ilay Avotr’aina :,:\nNatsangan-dRay ;\nResy ny ota\n:,: Ka ny mpanota :,:\nMihoby indray.\nAny naraina, …sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (130, 2, 'Hira hiraina\n:,: ny Tompon’aina :,:\nIsaoranay ;\nOry ny fony\n:,: mba hamitony :,:\nNy sentonay.\nHira hiraina, …sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (130, 3, 'Faly ny very\n:,: tsy manan-kery :,:\nNy helokay ;\nJesosy Tompo\n:,: dia mampanompo :,:\nNy rafinay.\nFaly ny very, …sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (130, 4, 'Hisy maraina\n:,: ny Tompon’aina :,:\nHo avy indray ;\nIzy hizara\n:,: ny lova tsara :,:\nHomena anay.\nHisy maraina, …sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (131, 1, 'Hobio, ry olombelona !\nJesosy dia velona,\nNy fasana dia rava ;\nMisokatra ny lanitra,\nDia resin’ny mazava.\nJeso Kristy\nNo deraina sy hajaina.\nFa Mpamonjy,\nTompon’aina mahavonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (131, 2, 'He ! misy làlana indray\nHo ao an-danitra, izay\nMiandry ao ambony ;\nFa vitan’ny Mpanavotra\nNy vonjy mahasambatra,\nKa sitraky ny fony\nHandray izay\nMitomany, mba ho any\nAmin-dRainy ;\nFa izany dia hainy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (132, 1, 'Tonga ny andro malaza,\nizay mahafaly,\nIndro nandresy Jesosy\nIlay vao nijaly ;\nVelona indray Izy,\nnatsangan’ny Ray,\nAoka isika hifaly !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (132, 2, 'Efa nahery Jesosy,\nka resy Satana ;\nMenatra mafy ireo\nfahavalo nitana ;\nKa mba zahao :\nrava ny fasana izao,\nRava, fa tsy nahatana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (132, 3, 'Helo sy fahafatesana,\nresy tokoa,\nary ny tambin’ny ota\ndia efa naloa ;\nJeso, Izay maty ka\nvelona indray,\nTompon’ny aina tokoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (132, 4, 'Mba mihobia,\nry olona mino rehetra ;\nAn’i Jesosy ny hery\nizay tsy voafetra ;\nIzy nanao làlana tsara\nvaovao\nResy ny ratsy rehetra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (132, 5, 'Faly Isika fa manana\nfanantenana,\nMbola hiantso Jesosy hoe :\n« Mitsangàna ! »\nAmin’izay, velona\nmandrakizay\nNy nodimandry tra-bonjy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (133, 1, 'Indro, fa velona\nJesosy Tompon’aina !\nFoana ny fasana\nResy izao Satana.\nIza  indray no hitomany,\nRaha nahare izany ?\nRy foko sasatra\nManahy fatratra,\nHeveronao izao ;\nHo afaka ny sentonao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (133, 2, 'Indro, fa velona\nJesosy Tompon’aina !\nIzy nitsangana ;\nRavo ny fo sy saina.\nJeso Tompo nolatsaina\nTonga loharanon’aina,\nRy havana, andrandrao\nNy lanitra izao :\nJesosy velona,\nKa azo ny fiainana !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 1, 'Efa namangy ny olony\nJeso, izay Tompon’ny aina\nLasa ny alina maizina,\n:,: Tonga indray ny maraina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 2, 'Maty ny Tompo, nalevina\nNidina nita ny ony,\nNefa nitsangana velona\n:,: Koa tsy maty intsony :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 3, 'Ory ny fon’ny sakaizany,\nAiza Ilay nantenaina ?\nHitany nefa tsy fantany,\n:,: Jeso, Izay \nTompon’ny aina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 4, 'Teniny tsy mba mivadika\nAmin’ny olona ory.\nTonga ny Tompo mitsidika\n:,: Ireo mpianany vory :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 5, 'Tonga ny taom-pamangiana,\nTondraka ny famonjena.\nVelona ny fihobiana,\n:,: Aina vaovao no nomena :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (134, 6, 'Teraka ny fifaliana,\nTonga ny Filazan-tsara,\nAndron’ny fitomaniana\n:,: Mody ho Pasaka tsara :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 1, 'Mifaly tokoa ny foko izao\nFa misy manambara\nNy Filazantsara,\n:,: O, mba raisonao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 2, 'Nitsangana Jeso,\nHaleloia re !\nHenoy teny soa\nKa dia minoa\n:,: Fa marina re ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 3, 'He ! rava ny fasana, ka mijere,\nFa efa niala\nNy Tompo malala,\n:,: Fa velona e ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 4, 'Ny fahafatesana resy tokoa,\nKa tsy mba mijaly\nNy mino, fa faly\n:,: Sy tretrika am-po :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 5, 'Misaotra, midera Anao\nizahay\nRy Jeso Mpamonjy,\n:,: Ho mandrakizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (135, 6, 'Haleloia !\nTsara ny Pasaka e !\nNy Tompon’ny aina\nIzao hoderaina,\n:,: Haleloia re ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (136, 1, 'Foana ny fasanao,\nJeso Tompon’aina ;\nTo tokoa ny teninao\nKa notontosaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (136, 2, 'Foana ny fasanao,\nTompo mana-kery,\nGaga teo ny rafinao,\nResy ‘lay Mahery !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (136, 3, 'Foana ny fasanao,\nFa efa novohana ;\nMaito ny fatoranao,\nTsy mba nahatana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (136, 4, 'Foana ny fasanao,\nZanaky ny Avo !\nAina no nasehonao,\nZava-maharavo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (137, 1, 'Ry Jeso, Tompon’aina !\nNalevina Hianao,\nKanefa he, deraina,\nFa velona ao izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (137, 2, 'Ny fasana nivoha\nNoho ny herinao,\nFa tsy mba nahatoha\nNy voninahitrao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (137, 3, 'Ireo miaramila\nNahery sy nahay\nDia tsy mba nahatana\nAnao Mpanjakanay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (137, 4, 'Nitsangana tokoa,\nHo tena santatray,\nHianao, ry Tompo soa\nSy Kapiteninay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (138, 1, 'Vita  izao ny fandresena :\nHaleloia, haleloia !\nAzo koa ny famonjena :\nHaleloia !\nResy re ny fasana\nSy ny fahafatesana\nFahavalo farany ;\nFa Jeso nitsangana\nTonga fanafahana :\nHaleloia ! (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (138, 2, 'To izao izay nambara :\nHaleloia ! haleloia !\nFa ny fototra nivaha\nHaleloia !\nFoana ny fasana,\nJeso re nitsangana\nTamin-kery lehibe,\nKa torio ry mino e !\nEran-tany, mba ho re :\nHaleloia ! (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (138, 3, 'Dia venteso ny hosana :\nHaleloia ! haleloia !\nAoka tsy hihambahamba\nHaleloia !\nFa  efa velona tokoa\nIlay Mpamonjy\ntsy mba roa,\n‘Zay nisolo voina anao ;\nIzy no nisantatra\nNy fiainan-tsambatra :\nHaleloia ! (in-3)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (139, 1, '‘Ty ny andro maharavo,\nOra fifalianay ;\n‘Lay Mpanarina ny lavo\nNo niady ho anay.\nHaleloia !\nHo Anao Mpanjakanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (139, 2, 'Azonao ny fandresena,\nRy Mpamonjy velona !\nVitanao ny famonjena.\nTafantsagana Hianao.\nHaleloia !\nHo Anao Mpanjakanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (139, 3, 'Resinao ny fahotana ;\nVoavoha ny fasanao\nTorotoro re Satana ;\nAfaka ny olonao.\nHaleloia !\nHo Anao Mpanjakanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (139, 4, 'Ry Mpandresy\nfandozana !\nRaisonao ny hajanay\nRy Mpamonjy,\nmitantàna,\nTano ho Anao ‘zahay !\nHaleloia !\nHo Anao Mpanjakanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (140, 1, 'Haleloia ! haleloia !\nHaleloia !\nHe ! vita re ny ady izao !\nNy ota efa resy re,\nNy feo mifaly no atao,\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (140, 2, 'Fahafatesana no tao\nFa Kristy naharesy izao,\nNy fandreseny no derao,\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (140, 3, 'Ny andro nijaliana e !\nNisolo fifaliam-be,\nKa mihirà izay mandre :\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (140, 4, 'Ny gadra voatapany,\nNy efitra efa ravany ;\nKa miderà ny heriny\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (140, 5, 'Ry Tompo ô ! ny fiainanao,\nMamelona ny olonao,\nKa hoderaina Hianao :\nHaleloia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (141, 1, 'Haleloia ! haleloia !\nMiderà ny Tomponao,\nManandratra feo hidera\nAn’i Jeso Kristy ‘zao\nFa nijaly mafy Izy,\nNefa indro velona Izy\nAo an-danitra tokoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (141, 2, 'Eny, velona  ny Tompo,\nMba ho santatsika koa\nKa ho velona ny mino\nSy hitsangana tokoa ;\nDia  handray ny lova soa\nSatro-bolamena koa,\nKa hidera ny Mpamonjy\nSy ho sambatra avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (141, 3, 'Haleloia ! haleloia !\nHaja, hery, saotra anie\nNo hatolotra ny Tompo,\n‘Zay Mpandresy lehibe,\nHoderaina lalandava\nFa Mpanjaka manan-jo,\nHaleloia ! haleloia !\nHoderaina eram-po !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (142, 1, 'Efa resy izao na dia ny fasana\naza\nKa asandrato re ny hira sy ny\nfeo\nJesosy nampikoy ‘lay rafy \n‘zay nandoza,\n“Ry fahafatesana ô ! aiza\nmoa ny herinao ?”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (142, 2, 'Lasa ilay tahotrao, ry foko\n‘zay malemy\nFa torotoro ‘zao ny\nfahavalonao,\nTsy nahatohitra ny sandrin’i\nJesosy\n“Ry fahafatesana ô ! aiza\nmoa ny herinao ?”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (142, 3, 'Efa lasa tokoa ny ozona\nrehetra,\nNasehon’i Jeso miharihary\nizao,\nNy fitiavany izay tsy misy\nfetra !\n“Ry fahafatesana ô ! aiza\nmoa ny herinao ?”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (142, 4, 'Kristy no santatra : ny làlana\nmankany\nHo ao an-tranon-dRay,\nmivoha tsara izao,\nNy fifaliana manerana ny\ntany !\n“Ry fahafatesana ô ! aiza\nmoa ny herinao ?”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (142, 5, 'Kristy no fiainana : izay\nmatahotra Azy\nOmeny tanjaka, fanahy, fo\nvaovao,\nKa atony velona, Mpandresy\ntahaka Azy.\n“Ry fahafatesana ô ! aiza\nmoa ny herinao ?”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (143, 1, 'Tafatsangana tokoa,\nHaleloia !\nJeso Kristy Tomposoa,\nHaleloia !\nMaty nefa velona\nHaleloia ! \nMihirà, ry olona,\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (143, 2, 'Foana ny fasana,\nHaleloia !\nIndro, Jeso velona,\nHaleloia !\nToky tsy mamitaka,\nHaleloia ! \nMihirà, ry olona,\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (143, 3, 'Andro lehibe tokoa,\nHaleloia !\nAndro fihobiana,\nHaleloia !\nAfaka ny tahotra,\nHaleloia ! \nMihirà, ry olona,\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (143, 4, 'Haja, laza, aterinay\nHaleloia !\nJeso ô, Mpanjakanay,\nHaleloia !\nRaiso re ny saotranay,\nHaleloia ! \nRaiso koa ny tenanay\nHaleloia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (144, 1, 'Vita izao ny fandresena,\nHaleloia !\nJeso Kristy no hobiana,\nHaleloia !\nAzo re famonjena,\nHaleloia ! \nVerinay tarý Edena\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (144, 2, 'Ka izao dia arahabaina,\nHaleloia !\n‘Lay Mpanjakanay\nhajaina,\nHaleloia !\nKa na zovy mitaraina\nHaleloia ! \nMisy vonjy azo maina\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (144, 3, 'Betlehema niandohana,\nHaleloia !\nKalvary niafarana,\nHaleloia !\nFasana tsy nahatana,\nHaleloia !\nToro teo ‘lay menarana\nHaleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (144, 4, 'Saro-bidy re ny rany !\nHaleloia !\nNy fitiavany tsy lany\nHaleloia !\nNy fatoranay vahany,\nHaleloia ! \nKa hobionareo, ry tany\nHaleloia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (145, 1, 'O ! avia, mba hidera,\nAndro jobily anio ;\nKa faingàna, manatera\nSaotra, laza, ka hobio\nIndro, tonga ny anjely\nKa ny vato mikodia !\nTantaraina mba hiely :\nVelona (in-3)\nNy Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (145, 2, 'Ifalio ny famonjena,\nAndro jobily anio ;\nAfaka ny firenena,\nVoavonjy androtr’io\nGaga teo ny mpanakora\nSy ny rafy marobe\nKa miaiky moramora :\nVelona (in-3)\nNy Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (145, 3, 'Mba venteso re ny hira\nAndro jobily anio ;\nResy ny mpaniratsira\nKa ny tahotrao ario\nHe ! ny fasana efa foana\nTsy nahazona  Azy tao\nTsy naharitra hateloana\nVelona (in-3)\nNy Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (145, 4, 'Efa resy ‘zao  Satana\nAndro jobily anio ;\nNy fatoranao vahana\nKa hobio sy ifalio !\n‘Lay Mpamonjy nolatsaina\nVelona ho avotrao,\nIo no mendrika\nhoderaina\nVelona (in-3)\nNy Tomponao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (146, 1, 'Efa velona tokoa\nHianao, ry Tomponay !\nKa ny herinao tsy toha\nNo manjaka hatrizay ;\nFa ny fasana, ny ota\nNoresenao avokoa.\nTantaraina mba hiely :\n:,: ‘Zay no tokinay mpanota\nTanjakay sy hery koa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (146, 2, 'Efa velona tokoa\nHianao, ry Tomponay !\nLasa nanamboatra koa\nFitoerana ho anay :\nTany tsy mba misy ota\nTsy hidiran’ny manjo ;\n:,: Tany soa sy madio\nMaharavoravo fo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (146, 3, 'Efa velona tokoa\nHianao, ry Tomponay !\nKa misolo vava koa\nSy mifona ho anay ;\nNy Fanahinao omena,\nMba hitari-dalana\nHe ! ny sentonay dia renao,\nJeso tena Havana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (146, 4, 'Efa velona tokoa\nHianao, ry Tomponay !\nAry mbola ho avy koa,\nAvy mba handray anay\nMaminay, ry Tompo tsara ;\nHiara-monina Aminao\nEny, tena manan-jara\n‘Zay mby eo akaikinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (147, 1, '(Hira Paska)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (147, 2, 'Anao ny dera,\nry Zanaky ny Ray ;\nAnao ny fandresena,\nRy Mpanjakanay\nNy fasana nivoha,\nNoho ny herinao\nNy Anaranao Tsitoha\nTsy voatana tao.\nAnao ny dera,\nry Zanaky ny Ray\nAnao ny fandresena,\nRy Mpanjakanay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (147, 3, 'Hianao nandresy,\nlao re ny fasana ;\nNy fanjakan’ny ota koa\nho lasana\nNy herin’i Satana,\nvato nikodia,\nNy Anaranao nandresy\nHaharitra doria,\nAnao ny dera,\nry Zanaky ny Ray\nAnao ny fandresena,\nRy Mpanjakanay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (147, 4, 'He, ravo aho :\nIzy no santatra ;\nIzaho koa hifoha,\nMba ho sambatra ;\nNy fandresen’i Jeso,\nLovako tokoa ;\nNy Anarany Tsitoha\nHanangana  ahy koa.\nAnao ny dera,\nry Zanaky ny Ray\nAnao ny fandresena,\nRy Mpanjakanay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (148, 1, 'Lasa ny Mpanavotsika\nAo an-danitra arý,\nMba hanoman-trano tsara,\nHitoerantsika ao.\n:,: Mifalia :,:\nHo tafiditra ao tokoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (148, 2, 'Lasa ny Mpisorontsika,\nAo an-danitra arý,\nHo Mpisolovava koa,\nHo an’olo-marobe.\n:,: Matokia :,:\nTsy ho very hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (148, 3, 'Lasa ny Mpanjakantsika\nAo an-danitra arý,\nMba handray ny fanjakana\nMety ho anjarany.\n:,: Mihobia :,:\nAsandrato re ny feo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 1, 'Ry varavaran-danitra ô !\nAmpandrosoy ny Tomponao,\nFa fandresena lehibe\nNo efa vitany tatý', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 2, 'Moa iza izao Mpanjaka\nizao ?\nMpandresy iza re izao ?\nNy lazany manerana\nNy tany sy ny lanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 3, 'Io no Kristy Mpamonjy soa\nNisolo olo-meloka,\nKa satro-boninahitra\nNo mendrika ny lohany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 4, 'Anjaranao, ry foko ô !\nHanompo sy hanaraka\n‘Lay Kapiteny lehibe\nNialoha ao an-danitra ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 5, 'Ry Kristy ô ! Mpanavotray !\nIrinay mba ho hita koa\nIzany voninahitrao\nSy ny fiandriananao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (149, 6, 'Raha tapitra ny andronay,\nVohay ny varavaranao,\nNy trano namboarinao\nHonenanay mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (150, 1, 'Efa lasa Jeso Tompo,\nHanamboatra izao\nFitoera-mahafaly\nIza re no tsy hankao ?\nFa ao ankoatr’i Jordana,\nAo an-danitra ao ambony,\nMisy fitsaharan-tsara\nHo an’ny madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (150, 2, 'Tany tsy idiran’ota,\nTany masina sy soa,\nKa izay rehetra hankany\nDia ho masina avokoa !\nFa ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (150, 3, 'Fa izay matoky tena,\nNy tsy mety mino koa,\nTsy havelany hankany,\nFa ho laviny tokoa !\nFa ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (150, 4, 'Ny mandao ny fomba ratsy,\nNy mahazo fo vaovao,\nNy navotan’i Jesosy,\nDia  izy no hankao !\nFa ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (150, 5, 'Ry mpanota saiky very !\nMibebaha re anio ;\nManiria hovonjena,\nKa ny fahotana ario !\nFa ao ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (151, 1, 'He ! tsara ny fonenanao,\nAndriamanitra ô !\nZiona tendrombohitrao,\nTanàna masina ;\nKa iza no hiakatra ao ?\nIzay mahitsy fo,\nIzay madio tànana,\nIzay manao ny to,\nKa mino ny Mpanavotra,\nIreo hiakatra ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (151, 2, 'Mba misandrata faingana,\nRy varavarana !\nHidiran’ny Mpanjakanay\nBe voninahitra.\nMoa iza izao Mpanjaka izao ?\nAndriamanitra\nMahery sy mandresy koa,\nMpanjaka masina ;\nHe, Izy ‘Lay Mpanjakanay\nBe voninahitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (151, 3, 'Miantranoa, Tompo ô !\nMba hifalianay ;\nVoavoatra ny tranonao,\nMidira aminay !\nJehovah no Mpanjakanay,\nMpanjaka tianay :\nNy hery, voninahitra,\nSy ny fisaorana,\nAnao tokoa, ry Tomponay,\nMandrakizay doria !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (152, 1, 'Miakara, miakara,\nRy Mpanjaka lehibe !\nVitanao ny famonjena\n‘Zay nalehanao tatý,\nMihobia, ry anjely !\nKa derao ny Tomponao,\nVoninahitra sy haja\nAtolory Azy izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (152, 2, 'Miakara, miakara,\nRy Mpandresy lehibe !\nEso, latsa, fahoriana\nNo anjaranao tatý ;\n‘Zao ny lanitra miandry,\nMiandry hiverenanao ;\nRenao ao ny feo manako,\nFeno fanajana Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (152, 3, 'Miakara, ry Mpamonjy !\nRavo ny navotanao ;\nIzahy atý an-tany,\nFaly te-hidera Anao ;\nRaisonao ny fiderana\n‘Zay atolotray izao ;\nHaleloia ! Haleloia !\nHo Anao Mpanavotray !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 1, 'Niakatra ao :,: an-danitra :,:\nNy Zanak’Andriamanitra,\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 2, 'Ka monina ao :,: an-danitra :,:\nJesosy Tompon-danitra,\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 3, 'Izao zavatra :,: rehetra izao :,:\nRy Jeso ô, hazoninao,\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 4, 'Isaoranay :,: ry Tompo ô :,:\nNy herinao sy indrafo !\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 5, 'Fa ny devoly resinao\n:,: he resinao ! :,:\nNy ratsy nofongoranao,\n:,: Haleloia ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (153, 6, 'Ry tany sy :,: ry lanitra :,:\nDerao Andriamanitra !\n:,: Haleloia ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (154, 1, 'Ao an-dapany, Jesosy\nDia derain’ny tapitrisa,\nFa ny lanitra sy tany\nDia samy mankamamy\n:,: Ilay Mpandresy mora fo ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (154, 2, 'Jeso Tompo dia ho avy\nHo Mpitsara atý an-tany,\nNa ny ratsy na ny tsara\nSamy hanana anjara,\n:,: Valin’ny natao tatý ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (154, 3, 'Ry Mpiantra be fitia,\nIantrao izahay mania ;\nRaiso ho Anao, tahio,\nHafanao, ampifalio,\n:,: Ento any Aminao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (155, 1, 'Izao Jesosy Tompontsika\nDia nasandratry ny Ray\nAo an-tsezam-panjakany ;\nIzay rehetra nialany\n:,: Voarainy  indray :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (155, 2, 'Tsy hadinony anefa\nIzay nilaozany tatý,\nFa omeny ny Fanahy\nMba hitondra sy hitahy\n:,: Ny mpianany :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (155, 3, 'Ao anilan-dRainy Izy\nManam-boninahitra,\nKa miderà Azy any\nIzay rehetra anjakany\n:,: Eran-danitra :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (155, 4, 'Tompom-pitsarana Izy,\nSamy mba hatsangany,\nNa ny ratsy na ny tsara,\nKa homeny izay anjara\n:,: Valin’asany :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (155, 5, 'He ! hohitantsika Izy,\nDia ny tena endriny,\nKa ny lova soa homena\nIzay nandray ny famonjena\n:,: Voatendriny :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 1, 'An-tsezam-panjakany,\nAmbony kintana,\nNo lapan’i Jesosy\nIlay nasandratra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 2, 'Atý an-tany iva,\nAnilam-pasana,\nNo ipetrahantsika\nZoim-pahoriana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 3, 'Kanefa mety  ihany\nNy monina atý,\nHiomana ho any\nAn-danitra  arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 4, 'Isika tsy kamboty,\nNa dia nilaozany ;\nNomeny ny Fanahy\nHo solon-tenany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 5, 'Ny fahoriantsika,\nDia efa maivana,\nNy fahafatesantsika\nDia afa-tsindrona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 6, 'Ny ain’i Jeso Tompo\nIainantsika atý ;\nTsy ela dia hiseho\nIndray ny tenany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (156, 7, 'Ka hira no venteso\nRy havany izao,\nNy senton’alahelo\nMba atsaharonao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (157, 1, 'Jeso Mpanjaka tokana\nNa aiza misy olona :\nNy fanjakany hanerana\nNy tany sy ny lanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (157, 2, 'Vavaka sy fisaorana\nHakarin’ny navotana ;\nHaharitra tsy tapitra\nHanolo-boninahitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (157, 3, 'Ny firenen-drehetra anie\nHidera ny fitiavam-be ;\nNy zaza aza hanatitra\nNy hajam-boninahitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (157, 4, 'Ny tany izay alehany\nDia feno fitahiany ;\nNy voagadra afaka,\nMiadana ny reraka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (157, 5, 'Izao rehetra izao anie,\nHanatitra fisaoram-be\nHo an’ny Tompo tokana,\nIzay Mpanjakan-danitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (158, 1, 'Hobio, ry tra-bonjy,\nFa indro ny Mpamonjy\nMiakatra indray ;\nTsijonvy Izy any\nMandray ny fanjakany\nAo an-kavanan-dRay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (158, 2, 'Ny hiran’ny anjely,\nManako sy miely\nArý an-danitra ;\nInjay, ry Ray Tsitoha,\nMiarahaba koa,\nMandray ny Zanaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (158, 3, 'O, miaingà, ry foko,\nFa aza mitoloko\nAtý an-tany  izao ;\nMiandrandrà, jereo\nIlay voahombo teo !\nTsitoha Izy ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (158, 4, 'Naharitra ny mafy,\nNiady sy nihafy\nNy Tomponao tatý !\nTahafonao izany,\nFa he ! ny fanjakany\nMiandry anao arý !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (159, 1, 'Raha  afaka entana aho atý\nKa tody any an-danitra arý,\nNy hanakaiky Ilay Tompo tia\nNo hifaliako mandrakizay.\nSatriko re ny hanome\nSaotra tokoa eran’ny fo\nAn’Ilay maty fa velona\nindray,\nHatrehiko ao anilan’ny Ray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (159, 2, '‘Ndrey ny haren’izao antrany\nizao !\nIzay handovako ilay tany\nvaovao !\nFa ny hahita ny tavany ao\nNo hiadanako mandrakizay.\nSatriko re ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (159, 3, 'Hava-malala hihaonako ao,\nHobin-tra-bonjy hiaraha-\nmanao ;\nMamy indrindra anefa izao ;\nKristy hatrehiko\nmandrakizay.\nSatriko re ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (160, 1, 'Satrohy voninahitra,\nAnjely masina ô !\nIlay voasatro-tsilo teo,\nDia Jeso, Jeso, Jeso,\nTompo tokana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (160, 2, 'Ry afa-pahoriana,\nIzay ao an-dapany !\nNombany tao an-dalana,\nKa saotra, laza, hery,\nAoka ho Azy re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (160, 3, 'Taranaky ny lavo ô,\nIzay natsangany,\nEkeo Izy eram-po,\nKa saotra, laza, hery,\nAoka ho Azy re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (160, 4, 'Ry firenena marobe\nAmbany lanitra !\nMiankohofa, maneke\nFa Jeso, Jeso, Jeso,\nNo Mpanjaka re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (161, 1, 'Kristy Mpanjaka,\nMendrika  hoderaina\nIzy no mitàna\nAntsika ho velona  aina,\nDerao, derao\nIlay Mpanjakanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (161, 2, 'Kristy Mpanjaka,\nMendrika  ho hajaina ;\nIzy no mifona\nHo antsika novonjena\nHajao, hajao\nIlay Mpanjakanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (161, 3, 'Kristy Mpanjaka,\nMendrika  ho hobiana ;\nIzy no hitsena\nNy olo-notahiana,\nHobio, hobio\nIlay Mpanjakanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (162, 1, 'Mivelatra eo imasoko\nNy fiandrianan-dehibe\nItoeran’ny Mpamonjiko,\nDia Jeso Ilay nandresy re.\nFa niakatra  Izy ka nandao\nIty tany fandalovana ;\nManjaka ao an-dapany,\nKa feno voninahitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (162, 2, 'Anjely no manotrona\nArahin’ny olo-masina\nIreo dia mandohalika\nAm-panajana fatratra,\nNy Tompo eo afovoany,\nMamiratra ny tavany,\nKa feno hasambarana\nIzay mahazo taratra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (162, 3, 'Kanefa tsy mipetraka\nHo endriky ny lanitra\n‘Lay Zanak’Andriamanitra\nFa miasa tsy mijanona.\nRenao va ny fifonany,\nHo an’ny Fiangonany\n‘Zay novidiny tamin-dRà,\nKa tiany hampakarina ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (162, 4, 'Ekeo, ry Fiangonana ô !\n‘Lay hany Mpanalàlana\n‘Zay mandohalika amin-dRay,\nKa mitomany hoa anao !\nRy Tompo aza sasatra,\nIfony lalandava e !\n‘Zahay izay navotanao.\nFa te-hankao an-danitra !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (163, 1, 'Amen, Jeso no Mpifehy\nAmen, ny Fanahiny,\nAmen, Amen re ny teny\nMbamin’ny fandraisany\nAmen, ny batisany,\nAmen, ny fiainany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (163, 2, 'Amen, Jeso no Mpanjaka,\nAmen, ny Anarany ;\nAmen, Jeso tonga maka\nAhy ho anjarany\nAmen, Amen ary to\nIzy, Izay inoako !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (163, 3, 'Amen, ny fanantenana\nZavatra maharitra ;\nAmen re, ny fifindrana,\nRaha  mankao an-danitra\nAmen, ny fiadanana,\nAmen, ny fiainana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (163, 4, 'Aoka  isika hihira Amen,\nAmen ho mandrakizay !\nJeso, mba valio Amen,\nDia  ho faly izahay ;\nAmen, no ho hiranay\nRaha  mby Aminao izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (164, 1, 'He, Jesosy Tokintsika !\nTompon’ny fiainana\nTonga eto amintsika\nOlon-kovangiana.\nIzy no hitory teny\nSoa, mahavelona,\nFa ny ainy no nomeny\nMba ho solo-voina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (164, 2, 'Andrandraonao ka andraso\nNy Mazava lehibe\nDia  Ilay tsy hita maso,\nMpanazava, Mpanome ;\nFa ny ainao dia ho very,\nRaha tsy arahinao\nIlay Mpanjaka manan-kery\nIzay nanavotra anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (164, 3, 'Eto afovoantsika\nIlay Mazava tokana ;\nSambatra izay mahita\nAzy fa ho velona.\nRy Jesosy, iresaho\nIzahay mpianatrao\nHafanao sy hatanjaho\nNy finoanay Anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (165, 1, 'Jesosy irery  ihany :\nFilazantsara soa !\nNentin’ireo mpanompo,\ntorina aminao\nMaizina avokoa izao rehetra\nizao,\nNefa he, ny mazava naposaka\naminao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (165, 2, 'Jesosy irery  ihany :\nno ivavahana,\nNy Anarany irery no\nfanavotana,\nNy olona rehetra no \nhandohalika\nEtý an-tany ka hatrany \nan-danitra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (165, 3, 'Jesosy tsy miova\nomaly sy anio\nAry mandrakizay,\nfa tsy mamitaka,\nMifona ho antsika eo\nan-kavanan-dRay\nJESOSY IRERY IHANY re\nno isaorana !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (166, 1, 'Haleloia ! Miderà !\n:,: Asandrato re ny feo :,:\nMiderà ny Ray Tsitoha,\n:,: ‘Zay Mpamorona anareo :,:\nFa Mpanjaka manan-jo,\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (166, 2, 'Haleloia ! Mihirà !\n:,: Lehibe sy masina :,:\n‘Lay Mpahary anareo.\n:,: Ao ny ranomasina :,:\nSy ny biby marobe\n‘Ndray mihoby ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (166, 3, 'Raiso re, ry Tompo ô !\n:,: Ny mpanota toa anay :,:\n‘Zay miantoraka Aminao\n:,: Mba henoy ny vavakay :,:\nHo fanatitra madio,\n‘Zay atolotray anio.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (167, 1, 'Derao ny Tompo lehibe,\nNahary izao rehetra izao ;\nHaleloia ! Haleloia !\nNy lanitra sy ny tany koa,\nSamia midera avokoa ;\nAsandrato izao ny dera :\nHaleloia !\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (167, 2, 'Ry fo voadion’ny rany soa ;\nAnkalazao ny Tompo tia ;\nHaleloia ! Haleloia !\nRy voavela heloka ô !\nTsarovy re ny indrafo,\nAsandrato izao ny dera :\nHaleloia !\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (167, 3, 'Miraisa fo ka miderà,\nFa Izy niahy hatrizay\nHaleloia ! Haleloia !\nDerao ny Ray sy Zanaka\nSy ny Fanahy Masina\nAsandrato izao ny dera :\nHaleloia !\nHaleloia ! Haleloia !\nAmena !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (168, 1, 'Midera ny Anaranao\n‘Zahay, ry Tompo ô !\nKa hira no Ventesinay\nHoe : haja, saotra, hery,\nMba raiso ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (168, 2, 'He ! voahombo Hianao\nNisolo heloka,\nKa mendrika hanaovanay\nHoe : haja, saotra, hery,\nMba raiso ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (168, 3, 'Endrey ! ny fitiavanao\nEndrey ! ny herinao,\nTra-pamonjena izahay,\nKa haja, saotra, hery,\nMba raiso ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (168, 4, 'Raha tonga ao an-danitra\n‘Zahay mpanomponao,\nHitohy re ny hiranay\nHoe : haja, saotra, hery,\nMba raiso ho Anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (169, 1, 'Ry Jeso ! ny Anaranao\nDia maminay tokoa ;\nMihira sy miderà Anao\nIzahay navotanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (169, 2, 'Tsy takatry ny hevitra\nNy fitiavanao :\nTsy tambonay tononina\nNy hasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (169, 3, 'Ny tsara eran-danitra\nSy eran-tany koa,\nTsy azo anoharana\nNy fitahianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (169, 4, 'Ho toy ny loharano e,\nNy fonay olonao ;\nTsy hanam-pitsaharana e,\nNy hiderana Anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (170, 1, 'Ry Tompo Andriamanitra,\nMpanao izao rehetra izao !\nMidera Anao ny lanitra\nNy tany koa mitsaoka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (170, 2, 'Hianao no Ray mandrakizay,\nMpanao izao tontolo izao,\nTongava re, ka mba tahio\nNy vavakay atao anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (170, 3, 'Ry Kristy ô, Mpanavotra,\nMpamonjy be fitia tokoa,\nHianao no Zanaky ny Ray,\nKa mendrika haja, saotra koa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (170, 4, 'Fanahy Masina ô, avia,\nSasao ho voadio izahay,\nHanoa sy hanara-dia\nIlay Mpamonjy Tomponay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (170, 5, 'Anao ny voninahitra\nSy dera tsy mitsahatra,\nRy Telo Izay Iray, Tsitoha\nRay, Zanaka, Fanahy koa !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (171, 1, 'Ry Jeso ny fitiavanao\nNo ifalianay\nFa voavono Hianao\nHisolo voina anay,\nMba raisonao re\nNy saotra aterinay izao,\nHo fanajana\nAnao, Mpamonjy tia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (171, 2, 'Mahery ny fitiavanao,\nKa itokianay ;\nIzay mikamabana Aminao\nDia tsy hangaihay,\nMba raisonao re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (171, 3, 'Ambony ny fitiavanao,\nKa tsy mba takatray ;\nKanefa noho ny herinao,\nHiezaka izahay,\nMba raisonao re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (171, 4, 'Hiezaka izahay, Jeso !\nKa mba ho tia Anao,\nKa tsy hatahotra eso koa,\nNoho ny Aminao !\nMba raisonao re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (172, 1, 'Jeso, Mpanjaka Malaza,\nNo Tompo Mpanjakanay ;\nMahery sy tsara indrindra,\nKa Izy no Aronay.\nMendrika hankalazaina\nNoho ny heriny ;\nEran-tany sy lanitra koa\nNy zo aman-dazany :\nRy Tompo, Mpanjaka\nMahery ô ! Mihainoa !\nAvy manaiky tokoa  izahay\nFa Tompon’ny hery rehetra\nHianao,\nTsy misy mihitsy ny toa Anao\nTsy tratry ny saina ny herinao\nNy fiderana izay entinay,\nRaiso re ! Raiso re !\nNy haja  atolotra  Anao etý.\nRaiso izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (172, 2, 'Tompo, Mpandahatra hendry,\nMpanao izao rehetra izao,\nNy zara sy tendry ataony\nDia tsara sy mahasoa ;\nMendrika ny hoderaina\nNy fandaharany ;\nAtolory ho Azy ny saotra,\nFa Izy tandrifin’io :\nRy Tompo, Mpanjaka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (172, 3, 'Tompo, Mpanjaka Tsitoha,\nKanefa be indrafo ;\nDeraina eram-po eran-tsaina,\nDeraina mandrakizay ;\nHe ! ny mpanompo voavonjy,\nSamy mihoby hoe :\n“ Haleloia, ho amin’ny Avo,\nFa Jeso no Tomponay ! ”\nRy Tompo, Mpanjaka ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (173, 1, 'Hoderaiko lalandava\nJeso Ilay Mpanavotro\nNoho ny fitiavana ahy,\nMaty mba ho soloko,\nHoderaiko, hoderaiko,\nFa ny rany masina\nNentiny hanavotra  ahy\nAfaka ny heloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (173, 2, 'Holazaiko lalandava\nNy tantara mamiko,\nIzay nitadiavana ahy,\nVery noho ny heloko :\nHoderaiko, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (173, 3, 'Hohiraiko lalandava\n‘Zao ny voninahiny\nFa ny fahafatesana aza,\nResy noho ny heriny ;\nHoderaiko, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (174, 1, 'Hosana ! Hosana !\nHosana !\nHosana no asandratray\nHo an’i Jesosy\nFa Izy no Mpanjakanay\nSy Tompo tia koa ;\nHosana no atolotray\nAnao, ry Tomponay !\nIty no andro Jobily,\nJobily, Jobily, Jobily,\nIty no andro Jobily,\nKa hifalianay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (174, 2, 'Hosana ! (in-3) *\nHosana ho Anao anie,\nRy Kristy Tomponay !\nFa avy Hianao tokoa\nHanjaka aminay !\nHosana no atolotray, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (174, 3, 'Hosana ! (in-3) *\nHosana ! fa ho sambatra\nIzay mpanomponao ;\nTsy hisy fahoriana\nEtý an-taninao !\nHosana no atolotray, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (174, 4, 'Hosana ! (in-3) *\nHosana ! fa miomana\n‘Zahay vahoakao\nHitsena Anao Mpanjakanay\nRaha  avy Hianao !\nHosana no atolotray, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (174, 5, 'Hosana ! (in-3) *\nHosana ! raisonao tokoa,\nRy Tompo tianay,\nNy dera, laza, saotra koa,\nIzay aterinay !\nHosana no atolotray, ..sns.\n\n*Ho an’ny feo (1) ihany\n\nNY FANAHY MASINA', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (175, 1, 'Fanahy Masina ô !\nNy hevitray izao\nOvay ho vao ;\nVonoy ny otanay,\nDiovinao izahay,\nKa ny fanahinay\nOnenonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (175, 2, 'Fanahy Masina ô !\nTsinjovinao izao\nNy olonao ;\nMpananatra ô ! ovay\nNy eritreritray ;\nNy hadisoanay\nEsorinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (175, 3, 'Fanahy hendry ô !\nJereo izahay izao,\nKa hazavao !\nAdala izahay,\nMania ny hevitray ;\nDia atoroy anay\nNy lalanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (175, 4, 'Fanahy Masina ô !\nMba asehoy izao\nNy antranao ;\nBe tahotra izahay,\nHenoy ny hatakay,\nKa ny fanahinay\nOnoninao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 1, 'Avia, Fanahy ô,\nFanahy Masina ô !\nMidina eto aminay\nHitari-dàlana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 2, 'Fa diso làlana\nKa ory izahay ;\nNy sainay dia maizina,\nNy fonay ratsy koa', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 3, 'Midina Hianao,\nRy Mpanazva anay,\nHanaisotra ny maizina,\nAtý anatinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 4, 'Avia, Mpananatra ô !\nMombà anay tokoa ;\nAmpahatsiarovinao\nNy teny mahasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 5, 'Ry Mpampianatra  ô !\nAmpionao izahay,\nKa aoka re ho marina\nIzay ianaranay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (176, 6, 'Mitomoera atý\nAnatin’ny fonay ;\nIzany indrindra, ry Tompo,\nNo angatahinay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (177, 1, 'Avia, Fanahy Masina ô !\nMidina eto aminay ;\nFenoy ny hasoavanao\nNy fonay noforoninao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (177, 2, 'Tongava, ry Mpananatra ô !\nNomen ‘Andriamanitra\nHo loharano velona\nHiboiboika ao am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (177, 3, 'Sokafy ny fanahinay\nHidiram-pitiavana ;\nNy tenanay malemy koa,\nAmpaherezonao anie !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (177, 4, 'Reseo ny fahavalonay ;\nOnòny koa ny tahotray ;\nTantano ao an-dalana\nHahatraranay ny lanitra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (177, 5, 'Tahio mba hitiavanay\nNy Ray sy Jeso Zanany\nSy hitokianay Anao\nIzay Fanahy Masina !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (178, 1, 'Ry Fanahy Mpanazava !\nMitariha anay izao\n!   ‘Zay nampanoratinao !\n:,: Dia hifaly :,:\nIzahay tarihinao ! (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (178, 2, 'Ry Fanahy Mpanazava !\nMaminay ny anatrao,\nFa matanjaka sy hendry\n‘Zay ampianarinao !\n:,: Hatanjaho :,:\nIzahay mpianatrao ! (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (178, 3, 'Ry Fanahy Mpanazava !\nAoka tsy hangia anay\nNy angano sy tantara\n‘Zay hamily dia anay !\n:,: Mba sakano :,:\nTsy handavanay Anao ! (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (178, 4, 'Ry Fanahy Mpanazava !\nNy Baiboly taminao\nDia fanilo sady toky\nHanatonanay Anao !\n:,: Mba tsilovy :,:\n‘Zay miankina Aminao (in-2)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (179, 1, 'Avia, Fanahy ô !\nMidina Hianao\nHanova ny fo vatonay,\nHo tonga fo vaovao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (179, 2, 'Izay afafinay,\nTondrahan-drano koa,\nTsy mety misy vokatra\nRaha tsy ambininao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (179, 3, 'Fenoy ny herinao\nNy fon’ny olona,\nHamelona finoana\nSy fitiavana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (179, 4, 'Fanahy Masina ô !\nTahio ny asa atao,\nAmpandrosoy am-po\nNy hasoavanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (180, 1, 'Ny Fanahy Masina efa tonga\ntokoa,\nMba homba ny mpitory Teny\nmahasoa.\nKoa mitsangàna izao,\nandeha ry mahery fo,\nHatramin’ny faran’ny tany,\nTorio ny Teny soa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (180, 2, 'Voasaron’aizim-pito ny\nNosinao ;\nNy ody sy ny sampy no\nnotompoiny tao ;\nNy fahazarana koa, nangeja\nmafy tokoa,\nKanefa tonga ny mazava :\nResy ny aizina ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (180, 3, 'Ry fanahy voatafy hery,\nny Tompo anie\nKa efa miandry anao,\ninjay miteny hoe :\n« Angony, eny antsoy,\nhanatona avokoa\nNy firenena eran-tany,\nHo mpianatro tokoa  ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (180, 4, 'Tompo tia ô, ekeko ny\nantsonao ;\nHafoiko ny hareko,\nny tenako izao,\nHo vavolombelona, hijoro,\nhanafaka,\nHitory sy hamafy koa ;\nTafio ny herinao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (181, 1, 'Ny Tompo, raha vao nanao\nVeloma ny tatý,\nNamela ny Mpananatra\nHo arony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (181, 2, 'Dia tonga Izy hanome\nNy hery mahasoa,\nDia hery fanadiovana\nNy fo tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (181, 3, 'Malemy ny fibitsiny,\nKanefa mora re,\nMahazo fiononana\nIzay mandre.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (181, 4, 'Tsinjovy, ry Fanahy ô !\nKa iantrao izahay ;\nAmboary ho tempolinao\nNy tenanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (182, 1, 'Fanahy Mpanazava,\nNy foko hazavao\nManiry lalandava\nNy Pentekositrao.\nNy aizina ao am-poko,\nFongary re ho lao ;\nNy foko mitoloko,\nManiry indrindra Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (182, 2, 'Fanahy Mpanadio,\nNy foko mba sasao.\nDiovy re anio\nHo tena lapanao ;\nNy hevitray misoko\nIzay te-handroaka Anao,\nDia ota te-hijoko,\nEsory sy fafao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (182, 3, 'Fanahy Mpampahery\nIzay rerakao sy voa,\nNy foko re dia tery\nHiantoraka Aminao.\nIzay mandrebireby,\nRoahy re izao,\nNy fo mitebiteby,\nHiondana Aminao !\nAmen.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 1, 'Ny Pentekosta tonga  izao ;\nFanahy ô, ny jironao\nVelomy ao anatiko\nHahafantarako ny to.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 2, 'Ampisehoy, Fanahy ô\nFa Jeso no fiainako,\nKa Izy dia làlana\nHo amin’ny fiainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 3, 'Dia mba tarihonao ny fo\nTohano ny fanahiko ;\nAmpio ny finoako\nHahita ny Mpanavotro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 4, 'Fa Hianao, Fanahy ô !\nHanova ny fanahiko,\nKa raiso sy diovinao\nNy foko ho fonenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 5, 'Ny hazo-fijaliana,\nAnjara hivesarana,\nFa Hianao, ry Heriko,\nMba miaraha amiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 6, 'Mba asehoy ho fantatra\nNy soa tsy ho tapitra,\nNy satro-boninahitra\nHomen’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (183, 7, 'O, ry Fanahy Masina ô !\nAmpaherezonao ny fo,\nKa hazavao ny lalako,\nRaha  avy ny hodiako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (184, 1, 'Midina, ry Fanahy ô !\nMidira ao anaty fo,\nAmpio ny mihafy,\nFa Hianao no Mpanome\nNy to sy fahazavana be ;\nNy vavanay sokafy\nIzao, hanao\nSaotra tsara, hanambara\nNy Mpamonjy,\nFa satria voavonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (184, 2, 'Ka raha re ny teninao,\nDia mba avia, hazavao\nNy hevitry ny teny ;\nMba ambarao aminay\nNy fomban’ny Mpanavotray,\nLazao ny famonjeny,\nIzahay, izay\nVory eto, andreheto\nJiron’aina,\nHialan’ny aizin-tsaina !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (184, 3, 'Ny maizina onenanay,\nTsy mety afaka aminay,\nKa tena mahavery,\nRaha Hianao tsy tonga izao\nMitondra filazam-baovao\nSy toky mampahery ;\nOvay izahay,\nHamasino ny te-hino\nAmpidiro\nEto aminay ny jiro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (184, 4, 'Mba hafanao re izahay,\nAmpio hitiavanay\nIndrindra ny Mpamonjy,\nNy rany mba ho avotray,\nNy didiny harahinay,\nHananay ny vonjy,\nJeso, Jeso\nNo torio sy bitsiho\nAo an-tsainay,\nMba hamelona ny ainay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (185, 1, 'Fanahy Mpanazava anay !\nMidina eto aminay,\nTarihonao ny sainay ;\nNy alahelonay savao,\nNy toetray havaozinao,\nVelomy re ny ainay !\nSento renao\nMba valio, ka tahio\nNy mahantra,\nRy Fanahin’ny Mpiantra !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (186, 1, 'O ! ry Fanahin’ny Ray ao\nambony\nAndriamanitra mandrakizay,\nAza avela hisaraka intsony\nNy olo-mino, fa aoka hiray ;\nTaomy ho an’i Jesosy\nMpamonjy\nNy mbola maizina ka hazavao,\nHahafantarany tsar any vonjy,\nDia ho faly ny mino Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (186, 2, 'O ! ry Mpiantoka ny\nfamonjena,\nAzo ny antoka tsara izao ;\nTonga ny iraka be fahendrena\nMba hanambara ny sitraponao,\nKa ny lelafo noraisin’ny mino\nSy ny fiteniny tonga vaovao\nDia nahagaga, ka tena\nnampino\nOlona maro hanaraka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (186, 3, 'Ry Mpampionona ô ! mba\nsitrano\nFa voaratran’ny ota izahay ;\nKa izay ory mba ampiadano,\nNy malahelo falio indray !\nO, mba ovaonao tokoa ny\nsainay,\nTaomy ka ampahareto ny fo ;\nKa raha ory, arovy ny ainay,\nMba tsy hafoinay intsony\nny to !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (187, 1, 'Fanahy, Mpanavao anay,\nSimban’ny ota izahay,\nKa amboaronao indray,\nHavaozy ny fanahinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (187, 2, 'Avia, ry Afo masina,\nNy ratsy aoka ho levona ;\nRy Loharano velona,\nTondrahonao ny efitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (187, 3, 'Ny lohataonanao anie\nMba hampaniry soa be\nSy hampamony ho Anao\nIzao tany karankaina izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (187, 4, 'Ny efitra sy tany lao\nHihoby sy hidera Anao,\nFa olom-potsy no ho ao ;\nMpanompo teo fa afakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (187, 5, 'Ry lohataona maminay !\nMidina faingana aminay,\nFanahy Mpanavao anay,\nNiandry ela izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (188, 1, 'Ny lanitra sokafinao,\nMidìna, ry Mahery !\nAmpisehoy ny sandrinao,\nRy Tompo manan-kery !\nHe! manakora izao\nNy fahavalonao,\nKa maro no mandà\nNy afo masina\nNarehitrao, ry Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (188, 2, 'O, ry Mpitia olona,\nMpanjaka voahombo !\nNy teninao dia velona,\nMahery sy mitombo ;\nAmpahafantaronao\nIzao tontolo izao,\nFa tsy mba foana\nNy ranao masina\nNalatsakao fahiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (188, 3, 'Mba ampiraiso hina re\nNy olom-boafidy,\nKa akambano mafy e,\nFa lova sarobidy,\nNy fitiavana\nMadio sy marina\nNo aoka hanavao\nNy fiangonanao,\nHandraisanao ny dera.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (188, 4, 'Ny Nosinay, ny tranonay,\nNy vava aman-tsainay,\nDia samy mba havaozinao,\nRy fototry ny ainay !\nAmpiononinao\nNy manina Anao !\nAvia, ka mamonje,\nAvia, mampandrese\nNy olonao mahantra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 1, 'Ry Fanahin’aina,\nMpanazava saina,\nMpampionom-po !\nO, mba akekezo\nIreto miambezo\nHamindranao fo !\nHazavao\nNy olonao,\nMba handraisanay tokoa\nFanomezan-tsoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 2, 'Mba ampivavaho,\nHafanao re aho\nHanakalaza Anao !\nHataka sy sento\nAsandrato, ento,\nKa valionao !\nAnehoy\nSy ampinoy\nNy famindrampo mamonjy\nIzay te-ho tra-bonjy !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 3, 'O ! mba amosahy\nFahazavam-panahy\nNy fisainanay,\nKa ny tarafo,\nAndreheto afo,\nNy fanahinay,\nKa ombay\nRe izahay,\nRaha  antsoinao hanambara\nNy Filazantsara !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 4, 'O ! ry Andon’aina !\nTany karankaina\nMba vontosinao !\nDia ho vanom-boa,\nHahavoka-tsoa\nRe ny volinao !\nIndrafo\nSy teny to\nNo harena antenaina\nHanavao ny aina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 5, 'O ! mba areheto\nSy ampahareto\nNy finoanay !\nIzahay afaho,\nAmpahatanjaho\nNy fanahinay !\nTompo ô !\nMiraiki-po\nAminao ny olom-bery\nTonga manan-kery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (189, 6, 'Raha mitoloko,o\nTano re ny foko\nTsy handao Anao,\nKa tsilovy dia,\nTsy haniasia,\nHahatody ao\nAminao\nNy Zanakao\nAo ny lova andrandraina\nFahasambaran’aina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (190, 1, 'O, ry Fanahy Masina ô !\nMba midira ao anaty fo !\nTaomy ny mania\nHody avokoa ;\nEny, mitahia,\nManomeza soa,\nMiantrà, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (190, 2, 'O, ry Mazava lehibe,\nHazavao ny teny izay ho re !\nAsehoy Jesosy,\nAmbarao ny vonjy ;\nIasao ny Nosy\nMba ho voavonjy.\nMiantrà, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (190, 3, 'Ry Afom-pitiavana ô !\nMba levoninao ny lolompo,\nAmpiraiso dia\nIreto ondry vitsy\nMba hifankatia\nHo iray mihitsy.\nMiantrà, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (190, 4, 'Ry Mpampifaly tsara ô !\nMba omeo toky re ny fo,\nMba hanantenanay\nFa ho voavonjy,\nAry handresenay\nNoho ny Mpamonjy.\nMiantrà, ry Tompo ô !\n\nNY TELO IZAY IRAY', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (191, 1, 'Ry Fanahy Mpanazava !\nMiandrandra Anao izahay !\nAza ela, fa tongava,\nTaomy ny fanahinay,\nMba ho fantatray mazava\nIzay lazain’ny teninao ;\nAreheto lalandava\nAo am-po ny afonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (191, 2, 'Ry Jesosy ! mitenena\nAraka ny fombanao,\nAminay izay manantena\nMba handre ny teninao ;\nFo marary sy manahy\nMba tsaboy sy iantrao ;\nNy te-hino fa tsy sahy,\nAnehoy ny holatrao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (191, 3, 'Rainay, Mpanome ny soa,\nMba henoy ny hatakay\nKa ny tananao tsy toha\nIarovinao anay ;\nNy tanora mba tahio,\nNy vaventy hafanao,\nMba ho hitanay anio\nFa atý ny tenanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (192, 1, 'Ny teny sy Fanahinao,\nRy Ray, mba avelao\nHandroso tsara, hanavao\nIzao tontolo izao.\nMitombo, mihabe\nNy ratsy izao, odrey !\nFa vitsy ny mpanomponao\nIzay ifalianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (192, 2, 'Vonjeo, ry Jesosy ô !\nNy Fiangonanao,\nFa misy marobe izao\nIzay mody  olonao,\nAnefa tsy manao\nIzay tena sitrakao.\nO, ry Mpanjaka lehibe !\nMba mamonje anie.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (192, 3, 'Fanahy ô ! mba raisonao\nIzay vavakay izao ;\nNy fonay mba havaozinao,\nNy maso hazavao,\nHahafantaranay \nIzay alehanay,\nMba hamoazanay izao\nIzay voa tianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (193, 1, 'Andriananahary, Aba, Ray,\nIzay ianteheranay,\nMba raiso re ny vavakay ;\nEsory sy vonoy ny helokay,\nVonjeo re, izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (193, 2, 'Ry Jeso Zanak’Andriamanitra,\nMpitarika ho an-danitra\nMpanolo-tsaina marina,\nMpamonjy tia ka nolavina,\nVonjeo re, izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (193, 3, 'Ry Mpampionona\nmandrakizay,\nAmpiomano izahay\nHandray ny famindramponao,\nNdrao very amin’otanay\nizahay,\nVonjeo re, izahay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (194, 1, 'Tahio re, ny teninao\nAnkalazao ny Zanakao,\nIzay nasandratrao ry Ray,\nHo kiady sy ho aronay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (194, 2, 'Ry Jeso ô ! Mpanjakanay,\nAmpandreseo izahay ;\nHo menatra ny rafinao,\nRaha hitany ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (194, 3, 'Ry Mpampionona anay,\nAmpikambano ho iray\nNy sampam-piangonana,\nHiray am-pitiavana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (195, 1, 'Andriananahary ô, endrey ny\nvoninahitrao !\nNy tany sy ny lanitra \nmamelatra ny lazanao,\nNy asanao rehetra e,\nny famonjena lehibe,\nMitari-dàlana anay hidera ny\nAnaranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (195, 2, 'Ry Zanakondry masina,\nJesosy Izay Isaoranay !\nHe, Hianao niantra anay, ka\nnentinao ny otanay,\nNy menarana resinao,\nny fasana vinakinao,\nKa Hianao nanokatra ny\nlanitra hidiranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (195, 3, 'O, ry Fanahy Masina,\nIzay mitarika anay\nHanetry tena sy hanaiky fa\nmpanota izahay,\nMitaona sy mananatra anay\nhandray ny avotra,\nKa manamasina anay, mba\nraiso ny fisaoranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (195, 4, 'Ny voavonjy mbamin’ny\nanjely mankasitraka,\nNy tany sy ny lanitra \nmikambana hanandratra\nAnao, ry Ray sy Zanaka,\nAnao Fanahy Masina,\nIzay maneho famonjena\nfeno fitiavana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (196, 1, 'Andriamanitra Ray\nloharanon’ny aina,\nMasina, masina re, Hianao ka\nderaina\nFa manome fahasoavana be,\nSoa tsy tratrin’ny saina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (196, 2, 'O, ry Jesosy Mpamonjy,\nMpanjaka mahery,\nTonga tatý mba hampody ny\nzanaka very,\nRaiso izao ny fiderana Anao,\nSaotra aman-daza sy hery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (196, 3, 'Ry Mpampionona ô,\nMpanazava ny sainay,\nMba ampinoy izahay, ka\nhavaozy ny ainay,\nMba hazavao ny manantena\nAnao ;\nRaiso ny hira hirainay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (197, 1, 'Ry Rainay ao an-danitra ô,\nIzay mendrika hoderaina,\nMahery sy be indrafo,\nTsy takatry ny saina,\nIzahay dia tonga zanakao,\nKa faly manana Anao :\nMihoby sy miravo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (197, 2, 'Izahay miondrika Aminao,\nMidera Anao tokoa,\nFa be ny fitiavanao,\nRy Ray mpanisy soa ;\nAnao izao rehetra izao,\nHo vitanao izay tianao,\nAndriananahary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (197, 3, 'Ry Jeso Kristy Tomponay\nMpanjaka manan-kery,\nIzay any ankavanan-dRay,\nMpanavotra ny very,\nHe, any no ifonanao\nHo an’izay navotanao,\nFa resinao ny ota.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (197, 4, 'Ny ranao no nalatsakao,\nFanati-tsaro-bidy !\nNy ainao ho atohanao\nNy olonao mpiady.\nNa atý na ao an-danitra\nJesosy Kristy tokana\nHo avotra sy vonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (197, 5, 'Ry Mpampianatra mahay\nFanahy Mpanazava,\nAmpianaro izahay\nHandroso lalandava,\nTariho ny fanahinay,\nHonenanay rahatrizay\nArý an-danitra Amen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (198, 1, 'Haleloia, deraina\nAndriamanitray,\nMpanjaka fototr’aina,\nMpikarakara  anay,\nNy zava-boahary\nDia eo an-tananao,\nKa tena lavorary\nNy fandaharanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (198, 2, 'Haleloia, deraina\nHianao, Mpamonjinay\nNijaly, noratraina,\nKa maty solonay,\nAnefa naharesy\nNy fahavalonay,\nNatsangana handresy\nMba ho fiainanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (198, 3, 'Haleloia, deraina\nHianao, Fanahy ô !\nNy herinao ny aina\nHamelona tokoa ;\nHianao mikarakara\nNy fiangonanao,\nManova, manatsara\nIzao tontolo izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (198, 4, 'Haleloia, deraina\nNy Telo Izay Iray !\nHobina sy hiraina,\nFa Andriamanitray,\nHe, Tompo foto-tsoa\nIainanay atý,\nFa ho izany koa\nMandrakizay doria.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (199, 1, 'Isaoranay Jehovah,\nFa Tompo lehibe,\nIzay tsy mba manova,\nNy famindramponao.\nMiorina aminay izao\nNy fiangona-masinao ;\nTorina aminay\nNy teny mampiray,\nKa ravoravo izao\nIzahay miderà Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (199, 2, 'Derao Jesosy Tompo,\nIzay Mpamonjinao,\nHiraonao re, ry foko,\nNy fitiavany.\nHaleloia ! haleloia !\nHaleloia ! haleloia !\nJesosy avotray\nDia Zanaky ny Ray\nNy ainy no voaray,\nHisolo helokay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (199, 3, 'Derainay, ry Fanahy,\nIzao ny asanao,\nTsy misy mampanahy,\nIzay tarihinao,\nKa anjakao re, ny fonay,\nArovy ny fiangonanay,\nVelomy izahay,\nAtaovinao mahay\nManoka-po izao\nHandray ny aim-baovao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (200, 1, 'Misaora ! misaora an’i\nJehovah,\nRy olona atý an-tany ô !\nFa lehibe ny indrafony\nKa maharitra mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (200, 2, 'Misaora ! misaora an’i Jesosy,\nRy olona atý an-tany ô !\nFa vitany ny famonjena,\nKa misokatra ny lanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (200, 3, 'Misaora ! misaora re ny\nFanahy,\nRy olona atý an-tany ô !\nFa fahazavana no omeny\nAry fanavaozana am-po.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (201, 1, 'Rainay ô Masina !\nTompon’ny lanitra,\nIzay tia anay ;\nKiady sy mandanay\nHo fiarovana,\nIzay hitokianay\nMandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (201, 2, 'Jeso ô, Tomponay !\nLalana marina\nAmin’ny Ray !\nFahamarinana\nAry fiainana\nAzonay aminao\nRy Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (201, 3, 'Fanahy Masina ô !\nIrak’i Jesosy\nTonga etý ;\nManazavà anay\nAmin’ny maizina ;\nNy hevi-dratsinay\nEsorinao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (201, 4, 'Andriamanitra ô !\nTelo fa tokana,\nTompo iray ;\nAza mandao anay,\nOry sy reraka\nNoho ny heloka\nIzay momba anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (201, 5, 'Aza tsaroanao\nNy hadisoanay,\nRy Tompo ô !\nFa mamelà anay,\nNoho ny Zanakao\nKa manomeza anay\nNy fo madio.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (202, 1, 'Midina, Tomponay !\nAmpionao izahay\nHidera  Anao ;\nRay soa tia  anay,\nHery sy Tanjakay,\nIzay hotompoinay\nMandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (202, 2, 'Avia, Jeso ô !\nReseo ny rafinay\nHo lovanao ;\nArovy izahay\nAmin’ny herinao ;\nNy fitarainanay\nHenoinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (202, 3, 'Fanahy Masina ô !\nTariho izahay\nHanaiky izao ;\nMahery Hianao,\nNy fonay anjakao,\nAza ilaozanao\nMpananatra ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (202, 4, 'Ry Telo izay Iray !\nDera mandrakizay\nOmena Anao ;\nNy voninahitrao\nAoka ho hitanay,\nKa raisonao izahay,\nHo Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (203, 1, 'Ry Ray malala ô !\nMba raisonao\nNy haja entiko\nHidera Anao.\nNy fitiavanao\nNo maiko izao\nIzaho dia Anao,\nRy Raiko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (203, 2, 'Ry Jeso tia  ô !\nMpanavotra,\nTa-hankalaza Anao\nNy vavako ;\nNy avo-dehibe\nManafaka  ahy e !\nIzaho dia  Anao,\nRy Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (203, 3, 'Fanahy Masina ô !\nMpananatra,\nMahiratra tokoa\nNy masoko,\nMazava re izao\nNy lalako hankao ;\nHe ! fifaliam-be\nNo ahy izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (204, 1, 'Ry Rainay be fitia,\nMpamelona anay !\nTariho tsy hania,\nHazoninao izahay ;\nFa be ny fahavalo\nMitady ny ainay,\nMamely tsy mimalo\nIlay ratsy hatrizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (204, 2, 'Ry Zokinay malala !\nRy Jeso havanay !\nToroy mba hahalala\nNy mety izahay ;\nNy miaraka  Aminao re,\nRy Jeso Tompo ô !\nNo mahasoa ny saina,\nNy fo, fanahy koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (204, 3, 'Fanahy Mpanazava\nSy Mpanadio ny fo !\nTomoera lalandava,\nMonena ao am-po ;\nKoa dia ampidino\nNy hasoavanao,\nOvay sy hamasino\nNy toetray izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (204, 4, 'Ry Tompo be fiantra !\nRy Telo izay Iray !\nNy tananao miantra,\nAtsory aminay ;\nRehefa tapitra andro\nKa hody izahay,\nMba raiso re, ry Tompo\nMba raiso izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (205, 1, 'Mamy ny fitianao,\nRy Jehovah Tompo ô !\nNahafoy ny Zanakao\nHanavotanao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (205, 2, 'Mamy ny antranao,\nRy Jeso Mpamonjy ô !\nTa-hamonjy Hianao\nSy hanafa-keloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (205, 3, 'Mamy ny fitaomanao,\nRy Fanahy Masina ô !\nNy Fanahinay ovay\nHankatò ny anatrao.\n\nNY SORATRA MASINA,\nTENIN’ADRIAMANITRA', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (206, 1, 'Ny teninao rehetra,\nRy Tompo tia  anay !\nNo tsy mba manam-petra,\nFa tò mandrakizay ;\nHo levona avokoa\nIzao rehetra izao,\nHaharitra tokoa\nIzao teninao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (206, 2, 'Ny tenin’ny Baiboly\nNo sambatra entinay\nHandresy ny devoly,\nIlay fahavalonay ;\nRaha misy mampahory,\nKa osa izahay,\nNy teninao mahery\nManjary tanjakay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (206, 3, 'Fa teny mamy koa\nHo an’ny mpakatò\nKa loharano soa\nMiboika ao am-po,\nHo fanomezan-toky\nNy azon’ny manjo,\nSy hani-mahavoky\nMamelona ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (206, 4, 'Fa teny mampisava\nNy aizim-be atý,\nFanilo manazava\nNy dia ho arý ;\nManolo-pahendrena,\nMananatra ny fo,\nMilaza famonjena\nSy lova tsy ho lo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (207, 1, 'Ny teny masinao,\nJehovah Tompo ô !\nNampanoratinao\nIreo mpanomponao,\nDia tiako ka tanako,\nHo lova sy fananako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (207, 2, 'Ny teny masinao,\nIzay noteneninao\nMilaza amiko\nNy hasoavanao ;\nIzay harena atolotrao\nHo entiko hidera Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (207, 3, 'Ny teny masinao,\nAndriamanitra ô !\nManeho ny fitia\nNataon’ny Zanakao,\nIzay maty tany Kalvary,\nHamonjy ny mpanota etý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (207, 4, 'Ny teny masinao,\nNo mampianatra\nNy saiko tsy mahay\nMba hahafantatra\nNy zavatra miafina\nIzay momba ny hasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (207, 5, 'Ny teny masinao,\nDia  tsy mba mety lo ;\nIzay ambarany\nTsy misy tsy ho to.\nNa levona aza ka tsy ao\nIzao rehetra àry ‘zao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (208, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (208, 2, 'Mamy ny teninao,\nJehovah Masina !\nSy to ny teninao\nTsy azo ovana :\nNy lanitra ho levona,\nFa tsy simba ny teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (208, 3, 'Madio ny teninao,\nKa tsara indrindra,\nDia fahendrena\nHo an’ny adala :\nMahafaka ny maizina\nKa mitondra ny mazava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (208, 4, 'Soa ny teninao,\nMahatsara ny fo,\nFa ao no hitanay\nMisy Mpanavotra ;\nJeso, Loharanon’aina,\nMahasasa ny meloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (208, 5, 'Irin’ny foko koa\nNy teny masina\nNoho ny harena\nNa fananana be :\nNy teninao no tanako\nHo lovako mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (209, 1, 'Ny teny masinao\nTorina hatrizao ;\nMaharitra tsy lo,\nMamabo izany fo,\nFa marina ka tianay\nHiainana mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (209, 2, 'Ny teny masinao\nMitondra herivao,\nMandrava ny manjo\nIreo izay ory fo ;\nKa mendrika ho tanana\nAnaty, ho fananana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (209, 3, 'Ny teny masinao\nDia tsy mba mankaleo ;\nFa hery velona\nMamelombelona ;\nKa satrinay ho re indray\nRy Andriananaharinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (209, 4, 'Ny teny masinao\nNo feo manorina ;\nNy teny masinao,\nNo feo manarina\nKa tianay hanenika\nNy tany sy ny lanitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (210, 1, 'Jesosy tena kiady\nSy reharehanay,\nNamoha vavahady,\nNy Tanindrazanay ;\nNampiditra ny teny\nHampahalala  anay\nNy Ray, ny fahendreny,\nMba hianaranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (210, 2, 'Na betsaka mifofo\nNy Baibolinay,\nMihaza sy milofo\nMoa ketraka  izahay ?\nJesosy no Mpiaro\nKa moa ho kivy ve ?\nVao mainka mihamaro\nNy mpankasitraka e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (210, 3, 'Mbola Izy no Filoha\nMpitarika anay,\nHitory ka hamoha\nIreo namanay :\nVahoaka an-tapitrisa\nNo mbola hasondrotray,\nHahita Paradisa\nHo zanaky ny Ray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (210, 4, 'Ry Jeso, hatanjaho\nNy fiangonana,\nHijoro, ho  tena taho\nMamoa mandrakizay.\nHianao anie no sandry\nIzay tena tanjakay,\nNo sady hany andry\nIzay hijoroanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (210, 5, 'Jesosy soa malala !\nTsy foinay Hianao,\nNy fonay dia handala\nIty lova taminao.\nFanahy ravoravo\nNoho ny teninao,\nRy Zanaky ny Avo,\nIzao misaotra Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (211, 1, 'Zava-tsoa tokoa ny fitsipikao\nTompo Andriamanitra ô !\nTeny mahasoa, aina, hery koa\nMba ho an’izay manoa.\nSoa tsy manam-paharoa ny\nfitsipikao\nIravoanay, fa harena be\nSarobidy, tsy afoy, izany\ndidinao,\nFamonjena ho an’izay\nmandre.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (211, 2, 'Maizina izahay, kely fantatray\nNefa ao ny teninao :\nJiro sy fanilon’ny tongotray,\nHankanesana  Aminao.\nSoa tsy manam-paharoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (211, 3, 'Raha alaim-panahy itony \nzanakao\nIndro, ao ny teninao,\nSabatry ny fo, hanoheranay\nIlay ratsy hatrizay,\nSoa tsy manam-paharoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (211, 4, 'Fifalianay, odi-tahotray\nNy Baiboly taminao :\nAvy ny manjo, nefa kosa, Ray\nAo tokoa ny tavanao\nIravonay ..sns.\nSoa tsy manam-paharoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (211, 5, 'Ry Jesosy ô Mpampianatray !\nMaminay ny teninao ;\nTaomy izahay, mba \nhahaizanay\nHankatò ny didinao.\nSoa tsy manam-paharoa ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (212, 1, 'Ilay Boky izay nodorana\nDia may, nefa tsy levona ;\nIzay natao ho lavenona,\nLasa vato fanorenana ;\nNoheverina ho foana,\nKanjo mainka koa velona ;\nIlay nafenina, nalevina\nNo nisandratra, nivelatra !\nIza moa no hahasakana\nEny, iza no hanohitra\nIlay teny voasoratra,\nDia ny tenin’Andriamanitra ?\nIzao rehetra izao ho levona\nFa ny Teny dia haharitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (212, 2, 'Rasalama nolefonina\nNisy hafa koa nodorana\nHantsana no namarinana,\nTora-bato: fahafatesana\nDia tao ireo nampinomina,\nNoterena mba hiozona :\nMoa izany nampivadika\nKristiana be finoana ?\nValin-teny voasoratra\nNentiny nandresy lahatra\nFeo tony, tava mirana,\nFeno hery, hasambarana :\n« Tsy mba misy atahorako\nAfa-tsy  Hianao, \nry Tompoko ».', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (212, 3, 'Ilay teny masina sy to\nManambara ny Mpamonjiko,\nTena saro-bidy ao am-po,\nKa vakiko sy ianako.\nFifaliako, fiarovako,\nFahazavana ao an-dalako ;\nKoa tanako ho lovako \nSy isaorako ny Tompoko,\nMaty aho, nefa velona\nFa Kristy no ahavelomako,\nMaty Izy, nohomboana,\nNoho  ny fahotana vitako\nNefa Izy dia natsangana,\nSantatry ny hitsanganako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (213, 1, 'Velona sy mahery\nNy tenin’Andriamanitra,\nKa na dorana aza\nDia tsy ho levona ;\nNy Sekoly Alahady\nDia mivelona aminy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (213, 2, 'Sabatra rao lela\nMaranitra, manindrona,\nMampisaraka aina\nSy fanahy, tonona ;\nFantany avokoa \nNy am-po miafina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (213, 3, 'Hotsaraina isika,\nKa ilay teny velona\nNo hanavaka antsika\nAry koa hanavotra,\nKoa  minoa ny teny,\nTeny fahasoavana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (214, 1, 'Ry fanahy mangetaheta,\nMila fitsaharana ;\nAza kivy, mahareta,\nSentonao hifarana !\nFa ny tenin''i Jehovah,\nSoa mangarangarana ;\nLoharano tsy miova,\nAo no hasambarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (214, 2, 'Ry tontolo mila vonjy,\nTe ho afa-patotra,\nAo ny tenin''ny Mpamonjy\nDia  Kristy Mpanavotra !\nTeny velona sy mamy,\nFeno fananarana ;\nHotorina eran-tany \nFa fampihavanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (214, 3, 'Ry Mpanjakan''ny mpanjaka,\nAo, tendreo ny lelako\nMba hitory sy hizaka,\nHampandre ny havako,\nDia  tahio ny Teny reny\nTsy ho very hasina,\nKa hamoy zato heny\nNy Baiboly masina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (215, 1, 'Finaritra ry Tompo ô !\nNy mankafý ny teninao\nSy mankatò ny didy.\nNy helony esorinao,\nNy sainy hohavaozinao,\nKa afaka hifidy\nNy lova sarobidy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (215, 2, 'Ny teninao no rakitra\nSy tokin''ny mpandefitra,\nFa teny manan-kery ;\nHe ! ao dia voasoratra\nNy Aminao Mpanavotra,\nFa Hianao irery\nNy avotry ny very.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (215, 3, 'Ny teninao no vatsinay\nManohana ny dianay,\nSakafo mahasoa \nSy ampy hivelomanay,\nKa ao am-parany indray\nHo azonay tokoa\nNy lovan''ny mpanoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (216, 1, 'Ny teny masina sy to\nNampilazainao, Tompo ô !\nNo manan-kery mahavoa\nNy fahavalonao tokoa,\nKa dia itokianay\nNa aiza no alehanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (216, 2, 'Mitaona ny fanahinay\nNy teninao, Mpanavotray,\nMananatra sy manambara\nNy ratsy ary koa ny tsara ;\nMpilaza mahafatra-po\nNy teninao ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (216, 3, 'Ny teninao no sabatray\nSy mpampionona anay ;\nNy teninao no mofon''aina\nSy mpanazava fo sy saina ;\nMpiaro sady tanjakay\nNy teninao, ry Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (216, 4, 'Ampinganay ny teninao\nAo amin''izao tontolo izao,\nKa raha levona ny tany,\nNy teny mbola ao ihany,\nDia izy iankinanay\nHo antoky ny avotray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (217, 1, 'Ny teninao ry Tompo ô,\nDia  ranon''aina soa, \nIzay mahalonaka ny fo\nMangetana tokoa,\nJereo ny tanimbolinao,\nFa tena mila io,\nHo feno voa ho Anao\nHatramin''ny anio', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (217, 2, 'Ny teninao dia sabatra\nMaranitra tokoa,\nMitsara sy mamantatra\nNy fonay sy ny voa.\nHetseho izao tontolo izao\nHandray ny teninao,\nO, asehoy ny herinao,\nHamorona  aim-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (217, 3, 'Ny teninao izay tokinay\nFanilon-dianay,\nNoraisinay ho lovanay\nSy fifalianay.\nAmpianaro izahay\nHanoa ny didinao,\nAtaovy izay hitiavanay\nNy teny masinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (218, 1, 'Ry Jesosy Tomponay !\nIndreto izahy mivory,\nNefa tsy mahay mandray\nIzay ambaranao akory :\nKa iraho ny Fanahy\nMba hanampy sy hitahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (218, 2, 'Maizin-tasina izahay,\nFa taranaka mpanota ;\nSimba ny fanahinay,\nTena voasoron’ota,\nTompo tsara ô, tongava,\nAposahy ny mazava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (218, 3, 'Ry Mazava lehibe,\nTaominao ny fo malaina ;\nTairo mba hifoha re,\nKa handray ny tenin’aina ;\nNy fivavakay valio,\nNy ataonay mba tahio.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (219, 1, 'Mba miangavy, Tompo ô !\nIraho ny Fanahinao,\nVangio ny mpanomponao,\nAmpianaro izy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (219, 2, 'Ampalasio ny sofinay\nHandraisanay ny teninao,\nTendreo koa ny lelanay,\nHahaizanay hidera Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (219, 3, 'Hiely be ny lazanao\nAmbara-pahatonganao\nHitsara izao tontolo izao,\nHampody ny navotanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (219, 4, 'Fanahy, Zanaka sy Ray,\nMba raiso ny fisaoranay ;\nKa raha hiala atý izahay,\nHidera Anao rahatrizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (220, 1, 'Ry Mpampianatra mahay\nFanahy Mpanazava,\nAmpianaro izahay\nHandroso lalandava,\nNy indrafon’ny Tomponay,\nVelaro eo imasonay\nHo fantatray mazava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (220, 2, 'Mivory eto izahay\nHandre ny tenin’aina,\nKa hazavao ho fantatray\nNy hevitra lazaina ;\nOvay ny sainay sy ny fo\nHamantatra sy hankatò\nNy tenin’ny Mpamonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (220, 3, 'Raha  hodimandry izahay\nKa tonga ny anjara,\nBitsiho ao an-tsofinay\nNy tokon’aina tsara ;\nAtaovy mora hitanay\nNy tavan’ny Mpanavotray,\nIzay hery mahavonjy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (221, 1, 'Ry Jeso Tompo Avotray,\nMitomoera aminay,\nAidino ny Fanahinao,\nTahio izahay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (221, 2, 'Ny sitrakao mba ambarao\nHo fantatray ny hevitrao ;\nNy fonay amboaronao\nHahay handray ny teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (221, 3, 'Ny vavanay hasinonao\nHahaizanay hisaotra Anao,\nFa be ny soa azonay\nNomenao, ry Mpanavotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (221, 4, 'Rahatrizay ny olonao\nHihoby sy hidera Anao,\nFa masina, fa masina,\nFa masina i Jehovah.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (222, 1, 'Mivory eto izahay,\nJeso Tompo ô ;\nAvia hanazava anay,\n:,: Hinoanay Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (222, 2, 'Maniry mafy izahay,\nHihaona Aminao ;\nOmeo hosotroinay,\n:,: Ny rano velona :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (222, 3, 'Ny mofon’aina mba zarao\nHo anay, ry Tompo ô !\nTahio re ny irakao,\n:,: Ho voninahitrao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (222, 4, 'Ny fonay dia atolotray\nAnao, Jesosy ô !\nOmeo hery izahay\n:,: Hanao ny sitrakao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (222, 5, 'Raha tapitra ny andronay,\nIzay voafetranao,\nDia  ento mody izahay\n:,: Ho any Aminao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (223, 1, 'Ry Lela afo masina\nNilatsaka fahiny\nTan-dohan’ny mpianatra\nSy tao anaty fony,\nMiandry fatratra Anao\nIzahay mivory eto izao :\nAvia ry Fanahy !\nAvia, ry Jesosy ô !\nAtreho ny mpianatrao\nHo velona am-panahy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (224, 1, 'Mba avia, malakia,\nRy mangetaheta ô !\nTeny tsara no ambara,\nKa Henoy eram-po,\nFa Jesosy ranon’aina\nNo torina sy lazaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (224, 2, 'Ry Mpamonjy  mahavonjy,\nMahatonga soa be,\nKa tsy maintsy ho tra-bonjy\nIzay manatona Azy re ;\nKa henoinao ny teny\nManambara ny omeny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (224, 3, 'Mba tariho  sy  tahio\nAho, ry Jesosy ô !\nMba ho ahy ny fanahy,\nHanazava ao am-po.\nHampahery fo sy saina,\nKa hamelona ny aina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (225, 1, 'Tafavory izahay ka manatona\nAnao\nZanahary nahary anay ;\nKa mba raiso izao\nIzahay Zanakao ;\nAza lavina re izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (225, 2, 'Tafavory izahay ka miantso\nAnao\nRy Jeso Mpamonjy anay ;\nMihainoa Tompo ô !\nMitenena izao,\nFa mitady Anao izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (225, 3, 'Tafavory izahay ka miandry\nAnao\nRy Fanahy Mpananatra anay ;\nAidino ilay afonao fahizay,\nAreheto atý aminay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (226, 1, 'Atý an-tranonao izao\nNo iangonanay\nHitady soa Aminao\nHahafaka ota anay ;\nHiantso ny Anaranao,\nHandinika ny teninao,\nIzay mahavelona anay,\nKa mba ambinonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (226, 2, 'Na vitsy aza eto izao,\nNa be no tonga atý,\nDia aza mba lalovanao,\nAtreho izahay !\nRaha  tonga eto Hianao\nDia ampy izany ho anay ;\nNy fofonain’ny Tomponay\nNo mahavelom-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (226, 3, 'Raha misy mangatsia-po,\nMba hafanao, ry Ray ;\nIraho ny Fanahinao\nHamelona anay.\nHanetsika ny mafy fo\nHo tonga mpino mankatò\nIzay lazain’ny teninao\nHataon’ny zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (226, 4, 'Izay rehetra tonga atý\nAmpirisihonao ;\nKa aoka samy hankafý\nNy fanavotanao.\nNy fonay mba sokafinao\nHandray ny fananaranao ;\nAo aminao ny hery be,\nEfao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (226, 5, 'Ambinonao ny irakao\nHitory teny izao ;\nTolorinao ny hevitrao,\nNy fony hafanao ;\nSokafy re ny vavany,\nTendreo koa ny lelany\nHilaza tsara aminay\nNy sitrakao, ry Ray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (227, 1, 'Tompo ô ! mba te-hihaona\nAminao ny olonao.\nKa ny oran-dohataona\nAmpombay ny teninao ;\nNy Fanahinao iraho\nAminay izay mitalaho ;\n:,: Mba vokiso zava-tsoa\nNy mangetaheta  Anao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (228, 1, 'Eto afovoanay Hianao,\nry Tompo soa,\nKa tahio ny vavakay\nMba ho maminao tokoa ;\nRaisonao ny fideranay,\nKa savao ny fahantranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (228, 2, 'Mitoera aminay\nNa hariva na maraina ;\nAza mahafoy anay\nMandra-pialanay aina ;\nKa ny dera tsy ho lany\nDia ho Anao ihany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (229, 1, 'Tafangona atý indray\nNy fiangonanao, ry Ray\nKa dia mba tahio !\nIraho ny Fanahinao\nHitarika ny zanakao\nHivavaka anio,\nAnao izao\nSaotra, laza, dera, haja,\nFa nomena\nHo anay ny famonjena.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (230, 1, 'Jesosy ô ! mba hasoavinao\nNy asanay, izay atombokay\nAmin’ny Anaranao ;\nKa mba ombay,\nSy tahio, Tompo ô !\nIzao atao izao\nDia tena sitrakao\nKa ho tanterahinay,\nMba ho fifalianay\nSy ho voninahitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (230, 2, 'Jesosy ô ! derainay Hianao,\nFa feno famonjena olona\nNy Anara-masinao !\nNy herinao manome\naim-baovao.\nNy teny masinao\nDia iankinanay\nKa Hianao isaoranay\nSy ankasitrahanay\nEto sy rahatrizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (230, 3, 'Jesosy ô ! ny fanorenana\nIainanay sy hahafatesanay\nDia ny Anaranao, fa Izy no\nManome toky be,\nKa hitsangananay\nHo velona indray ;\nDia mba horaisinao\nIzahay navotanao\nAmin’ny Anaranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (231, 1, 'Mba trotroy ny olona ory,\nRy Jesosy Tompo ô !\nTsy hirenireny saina,\nFa handray ny teninao.\n:,: Olona  osa, olona  osa,\nEnto mora, Tompo ô :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (231, 2, 'Faly izahay, Jesosy,\nTratr’izao anio izao,\nTafavory ka hihaino\nNy Filazantsaranao.\n:,: Raisonao re, raisonao re\nIzao fisaoranay izao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (231, 3, 'Jesosy Tomponay Tsitoha !\nTaomy ny sakaizanay\nMba ho tonga mpino koa\nSy hiankina Aminao.\n:,: Mba tariho, mba tariho\nIzy ho mpanomponao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (232, 1, 'Ry Tomponay Tsitoha !\nIlay Izy hatrizay,\nAvia, hampifoha\nNy fiangonanay\nAtreho ny mpivory,\nFa manantena Anao ;\nFohazy ny matory\nHandray ny teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (232, 2, 'Iraho ny Fanahy\nMba hanavao anay,\nKa homba sy hitahy\nNy teny inoanay\nNa lehibe na kely,\nAmpio sy hafanao\nHazoto hampiely\nAtý ny lazanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (232, 3, 'Ny asam-pamonjena\nIzay natombokao,\nAmbino tsy hihena\nTahio sy tontosao !\nNy herin’i Satana\nFolahy hatrizao,\nHo afa-pahotana\nIzahay navotanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (232, 4, 'Arovy ny finoana\nIzay naorinao ;\nVonoy ny hevi-poana\nMandà ny teninao\nIzahay na be na vitsy,\nHazony tsy hania,\nMba ho Anao mihitsy\nMandrakizay doria.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (233, 1, 'Ny Alahady andronao,\nJeso Tompo masina ô !\nFotoana nitsangananao,\nKa maminay mpianatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (233, 2, 'Anio, mivory izahay,\nTahaka  ireo mpianatrao ;\nKa enga anie ho hitanay\nNy hazavan’ny tavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (233, 3, 'Omeo anay, omeo indray\nNy herin’ny Fanahinao ;\nHavaozy ao anatinay\n‘Lay Pentekosta fahizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (234, 1, 'O ! Ray Malala, asehoy\nNy hasoavanao ;\nSasao ho afa-keloka\nIzahay ho zanakao ;\nHo mafy ny fiezakay\nHandia ny lalanao\nTsy hisy hijanonanay\nRaha  tsy an-tranonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (234, 2, 'O ! Ray Malala, mba henoy\nNy feonay zanakao ;\nTantano ao an-dalana\nHankany Aminao ;\nHo mafy ….sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (234, 3, 'O ! Ray Malala, hitanao\nNy hasambaranay ;\nNy avotr’ain’ny Zanakao\nNo itokianay ;\nHo mafy ….sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 1, 'Mahery Hianao,\nJehovah Tompo ô !\nMombà anay mpanomponao,\nKa miarova anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 2, 'Mpanjaka Hianao,\nRy Zanahary ô !\nAvia, anjakao izao\nNy ao anatinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 3, 'Miaro Hianao,\nAndriamanitra ô !\nHidera sy hisaotra Anao\nNo iangonanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 4, 'Miantra Hianao,\nRy Tompo tia ô !\nTongava aminay izao\nMba hifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 5, 'Ny fanomezan-tsoa\nIzay maminay tokoa\nDia  Jeso ‘lay malalanao\nNafoy ho avotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (235, 6, 'Mba raisonao, ry  Ray !\nNy saotra entinay,\nNy dera, laza, hery koa\nAnao Mpanjaka soa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (236, 1, 'Tafavory izahay\nEto anatrehanao,\nRy Jehovah Tomponay !\nMba hivavaka aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (236, 2, 'Te-hianatra izahay\nMba hanao sy sitrakao,\nMihainoa ny vavakay ;\nManampia anay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (236, 3, 'Fa mangataka aminao\nFanampiana izahay ;\nRaha tsy ampianao; \nDia ho diso izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (236, 4, 'Izay rehetra anananay\nEntinay miderà Anao,\nAoka hazoto izahay\nMba hanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (236, 5, 'Aoka handroso izahay\nMba hanompo tsara Anao,\nDia  hifaly izahay\nAmin’ny fitianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (237, 1, 'Avia, Tomponay !\nAtrehonao izahay ;\nFiderana no atolotray\nHo Anao ry Tomponay ;\nTongava atý an-tranonay,\nHandray ny dera atolotray,\nAvia, Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (237, 2, 'Avia, Tomponay !\nAtrehonao izahay ;\nEnga ka ho tonga olonao\nIzao izahay rehetra izao !\nNy vavanay hidera Anao,\nNy fonay mba diovinao ;\nAvia, Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (237, 3, 'Avia, Tomponay !\nAtrehonao izahay ;\nAmpidino ny Fanahinao\nHo atý an-tranonay ;\nAmpionao koa ny olonao,\nHilaza anio ny teninao :\nAvia, Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (237, 4, 'Avia, Tomponay !\nAtrehonao izahay ;\nFiderana no atolotray\nHo Anao , ry Avotray !\nMba raisonao ny deranay,\nEfao tokoa ny hatakay :\nAvia, Tomponay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (238, 1, 'Ry Jehovah Tomponay !\nTafavory izahay ;\nAvy hankalaza Anao\nIzahay vahoakanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (238, 2, 'Faly, ravo izahay\nNoho ny soa azonay ;\nTonga mba hisaotra Anao\nIzahay voavotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (238, 3, 'Saotra tsara entinay,\nHira no asandratray,\nMba ho fiderana Anao,\nRain’izao rehetra izao,\nAmen.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (239, 1, 'Mivory eto izahay\nHiantso ny Anaranao,\nJehovah Masina ô !\nDia manomeza anay anio\nNy soa avy taminao,\nHo fitahiana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (239, 2, 'Ny vavaka  tononinay\nMiakatra any Aminao,\nMpamonjy tia anay !\nMba raiso ao an-danitra\nHo zava-mani-pofona\nIzay ho tianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (239, 3, 'Ny saotra izay aterinay\nMba raisonao, Jehovah ô,\nAndriamanitray !\nNy vokatry ny molotray\nDia aoka ho fanatitra\nAnkasitrahanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (239, 4, 'Ankehitriny, Tompo ô !\nOmeo ny Fanahinao\nHitoetra aminay.\nHo fanalana aizina,\nSy fanoloran-kevitra\nHo an’ny olonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 1, 'Ry Jeso !  vory izahay,\nFa andronao anio\nDia avy mba hivavaka\nAtý an-tranonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 2, 'Ka na aiza na aiza ny olonao\nNo vory toy izao\nNy herinao mba asehoy,\nEfao ny teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 3, 'Ny hira ikambananay\nAtý an-tranonao.\nEkeo ho fanandratana\nNy voninahitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 4, 'Ny vavaka  tononinay\nEo anatrehanao.\nMba raiso ho fanatitra\nAnkasitrahanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 5, 'Ny teny  fitarainana\nAtaon’ny ory koa\nSoloy fifaliana\nHisaoranay Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (240, 6, 'Ny teninao halahatra\nHambininao anie\nHamelom-pitiavana\nAm-pon’izay mandre.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (241, 1, 'Alahady andro tsara,\nAndro fitsaharanay,\nNitsangananao ry Tompo,\nAndronao, Mpanavotray !\nEny maminay ny andro\nAndro fiangonanay\nHiderana Anao, Mpamonjy,\nSy ny voninahitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (241, 2, 'Tovy aminay ny teny\nIzay nataonao fahizay,\nTamin’ny Apositoly\nFony vory toy izao :\nRaha misy roa na telo\nAmin’ny ny Anarako\nVory mba hiantso Ahy,\nAo no hiangonako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (241, 3, 'Mba soraty ao am-ponay,\nTompo ô ! ny didinao.\nHiasanay saina tsara\nEto anatrehanao .\nMba avia, ry Jeso Tompo !\nTomoera aminay.\nHitandremanay ny andro\nIzay atokana ho Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (242, 1, 'Ry Jeso ô !  midina,\nJereo izahay izao ;\nAtreho sao variana,\nKa tsy mahita Anao,\nHazoninao ny saina,\nHijery tsara Anao,\nMba tsy ho very maina\nIzao fivoriana izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (242, 2, 'Ry Jeso !  hamasino\nNy toe-piainanay,\nKa dia ampidino\nNy hery ho anay ;\nNy bitsika mangina\nAtaovinao izao,\nHamelom-paniriana\nHanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (242, 3, 'Ry Jeso ô !  monena,\nMidira Hianao,\nNy fonay mamantena\nHandray Anao izao ;\nKa izay misalasala\nAmpaherezonao,\nDia  taomy hahalala\nNy fitiavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (242, 4, 'Ny hatakay mangina\nHenoy ka valio,\nKoa dia mitenana\nHianao ankehitrio,\nRy Jeso ! Mpanazava\nNy dianay atý,\nOmàny lalandava\nNy fonay ho arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (243, 1, 'Misaotra Anao izahay,\nry Ray !\nFa tsy nafenina anay\nIzay tena sitrakao tokoa\nMba hahazoanay ny soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (243, 2, 'Ka mitariha anay izao\nHandray ny toro-hevitrao\nIzay lazain’ny teninao\nHanaoavanay ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (243, 3, 'Ampahirato izahay\nHahaizanay mahita anio\nNy zava-mahagaga anay\nAo amin’ny lalànao soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (243, 4, 'Ampianaro izahay\nHo tia  tokoa ny teninao,\nKa hankatò ny didinao,\nHanaiky hotapahinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (243, 5, 'Ny asan’ny Fanahinao\nMba tanteraho aminay\nHampiorina ao anatinay\nIzay lazain’ny teninao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (244, 1, 'Mivory eto izahay,\nHandre ny teninao\nIzay hambara aminay,\nAndriamanitra ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (244, 2, 'Tolory fahendrena,\nKa ampio ny irakao,\nIzay hitory aminay\nNy teninao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (244, 3, 'Ny fon’izay mihaino koa\nSokafy rahateo,\nHo tia  sy hirakitra\nNy teny mahasoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (245, 1, 'Ry Jeso ! mba henoinao\nNy feonay mpanomponao\nIzay mivavaka  Aminao,\nMitady fitahiana', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (245, 2, 'Vokiso ny Fanahinay,\nNy teninao no haninay,\nHahaizanay mamahana\nNy reraka ao an-dalana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (245, 3, 'Ampaherezonao izao\nNy tànanay mpanomponao,\nHanohana ny olonao\nMiraviravy tanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (245, 4, 'Omeo fo mazoto re !\nTolory fahendrena be ;\nTarihonao ny hevitray\nTendreo koa ny molotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (245, 5, 'He ! tsinontsinona izahay\nMalemy sady tsy mahay ;\nKa dia ampaherezonao\nHanaovanay ny sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (246, 1, 'Ray ô ! asehoinao\nAminay ny antranao,\nRaisonao ny zanakay\nAtsangana ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (246, 2, 'Zanak’Andriamanitra ô !\nMba ekeo ny hatakay,\nTrotrinao ny zanakay\nTaomy ho mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (246, 3, 'Ry Fanahy Masina ô !\nMitoera aminay,\nIndro re ny zanakay\nAmpianaro, hazavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (246, 4, 'Ry Jehovah Izay Iray !\nNefa Telo, dia ny Ray,\nZanaka, Fanahy koa !\nMba tahio ny zanakay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (247, 1, 'Zazakely entinay\nMba hatao batisa izao ;\nIzy no atokanay\nSy atolotray Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (247, 2, 'Rano tsy mba mahasoa,\nRano tsy mba mahadio :\nKa tahionao izy izao,\nJeso ô, be indrafo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (247, 3, 'Ary ny Fanahinao\nDia ataovy ao am-po\nMba hitarika azy, sao\nHisy loza izay hanjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (247, 4, 'Ry Jehovah be fitia !\nRy Jesosy Tomponay !\nRy Fanahy koa, avia\nMba handray ny zanakay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (248, 1, 'Ry Tompo ô ! tsinjovinao\nNy mila famonjena\nSatria manantena,\nNy tany namboaranao\nHo an’izay mpianatrao\nHo lovany tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (248, 2, 'Fa Hianao dia tsy mandà\nNy ory sy mpanota\nIzay nandatsahanao ny rà\nHanaisotra ny ota ;\nKa tonga izahay izao\nMitady hodiovinao ;\nMba raisonao ny fonay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (248, 3, 'Mifona, Tompo, izahay\nIzay efa voaisa\nHo olonao, izay nandray\nNy soan’ny batisa ;\nAmpaherezonao ny fo\nMba tsy hafoinay ny to\nAmbara-pahafaty !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (249, 1, 'Nitondra ny zaza\nny reny tao Salema,\nMba hotahin’i Jeso Kristy\nbe indrafo ;\nNolavin’ny mpianatra izy,\nNefa hoy Jesosy hoe : \n“Avelao ny zaza \nhanatona  izao”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (249, 2, 'Fa Ahy ny zaza\nka tiako indrindra,\nMpiandry zanakondry Aho\nka aza mandà ;\nFa raha manantona Ahy izy,\nHotahiko tsara e :\n“Avelao ny zaza \nhanatona  izao”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (249, 3, 'Endrey mahagaga\nNy fiantran’i Kristy,\nFa indro zazakely aza\nasiany soa ;\nAvia, manatòna ilay \nMpiandry zanakondry, ka\n“Avelao ny zaza \nhanatona  izao”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (249, 4, 'Ry Jesosy Tompo !\nampanatony Anao re\nNy zazakely tafavory\neto izao\nIzay mandrara mba rarao.\nHo tò anie ny teninao :\n“Avelao ny zaza \nhanatona  izao”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (250, 1, 'Tompon’ny lanitra ô !\nmba tahio\nIzy izay ta-hanatona  Anao,\nKa ta-handray ny batisa anio\nSady maniry mba ho Zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (250, 2, 'Ampifalio ny fony, izay ory,\nSady miaky ny ota natao\nAry ny ratsy maniry fongory,\nKa mba omeo ny aina vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (250, 3, 'Izay manaiky hitana ny soa\nSady mandà ny devoly izao ;\nAoka hiseho ho mpino tokoa\nAmin’ny fomba sy asa \nvaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (250, 4, 'Afon’ny lanitra ô ! mba avia,\nKa ateraho ny fony izao\nAry tarihonao mba tsy hania\nFa te-ho sambatra ao Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (250, 5, 'Andriamanitra Ray be fitia,\nTaomy ny maro hanatona \nAnao\nNy Fiangonanao, ho be\nmpitia,\nHo marobe ny ankohonanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 1, 'Atolotray hatao batisa\nNy zanakay Jesosy ô !\nIrinay izy mba ho isan’\nNy zaza arovanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 2, 'Izahay izao efa nifidy\nAnao handray ny zanakay ;\nKa raiso izy fa voavidy\nNoho ny ranao soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 3, 'Soraty izy mba handova\nNy lanitra fonenanao,\nKa ny Fanahinao hanova\nNy fony ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 4, 'Tahio ny fahazazany\nHo fanorena-marina ;\nAmpianaro azy izany,\nMivavaka Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 5, 'Tantano izy mandra-pita,\nHo tonga ao an-danitra,\nKa aoka izy mba hahita\nAnao an-dalany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (251, 6, 'Ka amin’ny fotoana feno,\nHandaozany ity tany ity,\nOmeo toky, iteneno :\n« ‘Ndeha hody, anaka ».', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (252, 1, 'Ry Jeso Zana-Janahary\nSy fototry ny aim-baovao !\nMba aniraho ny Fanahy\nIzao madinikao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (252, 2, 'Ny fahafatesanao nandresy\nNo aoka hitombonany,\nNy herinao tsy mety resy\nNo mba ho fiarovany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (252, 3, 'Ka ny Anaranao hajaina\nDia mba omeo ny zanakay ;\nKa io no hialany aina\nSy hitsanganany indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (252, 4, 'Tantano izy tsy ho osa,\nRaha lavitra ny diany ;\nFa raha fohy izany kosa,\nMba raiso ny fanahiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (252, 5, 'Ka mba soraty ao am-pony\nRy Tompo, ny Anaranao,\nKa ny anaran’ny mpanompo\nSoraty ao an-tananao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (253, 1, '« Mba avelao ny zaza\nHanatona Ahy re,\nFa an’ny toa azy\nNy fanjakan’ny Ray ! »\nIzany teny tsara\nNo ifalianay,\nKa dia tsy rarana\nNy zazakelinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (253, 2, 'Ao amin’ny batisa\nTrotroiny izahay,\nKa tena voaisa\nHo zanany indray,\nAtsangany handova\nNy fahasambarana,\nFainan-tsy miova\nSy fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (253, 3, 'Sakaizanay malaza\nRy Jeso, Hianao,\nFinaritra ny zaza\nMiankina Aminao.\nAmpio, ry Mpamonjy,\nNy ondrikelinao,\nHo tonga voavonjy\nAo anatrehanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (254, 1, 'Avia, ry Mpanavotra.\nTahio ny fandraisana\nSakafo namboarinao\nIzao hatoninay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (254, 2, 'Ireo zava-masina\nAroso mba hohanina\nHo famelana heloka,\nHo aina sy ho tanjaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (254, 3, 'Tsaroanay ny dianao,\nNy ra sy fijalianao,\nNy nisoloanao anay,\nNy nitsangananao indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (254, 4, 'Ny teninao natolotra,\nNy ranao dia nalatsaka,\nSakafo iombonanay,\nHo tena sy fanahy iray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (255, 1, 'Avia, ry mangetaheta ô\nKa aza dia mba mamoy fo,\nFa indro misy rano velona\nIzay omena mba hamelona\nAnao mahantra sy variana\nRy zanakondry very làlana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (255, 2, 'Ny mofon’aina no atolony\nAnao manaiky mba ho olony\nNy tenany no ho fihinana,\nMba hahazoana fiainana ;\nKanefa tsy mba hovidina anie,\nFa tena fanomezan-dehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (255, 3, 'Jesosy Tompo no nanolotra\nNy rany masina ho avotra\nIzany raiso mba ho tanjaka\nSy toky ho anao, ry reraka\nRy mpivahiny sasatra ao\nam-po,\nAvia re, ka manaràna fo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (256, 1, 'Ny ran’ny Zanakondry latsaka\nNamelona  ahy tany Golgota,\nKa io no tena vatofantsika\nMaharitra ny tafio-drivotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (256, 2, 'Raha voaratra noho ny otako,\nKa lany foana ny heriko,\nNy rany masina nalatsaka\nNo nahitako fahasitranana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (256, 3, 'Izao hira vao izao no mamiko,\nKa tonga tsy misaraka amiko\nNy ran’ny Zanakondry no\nmahay\nManavotra ahy mba ho an’ny\nRay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (256, 4, 'Raha ketraka sy kivy re ny fo,\nVelomiko indray ny hirako\nNy amin’ny Jesosy sy ny rà\nKa afaka erý ny tahotra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (257, 1, 'Mivoaka ao Golgota\nIlay renirano soa\nIantsoana ny mpanota\nHo any avokoa ;\nNy ratran’ny Mpamonjy\nNo loharanon’io,\nKa ony mahavonjy\nSy tena mahadio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (257, 2, 'Mamelona ny tany\nIty ony soa ity,\nIzay rehetra ombany\nMiadana erý,\nVahoaka an-tapitrisa\nIzay misotro ao,\nMahita Paradisa\nSy fiainana  vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (257, 3, 'Ny fo izay maratra\nKa entina mankeo\nNy ory sy mahantra\nIzay manatona eo,\nMahita fanafody,\nKa sitrana avokoa,\nNy ota sy ny rofy\nDia  afaka tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (257, 4, 'Fanahy be ahina,\nAvia misotro ao,\nFa io no mampangina\nNy alahelonao ;\nFanahy ô, hatony\nIty ony soa ity,\nFa io no mahatòny\nNy fanirianao atý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (257, 5, 'Hianao, ry Tompon’aina !\nIsaoranay tokoa,\nNanaiky horatraina\nHandefa ony soa,\nNy ranao tao Golgota\nNo iankinanay,\nFa mahadio ny ota,\nMamelona aina anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 1, 'Ry Jesosy ô ! indreto\nIzahay nasainao eto\nAmin’ny latabatrao,\nMba tolory mofon’aina,\nO ! omeo ranon’aina,\nDia ny ra aman-tenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 2, 'O ! ry Loharanon’aiko,\nTonga aho, fa ilaiko\nHianao Mpamonjiko,\nRaiso ka diovy aho,\nAtafio izay mitalaho\nNy fahamarinanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 3, 'Ry Mpiandry tsara indrindra,\nTaomy aho mba hifindra\nAo am-balan’ondrinao ;\nMamonje ka mitahia\nIreto ondrinao nania,\nEnto any Aminao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 4, 'O ! ry Fahazavan’ny saiko\nSady tanjaky ny aiko,\nTonga aho ka vonjeo ;\nNoana ka mitaraina,\nMba vokiso mofon’aina,\nRano velona omeo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 5, 'Indro, miankohoka aho,\nTompo ô ! ka mitalaho\nEto anatrehanao ;\nAza mba manary ahy,\nIzay misento sy manahy,\nMamelà ny heloko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (258, 6, 'Ry Mpisolo ny mpanota,\nIzay nandoa ny tambin’ota,\nLatsa-dra ny tenanao,\nRaisiko am-panajana,\nFa nataonao fanasana\nIreto zava-masinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (259, 1, 'Manaraka ny hafatrao,\nTafangona izahay,\nMba hahatsiarovanay\nAnao, ry Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (259, 2, 'Tsy azo hadinoina\nNy fahorianao,\nNy dinitrao izay toy ny ra\nTsaroanay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (259, 3, 'Ny hazofijalianao\nDia tazanay izao\nRy Zanakondry masina ô !\nTsaroanay Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (259, 4, 'Nijaly mafy Hianao,\nNisolo voina anay ;\nRaha mbola velona izahay\nTsaroanay Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (259, 5, 'Raha tapitra ny andronay,\nKa tonga Hianao\nMba haka ny fanahinay,\nTsarovinao izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (260, 1, 'Ny latabatrao, ry Jeso !\nDia atrehinay izao ;\nNy fanahinay vokiso\nAmin’ny fitianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (260, 2, 'Tsy nitady zo sy laza\nHianao, ry Tomponay !\nFoinao ny ainao aza\nNanavotanao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (260, 3, 'Jeso ô ! ny ranao soa\nNo nataonao avotray,\nMba hanafaka avokoa\nIzay tsy mahadio anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (260, 4, 'Manomeza mofon’aina\nTompo ô ! ho tanjakay ;\nMihainoa  ny mitaraina,\nMamalia ny hatakay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (260, 5, 'Ny fanahinay ry Tompo !\nRaiso, fa atolotray ;\nFaly izahay hanompo\nSy hanao ny sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (261, 1, 'Ry Kristy ô ! malalako,\nSakafon’ny fanahiko,\nManaraka ny hafatrao,\nTsarovako ny teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (261, 2, 'Mangorakoraka ny fo,\nFa voavono Hianao !\nKa saotra no atolotray\nAnao, ry Ilay nalatsa-dra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (261, 3, 'Ny teninao izay raisiko\nFiainako vaovao tokoa ;\nNy ranao izay sotroiko\nNo manadio ny foko koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (261, 4, 'Ry Kristy ô ! ampitomboy\nNy fitiavako Anao.\nNy toe-piainako fenoy.\nNy tena hamasinanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (261, 5, 'Atolotro anio indray\nMba hoa Anao mandrakizay\nNy tenako, ny ananako,\nNy heriko, ny asako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 1, 'Ankehitrio no isehoanao,\nRy Jeso Tompo, izay \nmalalanay !\nAnkehitrio no anandramanay\nNy hafaliamben’ny lanitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 2, 'Ampiomany ao am-po izahay\nHampahadio ny eritreritray,\nHahatakaranay ny avotrao\nIzay namboaranao tao\nKalvary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 3, 'Eto no anoloranao anay\nNy mofon’aina, rano velona ;\nEto no ialanay sasatra\nAmin’ny adinay  sy  rafinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 4, 'Ry Jeso ô ! miandry izahay\nNy fihavian’ny voninahitrao,\nHo toy ny andro vao miposaka\nHo toy ny rano mahavelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 5, '“Raiso ny Tenako” no teninao\n“Ary ny Rako ho fiainana”\nAm-pitiavana no andraisanay,\nNa dia tsy mendrika aza\nizahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (262, 6, 'Mombà anay, ry Tompo tia ô !\nAza miala amin’ny fonay,\nAoka ny tavanao hazava re,\nAmin’ny làlana alehanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (263, 1, 'Indreto izahay,\nRy Jeso ô !\nManaraka izao,\nNy hafatrao,\nNy fahorianao,\nTsaroanay izao,\nFa fatratra tokoa\nNy nentinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (263, 2, 'Ny mofo haninay,\nRy Jeso ô !\nMilaza aminay\nNy tenanao ;\nNy fanavotanao\nMahafaka anay,\nFa saotra re izao\nAtolotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (263, 3, 'Misotro ny divay\nIzahay izao\nHahatsiarovanay\nNy antranao ;\nNy ra nilatsaka\nNo manadio anay,\nManafaka tokoa\nNy olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (263, 4, 'Tarihonao ny fo,\nRy Jeso ô !\nHo tia sy hankatò\nNy didinao ;\nKa tsy ny sitrakay,\nFa ny Anao, Jeso,\nNo aoka ho maminay,\nRy Tompo ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 2, 'Mandrenesa ry sofina,\nMifalia, ry fo ;\nNy feon’ny Filazantsara\nManatona anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 3, 'Ry olon’ota rehetra,\nRy ory sy noana,\nIzay mameno ny kibony\nAmin-java-poana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 4, 'Jehovah no mamboatra\nIzay mofon’aina,\nHo an’izay rehetra\nMety hihinana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 5, 'Kraist’sy ny fitahiany\nIzany mofo izany,\nFa Izy no mamelona\nNy maty mosary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (264, 6, 'Avia, ry mosarena,\nKa manarana fo ;\nNy mofon’aina tsy lany\nFa ampy ho anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 1, 'He mahagaga anay\nNy fitiavana\nNasehonao, ry Jeso ô !\nMba ho anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 2, 'Ny fijalianao\nIlay tao Golgota,\nIndrisy ! lalina tokoa\nNy ngidiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 3, 'Na mafy aza izay\nTsy nandosiranao,\nNataonao tsinontsinona\nNy teninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 4, 'Ny fitiavanao,\nFa tsy ny fantsika,\nNo anton’ny nihantonanao\nTao Golgota.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 5, 'Ny ra nalatsakao\nNo vidin’ny ainay,\nNo fijaliana nentinaonao\nNo avotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (265, 6, 'Tsaroanay izao,\nDerainay avokoa\nIlay fitiavanao anay,\nRy Tompo soa !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 1, 'Ny fitiavanao\nNo mahagaga anay,\nRy Zanak’Andriamanitra ô !\nRy Jeso Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 2, 'Hanavotra aina anay\nNo nijalianao,\nKa famonjena lehibe\nNo azonay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 3, 'Ny mofo sy divay,\nIzay haninay izao,\nDia mampiseho aminay\nNy fitiavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 4, 'Avia, Jeso ô !\nAvia ankehitrio,\nKa ny fanahinay sasao\nHo tsara sy madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 5, 'Mampaneke anay\nHanao ny sitrakao,\nKa aoka tsy ho tianay\nIzay tsy tianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (266, 6, 'Monena aminay,\nRy Tompo be fitia !\nKa aza ilaozanao izahay\nMandrakizay doria.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (267, 1, 'Ry Jeso Tompo, Hianao\nIsaorako tokoa izao,\nNy ranao sy ny tenanao\nNo nihinanako izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (267, 2, 'Ry foko faly, ambarao\nNy soa azonao izao ;\nJesosy monina ao am-po\nEndrey ny hafaliako !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (268, 1, 'He, ny andon’ny maraina\nLatsaka ao an-tsaha re,\nKa mamelona ny maina,\nIzay maniry rano be !\nRavoravo e, ny tany,\nFa he, ny andro maina efa\nlany,\nKa mitsahatra ny fahantrany,\nManaroka ny hazo be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (268, 2, 'Ny fanahy karankaina\nMisy rano velona,\nFa Jesosy andon’aina\nManome fiainana :\nVelona ny jentilisa,\nNy tany hay dia tonga\nParadisa\nMarobe no efa voaisa\nHandray ny lova masina !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (268, 3, 'Misy làlana mizotra,\nIzay mankao Ziona re ;\nAny no mitanjozotra\nNy vahiny marobe ;\nKa Ziona dia mivoha\nHidiran’ny navotan’ny \nTsitoha ;\nAo no ivorian’ny mifoha,\nFa an’ny Tompo lehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (268, 4, 'He, mikambana ny mino\nAmim-pifaliam-be.\nKa ny hobiny maneno\nTahaka ny rano be !\nAo mitovy avokoa,\nTsy misy Tompo sy \nmpanompo koa,\nEny, razana iray tokoa\nNy mponina ao Ziona re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (269, 1, 'Jesosy no Mpiandry\nNy Fiangonanay ;\nNy feon’izy ireny\nNo feo fantatray,\nKa tsy mba vala maro\nNy Fiangonanay ;\nIray tsy hay zaraina,\nTapahin-Tompo iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (269, 2, 'Iray no Vatolampy\nIzay iorenanay,\nIray no Fehizoro\nIzay ikambanany,\nIray no Mofon’aina\nFihinanay etý,\nIray no antenaina\nHo azonay arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (269, 3, 'Tabain’ny hevi-diso,\nKanefa mbola iaraý ;\nNy saina tsy mitovy\nTsy mahazara anay ;\nFisoko maromaro,\nKanefa vala iray\nNy Tompo no mpiaro\nNy Fiangonanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (269, 4, 'Ny teny samy hafa,\nNy fomba tsy miray,\nTsy misy maharava\nNy fikambananay ;\nNy fanenjehana aza\nVao mainka mampiray\nSy mahatsara laza\nNy Fiangonanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (269, 5, 'Ry Jeso ! hatanjaho\nNy Fiangonanao,\nNy fony halalaho\nMba hitoeranao ;\nIzay firaisan-tsaina\nMiseho hatretý,\nVelomy hanana aina\nHitohy hatrarý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (270, 1, 'He ! tianay ny monina eto,\nFa maminay ny valanao,\nTsy tianay, fa mampisento,\nNy lavitra ny tranonao,\nHe ! tianay ny monina eto,\nFa maminay ny valanao,\nTsy tianay, fa mampisento,\nNy lavitra ny tranonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (270, 2, 'He ! tianay, fa misy aina,\nRy Tomponay ny valanao !\nTsy tianay, fa tany maina\nNy lavitra ny tranonao,\nHe ! tianay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (270, 3, 'Fehezinao, Mpiandry tia,\nMilamina ny valanao ;\nTsy misy tsy mifankatia\nNy ondry izay tarihinao,\nHe ! tianay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (270, 4, 'Ny fanimban’ny fahavalo\nDia lavitra ny valanao ;\nMitazan-davitra mandalo\nNy rafinao tsy tia Anao,\nHe ! tianay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (270, 5, 'Ny fototra izay mahatsara\nNy valanao, ry Tomponay !\nDia Hianao Mpiandry tsara\nNo miara-monina aminay.\nHe ! tianay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (271, 1, 'Ry Tompo, Izay nanangona\nAnay ho vato velona !\nHazony tsy handao Anao\nNy vato voarafitrao ;\nFa maro no mandronjina\nHandevona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (271, 2, 'Jesosy Fehizoro ô,\nMpifehy mora sady to !\nTohano mafy izahay\nMba tsy hirodana indray,\nFa indro ny mpisompatra\nMandrirotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (271, 3, 'Ry Vato fampiraisana !\nTariho mba hikambana\nNy vato voapaika re,\nHo tonga trano lehibe,\nTsy zakan’ny mpandevona\nHarodana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (271, 4, 'Ry Kristiana marobe\nMba ampiraiso tsara re,\nHiray am-pitiavana,\nHo velona finoana,\nKa mba hiaraka hanao\nNy asanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (272, 1, 'Mba mivavaha raha vao\nmaraina,\nTsarovinao Ilay Mpanao\nanao.\nIzay niaro ny fanahy sy aina\nMba ho salama toy  izao ;\nTsarovy re ny Tomponao \nIzay Mpiaro  anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (272, 2, 'Rehefa tonga koa ny\nantoandro,\nO ! miambena tsara hianao,\nFa manangoly saina isan’andro\nIlay devoly rafinao ;\nTsarovy re ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (272, 3, 'Raha alina mangina ka\nhatory\nTsarovy eo ny ota izay natao\nKa dia ifony sy itsory\nFandrao maningotra anao ;\nTsarovy re ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (272, 4, 'Na aiza na aiza Hianao\nmitoetra\nSatana fahavalo ao tokoa,\nKa aza kivy na mba \nmanjoretra\nFa mivavaha tsy ho voa ;\nTsarovy re ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (273, 1, 'Faly izahay nandre\nHavan-tia nanao hoe :\n“ Miaraha aminay,\nTrano soa halehanay ”.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (273, 2, 'Efa tonga izahay\nMba hivavaka Aminao :\nRy Jehovah Tomponay,\nMitahia ny olonao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (273, 3, 'Miandrandra Anao izahay,\nKa mangataka  Aminao\nHanomezanao anay\nSoa avy Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (273, 4, 'Dia sambatra izahay,\nFa mitoky Aminao ;\nKa ho tanterahinay\nIzay rehetra sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (273, 5, 'Hoderainay Hianao\nAndro aman-alina,\nMbamin’ny Anaranao,\nFoto-piadanana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (274, 1, 'He ! vokatra ny tany,\nJehovah Tompo ô !\nFa be tsy mety lany\nNy hasoavanao :\nNy andro fararano\nNo ifalianay ;\nVoataona ao an-trano\nNy vokatra azonay :\nSaotra tsara entinay\nHo Anao tokoa,\nNy olonao rehetra  izao\nMifaly avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (274, 2, 'Ny tany tsy misaona,\nFa sambatra tokoa,\nTsy tapaka isan-taona\nNy fanomezan-tsoa ;\nEndrey, nahazo maina\nIzao rehetra izao ;\nNy zava-manana aina\nMifaly Aminao :\nSaotra tsara ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (274, 3, 'Tontosa izay nirina,\nNahazo izahay,\nFa misy taona zina\nNataonao ho anay ;\nTsy diso isan-taona\nNy fitahianao.\nNy olonao mihaona\nMba hankalaza Anao\nSaotra tsara ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (275, 1, 'Tao an-tany karakaina\nNisy olo-maro be,\nTsy mba nampodina maina,\nTao Jesosy  nanome.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (275, 2, 'Na dia mofo kely aza\nTonga nahavoky  ireo,\nFa nitombo mahagaga,\nSamy faly izy  ireo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (275, 3, 'Fa ny herinao, ry Jeso,\nIzay namonjy fahizay\nNo miasa lalandava\nHahavoky soa  anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (275, 4, 'Dia tahio ny fambolena\nHahavokatra tokoa ;\nMiantrà ny firenena\nTsy ho diso zava-tsoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (275, 5, 'Na dia voky fatratra aza,\nMora reraka  izahay ;\nAo ambony ny mateza\nIankinan’ny ainay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (276, 1, 'Raisonao ry Tompo soa,\nNy fanatitray izao ;\nHamasino avokoa\nIzay atolotray Anao,\n:,: Raiso re, ho Anao,\nHo fanati-tsitrakao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (276, 2, 'He, Anao, ry Tompo soa\nIzay rehetra anananay\nNy harena, vola koa,\nTaminao, Jehovah Ray,\n:,: Raiso re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (276, 3, 'Na dia kely aza, Tompo,\nKa tsy mendrika Anao,\nRaiso ho entina hanompo\nSy hanao ny sitrakao.\n:,: Raiso re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (276, 4, 'Indro, Tompo, raiso koa\nTena, fo, fanahinay,\nMba ho fiasana koa\nEo an-tananao izahay.\n:,: Raiso re, ..sns.\nHo voninahitrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (277, 1, 'Ry Jehovah, Ray Mpahary\nSy Mpamelona anay,\nTsapanay fa tsy manary\nHianao Mpitondra anay ;\nHe, mandrotsa-pitahiana\nSesehena Hianao,\nKa dia feno fifaliana\nIzahay mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (277, 2, 'Noteneninao ny tany,\nNotarihinao izahay,\nKa nitobaka  aoka  izany\nIzao ny vokatra azonay.\nFeno hika isan-taona\nNy fitiavanao anay,\nKa aoreno tsy ho foana,\nTompo ô ! ny asanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (277, 3, 'O ! mba raiso re, ry Tompo,\nIzao fanatitray izao ;\nEnga anie ka mba hitombo,\nHandrosoan’ny hanitrao.\nRaiso koa sy hamasino,\nNy fonay hiasanao ;\nNy Fanahy ampidino\nHampahery, hanavao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (278, 1, 'Indro, Tompo, vory eto\nAnatrehanao izahay,\nMba hanatitra ireto\nVokatry ny asanay,\n:,: Raiso re, raiso re !\nHo fanati-tsitrakao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (278, 2, 'Hianao no mampaniry\nSy mampisy vokatra ;\nIzahay dia mpitahiry,\nFa Anao ny zavatra.\n:,: Raiso re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (278, 3, 'Ary na tsy mendrika aza,\nAoka tsy holavinao,\nFa ho enti-mankalaza\nNy Anaran-tsoanao.\n:,: Raiso re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (278, 4, 'Raiso koa re, ry Tompo,\nHo Anao ny tenanay,\nAmboary ho mpanompo\nMahafatra-po izahay.\n:,: Raiso re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (279, 1, 'Mba ampio, ry Tompo soa\nIzahay navotanao,\nFa mikasa mba hanoa\nSy hanaraka Anao ;\nHitanao fa kely hery\nIzahay mpanomponao,\nTsy mahefa raha irery,\nKoa mba tarihonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (279, 2, 'Izahay manolo-tena\nMba hiasa ho Anao,\nKa maniry, manantena\nFa hanampy Hianao ;\nHitanao fa kely hery, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (279, 3, 'Tianay hiara-dia\nAminao, ry Tomponay,\nKa tantano tsy hania\nAmin’ny alehanay.\nHitanao fa kely hery, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (280, 1, 'Raisonao, ry Tompo soa\nIzato asan-tananay,\nFa nomenao avokoa\nIzay rehetra anananay\nSimba izahay rehetra,\nSady very hatrizay,\nNefa soa tsy misy fetra\nNo novalinao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (280, 2, 'Hianao nanetry tena,\nTsy nitandro henatra,\nBe fiantra ka onena\nNy tra-pahoriana,\nMba tsarovanay izany,\nKoa dia tahafinay,\nNefa fiasana  ihany\nAo an-tananao  izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (280, 3, 'Arosoy sy ampitaro\nAminay ny teninao ;\nHazavao ny olo-maro\nIzay tsy mahalala Anao !\nNy misaona mba vangio,\nNy kamboty iantrao,\nIzahay izay tonga anio\nMba ataovy irakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (280, 4, 'Eny, ry Mpamonjy tsara,\nAmboary izahay\nMba ho tonga mpanambara\nNy fitianao anay ;\nAry dia mba tahio\nIzato fikambananay,\nHafanao, ampirisiho,\nHamasino izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (281, 1, 'Ry Tompom-boninahitra,\nJereonao izao\nIreto izay milahatra\nHo tena irakao ;\nNy fanasana fatratra\nOmaninao izao,\nIlà-mpitondra hafatra\nHamory izay hankao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (281, 2, 'Hitety sampan-dàlana\nSy vohitra  izahay\nHanetra fisakanana\nSy ady mahamay ;\nKa aoka hotafiana\nNy fiadianao,\nFa tena fanafihana\nIzay hatao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (281, 3, 'O, ry Fanahy Masina,\nMonena aminay !\nKa aoka ho fanasina\nTokoa izahay,\nFihino tsy hihemotra,\nAtaovy irakao\nManefa zava-tsarotra\nHo voninahitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (281, 4, 'Ry Tompom-boninahitra,\nNy voninahitrao\nNo aoka mba hitaratra\nAn-tavanay izao,\nKa raha ao an-danitra\nFonena-masinao,\nDia  mainka vao hamiratra\nHo voninahitrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (282, 1, 'Vory eto izahay\nHihevitra ny asanao,\nTompo, manatreha  anay\nKa asehoy ny sitrakao.\nAoka Hianao no mba hitarika\nanay \nTompo, tahio izahay\nMba ho voninahitrao ny\nasa vitanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (282, 2, 'Hahavita inona\nNy saina aman-kevitray ?\nTena tsinontsinona,\nRy Tompo ô ! ny herinay,\nKa mba midina eto afovoanay\nHanana aina masina ny \nfivorianay !\nTompo, tahio izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (282, 3, 'Hianao no nanome\nNy asa hoheverinay,\nTena adidy lehibe\nNo voatolotra anay,\nEnga anie ny sitrakao irery\nno hatao !\nAoka tsy ho vanona izay\ntsy sitrakao !\nTompo, tahio izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (282, 4, 'Areheto aminay\nNy afom-pitiavanao,\nMba hahaizanay miray\nMitory ny Anaranao,\nHahavory marobe hanatona\nAnao,\nHampamirapiratra ny \nvoninahitrao !\nTompo, tahio izahay ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 13, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 1, '(Manaraka ny famakian-teny voalohany)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 2, 'Ry Jeso, Tompo velona,\nMpiandry sy Mpamelona\nNy Fiangona-masinao,\nAtreho izahay izao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 3, 'Indreo (Inty*) mpanomponao\nvaovao\nAtokanay, ka raisonao\nHanao ny raharahanao,\nHamahana ny ondrinao.\n\n(Manaraka ny faharoa)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 4, 'Ny tany no nakana anay,\nMihelina ny andronay,\nKa vetivety izahay\nDia hody vovoka indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 5, 'Ry Jeso ô, Mpitia anay,\nIzay Mpanjaka hatrizay,\nEndrey ny voninahitrao,\nManerana ny lapanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 6, 'Ny deranao hasandratra\nHitohy tsy hitsahatra ;\nHazava toy ny kintana\nNy tsara fanompoana.\n\n(Manaraka ny fahatelo)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 7, 'Mba raison y mpanomponao,\nKa aoka hamboarinao\nHo mpanambara mankatò\nNy hafatrao, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 8, 'Omeo Afo Masina\nSy vava mampionona ;\nNy sainao sy ny feonao\nOmeo ny mpanomponao.\n\n(Manaraka ny fahefatra)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 9, 'Raha misy tafio-drivotra,\nTohano tsy hatahotra,\nHahery sy hisakana\nNy rafim-piangonana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 10, 'Ka mba ampirisihonao\nHanaraka ny dianao :\nTantano raha mbola atý,\nHandresy ka ho tonga arý.\n\n(Manaraka ny ordinasiona)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 11, 'Ka mba ampirisihonao\nFa kely ny finoany ;\nNy ranao masina anie\nHanafaka ny helony !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 12, 'Ampio mba haharitra,\nRaha mbola atý an-efitra ;\nRaha avy ny hodianay,\nMba hazavao ny làlanay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (283, 13, 'Raha tonga ny harivany,\nKa ao ny ady farany,\nDia raiso ny mpanomponao\nHiala sasatra Aminao !\n\n*) Inty, raha olona iray no\natokana', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (284, 1, 'Manan-jara, ry Jesosy,\nNy mpanomponao,\nIzay natokanao hitondra\nNy Anaranao :\nNofidinao ho mpitory\nNy fitiavanao,\nNitokianao ho mpamory\nNy vahoakanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (284, 2, 'Izao no zara fanompoako\nVoatendrinao,\nKa ny mafy tsy ahoako\nHahefako izao ;\nTsy handosi-pahoriana\nNy mpiasanao,\nFa ny hazo fijaliana\nLova taminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (284, 3, 'Na ho voky na ho noana\nNy mpanomponao,\nTsy handao ny fanompoana\nNametrahanao ;\nSitrakao ny fihafiana,\nKa mba tiako koa ;\nZakanao ny tsy fisiana,\nKa zakaiko  koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (284, 4, 'Tsy ahiko ny handany,\nHolanina koa,\nFa famoizan-tsoa no hany\nAhefany soa ;\nNa ny saiko, na ny aiko,\nAndro, vola koa,\nTsy mba misy holalaiko,\nFoiko avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (284, 5, 'Nefa raha  izaho irery\nTsy mahefa  izao,\nKa mangataka ny hery\nEo an-tananao,\nAoka ho feno ny Fanahy\nNy mpanomponao,\nMba ho tena lehilahy*\nVoahosotrao..\n\n*) na : « vehivavy »', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (285, 1, 'Mangataka aminao ny olonao\nry Tompo !\nMba hitahianao ny havan-\ntianay\nIzay atokanay hitondra sy\nhanompo\nNy Fiangonanao, henoy ny\nvavakay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (285, 2, 'Tafio ny hery fa tsy mahefa\nirery,\nFenoy ny hevitrao ho toy ny\nzanakao.\nMpitady ny mania, mpikaroka\nny very.\nHitondra ireo ho tia ny\nvalan’ondrinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (285, 3, 'Ny sainy hazavao hahazo\nfahendrena\nHahay hitondra izao vahoaka\nbe izao.\nAmpianaro koa mba tsy\nhatoky tena,\nTolory saina soa hiankina\naminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (285, 4, 'Raha ory, aza afoy, tohàno sy\nonòny;\nMarary, mba tsaboy ;\nmahantra, mba jereo ;\nApetraka aminao ny ainy sy\nny fony ;\nKa mba tsinjovinao, arovy sy\nvonjeo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (286, 1, 'Mivory maro eto\nIzahay navotanao,\nNy saotranay no ento\nHo voninahitrao ;\nMifaly sy mihoby\nMandray ny irakao,\nKa eto no mitoby\nMangataka aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (286, 2, 'Ry Kristy tena Andry\nSy Vatolampinay !\nRy Lohan’ny mpiandry\nSy Tompo tokinay !\nTohano mba hijoro\nIty mpanomponao,\nTinendry ho mpanoro,\nMpilaza hafatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (286, 3, 'Handray ny asa mafy,\nKoa aza ilaozanao ;\nHitory, ho mpamafy,\nKa dia mba hazavao.\nVokiso lalandava\nNy saina tianao ;\nOmeo ny andro lava\nHanao ny asanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (286, 4, 'Tahio mba ho salama\nHahefa ny anao,\nHo iraka mafana\nAmpian’ny herinao ;\nMandrava manda mafy\nMba hampanjaka  Anao,\nKoa  aoko mba hihafy,\nFa  izay no sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (286, 5, 'Ny fitaizam-panahy\nAdidy lehibe,\nAmpio mba tsy hanahy\nNy asa maro be,\nKa dia tahio ny asa\nHataony ho Anao ;\nNy fony no mikasa\nHanoa izay didinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (287, 1, 'Ry Jeso Mpitarika hendry !\nTsinjovy ny olom-boatendry\nTolory ’zay hevitra hendry\nHandinika sy hahakendry.\nRy Tompo Mpanjaka mahery ;\nVangio raha ory sy tery\nTohano raha mahatsiaro fery\nVimbino ‘ndrao saraka irery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (287, 2, 'Raha miasa dia miasa ny\nsaina\nKanefa ny tany no maina,\nKa mila ho kivy ny aina.\nTondrahy fandrao karankaina.\nNy mpiasa izay voafidy\nHamoha ny fo izay mihidy\nHitari-panahy saro-bidy,\nTantano hanaraka didy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 1, 'O ! miaingà izao, ry\nmiaramila soa,\nAntsoin’ny Tomponao hiady\nhianao.\nNy fahavalo ao, kanefa\nmandrosoa,\n:,: Mahery re no momba\nanao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 2, 'Izao tontolo izao izay feno\nfahotana\nNo iantsoana anao mba\nhotafihinao\nIndreo mihoraka ao ny\nfehin’ny Satana\n:,: Jeso anefa momba anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 3, 'Ny aizim-be izay mandrakotra\nny tany\nNo iantsoana anao ka andeha\nmba savao ;\nN Tompo no tafio, alao ny\nhazavany,\n:,: Tsilovy izao tontolo izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 4, 'Ny fahoriam-be izay\nmanarona eto\nNo iantsoana anao, ka moa\nve handà ?\nAmpiononinao ny ory sy\nmisento\n:,: O ! mazotoa ka miaingà :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 5, 'Na inona hanjo mbola ao ny\nfamonjena\nHahery hianao ! izay no\nantenao ;\nAmpoizinao tokoa izao ny\nfandresana,\n:,: Mahery Ilay arahinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (288, 6, 'O ! mahereza izao, ry\nmiaramila soa !\nNy hatrotrahanao iareto sy\nzakao.\nJesosy no jereo, ka matokia\ntokoa.\n:,: Fa Izy re no herinao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (289, 1, 'Sambatra izay voafidy\nHo mpiasanao\nKa hanohy ireo adidy\nIzay natombokao.\nHo mpitaiza ny fanahy\nToy ny vitanao\nHo mpiady tena sahy\nTarikao Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (289, 2, 'Dia mpiasa tsy an-tery\nKa mafana fo\nTe-hanangona ondry very\nMarobe tokoa.\nTe-hitaona ny tanora\nSy ny zaza koa\nTsy hifidy asa mora,\nFa ho mpankatò.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (289, 3, 'Hianao, ry Jeso Tompo,\nNo nampianatra\nNy olonao mba hanompo\nTsy mitsahatra.\nTsy handala ny talenta\nIzay natolotrao :\nFa ny aina re no fetra\nEntina manao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (289, 4, 'Nefa tsy matoky tena\nFa malemy moa,\nKa dia mila fahendrena\nMila hery koa ;\nHo mpiasa mahavita\nIzay anjara etý,\nMandra-pody ao am-pita\nAminao arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 1, 'Hitsangana izao\nIzahay, ry Tompo ô !\nMba hanompoanay Anao,\nAmpionao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 2, 'Ny fahotana be\nIzay vitanay tatý,\nDia ibebahanay izao,\nKa mamelà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 3, 'Ny tsy finoanay\nNo nijanonanay\nTsy nanatonanay Anao\nIzay ela be izay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 4, 'Sasao madio izahay,\nAsio fo vaovao\nHo ao anatinay izao,\nRy Jeso Avotray !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 5, 'Inoanay tokoa\nFa maty Hianao,\nHisolo olo-meloka\nNo nijalianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (290, 6, 'Mba manampia anay\nHiankina aminao,\nFa tokinay ny avotrao\nNatao tao Kalvary.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (291, 1, 'Indreto fa mivory izao\nZatovo vonon-kanavao\nIlay fanekena tsara ;\nMba raisonao, Rainay ô !\nNy fitarainanay izao.\nOmeo toky tsara,\nHenoy, fenoy\nAim-panahy, mba ho sahy\nLalandava\nHo mpanaiky ny mazava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (291, 2, 'Mandà ny ratsy izy izao,\nManaiky fa ho zanakao\nHandia ny lalan’aina ;\nRy Ray ô ! mba tsarovinao\nIreto voasoratrao\nAo amim-bokin’aina ;\nTezao izao\nTsy ho resy, fa handresy,\nKa omeo\nAzy hery mahaleo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (291, 3, 'Natsanganao ho zanakao\nSy ondry mba handrasanao,\nKa tena sarobidy ;\nArovy re ny ondrinao,\nTantano ny malalanao.\nRy Tompo Izay nifidy \nAnay handray\nLova soa tsy miova,\nKa hazony\nTsy handao Anao intsony !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 1, 'Efa tonga zana-dRay Tsitoha\nhianao,\nRy tanora, tamin’ny batisa\nmasina ;\nIzany no tenin’ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 2, 'Koa aza manadino ny\nbatisanao,\nZava-tsoa sarobidy efa\nazonao\nTamin’ny ranon’ny aina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 3, 'Famelana heloka sy\nfahasoavana,\nFanavaozana ny fo sy\nfiadanana,\nSamy nomen’ny Mpamonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 4, 'Dia arahonao Jesosy, fa\nMpamonjinao ;\nAoka hahafaly Azy ny\nfiainanao,\nTovy izay sitrapony !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 5, 'Mivavaha, miambena ao\nan-dalana !\nMisy fahavalo fetsy\nta-hamitaka\nNy ondrikelin’ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 6, 'Mahatoky ny Mpamonjy,\naza kivy fo,\nIzy hampionona raha avy ny\nmanjo ;\nAzy ny hery rehetra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (292, 7, 'Ary raha tapitra ny andronao\natý,\nEndrey ny fifalianao rehefa\n‘mby arý\nAmin’ny Tompo Jesosy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (293, 1, 'Izahay natao batisa,\nVoarain’ny Tomponay,\nAry efa voaisa\nMba ho Zanaky ny Ray ;\nVoavelany tokoa\nNy fahadisoanay ;\nNefa mbola mora voa,\nMora resy izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (293, 2, 'Na ny asa, na ny teny,\nNa ny hevitra ao am-po,\nMbola tsy madio ireny,\nKa diovy Tompo ô !\nMora aminay ny ratsy,\nSarotra aminay ny to ;\nMba ampio tsy ho resy\nIzahay, ry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (293, 3, 'Raha mbola atý an-tany\nIzahay izay zanakao,\nMba ampio tsy ho lany\nNy finoanay Anao.\nRy Jesosy Tompo soa,\nTano mafy izahay,\nMba ho azonay tokoa\nIzay anjara lovanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (294, 1, 'Izaho nentina nanatrika\nNy Tompo sy ny fiangonana,\nNy ray sy reny no niantoka\nHitaiza sy hitari-dàlana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (294, 2, 'Ka faly aho fa noraisina\nNatao batisa mbola zaza re,\nNanaovam-panekena masina\nHo zanaky ny Tompo lehibe.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (295, 1, 'Ampio aho, Zanahary ô !\nHanaraka ny « eny » masina,\nIzay nataoko taminao, ry To,\nKa tsy mba hisy fivadihana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (295, 2, 'Ry Tompoko, Izay\nnanomboka\nNy asa tsara ao anatiko,\nArovy aho ho tanteraka,\nMba tsy ho faty ny finoako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (296, 1, 'Miorena mafy tsara\nAmin’Ilay Vatosoa,\nMandrosoa, mifahara,\nMifikira mafy koa,\nHe ! ny Tompo manambara\nFa ho sambatra tokoa,\nKa ho tsara miafara\nNy mpanompo mahasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (296, 2, 'Raha misy fahoriana\nIzay midona aminao,\nRaiso re ho fitahiana\nIzay omen’ny Tomponao ;\nFa ny lalam-pijaliana\nIzay alehanao izao\nDia mamelom-paniriana\nHitomboan’ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (296, 3, 'Miadia ny ady tsara,\nHo mpandresy hianao ;\nTontosay izay anjara\nEo am-pelatananao ;\nJeso Tompo no mijery\nSy mahita ny atao,\nKoa dia tsy mba hisy very\nIzay rehetra vitanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (296, 4, 'Ry Jesosy ! hatanjaho\nIty mpanolo-tenanao,\nMiangavy, mitalaho,\nKa iteneno, Tompo ô !\nAoka re ho tena taho\nIzay mikambana aminao.\nKa ny foko halalaho,\nDiovy ho Tempolinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (297, 1, 'Ankehitriny\nJereo ry Ray !\nNy mpivahiny\nMiampy   indray ;\nKa manomeza hery\nNy fiangonanao,\nMba tsy hiasa irery\nNy olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (297, 2, 'Avia hitahy\nNy olonao ;\nKa ny Fanahy\nOmeonao ;\nHo hery izay handrava\nNy toetra ratsinay,\nHo jiro hanazava\nNy lalana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (297, 3, 'Ry Tompo tia !\nTarihonao\nHifankatia\nNy olonao,\nMba tsy ho rava  intsony\nNy fikambananay,\nHamantana ao ambony\nNy dianay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (298, 1, 'Ry havanay malala !\nManolo-tena izao,\nFotoana mahafaly\nIraisantsika anio ;\nFaly izahay mandray anao,\nHiara-manompo ny Ray,\nJeso anie handray anao !\nMba ho sakaizany tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (298, 2, 'Hajao ny fanekena,\nAdidinao efao\nAtaovy tsy terena\nIzay andraikitrao.\nJereo Jeso, ka matokia,\nFa Izy no hiahy anao,\nKa mandrosoa sy mifalia\nHanefa izay anjaranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (299, 1, 'Aina no fetra, ry Tompo\nMpanjaka !\nAina no fetra anarahana Anao\nKa ny faneva tsy maintsy ho\nzaka,\nNy fanompoana tsy maintsy\nhatao.\nIndreto izahay hilanja faneva :\nIlay hazo izay nanavotana\nanay.\nAn-tsitrapo no anoloranay\ntena,\nKa tano  mba ho Anao  izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (299, 2, 'Aina no fetra ho enty-manoa\nIlay Zanakondry nisolo anay ;\nFandavan-tena sy fanaovan-\ntsoa\nNo fianarana atolotra anay,\nIndreto izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (299, 3, 'Aina no fetra, ry Tompo\nmalala !\nAino no fetran’ny fitiavanay ;\nKa sanatria izay handao na\nhiala ;\nMandrakizay, ho Anao izahay.\nIndreto izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (299, 4, 'Aina no fetra, ny hofo anefa\nMora mirona ho lavo indray,\nKa manampia, ry Mpandresy\nmahefa !\nO ! mba tantano, hazòny\nizahay.\nIndreto izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (299, 5, 'Aina no fetra, ka ampio,\nry Fanahy !\nAina no fetra, vonjeo e,\nry Ray !\nAina no fetra, izahay re no\nsahy ;\nAina no fetra, handresy\nizahay.\nIndreto izahay ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (300, 1, 'Tompo mahery Tsitoha,\nFaly izahay mahatsiaro,\nFa Hianao Izay Loha\nEfa niantso olo-maro.\n:,: Mba hitohizan’ny asa,\nAsa natomboka teto :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (300, 2, 'Raiso ny saotra aman-dera\nNoho ny zavatra vita,\nFa Hianao nampahery\nNy mpiasanao izay tafita.\n:,: Raiso fa Anao samy irery\nNy fahefana sy hery :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (300, 3, 'Dia hamasino ny asa\nSy ny fiezahana mafy,\nEfa nataon’ilay mpiasa\nTamin’ny fo sy ny hafy.\n:,: Ho voninahitrao, ry Tompo\nIzay niantso  azy  hanompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (300, 4, 'Ary izahay izay mandova\nSy ta-hanohy ny asa,\nAoka ny toetra ho lova\nHoraisinay, enti-miasa.\n:,: Ho voninahitrao, ry Tompo\nIzay efa niantso  mpanompo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 1, 'Ny tany izay vangianao,\nRy Jeso Masoandronay,\nTsy maintsy lasanao, Jeso,\nMba hanjakanao avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 2, 'Ny fo sy tany lasanao\nHo feno hasambarana,\nNy loza hosakananao,\nNy soa hampidinina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 3, 'Ny vavakay sy dera koa\nTsy manam-pitsaharana,\nHiakatra Aminao tokoa\nHitady fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 4, 'Ireo olona voaforona,\nDia àry ho Anao tokoa :\nHohasoavinao ireo\nHanjary masina avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 5, 'Ny maty hovelominao,\nNy ratsy hohavaozinao,\nNy very hotadiavinao,\nNy diso, re, horaisina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (301, 6, 'Ry vazantany efatra !\nRy Nosy tanindrazanay !\nMba raiso Ilay Mpanavotra\nHo Tomponao mandrakizay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (302, 1, 'He, sahanao, Mpamonjy ô,\nIzao rehetra izao.\nKa be izao ny vokatrao,\nFa vitsy ny Mpanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (302, 2, 'Indreto, tonga izahay\nHivavaka Aminao,\nHanolotra ny tenanay *\nHiasa ho Anao.\n\n* na Haniraka ny namanay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (302, 3, 'Ny firenena marobe\nSy nosy mba tahio,\nIndrindra fa ny Nosinay,\nRy Tompo ô tahio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (302, 4, 'Vimbinonao ny irakay,\nNy feony hamamio,\nIndrindra fa omeonao\nNy zotom-po madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (302, 5, 'Tahio re ny olonao\nHihaino sy hanoa,\nMba hahitany Aminao\nNy antra mahasoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (303, 1, 'Miandry ao ny Tomponao,\nSamia manendry tena,\nFa be ny asa izay hatao ;\nAtreho ny firenena !\nTopazy maso kely\nNy ondry izay mania\nIlay zandry lasa niala\nAtaovy hoe : modia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (303, 2, 'Miandry ao ny namanao,\nMoa ve tsy hovonjena ?\nHatòny re dia ilazao :\nKristy no famonjena.\nTopazy maso ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (303, 3, 'Hevero e ! ny Nosinao\nIty Madagasikara !\nMba efa aiza izy izao\nAra-Pilazantsara ?\nTopazy maso ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (304, 1, 'O ! mandrosoa,\nry mpanaraton’i Jeso,\nIzay no baiko azontsika,\nka mandrosoa ;\nJesosy Tompo no mibaiko,\nmandrosoa :\nRaiso ka  arotsahy\nny haratonao.\nMiaingà, namako ô !\nJeso no mibaiko\nMandrosoa, mandrosoa,\nmandrosoa tokoa ;\nAza kivy koa,\nna dia  tsy mahazo :\nMandrosoa amin’ny lalina\nkokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (304, 2, 'O ! mandrosoa,\nry mpanaraton’i Jeso,\nSaina mora kivy\naoka ho lavinao ;\nNy mora kivy\ndia hanenina tokoa ;\nRaiso ka  arotsahy\nny haratonao.\nMiaingà, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (304, 3, 'O ! mandrosoa,\nry mpanaraton’i Jeso,\nMbola ao ny hazandrano\nho azonao ;\nTsy azonao anio,\nampitso mandrosoa,\nRaiso ka  arotsahy\nny haratonao.\nMiaingà, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (304, 4, 'Mba mitoera,\nJeso ao an-tsambonay\nMba ho renay lalandava\nny teninao ;\nNy baikonao, ry Tompo,\nhankatoavinay\nKa harotsakay izao\nny haratonay.\nMiaingà, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (305, 1, 'He ! ali-maizim-pito e !\nSao very ao ny marobe,\nSatana no manjaka ao,\nMivadi-po ny olona ao :\nAvia, ry Masoandronay,\nMba hazavao ny taninay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (305, 2, 'Fa ny mazava ho anay\nAo Aminao, Jehovah Ray !\nMamirapiratra tokoa\nIlay Fitarikandro soa.\nAvia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (305, 3, 'Ry Jeso Masoandronay !\nMiandry eto izahay ;\nMba misehoa Hianao\nFanilon’ny fanahy ô !\nAvia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (305, 4, 'Dia hazavao ny taninay,\nAndrao very eto izahay ;\nSavao ny aizim-be izao\nHizorany mankaminao\nAvia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (306, 1, 'O ! miasà, sakaiza,\nJeso no herinao ;\nNa misy mafy aza,\nIzy tsy handao.\nDia matokia tokoa,\nFa Jeso momba  anao,\nKa hisy vokan-tsoa\nHifalianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (306, 2, 'Maro ny andro lasa\nIzay tsy nanaovanao,\nKa mbola be ny asa\nIzay tsy vitanao,\nKoa dia mitsangàna,\nHanao tokoa izao,\nHahavitanao tsara\nNy anjaranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (306, 3, 'Indro fa mihariva,\nFohy ny andronao ;\nMoa tsy mba fantatrao va\nKa tsy hitanao ?\nEny, fa manakaiky\nJeso Mpitsara re,\nAry efa ‘mby akaiky\nNy hodianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (306, 4, 'Rehefa tonga Jeso,\nTompon’ny asanao,\nAry hamaly soa\nNy mpiasany.\nMandrakizay doria\nNo hifalianao,\nRaha mpanompo tsara\nMahatoky e !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (307, 1, 'Mamafaza voa,\nka  aza mba malaina\nFa tsy fantatrao re\nizay ho vanona\nNa  amin’ny hariva,\nna  amin’ny maraina\nMamafaza voa,\ntsy mijanona.\nMatokia re,\nmatokia re,\nHisy amboara,\nho anao anie !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (307, 2, 'Mamafaza voa\namin’ny mahantra,\nKa  aza mba mifidy\nizay hasian-tsoa ;\nFa ny fo mazoto\nsady feno antra\nDia  homen’ny Tompo\nvalisoa tokoa,\nMatokia re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (307, 3, 'Mamafaza voa \namin’ny malemy,\nAry mba ampio izay\nreraka ao am-po ;\nAsehoy Jesosy\nloharanon-kery,\nAo no anovozy\nizay ilain’ny fo.\nMatokia re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (307, 4, 'Miasà sakaiza ! \nmilofosa tsara\nDieny misy asa\nazonao atao ;\nMatahora sao\ntsy vita ilay anjara\nIzay napetraky\nny Tompo aminao\nMatokia re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (308, 1, 'Iza no hanompo an’i Jeso\nsoa ?\nIza no hanaiky mba ha Azy\nmoa ?\nHe, ny hatsarany ! he, ny\nantra be !\nFa Izy ta-hamonjy, ka meteza\nre !\nJeso Tomponay ô !\nIndreto izahay\nAvy mba hanaiky,\nKa mandraisa anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (308, 2, 'Iza re no mety, iza re, lazao,\nHanome ny fony mba ho Azy\nizao ?\nHe, miantso mafy Jeso\nTompo tia,\nIzay rehetra mety, ô ! avia,\navia !\nJeso Tomponay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (308, 3, 'Iza no mba sahy, sahy ao\nam-po\nHo miaramila fehin’i Jeso ?\nIza no hiady ady tsara re,\nFa ny fahavalo dia marobe ?\nJeso Tomponay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (308, 4, 'Iza re no homba Azy\nankehitrio\nMba hamonjy  ireny azon’ny\nmanjo ?\nIza no hihafy sady hanome\nAndro, hery, saina ?\nMamalia e !\nJeso Tomponay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (308, 5, 'Iza no hazoto mba hiasa\nmoa ?\nIza no handroso mba ho\nhendry koa ?\nIza no hanaiky, iza re ?\nlazao !\nMba ho an’i Jeso, izao anio\nizao ?\nJeso Tomponay ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (309, 1, 'O ! miaingà, miasà izao,\nIrak’i Jesosy hianao ;\nNy masoandro miposaka ao\nMba hanazava ny dianao,\n:,: Ka dia mazotoa ô :,:\nO ! miaingà, miasà,\nmitoria,\nAza ho kivy, fa mba\nmihafia ;\nDia mba lazao ny nataony\ntatý,\nMba hanazava ny maizina\natý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (309, 2, 'O ! miaingà, miasà izao,\nKa afafazo ny voanao\nHo ao an-tany mahantra ao ;\nJeso Mpamonjy no aronao\n:,: Hitahy ny asanao :,:\nO ! miaingà, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (309, 3, 'O ! miaingà, miasà izao,\nAoka hiezaka fatratra ;\nJeso hanambina anao atý\nMandra-pahazo ny vokatra\n:,: Izay valin’ny asanao :,:\nO ! miaingà, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (309, 4, 'O ! miaingà, miasà izao,\nFa mby akaiky ny Tomponao\nMba hanome valisoa anao,\nAraka izay voa  nafafinao,\n:,: Ka dia miasa izao :,:\nO ! miaingà, .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (310, 1, 'Iza no hitantara\nNy Tompo Jesosy,\nIlay nandao ny lapa\nKa nidina tatý ?\nNitafy nofo Izy,\nNanetry tena koa ;\nKa maty ho Mpisolo\nIzao rehetra  izao\nIza no hitantara (in-3)\nNy Tompo Jesosy ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (310, 2, 'Iza no hitantara\nNy fitiavany ?\nFa tia ny mpanota\nMahantra, very koa ;\nKa izay manatona Azy\nDia tsy hariany,\nFa raisiny tokoa\nMba ho sakaizany ;\nIza no hitantara (in-3)\nNy fitiavany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (310, 3, 'Iza no hitantara\nNy zava-marobe ?\nIzay raisin’ny voavonjy\nAmin’ny alalany ?\nTsy mbola hita maso,\nNa ren’ny sofina,\nNa takatry ny saina,\nNy hasoavany ;\nIza no hitantara (in-3)\nNy hasoavany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (310, 4, 'Iza no hitantara\nNy soa be arý ?\nHe! lova tsara fara\nHasolo ny atý ;\nAny an-danitra any\nBe haravoana,\nFa ao ny Tompo Jeso\nIzay hifaliany ;\nIza no hitantara (in-3)\nNy soa arý ?', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (311, 1, 'Misy maro ao an-tsaha\nmitaraina\nKa ento ny jiro mba hankao,\nTsy mba misy antoandro\nna maraina,\nFa maizina re ny ao ;\nIndro olo-maro be\nNo maniry mafy hoe :\nMba midína mankatý,\nItondray Baiboly re\nIzy ireo, fa ta-handre\nAn’i Kristy Tompo soa ;\n« Mandehana, »\nhoy ny Tompo\n« Mandehana ho amin’izao\ntontolo izao ».\nMitsangàna ary,\neny mitsangàna,\nFa Izy no homba anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (311, 2, 'Mandehana mba hitarika\nny jamba,\nMba ampifalio ny ory fo ;\nNy mahantra ilazao fa misy\nlamba\nTsy miova, tsy mety lo ;\nJeso Mofon’aina soa,\nSady Ranon’aina koa,\nAsehoy ho hitany,\nTena Andriamanitra,\nTompo tao an-danitra,\nAmbarao ho fantany :\n« Mandehana, » ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (311, 3, 'Mandehana hanatanjaka ny\nosa,\nOmeo toky re ny ketraka ;\nNy anjaranao, ataovy ho\ntontosa,\nKa aza dia reraka,\nMandehana hianao, ka vitao\nny asanao,\nFa ny tambinao arý ;\nMbola any ihany re\nNy handraisan-tamby be,\nDia rehefa tsy etý,\n« Mandehana, » ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (312, 1, 'Miely lavitra any\nNy olo-marobe\nKoa iza no hankany,\nHamonjy azy re ;\nHanorina sekoly,\nHitory teny koa,\nHamaky ny Baiboly\nMilaza zava-tsoa ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (312, 2, 'Indreny olona ory\nTsinjovy rizareo !\nTsy misy ny mpitory,\nKa moa hafoinareo ?\nTsarovy Jeso Tompo\nNandao ny lapa soa,\nKa tonga mba hanompo,\nNanolotra aina koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (312, 3, 'Ry Lohan’ny mpiandry !\nTendreo ny irakao,\nFa reraka mandry\nNy ondrikelinao ;\nIraho ny mpitory\nHandeha ankehitrio,\nKa tsy handà akory\nHanao veloma anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (312, 4, 'Ny tia famonjena\nMiaramila soa ;\nIzay manolo-tena\nNo iraka tokoa ;\nNa lavitra ny tany,\nHanendry tena hoe :\n“ Izaho no hankany,\nIraho aho e ! ”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (313, 1, 'Mitoera, ry Jesosy !\nAo an-tanimbolinao,\nKa amboary izao ny voly\nMba hitombo sy hamoa,\nTomoera ;\nIasao ny nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (313, 2, 'Mba onenonao ny lasy\nItoeranaoy izao,\nKa ovay ny Malagasy\nMba ho tena zanakao,\nO ! nomena ;\nHazavao ny nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (313, 3, 'Akekezo ireo mpania\nTafaverina Aminao,\nKa tantano mba ho tia\nNy miresaka Aminao,\nO ! tariho\nHo Anao ny nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (313, 4, 'Dia fohazy ny mpitory,\nMba hanao ny asanao,\nKa hamoha ireo matory\nMba handre ny sitrakao,\nHamasino\nHo Anao ny nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (313, 5, 'O ! Jesosy feno antra !\nDera, laza, ho Anao,\nNy lohalika rehetra\nHandohalika Aminao,\nMitoera,\nFa Anao ny nosinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (314, 1, 'Miantra ny firenena,\nRy Jesosy Tomponay !\nAmbarao ny famonjena\nIzay nataonao fahizay ;\nAsehoinao ny hery\nNamonjena marobe ;\n:,: Ry Mpamonjy olom-bery !\nMamonje indray anie :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (314, 2, 'Mba heveronao ry Nosy\nItoeranay izao ;\nIantraonao, ry Jesosy,\nNy tsy mahalala  Anao ;\nIantrao ireo faritany\nAnjakan''ny aizim-be,\n:,: Mba ho tonga faingana any\nNy mazava lehibe :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (314, 3, 'Be ny tany tsy voasa,\nVitsy ny mpanomponao ;\nKoa izahay mpiasa\nAo an-tanimbolinao\nMba omeo fo minia\nHankato ny teninao,\n:,: Ka aoka mba hikely aina\nIzahay hiasa ao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (314, 4, 'Mankanesa, ry Mpitory !\nAo an-tany lavitra ao,\nBe tsy mahalala  akory\nAn''i Jeso Tomponao,\nHe, ny fahantrany, indrisy !\nHe, ny hadalan''ny ao,\n:,: Fa ny olona tsy misy\nHampianatra azy ao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (314, 5, 'Ny harena anomezo\nSy ny fahendrena koa,\nEny, Kristiana ! aelezo\nIzay rehetra mahasoa ;\nMankanesa re izay tia\nNy Mpamonjy lehibe ;\n:,: Miantrà ka manampia\nNy mahantra marobe :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (315, 1, 'Injany, misy feo !\nKa iza no tsy mandre ?\nRy Kristiana, raiso\nAdidy lehibe,\nManerana ny tany\nNy antso avo izao,\nHo an''i Jeso Tompo\nIzao tontolo izao,\nMihobia\nSy mitoria\nNy teny mahafaly\nIzay voarainareo ;\nIzao no tena fetra :\nHo an''i Jeso soa\nNy olona rehetra ho Azy\nAvokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (315, 2, 'Tsy ho antsika irery\nNy fahasoavam-be,\nFa ho an''ny olom-bery\nTsy an-kanavaka  e !\nNy fiantran''ny Tompo\nTsy misy fetra, koa\nLazaonareo mpanompo\n''Lay fitiavan-tsoa.\nMiasà.\nKa milazà\nNy teny mampionona\nMahafa-po ;\nVoarainareo ny baiko :\nHo an''i Jeso soa\nNy olona rehetra ho Azy\nAvokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (315, 3, 'Ny Tompo no milaza :\n« Andeha mitoria, »\nKa na dia sarotra aza\nNy asa, matokia ;\nFa tsy mandeha  irery\nIzay mitondra soa,\nFa omban''Ilay Mahery\nNy irany tokoa,\nKa sahia\nDia  mitoria\nNy famonjena lehibe\nVoarainareo ;\nVoatsangana ny saina :\nHo an''i Jeso soa\nNy olona rehetra ho Azy\nAvokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (315, 4, 'Ry Tompo sy Mpanjaka !\nAmpio ny olonao\nHanoa sy hanahaka\nKa hankalaza  Anao,\nDia aoka hokendreny\nIzay katsahinao.\nKa  hahita famonjena\nIzao rehetra izao,\nRy Tsitoha !\nMba manehoa\nNy hery mahagaga izay\nAnananao :\nTongava mba hanjaka :\nKa  angony avokoa\nNy olona navotanao\nRy Tompo soa !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (316, 1, 'Ry Ziona, mitsangàna,\nKa mihazavà izao ;\nAndrandrao ny fahazavana,\nIzay miposaka  aminao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (316, 2, 'Maizina ny firenena\nAmin-tany fantatrao,\nFa tsy manam-pamonjena,\nNefa ankohonanao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (316, 3, 'Atopazy re ny maso !\nMarobe ny zanakao ;\nToy ny tany feno hazo,\nEo anatrehanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (316, 4, 'Asandrato ny fanilo\nHanazava lavitra,\nMba ho marobe no hino,\nAn''Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (316, 5, 'Ry Jesosy, notahianao\nAnie ny lovanao ;\nOlo-maro izay irinao,\nTaomy mora ho Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (317, 1, 'Maizina ny firenena,\nKoa diso lalana,\nTsy mahita famonjena,\nTia zava-poana ;\nEndrey, ny haverezan-tsainy\nLany foana ny ainy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (317, 2, 'Lehibe ny aizin''ota,\nTakona ny lalana,\nHandosiran''ny mpanota,\nNy fahaverezana ;\nIndrisy, very re ny aina !\nInjay ny feo mitaraina !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (317, 3, 'Mitsangàna izao, ry mino,\nMandehàna faingana,\nEnto aminy ny jiro,\nAtoroy ny lalana ;\nHianao va tsy onena\nNy tsy manam-pamonjena ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (317, 4, 'Dia ento ny mazava\nIzay naseho taminao ;\nMazotoa hampisava,\nIzany aizim-pito ao,\nKa ny namanao mahantra\nItorio ny Mpiantra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (317, 5, 'Eny, tsara sy mahery\nNy Filazantsara re !\nAfaka ny olom-bery,\nSambatra ny mahare ;\nKa mitandrina tokoa !\nIzany teny mahasoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (318, 1, 'O, ry Rain’ny fahazavana\ntsy milentika !\nAvy izahay mitondra ny\nfisaorana ;\nAloky ny fasanay\nno nipetrahanay,\nNefa andro no nasolonao\nny alinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (318, 2, 'Enga ka handroso tsara\nny mazava e,\nMba ho fantatray tokoa\nIlay manoa hoe :\n« Izaho no fahazavan’izao\ntontolo izao ».\nHanazava sy hanitsy\nizay alehanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (318, 3, 'Ny Fitarikandron’aina\nefa hita re,\nManazava na ny kely\nna ny lehibe.\nMahasoa, mahahendry,\nmahasambatra,\nKa akory re ny andro\nizay hiposaka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (318, 4, 'Mitsangàna, ry Ziona,\nka mihazavà !\nVoninahitry ny Tompo\nno miposaka ;\nAtopazy manodidina\nny masonao.\nNy haren’ny firenena\ntonga aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (319, 1, 'Mandrosoa, ry mpanarato,\nAza ketraka izao !\nMandrosoa tsy miato,\nJeso re no herinao ;\nKa ny jentilisa maro,\nHavana sy tapaka,\nMba toroy, ampahafantaro\nAn’Ilay Mpanafaka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (319, 2, 'Mandrosoa, ry mpanarato,\nMiezaha fatratra,\nKa na iza hisahato,\nAza mba matahotra,\nNotolorana adidy\nHianao ka miasà !\nNa ho mamy na mangidy,\nAza fay na mba handà !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (319, 3, 'Mandrosoa, ry mpanarato,\nAo an-drano lalina !\nMisy ve hitora-bato ?\nAnehoy fitiavana !\nRaha kivy ny mpanota\nNoho ny haratsiny,\nEnto any Gologota\nAmin’ny Mpamonjiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (319, 4, 'Mandrosoa  re no baiko\nIzay mifehy ahy  izao,\nKa ny tena sy ny aiko\nRaiso, Tompo, ho Anao !\nHe, afoiko manontolo\nIzay ananako izao\nKoa raisonao daholo\nMba ho voninahitrao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (320, 1, 'Mba iraho ny Fanahy,\nRy Jesosy Tompo ô !\nMba hanampy sy hitahy\nNy mpiasa irakao.\nTany mafy no asaina,\nNefa kely izahay ;\nMarobe no tsy misaina\nRaha itorianay !\nTanteraho re ny toky,\nIzay nomenao fahizay.\nKa mombà anay mpitory\nMandra-podi-mandrinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (320, 2, 'O, ry Masoandron’aina,\nHazavao ny namanay,\nMba fafazo andon’aina,\nFa  efa toy ny tany hay ;\nTapa-kazo no hajainy,\nKa tsy fantany ny To ;\nFeno aizina ny sainy,\nEfa momoka sy lo ;\nNy sikidy mahavery\nNo ataony solonao,\nSampy vato, hazo kely\nNo hajainy toa Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (320, 3, 'Averenonao ireny\nMba ho tonga olonao ;\nTadiavo tsy ho very,\nRaiso re fa lovanao !\nEntonao ny famonjena\nKa ovay ny fony izao ;\nAoka hisy sesehena\nNy hanaiky ho Anao !\nDia  hiova re ny tany\nKa hanjary lonaka,\nAry hisy voa mamy\nIzay ho tena vokatra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (320, 4, 'Aoka re mba tsy ho rava\nNy mazava hitanay !\nMba handroso lalandava\nNy anjara asanay,\nMba ho dera aman-daza\nHo Anao, ry Tomponay !\nHatanjaho ny mpiasa,\nDia tsy mba hety fay ;\nAoka re ny olo-mino\nMba hamory ho Anao,\nKa hitombo be ny mpino\nHiandany Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 1, 'Manana fiorenana\nNy fiangonan''i Kristy ;\nEla, fa he, tsy levona\nIzy na maro na vitsy ;\nVavan''ny olo-masina\nMety ho voatampina,\nNefa tsy maty ny feo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 2, 'Ny fiangona-masina\nTrano tsy mety mikoa,\nIzy no vato velona\nRafitry ny Ray Tsitoha ;\nJeso no fehizorony,\nIsika mirafitra aminy,\nMonina ao ny Fanahiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 3, 'Nefa mba misy koa atý\nTrano nataon-tanantsika\nHo voninahitr''i Kristy,\nMendrika ho tiantsika ;\nFa ao no andrenesana\nNy tenin''Andriamanitra,\nMamin''ny olona mino.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 4, 'Ao ny Batisa masina,\nIzay tena ranon''ny aina ;\nAo Ilay Mpihaino vavaka,\nTokin''izay mitaraina ;\nAo koa ny Fandraisana,\nDia ny sakafo masina,\nTena aman-dràn''i Jesosy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 5, 'Enga ka mba ho fantatra\nAmin''ny tany rehetra ;\nIlay loharano velona,\nSady tsy mety ho ritra !\nRaha  ny lakolosy velona,\nFaly izay mpivavaka,\nFa manantena hihaona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (321, 6, 'Raiso, ry Kristy, ho Anao,\nNy fideranay anio !\nAmpandrosoy ny teninao,\nNy ondry very vorio\nMandra-pikambany hiray,\nAmin''ny valanao indray,\nFa Hianao no Mpiandry.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (322, 1, 'Ry Jesosy Tomponay\nRavoravo izahay ;\nTonga izahay izao\nMba hihaona Aminao ;\nIndro misy tranonao\nHitokananay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (322, 2, 'Ka mangataka  Aminao\nIzahay, ry Tompo ô !\nAoka mba horaisinao\nIzay nataonay ho Anao :\nTrano fiangonana\nTrano fivavahana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (322, 3, 'Aoka mba ho fantatray\nEto ny alehanay,\nFa vahiny izahay,\nKa ny tanindrazanay\nDia any Aminao ;\nKoa mba tarihonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (322, 4, 'Aoka ny Anaranao\nMba hohamasininao ;\nAoka ny Fanahinao,\nMba ho tonga hanavao\nNy mpanota marobe,\nDia hovoavonjy re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (322, 5, 'Ka ny fiadananao\nAmpombay ny olonao,\nHampikambana ny fo,\nKa ho toy ny iray tam-po,\nMba hifankatiavanay\nIzao sy rahatrizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (323, 1, 'Indro, Tompo, misy trano\nIzay namboarinay ho Anao,\nAngatahanay tsodrano,\nItokananay ho Anao\nTompo ô, mba hamasino\nHo fihaonana Aminao,\nHo tempoly tian''ny mino\nFivavahana Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (323, 2, 'Diovy, Tompo, mba ho trano\nIzay hajaina ho Anao,\nKa omeo sy itokano\nMba ho fideràna Anao.\nTompo masina ô, idiro\nMba ho mendrika Anao\nKa aoka  Hianao ho jiro\nIzay hamirapiratra ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (323, 3, 'Mba tahio, ry Ray Mpitahy,\nHo tempoly masinao\nHo Betelan''ny fanahy,\nVavahady ho Aminao.\nItoero, mba ho kianja\nMahamanina ny fo,\nMba ho trano mendri-kaja,\nMampivavaka ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (323, 4, 'Mandrosoa ka midira\nHo mpanjaka tokana  ao\nKa ny vavaka sy hira\nMba tahio ho sitrakao,\nNefa tsy ny trano ihany\nNo atolotray, ry Ray,\nIndro koa mba itokany\nHo Anao ny tenanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 1, 'Vory ny mpanomponao\nEto anatrehanao,\nRy Jesosy Tomponay !\nHe ! faly ny fonay,\nFaly, faly, faly,\nHe ! faly ny fonay,\nVory maro toy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 2, 'Mba atrehonao iizahay \nFa mitokana  eto izao\nNy trano ho Anao :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 3, 'Eto hitorianay\nAn''i Jeso Tomponay,\nMpamonjy lehibe :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 4, 'Eto hanomezanao\nTsara ny Fanahinao\nMba hanadio ny fo :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 5, 'Eto no hanananao\nLaza saotra ho Anao ;\nNy maro mba vonjeo :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (324, 6, 'Aoka re ny tranonay,\nTrano iangonanay,\nMba ho tempolinao :\nHe ! faly ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (325, 1, 'Ry Andriananaharinay !\nIsaoranay ny Anaranao ;\nNambininao ny tranonay\nKa vita tsara ho Anao :\n(Feo 1-2)\nFenoy, fenoy, fenoy\nNy hamasinanao\nNy tranonay ho tranonao.\n(Feo 3-4)\nFenoy, fenoy\nny hamasinananao\nFenoy ny hamasinanao\nNy tranonay ho tranonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (325, 2, 'Ry Jeso ! avy izahay\nHitokana ny tranonao,\nNy trano izay naorinay\nHo voninahitrao anie :\nFenoy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (325, 3, 'Avia, Fanahy Masina ô !\nHitoetra eto aminay,\nNy aizinay mba hazavao,\nHanananay fo be fitia :\nFenoy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (325, 4, 'Mba raisonao ny deranay\nIzay enti-mankalaza Anao ;\nNy trano itokananay\nEkeo tokoa ho tranonao :\nFenoy, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (326, 1, 'Ry Jehovah Tompo soa !\nVory eto izahay,\nKa mifaly avokoa,\nRaisonao ny vavakay !\nAndro fitokanan-trano,\nFifaliam-be izao,\nAmpidino ny tsodrano\nAvy ao an-danitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (326, 2, 'Ry Jesosy ! mandrosoa\nFa Anao ny tranonay,\nHiderana Anao tokoa,\nKa henoy ny hatakay !\nNefa tsy ny vatan-trano\nNo honenanao izao,\nFa ny fo no itokano,\nItoero, hafanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (326, 3, 'Ry Fanahy Mpanazava !\nAnjakao tokoa izahay,\nDia hazava lalandava,\nNy fanahin-janakao ;\nKa fenoy fanahy mino\nHo tempolinao tokoa,\nHatrizao dia hamasino\nKa hitombo sy hanoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (327, 1, 'Atreho ny mpanomponao\nIzay mivory eto izao\nKa efa nametrahanao\nAdidy masina hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (327, 2, 'Hianao, izay Tompon’ezaka\nNifidy anay tsy mendrika\nKa efa nampirisika\nHandroso tsy hiherika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (327, 3, '‘Ndrisy mpanompo tsy manoa\nIzahay izay nofidinao.\nMpiasa tsy mba mahasoa,\nTompo ô ! mifona Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (327, 4, 'Na dia kely aza izao\nNo azonay atolotra,\nAoka re mba ho sitrakao\nHaroso ho fanatitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (327, 5, 'Dia saotra no omena Anao\nKa raiso re, ry Tomponay\nNy fonay koa onenonao\nSy anjakao mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (328, 1, 'Isaoranay Anao,\nRy Tomponay Tsitoha,\nNy nanomezanao\nMpiasa mahasoa.\nKa nony vao nandre\nIlay antso taminao,\nDia roso ka nandeha\nNanao ny asanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (328, 2, 'Natokanay ho Anao,\nNy taona maro be ;\nIlay fitiavanao,\nNataony rehareha.\nNa nisy ny manjo,\nTsy kivy sanatria\nHianao ilay ao am-po\nNanao hoe: « Matokia ».', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (328, 3, 'Izay nataony anie,\nHambininao tokoa,\nHitombo be dia be\nHitera-boka-tsoa,\nHianao Ilay Tompo tia\nNo angatahanay ;\nTongava, manampia\nIzay vitany ho anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (328, 4, 'Ka mba tsinjovinao,\nSikino hery indray,\nIty mpanomponao,\nAnio sy rahatrizay,\nIlay teny taminao\nHo aminy anie ;\nMpanompo sitrakao\nSatria nahefa be.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (329, 1, 'Mba tahio, Jeso ô !\nHira, hatakay sy teny ;\nMba tarihonao ny fo\nHanarahanay ireny ;\nHianao no Tompon’aina,\nSady mendri-koderaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (329, 2, 'Izahay mpanomponao\nHody ao an-tokantrano,\nFaly manana Anao,\nKa vimbino sy tantano ;\nAza avela ho irery,\nIzahay, fandrao ho very.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (329, 3, 'Saotra no atolotray,\nRaha mbola atý an-tany ;\nAo an-danitra indray\nDia dera tsy ho lany\nNo hatao sy hohiraina\nHo Anao ry Tompon’aina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (330, 1, 'Saotra, dera, no homena\nHo Anao Mpanavotray,\nHianao manetry tena\nMba hanandratra anay.\nMba tariho Tompo ô !\nNy fanahy sy ny fo ;\nTianay ny hiara-dia\nAminao ka tsy hania.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (330, 2, 'Raha ory sy mijaly\nEto aza izahay,\nTsy ho kivy, fa hifaly\nAmin’ny anjaranay.\nHianao ry Tomponay,\nNo mba iankinanay,\nKa inoanay homena\nHo anay ny famonjena.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (331, 1, 'Sambatra ny mankatò\nIzay lazain’ny Tompon’aina ;\nSy manaiky eram-po\nFa hanolotra ny saina,\nKa mitandrina ny didy\nToy ny zava-tsarobidy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (331, 2, 'Sambatra izay mandà\nRaha taomina hanota,\nKa na dia halatsa-drà,\nTsy mba mety resin’ota,\nTsy ekeny ny mpanimba,\nNa ireo mpanazimba.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (331, 3, 'Sambatra ny olona\nIzay misaina ao am-pony\nAndro aman-alina\nNy lazainao, ry Mpamonjy ;\nFa ataonao toy ny hazo\nMaitso tsara, tsy malazo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (332, 1, 'Mba soraty ao am-poko\nJeso, ny Anaranao ;\nMba arovy tsy haloto\nNy kasen’ny tananao.\nHianao, Jesosy ô !\nNo ho voninahitro :\nHianao, ry voahombo,\nNo Mpanjakako sy Tompo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (333, 1, 'Finaritra ny olonao\nJesosy Tompo ô !\nFa manana ny aim-baovao\nKa ravo eram-po.\nNy famonjena vitanao\nDia efa voaray  izao.\nNy fifaliany atý.\nHitohy hatrarý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (333, 2, 'Izaho va Jesosy ô,\nMba isan’ny Anao ?\nNy solika fitaziako\nMoa misy va izao ?\nHevero ry fanahiko,\nKa aza mety afa-po\nAmbara-pahafantatrao\nFa Jeso no anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (334, 1, 'Hody Tompo, izahay\nMiaraha aminay,\nKa tahio ny teninao\nMba hamorona aim-baovao,\nSao mivory foana\nIzahay mpiangona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (334, 2, 'Marobe ny rafinay\nTa-hamandrika anay ;\nTompo, miarova anay,\nSao dia very izahay ;\nMitoera aminay\nAo an-tokantranonay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (334, 3, 'Efa renay taminao\nFa anay ny lanitrao,\nEfa namboarinao\nMba ho an’ny olonao ;\nKa tariho, Tompo ô,\nHody ao ny Zanakao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (335, 1, 'Sambatra ny sofiko\nFa nandre ny teny,\nTenin’i Jesosiko,\nTenim-pamonjeny.\nHe ! izaho tiany\nMba hiara-dia\nSy hianatra aminy\nAry tsy hania.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (335, 2, 'Sarotra ny làlako\nSady mampanahy,\nNefa ny Mpamonjiko\nMahatoky ahy ;\nVetivety foana\nDia  ho avy Izy\nAmim-boninahitra,\nKa handray ny Azy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (336, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (336, 2, 'Hody izahay, Zanahary !\nTahio tsara izahay ;\nAmpifalionao ny fonay\nAmin’ny fitianao :\n:,: Ampifalio :,:\nIzahay etý an-tany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (336, 3, 'Mankasitraka Anao izahay\nNoho ny teny soa\nIzay nampianaranao\nSaina hahasoa anay.\n:,: Tomoera :,:\nAminay isan-andro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (336, 4, 'Raha ho faty izahay\nKa handao ity tany,\nDia ampifalio izahay,\nEnto ho an-danitra ;\n:,: Dia faly :,:\nIzahay mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (337, 1, 'Ampodio izahay, Jehovah,\nAmin’ny fitianao ;\nNa  aiza na  aiza no haleha\nAoka hovimbininao :\n:,: O, ambino :,:\nIzahay mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (337, 2, 'Fa ny teny renay any\nAoka ho tadidy koa,\nKa mba hiasanay saina\nHahazoanay ny soa\n:,: Isan-andro :,:\nIzay hiainanay  atý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (338, 1, 'Ry Jehovah, Tompo ô !\nTompo masina sy to,\nAzonay ny teninao\nMampandre ny antranao ;\nAoka mba harahinay\nKa ho masina aminay,\n:,: Mba hanao ny sitrakao\nIzahay mpanomponao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (338, 2, 'Hody izahay izao\nAmin’ny Anaranao ;\nMitoera aminay\nAo an-tokantranonay,\nMba ho velona aminao\nIzahay navotanao ;\n:,: Be ny fahavalonay\nKa arovy izahay :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (339, 1, 'Arovy aho, Tompo,\nAtolotro izao\nNy tenako hanompo\nSy hankalaza  Anao.\nOmeo ahy hery\nSy faharetam-po,\nArovy tsy ho very\nIzay anjarako !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (340, 1, 'Ry Jesosy, mba ampio\nAho ho madio fo ;\nRaha mivily ahitsio\nHanarahako ny to.\nMba ampaherezo aho\nHanompoako Anao ;\nRaha ory hatanjaho\nMba hifikitra Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (341, 1, 'Tompo ô ! mba hasoavy\nSy arovy izahay ;\nTompo ô ! mba aposahy\nAminay ny tavanao ;\nTompo, mitodiha  anay,\nDia hiadana izahay ;\nRay sy Zanaka, Fanahy,\nMitahia anay, Amen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (342, 1, 'Tompo ô ! ampitandremo\nIzay lazainao izahay ;\nHazavao ka mba omeo\nHery ny fanahinay ;\nKa ny fo ampionony\nTsy hitebiteby intsony ;\n:,: Mba diovy, hamasino\nIzahay, ry Tompo ô ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (342, 2, 'Ka rehefa hodimandry\nIzahay, ry Tomponay,\nTsy ho taitra, fa hiandry\nNy handraisanao anay,\nRy Jesosy Tompon’aina,\nHianao no antenaina,\n:,: Hianao no hanazava\nNy hodianay arý :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (343, 1, 'Ny fahasoavan’ny Jesosy\nKristy\nSy ny fitiavan’Andriamanitra\nAry ny firaisana  amin’ny\nFanahy Masina\nHo amintsika rehetra anie,\nAmen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (344, 1, 'Ry Jeso Tompo soa,\nRy Vatolampiko !\nEsory avokoa\nNy hevi-dratsiko.\nOmeo ahy hery,\nArovy tsy ho very\nNy ondry kelinao,\nMba hifalianao ;\nJesosy ô ! henoy re, henoy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (345, 1, 'Fiadanana be,\nHasambarana re,\nNo miandry antsika  ao\nan-danitra e !\nKa handeha handao\nIzao tontolo izao,\nKa hitady tokoa ilay\ntany vaovao.\nMiaingà hody ao !\nMiaingà hatrizao !\nMba modia hianao (izao)\nAo amin’i Jesosy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (346, 1, 'Mba tarihonao, ry Raiko,\nNy fanahy sy ny foko,\n:,: Mba ho any Aminao ! :,:\nO, ry Loharanon’aiko,\nIzao ihany no ilaiko,\n:,: Dia ny hahita Anao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (347, 1, 'Ny teninao, Jehovah ô !\nVoatory aminay\nTahio tsy ho levona,\nVelominao anie !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (347, 2, 'Izay voatory tsy anay,\nFa avy Aminao,\nKa dia  ny fanambinanao\nNo mba ilainay koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (347, 3, 'Fa tsy mba ampy ho anay\nNy toriteninay,\nKa tsy ho tokinay ireo,\nRaha  tsy ambininao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (347, 4, 'Dia  ampitoeronao izao\nHo eto aminay\nNy herin’ny Fanahinao,\nMba hahasoa anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (348, 1, 'Derao Andriamanitra,\nFa Izy Loharanon-tsoa\nDerao ny Tompo tokana,\nRay, Zanaka, Fanahy koa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (349, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (349, 2, 'Andriamanitra !\nTahio ny mihaino ;\nNy tsy manoa vava-poana\nHahazo ny iriny,\nAndriamanitra\nHo masoandrony ;\nKa ny Fanahy Masina\nMpitari-dàlana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (349, 3, 'Ny Anaran’i Jeso\nNo voninahiny ;\nNy hamarinan’i Kristy\nNo fihaingony,\nTiavo Jesosy,\nRy mino ny teny !\nKatsaho fihavanana,\nRy tsy mino rehetra !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (350, 1, 'Dera re, laza anie !\nHo an’Andriamanitra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (350, 2, 'Aoka ny tany re,\nMba hiadana anie !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (350, 3, 'Ary ny olony\nAoka hankasitrahany !\n\nNY FIAINANA KRISTIANA\n1. NY ANTSON’NY FILAZANTSARA', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (351, 1, 'Hianao, ry Ray, no mibaiko\nHanaovanay ny asanao,\nKa ny handà e mba tsy haiko,\nNa sarotra aza ny atao.\nNy fitoriana lalaina,\nNatomboky ny mpianatrao\nDia tsy azo atao very maina,\nFa he, tohizanay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (351, 2, 'Fa mbola  adidin’ny mazava\nMitondra ny fanala izao,\nHiainga, handeha hampisava\nNy aizina  ao an-ala ao.\nIreo rahalahy sy rahavavy\nDia be tsy mahalala  Anao\nKoa indro, Tompo, aho avy\nHitory ny Anaranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (351, 3, 'Kanefa tsy ny eto ihany\nNo anirahanao anay,\nFa na ny any lavitra any\nDia samy mbola  adidinay.\nHiezaka, hikely aina,\nHamafy koa ny teninao\nIzay hany tena mofon’aina,\nHo an’izao tontolo izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (352, 1, 'Tsy hangina re ny hoby,\nnoho hianao ry Ziona ;\nFa miposaka aminao\nny fahazavana soa ;\nNy fanilom-pamonjena,\nindro fa torina\nHanazava ny olona tokoa.\nMivoaha, asandrato ny\nfaneva soanareo ;\nTsenao re ny Mpanjaka,\nfa avy aminao\nNy famonjeny sy ny lova\nizay atolony anao\nSatria hianao dia tsy foiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (352, 2, 'Voninahi-panjakana\nisaloranao\nSy anaram-baovao no\niantsoana anao\nFampakaram-bady, fety\nanaingàna anao\nMba ho an’i Kristy,\nIlay Tomponao.\nMivoaha, asandrato ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (352, 3, 'O ! Ry mpiambina tinendry\neo ambony manda,\nAza  avelanao hangina re\nny feonao\nKa hijoro izao ny mino,\nfa tsy hisy banga\nEo an-tobin’i Jeso Tomponao.\nMivoaha, asandrato ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (353, 1, 'Fantatrao va ny Mpamonjy,\nIlay Tompon-danitra ?\nJeso, Kristy sy Mpanjaka,\nZanak’Andriamanitra.\nIlay nandao ny lapan-dRainy\nFeno voninahitra,\nTompo tonga mba hanompo,\nNoho ny fitiavana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (353, 2, 'Eny, zava-mahagaga,\nFietrena lalina :\nSaro-bidy ka tsy foiny\nIsika olombelona.\nIlay Mpisorona Ambony\nIzao no asa sorona,\nKa manolotra ny ainy\nMba ho fanavotana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (353, 3, 'Ao ny lanitra mivoha\nMiantso anao hiakatra ;\nAoka tsy hiroa saina,\nManapaha hevitra.\nHe mitsotra moramora\nMba handray ny tanany ;\nMitodiha : izao no ora,\nManeke ho zanany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (354, 1, 'Mba sainonao ange,\nSao tsy mba fantatrao\nNy zava-dehibe\nNataoko ho anao :\n:,: Hevero, ka lazao, lazao\nIzay mba vitanao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (354, 2, 'Ny lapako tarý,\nNy fahefako tao,\nNilaozako tatý\nHanavotra aina anao :\n:,: Hevero, ka lazao, lazao\nIzay mba foinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (354, 3, 'Heveroronao ange,\nNy nisoloanao anao ;\nNangidy indrindra anie\nNy nahazoana anao :\n:,: Hevero, ka lazao, lazao\nIzay mba nentinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (354, 4, 'Endre ! ny aiko koa\nNomeko ho anao ;\nTsy misy zava-tsoa\nTsy foiko ho anao :\n:,: Hevero, ka lazao, lazao\nIzay mba taminao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (354, 5, 'Ry Tompo be fitia !\nEndrey, ity vitanao ;\nIzaho izay mpania\nTsy mahavaly izao :\n:,: Ny aiko no mba raisonao\nHanao ny sitrakao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (355, 1, 'Ny Mpamonjy no mitady\nFihavanana aminao ;\nIzy no manambitamby\nHanatonanao izao ;\nSao manjary fandatsana\nNy fifonana aminao,\nKa ny mety, mihavàna\nAmin’ny Mpamonjy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (355, 2, 'Dieny misy hafanana\nKa mangoraka ny fo,\nManatòna ny Mpamonjy\nSao dia mangatsiaka indray\nIzao no andro famonjena,\nKa vonjeo sao neninao ;\nRy mpanota, miverena\nIzao ankehitriny izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (355, 3, 'Sao ity no toriteny\nFara-fanasana  anao ;\nSao anio no fotoana\nFara-fahavelomanao :\nKa aza mba misalasala,\nSao manjary neninao,\nRaha tsy mety mahalala\nIzao fotoana mety izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (355, 4, 'Indrisy ! be ny fahotako,\nBe ny ratsy vitako ;\nSao tsy raisin’ny Mpamonjy\nNy mpanota tahaka  izao ?\nMiverena, fa ny rany\nAmpy hahadio anao ;\nManatona hosasany\nIzao ankehitriny izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (356, 1, 'Ry namako mahantra\nManaram-po izao !\nNy ota no mandratra,\nSao simba hianao ;\nIzay anao rehetra\nHo lasa avokoa,\nFa tsy mba misy fetra,\nHo levona avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (356, 2, 'Ny Tompo be fiantra\nMangora-po tokoa\nMahita anao mahantra\nFa tsy mahita soa ;\nNatolony ny ainy\nHo vidin’ny ainao,\nNalatsany ny rany\nMba hanadio anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (356, 3, 'Ny Tompo malahelo,\nTsy foiny hianao,\nNy feo mamelovelo\nMibitsika aminao ;\nOnena anao ny fony\nKa mitaraina hoe :\nNy ota ajanòny,\nMba ajanòny re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (357, 1, 'Ry mpanota ! mihevera,\nFa miantso Kristy izao :\nIzy ta-handray mpanota\nManatòna hianao :\n:,: Mihainoa, mihainoa,\nKa ario ny ditranao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (357, 2, 'Ry mpanota ! miverena\nAo am-balan’i Jeso ;\nAza dia mirenireny,\nSao iharan-doza izao\n:,: O ! modia, O ! modia,\nIalao ny ditranao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (357, 3, 'Ry mpanota ! manatòna\nDieny misy antso izao\nFa ho avy ny fotoana\nMbola hanenenanao :\n:,: O ! avia, O ! avia,\nIzao ankehitriny izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (357, 4, 'Ry mpanota ! matokia,\nTompo mora fo Jeso ;\nIzy dia tena tia\nIzay mibebaka tokoa :\n:,: Matokia ! matokia,\nMibebaha re izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (357, 5, 'Ry Sakaizan’ny mpanota,\nEnto aho ho Anao ;\nHe ! ny tenako rehetra\nNo atolotro izao :\n:,: Raisonao re, raisonao re,\nHo fanati-tsitrakao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (358, 1, 'Efa tonga atý izao\nTeny fiainana :\nRaiso mba ho fiainanao,\nTeny fiainana.\nHe ! ny teny tsara,\nTeny manan-kery ;\nTenin’ny Ray\nTsara tokoa\nTeny fiainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (358, 2, 'Io no Filazantsara soa,\nTeny fiainana :\nTeny mamy indrindra koa,\nTeny fiainana.\nRaisonao ny teny,\nTeny soa nomeny :\nTenin’ny Ray, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (358, 3, 'Ry mpanota ! henoy izao,\nTeny fiainana :\nFa fiainana ho anao,\nTeny fiainana.\nKristy no nomena\nMba ho famonjena :\nTenin’ny Ray, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (359, 1, 'He ! ry namako mahantra,\nIndro Jeso Tompo tia,\nIlay Mpamonjy feno antra,\nNo mitady ny mania ;\nOlom-bery tsy mba foiny,\nKa na diso aza re,\n:,: Mbola tiany sy antsoiny\nHanam-pifaliam-be :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (359, 2, 'He ! fa tsy mba misy fetra\nNy homena lova soa\nFa ny olona rehetra\nSamy tiany avokoa ;\nNefa  izay be hataka  andro\nDia hanenina tokoa ;\n:,: Misy fetra ny fotoana\nAzo amindrana fo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (359, 3, 'Manatòna ankehitriny, \nRy mpanota ory fo !\nMba hesoriny ny tsiny\nKa hadio indray ny fo ;\nFa ny ota hosasany\nDia ho afaka avokoa,\n:,: Izany re no fikasasny\nKa tsy maintsy to tokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (359, 4, 'Fa izay mandà ny teny\nSy maditra, mafy fo,\nHampangain’ny teny reny,\nFa tsy nety nakatò ;\nJeso Tompo no hitsara\nSy hamaly ny natao,\n:,: Nefa kosa ny anjara\nDia miankina aminao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 1, 'Tsy renao va ny feon’ny Ray,\nRy malahelo fo !\nMiantso hoe : « Avia, avia,\nModia hianao ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 2, 'Ny Rainao be fitiavana\nHifaly aminao,\nKa hanome malalaka,\nRaha hody hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 3, 'Na diso aza hianao,\nMitebiteby koa,\nNy Rainao ta-handray anao\nHo sambatra tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 4, 'Modia an-tranonao arý,\nMahantra hianao ;\nTsy misy zava-tsao atý\nHahafa-po anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 5, 'Ny lamba efa rovitra\nHesorina aminao,\nKa hafa tsara lavitra\nNo hitafianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (360, 6, 'Mba maneke ny teniny\nAnkehitriny izao,\nFandrao dia hisy loza re\nHihatra aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (361, 1, 'Mba hevero, ry mpanota !\nNy nataon’ny Tomponao :\nIzy tsy mba manana ota\nNefa tonga solonao ;\nHe ! ny lanitra nafoiny\nMba hidinany tatý\nOlom-bery no antsoiny\nMba handova ny arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (361, 2, 'Mba hevero, ry mpanota !\nFa nijaly Jeso tia,\nMaty tany Gologota\nMba hamory ny mania,\nIndro, latsa-dra ny Tompo\nNefa tsy mba diso re !\nFa nisolo ny mpanompo\nIzay  nanohitra   elabe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (361, 3, 'Mba hevero, ry mpanota !\nAlatsaho ao am-po ;\nMahavery re ny ota\nRaha tsy voadio ny fo,\nMangataha ankehitriny,\nDia hodiovin’i Jeso,\nFa izany no iriny\nMba handraisanao ny soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (361, 4, 'Mba hevero, ry mpanota !\nNy fifonana aminao\nHahafoizanao ny ota\nTena fahavalonao.\nManatòna hovonjena\nSao hiharan-doza be !\nManatòna, fa homena\nFifalian-dehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (361, 5, 'Mba hevero, ry mpanota !\nInona ary no hatao ?\nAza mety resin’ota,\nTiavo Jeso Tomponao ;\nNy lalàny no araho\nSitrapony tontosay ;\nNefa Izy angataho\nHomba anao mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (362, 1, 'Manatòna, ry malala !\nAn’i Jeso Tomponao ;\nRaiso re ny famonjena,\n:,: Tsy ho faty hianao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (362, 2, 'Indro Jeso, fa miandry\nMba handray anao izao ;\nMiverena aza kivy,\n:,: Mazotoa, sy matokia :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (362, 3, 'Aza mba mangataka andro,\nSao dia tratra hariva etý\nTahotra sy horohoro\n:,: Hampivadi-po anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (362, 4, 'Ao ny tany soa homeny\nMampifaly fo erý\nSady tsy mba misy ratsy,\n:,: Mandrosoa ho tonga arý :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (363, 1, 'Tsy fantatrao va Ilay namonjy\nanao ?\nRy havako ory izao ?\nMitady anao Ilay namonjy\nanay.\nManiry hamonjy anao ;\nMihainoa ny hamonjy anao\nManao hoe : “ Manatòna\nanio,\nNa dia be aza ny ratsy\nnatao,\nDia mety ho afaka  izao. ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (363, 2, 'Fa tonga tatý Ilay namonjy\nanay\nKa nampijaliana tokoa ;\nNy rany izay namonjeny\nanay\nHamono ny helokao koa ;\nMihainoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (363, 3, 'Misento anao Ilay namonjy\nanay,\nRy olo-mahantra atý !\nTomany anao Ilay namonjy\nanay\nFandrao dia ho very arý ;\nMihainoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (363, 4, 'Henoinao izao ny hamonjy\nanao,\nHiadam-pinaritra am-po ;\nFaingàna handray ny homeny\nanao\nDia lova tsy mety ho lo ;\nMihainoa ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (364, 1, 'Ry olo-mahantra\nMihainoa izao !\nSaino tsara ny teny\nIzay lazaina aminao ;\nRy olo-mahantra !\nModia, modia,', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (364, 2, 'Fa ela dia ela\nHianao no nania,\nTsy nahita ny tsara,\nTsy nahazo ny soa ;\nRy olo-mahantra ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (364, 3, 'Tsy misy mpiantra\nMba hitsinjo anao\nAfa-tsy ny Mpamonjy,\nIzay mitady anao ;\nRy olo-mahantra ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (364, 4, 'Tsarovy ny Rainao\nMalahelo anao\nKa miandry tokoa\nHampandroso anao ;\nRy olo-mahantra ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (365, 1, 'Ry mpanota ! ry mpanota !\nRaiso Jeso Tomponao,\nAza mba misalasala,\nManatòna Azy izao.\nKa ekeo ny feon’i Jeso\nIzay nifona ho anao\nTeo ambony hazo avo\nMaty Izy ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (365, 2, 'Indro Jeso, ry mpanota !\nFaly hohatoninao,\nSady Tompo tsy mandatsa,\nFa mamindra fo tokoa,\nKa miteny moramora\nTa-hamela heloka :\nManatòna, ry mpanota !\nMba handray ny avotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (365, 3, 'Ry mpanota ! ry mpanota !\nMisy vonjy ho anao,\nKa toneo ny alahelo\nMampahory fo anao ;\nAza mety resin’ota,\nFa jereo ny Tomponao ;\nIzy no handresy tsara\nIzay miady aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (365, 4, 'Hianao tsy manan-toky\nHo mpamela heloka,\nAfa-tsy ny Tompo Jeso,\nIlay nanavotra aina anao ;\nKa aza mba misalasala,\nRy mpanota very ô !\nFa azonao ny lova tsara,\nRaiso Jeso Tomponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (366, 1, 'He ! ry mpanota ana, sasatra,\nMitondra fahoriana lehibe ;\nJeso mpanavotra olo-meloka\nNo mahavonjy ny fanahinao.\nMatokia ny mpamela helokao,\nAza kivy, tsy ho faty hianao ;\nMatokia re, matokia re,\nMatokia,\nJeso tsy handao anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (366, 2, 'Indro ny fitiavan-dehibe !\nNy ainy tsy nataony ho\nzavatra\nHamonjy ny mpanota meloka\nSy manana entana mavesatra.\nMatokia re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (366, 3, 'Mba mahareta, Jeso aminao\nNy tanany mahery hiambina\nAza malaky resin-tahotra,\nFa Izy tsy hiala aminao :\nMatokia re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (367, 1, 'Andeha, ry hava-malala !\nAndeha hiaraka izao,\nHitady fonenana tsara\nHonenana mandrakizay\nAo amin’ny tanin’ny Tompo\nNy fahasambarana be,\nNy ota tsy miditra akory,\nTsy misy ny loza manjo,\nDia aoka hiaraka  isika,\nRy hava-malala izao !\nHitady fonenana tsara\nHonenana mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (367, 2, 'Ny trano dia maro tokoa,\nMifaly ny mponina ao,\nTanteraka soa ny aleha,\nFitia omena ny ao ;\nMiara-midera ny Tompo,\nMpanavotra be indrafo ;\nMiara-mandova ny soa,\nMitafy ny lamba madio.\nDia aoka hiaraka ..sns.\n\n3-Na ety ny lalana aza,\nNa maro ny zava-manjo,\nDia aza matahotra akory,\nMandroso ny mety hatao ;\nAnjely tsy tambo isaina\nHirahin-kanampy anao ;\nMpanompo ny Avo indrindra\nHitoby hiaro anao.\nDia aoka hiaraka ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (368, 1, 'Ry zanako mania !\nHenoinao izao :\nIzaho be fitia\nMangora-po anao ;\nRy zanako ! modia,\nSao maty hianao,\nFa be ny bibidia ;\nModia re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (368, 2, 'Ny zavatry ny tany\nSao mahasimba  anao,\nHarena mety lany\nTsy mahavonjy anao ;\nIzaho irery ihany\nNo hiadananao ;\nKa mbola manontany :\nTsy hody va ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (368, 3, 'Mamiratra jerena\nNy lalan-tsitrapo\nFa akoso-bolamena\nMamitaka ny fo ;\nNy sitrapon’ny tena\nMamery ny ainao,\nArio ka miverena,\nModia re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (368, 4, 'Izaho Tompon’aina,\nNijaly ho anao,\nKa he, manolotra  aina\nMba hamonjena anao ;\nNy rako mitaraina,\nModia hianao ;\nKa aza mba malaina,\nModia re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (369, 1, 'Manatòna ry mpanota\nOry, jamba, very koa !\nHianao simban’ny ota,\nIndro, ao ny Tompo soa\nRy tsy mendrika hovonjena,\nMisy izay miantra  anao ;\nFifaliam-be homena,\nFamelana helokao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (369, 2, 'Mba henoy ny teny tsara\nIzay lazaina  aminao,\nSao very ny anjara\nSoa tadiavinao ;\nFeno antra, manan-kery,\nJeso no mitaona anao,\nKa ny mino tsy ho very,\nManatòna re izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (369, 3, 'Ny miato tsy mba tsara,\nNy halainanao ario ;\nSao helohin’ny Mpitsara,\nManatòna re anio ;\nHe ! miandry ny Mpamonjy,\nMba faingana hianao ;\nRaha te ho voavonjy,\nAza ela hianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (370, 1, 'Faingàna ry hava-malala,\nHatòny ny Tompo izao,\nNy fo sy ny feo asandrato,\nDerao Ilay nanavotra anao,\nNy avotra ny famonjena,\nIzay vitany tao Kalvary,\nNo fanantenan’ny mpanota\nHahazo fiainana arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (370, 2, 'He Izy mitondra irery\nNy mafy, nisolo anao,\nKa maty hamonjy ny very ;\nNy rany nandriaka tao.\nNiaritra latsa sy eso,\nNy hombo sy lefona koa ;\nNivesatra ota Izy teto\nHanananao fiainana koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (370, 3, 'Endre ! ny fitiavan’i Jeso,\nRy olo-mahantra, jereo !\nNijaly hamonjy, hevero,\nNy ota mahazo ekeo ;\nEkeo, fa ny fo mihamafy,\nDia loza manenjika anao ;\nFaingàna hamonjy ny ainao,\nHararaoty ny andro izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (370, 4, 'Ny masom-panahy atopazo,\nNy lanitra no andrandrao ;\nNy Tompo maniry hamonjy,\nHenoy fa miantso anao.\nFaingàna, ry hava-malala !\nModia an-tranon’ny Ray ;\nMiandry anao re ny lova,\nFiainana mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (371, 1, 'Mba henoy ny feon’i Jeso,\nRy mpanota very ô !\nAtsaharo re ny ota,\nIzay mamery aina anao ;\nJeso Tompo no miantso,\nAoka tsy ho lavinao ;\nDieny mbola re ny antso,\nManatòna hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (371, 2, 'Izao no tenin’ny Mpamonjy\nIzay lazainy aminao :\n“ Manatòna Ahy, Vonjy\nSy Mpamela helokao ;\nIzaho, Tompo feno antra,\nHanome anao ny soa,\nKa ny helokao rehetra\nDia ho afaka tokoa ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (371, 3, 'Izaho koa no Vavahady\nIzay hidiranao izao,\nSady Lalan-toratady\nIzay mankao an-danitra ao ;\nIzaho dia Mofon’aina\nSady Raon’aina koa ;\nTsy mba misy miala maina\nIzay rehetra tia tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (371, 4, 'Indro, efa maty Aho\nHanavotako anao ;\nMoa tsy hatolotra Ahy\nNy fanahinao izao ?\nRy mpanota ! mihevera,\nAoka izay ny ditranao,\nManatòna, manatòna\nAhy, Jeso Avotrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (372, 1, 'Mpanota ory sy mahantra\nHenoy ny teny ho anao\nJesosy Tompo be fiantra,\nNisolo voina anao,\nKa Izy feno indrafo\nMitaona anao hiova fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (372, 2, 'Jesosy Tompo sy Mpamonjy\nMiantso ny anaranao\n« Mpanota very mila vonjy\nMinoa Ahy hianao ;\nNy sainao aoka ho vaovao,\nNy fomba ratsy ialao ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (372, 3, 'Jesosy koa mampahery\nNy sasatra ao an-dalana ;\nNy rany soa manan-kery\nHahafaka ny entana ;\nJesosy tsy mandroaka\nNy ory izay manatona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (372, 4, 'Ka itokianao ny teny\nNaloaky ny vavany !\nNy mino Azy hovonjeny\nHanatrika ny tavany.\nSakaizako mahantra ô !\nArio ny famoizam-po.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (373, 1, 'Tsy mba hisy tonga arý\nan-danitra\nRaha tsy mandrombaka\ntokoa ;\nNy mpiady tsara izay\nmaharitra\nNo handray anjara lova soa.\nEty sady tery ny vavahady ao,\nKely sy mideza ny lalana\nmankao ;\nNefa aza kivy, ry havako izao,\nFa izay malaina dia ho very.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (373, 2, 'Maro no mitady hisakana\nanao,\nKoa mba tohero, ry sakaiza ;\nMba reseo mafy ny\nfahavalonao.\nFetsy izy  ireo ka mahaiza,\nIzay rehetra mamin’izao\ntontolo izao\nDia avelao, fa mamandrika\nanao ;\nAoka mba Jesosy no\nhifalianao,\nEny hifalianao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (373, 3, 'Satroboninahitra tsy ho\nazonao\nRaha tsy katsahinao tokoa\nFa hevero kosa ny fifalianao\nRaha tonga ao an-dapa soa,\nFa hahita maso ny Tompo\nhianao,\nKa hifaly amin’\nAndriamanitrao.\nKoa miomàna re,\nry sakaiza ô !\nMba ho faly ao am-pitsarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (373, 4, 'Dia miangavy, mangataka\naminao,\nMba modia, ry mpirenireny !\nFa ny Tomponao ta-handray\nanao izao.\nHianao tsy sitrany ho very.\nIzy dia mananty ny tanany\nizao\nKa ny fonao simba mba\natolorinao\nHosoloan’i Jeso fo tsara sy\nvaovao ;\nIzany mba hevero, ry sakaiza !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (374, 1, 'Ho aiza, ho aiza izao hianao\nRy havako izay mbola tia\nNy fomban’ny nofo manimba\nanao ?\nAvia ry olo-mania !\nHenoy fa miantso ny Tompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (374, 2, 'Ho aiza, ho aiza izao hianao,\nRy havako mbola tamana\nAo amin’izao fivelomana izao,\nKa fatra-pikiry hitana\nIzay zavatra halan’ny\nTompo ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (374, 3, 'Hevero fa tsy mba mahafaka\nanao,\nKa tsy mba heken’ny Mpitsara\nNy fahamarinana ao aminao ;\nNy an’i Jesosy no tsara,\nKa dia katsaho ny Azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (374, 4, 'Ekeo ny tenin’ny Tompo soa\nInoy ny toky nomeny,\nKa dia ho hita ny lalam-\nbaovao,\nIzay voatoron’ny teny :\nJesosy no lalana tsara.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (375, 1, 'Torotoro va ny fonao,\nRy mpanota re, izao ?\nKa ifonanao tokoa\nVa ny hadisoanao ?\nHianao va mitomany\nKa maniry mba ho any\nAmin-Jeso, Izay nandany\nRe ny ainy ho anao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (375, 2, 'Ny Fanahin’ny Mpamonjy\nMoa nangatahinao\nHampandray anao ny vonjy\nHahamasina anao ?\nMoa fantatrao tokoa\nFa ny rany mahasoa\nDia avotra naloa\nMba hahafa-trosa anao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (375, 3, 'Maro no manaiky  ihany\nAraka ny vavany,\nFa hanoa ny Mpamonjy,\nKa ho tena havany.\nNefa kosa mbola tia\nFomba ratsy, ka mania,\nDia tsy handray fitia\nRaha ao am-parany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (375, 4, 'Koa dia miangavy\nAho, ry sakaizako\nMahereza mba hitady\nFiadanana ao am-po\nMba hatòny ny Mpamonjy,\nAngatahonao ny vonjy\nDia tsy maintsy ho tra-bonjy,\nJeso no mamindra fo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 8, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 1, 'Mba mifohaza re, henoy izao\nNy hafatra ataon’ny\nTomponao,\nNy Tompo, izay Mpanjaka\nmanan-kery\nMikatsaka anao,\nry olom-bery !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 2, 'Dondòniny ny fo, ka hianao\nMandrindrina ny\nvaravaranao !\nTandremo sao dia sasatra ny\nfony,\nSao lasa Izy ka tsy avy\nintsony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 3, 'Fa indro Izy mbola atý izao\nMitady hampiverina anao\nKa aza lavina ny Tompo tsara,\nSao hianao ho ratsy fiafara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 4, 'Faingàna ary, ka ampandrosoy\nIlay Sakaiza Izay nahafoy\nNy ainy hoanao mpanota very,\nTsy misy Mpamonjy afa-tsy\nIzy irery', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 5, 'Ifony aminy ny helokao,\nHariany ka tsy ho hitanao,\nKa hianao izay mendrika ho\nvoa\nHatsangany ho zana-dRay\nTsitoha', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 6, 'Ka raha renao ao anaty fo\nNy fiadanana izay mahatò\nNy famelana ny helokao\nrehetra\nDia aoka mba handrasanao ny\nfetra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 7, 'Akaiky re ny fahafahana,\nKa mba zakao ny\nhatrotrahana,\nHo avy faingana Jesosy\nTompo\nKa dia ho afaka  ady ny\nMpanompo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (376, 8, 'Rehefa hitanao ny Tomponao\nDia  afaka ny hasasaranao,\nHijanona ny sento sy taraina\nFa hobin-danitra no\nhohiraina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (377, 1, 'He, ny  fiantsoana\nFeno fitiavana,\nTeny masina sy to\nHo anao, ry ory fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (377, 2, 'Hoy Jesosy Tomponao :\n« Manatòna Ahy, re,\nRy miasa fatratra\nSy mavesatra entana! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (377, 3, '« Fa Izaho hanome\nFitsaharan-dehibe\nSy fiainana vaovao\nHahasambatra anao »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (377, 4, 'Ry Mpamonjy tsara ô !\nSaotra no atolotro ;\nKa ampio aho izao\nFa manaiky ho Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (378, 1, 'Ry olo-matory ô ! mba\nmifohaza,\nFa indro Kristy,\nIlay Fahazavana Izay tena\nmalaza,\nDia tonga atý,\nKa aza matory,\nFa mba atolory\nNy fonao izao\nMba hanazavany anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (378, 2, 'Atý dia matevina sady mahàna\nNy zavona be ;\nNy sento, taraina sy\nfitomaniana\nDia misy anie,\nRy olo-mahantra,\nJereo ny Mpiantra\nHenoinao izao\nJesosy nanavotra anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (378, 3, 'Atý dia mangentana sady be\nady,\nKa maro no voa ;\nTsy tambo isaina ireo mitady\nFiadanana am-po\nDia  aza tomany,\nFa eto ihany\nIlay nanome\nNy fahasoavana be !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (378, 4, 'O, miandrandrà ka jereo ao\nambony,\nFa  antoandro izao,\nFalia toa zaza, tahafo ny fony,\nNy Tompo derao,\nNa misy ny onja\nSy ota mandoza,\nDia mbola derao\nJehovah Mpiaro anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (378, 5, 'O, mendrika hankalazaina\nJehovah\nIzay manome\nNy lalana sy fahamarinana\nkoa\nSy fiainana be\nTsilovy, ry Kristy,\nNy lala-mahitsy\nIzorako izao,\nTantano re ny zanakao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (379, 1, 'O, ry mpanota, aza mandà,\nJeso miantso, ka miovà ;\nMba manatòna Azy izao,\nIzy hamonjy anao.\nHe, mivoha izao ny lanitra,\nRy mpirenireny lavitra ;\nIndro Jesosy, Izy no ao ;\nAiza no hisy anao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (379, 2, 'Be ahiahy va hianao\nNo tsy mandroso faingana\nizao ?\nDia tapaho ny hevitrao,\nKa miovà hatrizao.\nHe, mivoha ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (379, 3, 'Mba miverena re hatrizao,\nKa hararaoty ny andronao,\nFa ny atý ho simba sy lao,\nKa mba modia izao.\nHe, mivoha ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (380, 1, 'Ry olom-bery, tsy ho very\nFoana ny androanao,\nRaha ‘mby an-tsainao, fa ny\nainao\nTsy fanananao,\nNy hery misy, nefa indrisy,\nResim-pahalainana !\nAtý an-tany mora lany\nNy fiainana.\nMba mazotoa, ry havako,\nKa aoka hoheverinao\nNy andro lasa, sao ny asa\nTsy ho vitanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (380, 2, 'Ka manenena, miambena,\nMivavaha hianao !\nEfao ny ady, ao ny kiady\nVoninahitrao.\nMba ahitsio ankehitrio\nNy dia sisa hatrizao,\nFa be ny simban’ny\nmpanimba\nKa migadra izao.\nSakaiza, ô fantaronao,\nFa nijalian’ny Tomponao\nNy fahotana tsy ialana,\nIlalaovanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (380, 3, 'Dia mba ampio sy tahio\nIzahay, ry Tomponay,\nFa mba hiady ka hitady\nVonjy ho anay.\nHomenao soa ny manoa\nAmin’ny fotoanao.\nO, ry Mpanjaka avia haka\nNy malalanao,\nNy olonao dia te hankao\nAn- danitra fonenanao.\nRy Jeso tia, mba avia\nFaingana izao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (381, 1, 'Ry Mpamonjy be fitia\nNo miantso hoe: Avia !\nRy mahantra, raiso izao\nFamonjena ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (381, 2, 'Hoy  ny Tompo :\nRy mpanota,\nManatòna, fa ny ota\nVoavela, anaka \nAfaka ny helokao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (381, 3, 'Teny mamy, re izany,\nFaly ireo mitomany,\nKa araho hatrizao,\nDia ho sambatra hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (381, 4, 'Ry Jesosy, Izay Mpiantra,\nIzaho ory sy mahantra,\nKa ampio aho izao\nFa Mpamonjy Hianao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (381, 5, 'Efa azoko ny teny\nKa hazoniko ireny ;\nHianao, Jesosy ô,\nAmpy hiankianako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (382, 1, 'Jesosy Tompo soa\nNo miantso mafy hoe :\n« Manatòna avokoa\nRy mpanota marobe !\nFa izay manatona  Ahy\nTsy mba ho horoahiko ;\nAza manana ahiahy,\nRaisonao ny teniko »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (382, 2, 'Aza mba misalasala\nKa mihevitra hoe ;\nSao ny Tompo tsy hamela\nAhy efa diso be.\nTsia, tsia, ry mpanota,\nMatokia marina,\nAnenenonao ny ota,\nJeso hanamarina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (382, 3, 'He, ny rany sarobidy\nNo mahafa-keloka\nNy mpanota voavidy\nKa miaiky  heloka.\nEny, afaka ny revo\nNoho ny Mpanavotra,\nNy devoly manandevo\nEfa resy lahatra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (382, 4, 'Ny Mpamonjy mampifidy,\nTsy manao an-keriny,\nKa antsika ny hifidy\nSy handray ny teniny.\nKoa raha misy very,\nDia tsy avy aminy ;\nNy manao ny mahavery\nDia tsy mba maminy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 1, 'Ampihetsehin’inona\nIzao ireo vahoaka,\nAry iza no arahiny\nNo dia samy maika erý ?\n:,: Ry jamba ô, fantaronao,\nJeso mandalo anao izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 2, 'Jesosy Iraky ny Ray\nDia samy mety no mahay\nManafaka ny rofinao\nSy mampahiratra anao ;\n:,: Koa mitsangàna hianao,\nJeso mandalo anao izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 3, 'O, ry mivesatra entana,\nHantòny ny Mpanafaka !\nRy zaza very hatrizay\nModia ao an-tranon-dRay\n:,:Ry jamba ô, heveronao\nJeso mandalo anao izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 4, 'Ny lalan-tsarotrao atý\nTsy misy tsy nalehany,\nNy rofy nositraniny,\nNy maty novelominy,\n:,: Ny jamba faly nahare ;\nIndro Jeso mandalo re ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 5, 'Ny indrafony mbola  injao,\nMitety izao tontolo izao:\nNy tranontsika alehany,\nNy fo mihidy doniny.\n:,: Ampandrosoy Izy hoe :\nJeso ô ! aza mandalo re ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (383, 6, 'Fa raha tsy ahoanao\nIzao fandalovany izao,\nKa nony efa farany\nVao tia ny hovangiany.\n:,: Indrisy tsy ho hitanao\nJeso Izay efa nandalo tao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (384, 1, 'Tsy maintsy hateraka indray\nhianao\nIzay ta-handova\nNy lanitra sy ny fiainam-\nbaovao,\nIzay tsy miova ;\nNy havan’ny Tompo ihany\nNo tena hahazo izany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (384, 2, 'Ny fo vatolampy esory izao,\nFa tsy hovonjena ;\nKatsaho ny fo feno aina\nvaovao\nFa io no ekena\nHoraisina any ambony,\nKa tsy hitomany intsony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (384, 3, 'Ny Tompo mitondra ny hery\nvaovao ;\nFanahy sy afo,\nIzay mampiova ny fonao izao\nHo tonga fo nofo ;\nNy olona lasan-ko babo\nHo afaka sady hiravo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (384, 4, 'Ry Masina ô, ateraho indray\nIzay mitaraina,\nVelomy ny maty hifoha,\nhandray\nIlay tena aina ;\nMba raiso, ry Ray be fitia,\nNy zanaka adala mania !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (385, 1, 'Ny perla madio, ny perla\nvaovao\nNo tonga hampanan-karena\nny very,\nNy perla madio katsaho izao ;\nArio ny ota, izay mahavery ;\nTapaho ny gadra, izay\nmahamay,\nFa sao ho anjaranao\nmandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (385, 2, 'Ny ota nataonao, ry havana e,\nMiteraka gadra, izay\nmahafaty ;\nKanefa ny maro miezaka re,\nHahazo ny voan’ny ota, ka\nmaty ;\nReseo ny ota, reseo izao,\nKatsaho indrindra ny perla\nvaovao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (385, 3, 'Avia, mpanota, faingàna izao,\nFaingàna ka dia hahazo hijery\nNy perla madio, ny perla\nvaovao,\nIzay tonga hampanan-karena\nny very.\nRy Jeso, Izay perla madio\nvaovao,\nAvia hampanan-karena izao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (386, 1, 'He, Jesosy Tomponao\nMalahelo, mitomany,\nFa izao tontolo izao\nMbola mihomehy ihany,\nKa ho ratsy fiafara,\nFa manda ny Tompo tsara !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (386, 2, 'Indro, tevana no ao,\nFa tsy hitan’ny mpanota,\nIzay mandihy faly izao\nSy manaram-po manota.\nRy adala miverena,\nSao ho very famonjena.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (386, 3, 'Mba heveronao izao\nFa ny tenanao ho faty ;\nKoa ny fifonanao\nAza ataonao matimaty !\nHe, ny Tompo mitomany !\nSa tsy azonao izany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (386, 4, 'Latsa-dranomaso be\nSy onena ny Mpamonjy ;\nKoa mifohaza re\nManatòna ho tra-bonjy,\nFa Jesosy be fitia\nMitomany ny mania.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (387, 1, 'Aizan’ny lanitra hianao,\nRy zanak’olona ô ?\nMoa mba efa niditra tao\nSa ao ivelany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (387, 2, 'Lapan’Andriamanitra ao\nKoa tsy mankao\nAfa-tsy izay mandray fo\nvaovao :\nOlona masina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (387, 3, 'Any ny masina marobe\nVelona mandrakizay,\nKa fiderana tsara no re,\nEran’ny lanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (387, 4, 'Tapitra irina izay tonga ao,\nSambatra mandrakizay ;\nAny ny Paradisa vaovao,\nIzay hiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (387, 5, 'Aizan’ny lanitra hianao,\nRy zanak’olona ô ?\nO, mba hevero izay toetrao\nMbamin’ny asanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (388, 1, 'Ry olona mbola tamana\namin-dratsy,\nHevero ny loza hihatra\naminao\nFa indro Satana izay lohan’ny\nratsy\nMandefa tsipika hamono\nanao.\nKanefa ny Tompo izay\nZanahary\nNo tonga hamonjy, fa tsy mba\nmanary ;\nAvia, ry olo-mahantra, hihary\nNy tena harena tsy mety ho\nlo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (388, 2, 'Jesosy mamoha anao,\nry matory,\nFa Izy maniry hitarika anao,\nIlaozy ny ota, fa he,\nmampahory,\nHenoy ny feo miantso anao !\nJereo Ilay kintana, Izay tsy ho\nrava,\nFa indro ny aizina dia hisava ;\nAraho Jesosy, fa tena mazava,\nIzay hanazava tokoa anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (388, 3, 'Ny Tompo mamelona ny efa\nmaty,\nKa aoka hitsangana re\nhianao !\nFadio tokoa izay mahafaty,\nKatsaho indrindra ny aina\nvaovao !\nJesosy Mpamonjy no mba\nitafio,\nKa dia tandremo fa lamba\nmadio !\nRy olo-mahantra mba raiso\nanio\nJesosy, Izay mahavonjy anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (388, 4, 'Jesosy Mpamonjy mitondra\nny teny\nIzay hahavelona mandrakizay ;\nKa dia henoy ny toky nomeny,\nRy olo-maniry ho amin’ny\nRay !\nFainganà tokoa, ry efa mifoha\nFa indro ny lanitra efa mivoha,\nKa faly indrindra ny Tompo\ntsitoha\nHitsena ny zanany velona\nindray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (389, 1, 'Iza no hiara-dia\nHo arý an-danitra,\nKa hanaiky sy ho tia\nAn’Andriamanitra ?\nRy mikatsaka ny To,\nAtolorinao ny fo ;\nIzao tontolo izao ario,\nKa ny lanitra fidio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (389, 2, 'Sao ho lany amin’ota\nNy anjara andronao,\nKa ny helo valin’ota\nNo hanjary lovanao ;\nMiovà re hatrizao,\nMiverena hianao ;\nTadiavinao tokoa\nNy Mpamonjy Tompo soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (389, 3, 'Mba avia, mianara\nIzay fitondrantena vao ;\nMikeleza aina tsara,\nKa reseo ny nofonao,\nAza mba mamindra fo\nAmin-dratsy ao am-po,\nFa ongoty avokoa\nIzay misakana ny soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (389, 4, 'Tsy mba misy lalan-kafa\nHiakaranao ho ao,\nKoa andeha manahafa\nNy nataon’ny Tomponao.\nTsy mba mahavonjy anie\nNy fahaizan-dehibe ;\nRaha ny nofo no hajaina,\nDia ho very re ny aina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (389, 5, 'Dia ny lanitra katsaho\nHo anjara lovanao ;\nNy Mpamonjy angataho\nHo mpitarika anao ?\nFa izay mpianany\nDia hotantanany\nIza no hiara-dia ?\nMitsangàna ka avia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (390, 1, 'Ny sento ajanòny\nRy malahelo ô !\nKa aza misy intsony\nIzay hamoy fo !\nFa indro ny Mpamonjy\nDia eo anilanao,\nKa tonga mba hamonjy\nSy hitondra anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (390, 2, 'Jesosy no nitory\nNy teny soa re,\nNy rera-po sy ory\nAntsoiny mba handre ;\nMinoa fa ny ota\nDia voavelany,\nSakaizan’ny mpanota\nNo anarany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (390, 3, 'Miposaka ny taona\nAnkasitrahana,\nKa be ny voataona\nAm-pitiavana.\nHevero, ry mahantra,\nIzay anjaranao !\nJesosy be fiantra\nNo Mpamonjinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (391, 1, 'Tompo Tsitoha, izahay\nmifona,\nMba mihainoa, ô mba\nmanatòna !\nKa mamindrà fo aminay\nmpanota,\nHo afaka ota.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (391, 2, 'Raha averina aminay mpanota\nIzay mendrika ho anay ho\nvalin’ota,\nDia ho anjaranay re ny hijaly,\nFa tsy hifaly.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (391, 3, 'Indro izahay, fa vovoka sy\ntany,\nReraka, osa, sady mitomany,\nTraty ny mafy\nmampiferinaina,\nKa mitaraina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (391, 4, 'Noho Jesosy Zanaka malala,\nIzay nijaly solonay adala,\nDia mba vonjeo izahay\nmahantra,\nRy be fiantra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (391, 5, 'Ray be fitia, tokin’ny\nmahantra,\nMba manampia anay,\nry be fiantra,\nFa miandrandra izahay\nmijery\nAnao irery !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 1, 'Izaho nivily, ka simba ny fo,\nNafoiko ny làlana tery ;\nIzaho nanary ny soa sy to,\nKa resin’ilay  mahavery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 2, 'Ka nony adala sy mafy ny fo,\nDia tonga ny Filazantsara\nNaneho ny teny mazava sy to,\nDia teny izay manambara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 3, 'Fa tonga ny Tompon’ny \nlanitra izao\nHamonjy izay olom-bery ;\nModia anio, ry havako ô,\nAraho ny làlana tery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 4, 'Jesosy nijaly nisolo anao ;\nNy fony, ny ainy nijaly ;\nArio ny ratsy, fidio izao\nNy soa izay mahafaly.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 5, 'Izaho fahiny nahery nandà\nTsy nety nifidy ny soa,\nFa nohon’ny herin’ny afo\nsy ra,\nNanaiky ny foko fa voa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 6, 'Izaho izao dia tretrika am-po\nFa afaka tsara sy sahy\nHiala amin-dratsy, hitady\nny to ,\nKa mino fa Jeso no ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (392, 7, 'Ry Jeso Mpamonjy, arovy\nny fo,\nAhiko ny fandrika maro !\nIzaho mikasa ao anatin’ny fo\nHanaraka Anao, ry Mpiaro.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (393, 1, 'Ory sady mitomany\nAho, ry Jesosy ô,\nFa tsaroako izany\nVesatry ny heloko ;\nKoa mba todihonao,\nRaiso ho mpianatrao,\nNy fanahiko sitrano,\nAtsanganonao ka tano !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (394, 1, 'Ry Jeso Tompo tsara ô !\nHe, resy lahatra aho izao\nFa be ny ratsy vitako\nMandifotra ny tenako !\nMilela-paladia, re,\nFa efa diso lehibe ;\nEsory tsy ho hitanao\nNy mariky ny otako,\nAmpiasao ny indrafo\nKa mamelà ny heloko !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (395, 1, 'Ny fasika amoron’ny rano\nTsy tambo isaina, fa maro ;\nNy kintana an-danitra koa\nTsy azo isaina avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (395, 2, 'Ny otako toraka izany,\nNy isany tsy hita lany ;\nIzaho nanary ny soa,\nKa dia mahantra tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (395, 3, 'Indrisy, izaho nania,\nKa very ny valim-pitia ;\nNy foko tsy mety mangina,\nFa maro ny heloka ahina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (395, 4, 'Izaho maniry hahazo\nNy lova izay tsy halazo ;\nIriko ny ho tafafindra\nHo amin’ny Avo indrindra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (395, 5, 'Ry Jeso Mpamonjy Mahery,\nVariana aho fa very,\nKa noho ny ranao madio,\nNy heloko re mba ario !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (396, 1, 'Jesosy Kristy Tompo ô !\nMba mamindra fo, e !\nFa raha tsy hatoninao,\nMahantra aho  re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (396, 2, 'Mifona sy mangataka\nIzaho Tompo ô,\nNoho ny ranao latsaka,\nVonoy ny heloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (396, 3, 'Ny famonjena vitanao,\nTsy hain’ny hafa  atao ;\nNy teny sy ny herinao\nDia  ampy  hanavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (396, 4, 'Ho heriko ny indrafo\nSy ny fitiavanao,\nKa faly ny fanahiko,\nFa velon-ko Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (396, 5, 'Ny hazofijaliana\nAnjarako atý,\nFa satro-pifaliana\nHo azoko arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (397, 1, 'Manenina aho, Ray Masina ô !\nFa simba ny tsirin’ny aina ;\nIzay nambolenao tanatin’ny fo\nDia  lanin’ny  ota ka maina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (397, 2, 'Indrisy ny fahadisoako, re,\nFa maro, tsy tambo isaina,\nKanefa, ry Tompoko, mba\nmamonje\nIty ory, izay mitaraina !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (397, 3, 'Havaozy indray ny fanahiko\nizao\nAndrao lasa ho babon’ny\nhelo ;\nKa ny fivalozako mba\nraisonao,\nVonjeo ny fo malahelo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (398, 1, 'O ry Raiko izay malala,\nTonga aho zanakao,\nIzay nania, fa adala, \nKa mifona Aminao ;\nMamelà ny fahotako\nTeo anatrehanao ;\nAry ny fahadalako\nAza mba heverinao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (398, 2, 'He, izaho nanantena\nIzao fiainana izao,\nKa nandà ny famonjena,\nIzay lazain’ny teninao ;\nKanjo hitako tokoa,\nFa izao tontolo izao\nTsy mba manan-java-tsoa\nHampiadana ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (398, 3, 'Enga aho, izay mahantra,\nMba ho isan’ny Anao ;\nHianao ry Ray Mpiantra,\nMiantrà ny zanakao,\nAry nohon’ny Mpamonjy,\nIzay nanavotra ahy re,\nRaiso aho ho tra-bonjy,\nHa Anao, ry Ray ! Amen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 1, 'Jehovah, Ray mahari-po,\nIzay tia sy mamindra fo\nO, mihainoa ny hatako :\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 2, 'Izaho izay mangataka\nTsy azo hamarinina ;\nIndrisy ! be ny tsiniko ;\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 3, 'Tsy tamboko isaina re\nNy fahotako marobe,\nKa be tokoa ny tahotro ;\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 4, 'He ! misy hanatonako\nAnao, Mpandray mpibebaka :\nIzaho diso fatratra ;\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 5, 'Fa Jeso no Anarana\nIzay entiko mangataka\nNy hamelanao heloka :\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 6, 'Tsinjovy re ny zanakao\nMiverina Aminao izao\nFa mitaraina Aminao :\nMba mamindrà fo amiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (399, 7, 'O ! mba jereo ka iantrao\nIzaho miandrandra Anao ;\nNy heloko vonoinao,\nFa mampisaraka Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (400, 1, '« Ry zaza mpiodina,\nmba miverena ! »\nAntsoin’ny Ray ;\nIsika hamaly am-po\ntsy terena :\n« Indreto izahay\nMiaiky fa ratsy\nMaratran’ny rafy ;\nSitrano izahay,\nFa simba ny toetranay. »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (400, 2, '« Indrisy, nanota\nka mendrika ho faty\nSy very izahay !\nNy razanay dia nanompo ny\nmaty\nKa narahinay.\nRy Ray ô, jereo\nKa dia vonjeo,\nMba ho zanakao\nIzahay mitanondrika izao »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (400, 3, '« Sitraniko ny \nfihemoranareo,\nRy zanako ô !\nTsy ampanjomboniko\naminareo\nNy tavako koa. »\nIzany irinay\nHo re ao am-ponay,\nFa Ray Hianao\nMampionona ny olonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (401, 1, 'Ry Jeso ô ! jereo,\n‘Ndrao maty, ka vonjeo ;\nRy Kristy, Vonivatonay !\nMba raiso izahay,\nMahantra izahay,\nKa ory ny fonay ;\nRy Tompo be fiantra ô !\nMba miantrà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (401, 2, 'Mpanavotra ô ! jereo,\nAndrao very, ka vonjeo ;\nRy Kristy, Orimbatonay !\nAvoty izahay,\nMpanota izahay,\nMaloto ny fonay ;\nRy Zanak’Andriamanitra ô !\nVonoy ny otanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (401, 3, 'Mpanjaka ô ! jereo,\nAndrao lavo, ka vonjeo ;\nRy Kristy, Harambatonay !\nArovy izahay,\nMalemy izahay,\nKa kivy ny fonay ;\nRy Tomponay mahery ô !\nTantano izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (402, 1, 'Ry Zanahary Tomponay,\nMpanao izao rehetra izao !\nTsinjovy re ny olonao,\nKa miantrà ny lovanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (402, 2, 'Ry Tokinay sy Tanjakay !\nMba mitalaho izahay ;\nTsarovy re ny zanakao,\nKa mamelà ny helokay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (402, 3, 'Ry Tomponay, Mpamonjy \nsoa !\nTariho izahay izao\nHanompo sy hanoa Anao,\nHo voninahitrao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (402, 4, 'Rehefa hody izahay,\nMba raiso ny fanahinay\nHandray ny hasambarana\nAtolotrao, Mpanavotray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (403, 1, 'Jehovah Tompo ô !\nIndreto izahay\nMifona Aminao ;\nFa be ny helokay ;\nHenoy  izao, ry Ray !\nHenoy ankehitrio,\nHenoy ny vavakay,\nHenoy ka mba valio.\nRy Andriamanitray !\nmijere !\nJeso Kristy Tomponay !\nmamonje !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (403, 2, 'Vonjeo, vonjeo, ry Ray !\nVonjeo ny olonao ;\nMifona izahay,\nIsao ho zanakao ;\nHenoy  izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (403, 3, 'Mpanota izahay,\nMba mamelà anay ;\nMaloto ny fonay,\nSasao ho voadio ;\nHenoy  izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (403, 4, 'Jereo ny Zanakao\nMisolo vava  anay ;\nIlay Malalanao,\nMifona ho anay ;\nHenoy  izao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (404, 1, 'Ry Mpamonjy, Tompo soa !\nAvy izahay izao,\nMba hanaiky sy hanoa,\nKa hiverina Aminao,\nHianao no ranon’aina\nMahavelona ny fo ;\nHianao no antenaina\nMba hanala ny manjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (404, 2, 'Indreto izahay mpanota\nEfa diso taminao,\nMamonje sy manavota\nNy fanahinay izao,\nHianao no mahavonjy\nIzay miankina Aminao ;\nKoa raiso, ry Mpamonjy !\nNy fifonanay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (404, 3, 'Ry Jesosy be fitia !\nMba tantano izahay,\nFa ho lavo sy hania\nRaha tsy hazoninao.\nTsy mba misy mety very\nEo am-pelatananao,\nFa Aminao no misy hery\nMahatanjaka ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (404, 4, 'Tompo ô ! mba tanteraho\nIzay rehetra mahasoa,\nKa ny otanay afaho,\nFa manimba anay tokoa,\nMampianara anay haniry\nMba hanao ny sitrakao,\nHahazoanay ny hery\nEo am-pelatananao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (405, 1, 'Jeso, Vatolampinay !\nO ! arovy izahay,\nKa ny fonay mba sasao\nManafaha  anay izao,\nMba tsy hisy  izay hanjo\nOlon-tsy mahitsy fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (405, 2, 'Tsy ny asa izay atao\nMahatò ny didinao ;\nTsy ny hazotoam-po\nNo mahazo manadio ;\nTsy ny ranomasonay\nNo mahafa-trosa anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (405, 3, 'Tsinontsinona  izahay,\nSady be ny helokay ;\nRy Mpiaro lehibe,\nJeso Tompo !  mamonje,\nFa miankina Aminao\nIzahay mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (405, 4, 'Tomoera aminay\nRaha  ho faty izahay,\nAry raha  hiakatra ao\nMba hiseho Aminao,\nJeso, Vatolampinay !\nO ! arovy izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (406, 1, 'Mifona Aminao izahay,\nAndriamanitra ô !\nMba mamelà ny helokay,\n:,: Fa diso izahay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (406, 2, 'Mivavaka  Aminao izahay,\nRy Jeso Tompo ô !\nAvoty ny ranao izahay,\n:,: Fa very izahay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (406, 3, 'Mangataka  Aminao izahay,\nFanahy Masina ô !\nVelomy ny fanahinay,\n:,: Fa maty izahay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (406, 4, 'Jehovah ô ! Izay iray,\nKanefa Telo koa,\nMba atokàny izahay,\n:,: Ho olonao tokoa :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (407, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (407, 2, 'Mamindrà fo amiko,\nRy Jehovah Tompoko !\nFa miantra Hianao,\nMamelà ny heloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (407, 3, 'Iza no mamindra fo\nToa an’Andriamanitra ?\nIza no Mpanavotra\nAfa-tsy ny Zanany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (407, 4, 'Jeso Kristy no mahay\nMamonjy ny mpanota\nIzay miankina Aminy\nKa tsy mandoka tena.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (407, 5, 'Aza mahafoy ahy,\nRy Jehovah Tompoko !\nMamindrà fo amiko,\nMamelà ny heloko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (408, 1, 'Nandao Anao ela aho,\nRaiko ô !\nIzao dia  te-hody aho ; \nManiry hiankina an-tratranao\nIzao dia  te-hody aho,\n:,: Inty tamy aho, Raiko\nmalala ô\nRaiso ny tanako :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (408, 2, 'Maniry hanana fiadanam-po\nIzao dia te-hody aho,\nSy mba hivesatra ny zioganao\nIzao dia te-hody aho,\n:,: Inty tamy aho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (408, 3, 'Itambesaran’ny ota natao\nIzao dia te-hody aho,\nMatoky anefa fa horaisinao\nIzao dia  te-hody aho,\n:,: Inty tamy aho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (408, 4, 'Raha tsy mendrika ny ho\nzanakao,\nIzao dia  te-hody aho,\nAtaovy ho isan’ny \nmpanomponao\nIzao dia  te-hody aho,\n:,: Inty tamy aho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (408, 5, 'Indro fa tazako ny sandrinao\nIzao dia  te-hody aho,\nMitsotra mba hisakambina re,\nIzao dia  te-hody aho,\n:,: Inty tamy aho, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (409, 1, 'Amorony fo madio\nAho, ry Jehovah ô !\nKa havaozinao anio\nNy fanahiko izao.\nBe ny ratsy vitako\nKa ny heloko sasao ;\nMamindrà fo amiko,\nRy Jehovah Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (409, 2, 'Amorony fo madio\nAho, ry Jehovah ô !\nKa diovy ankehitrio\nNy fiainako izao.\nSimba ny fanahiko,\nKa amboary ho vaovao ;\nAza esorina amiko,\nNy Fanahy Masinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (409, 3, 'Amorony fo madio\nAho, ry Jehovah ô !\nKa ny foko ahitsio,\nHamasino ho Anao.\nTaminao dia taminao\nNo mba nanotàko, Ray ;\nKa ny heloko avelao\nNy fanahiko ovay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (409, 4, 'Amorony fo madio\nAho, ry Jehovah ô !\nKa ny fo omeko anio,\nRaiso ho fanananao ;\nFo mangorakoraka\nNoho ny ratsy izay natao ;\nFo miaiky heloka,\nKa mba raiso, Tompo ô.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (410, 1, 'Ry Raiko ô ! Ry Raiko ô !\nEfa nanota teo imasonao\nIty Zanaka mahantranao ;\nNinia niala teo an-tranonao\nMangovitra ny feoko,\nTorovana ny foko,\nKanefa tazako Hianao\nKa mamelà, mamelà,\nRaiko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (410, 2, 'Ry Raiko ô ! Ry Raiko ô !\nHo tanterahiko ny anjarako ;\nMandao ny lalako taloha;\nIzay nanalavitra ahy taminao\nTratrariko ny eo aloha,\nIlaozako ny lasa,\nInoako ny famindramponao\nKa mamelà, mamelà,\nRaiko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (410, 3, 'Ry Raiko ô ! Ry Raiko ô !\nAtsory amiko ny tananao\nHamelona ny heriko\nFa reraka aho, ka tantanonao\nNy fitiavanao, Ray ô !\nFanilo sady toky\nAndraisako fiadanam-po,\nKa mamelà, mamelà,\nRaiko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (410, 4, 'Ry Raiko ô ! Ry Raiko ô !\nHo olom-baovao itý zanakao\nRaha tonga eo akaikinao\nFa ho sasan’i Jeso Tompo tia\nTsy tapitra ny antranao,\nFa manana amby ampy\nDia manantena aho izao\nKa mamelà, mamelà,\nRaiko ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (411, 1, 'Ry Raiko feno antra,\nInty aho fa ory ;\nMijaly sy mahantra,\nKa tsy mahatraka  akory,\nJesosy no nilonjy\nNy ota vitanao ;\nNy rany mahavonjy,\nNomeny ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (411, 2, 'Nandao ny tavanao,\nNikaro-java-tsoa ;\nKorana sy lalao,\nSambo-drivotra avokoa.\nJesosy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (411, 3, 'Izaho fahavalo\nFa tsy narianao,\nNy loza natakalo\nHasambaram-baovao.\nJesosy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (411, 4, 'Hanompo no iriko\nFa zanaka nania,\nRy hany itokiako\nMba ho Tompoko  doria.\nJesosy ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (412, 1, 'Andriamanitra ô, Ray\nMpamindra fo !\nAvy mankalaza Anao\ntokoa izahay  ;\nN''inon''inona midona,\nzavatra manjo\nEo ambany elatrao,\nmatoky izahay.\nMamindrà fo amiko,\nAndriamanitra ô !\nAvelao ny heloko,\nJeso Mpamonjy ô !\nHazavao ny lalako,\nFanahy Masina ô !\nFa izay no fahasambaran''\nny fanahiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (412, 2, 'Jeso Kristy Tompo sy\nZokinay,\nMiaraka Aminao\nafa-tahotra tokoa,\nHetraketrakin''ny tany\ntsy hanimba anay,\nIzay mialoka Aminao\nho ampoky ny soa,\nMamindrà fo ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (412, 3, 'Ry Fanahy Masina\nMpanavao,\nHazavao re, ny ao am-poko\nmiafina ao\nKa ny lalana ombàko mba\ntarihonao,\nNy ota mahazatra mba levony\nkoa ho lao,\nMamindrà fo ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (413, 1, 'Ry Andriananaharinay !\nNy hamasinanao\nDia tena lehibe tokoa,\nRaha te-hahita Anao\nizahay,\nTsy maintsy tsara fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (413, 2, 'Ny mason''ny anjelinao\nMahita Anao tokoa,\nKa tsy mba manjambena\nireo,\nNa dia mamirapiratra,\nNy hamasinanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (413, 3, 'Fa tsy nanota taminao\nAkory izy ireo,\nTsy meloka, tsy menatra,\nKa misy hahazoany\nHijery tsara Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (413, 4, 'Fa izahay, maloto fo,\nRaha te-hahita Anao,\nTsy mana-mahamendrika,\nFa zanak''olombelona ;\nAhoana no hatao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (413, 5, 'Ry Andriananaharinay !\nNy ran''ny Zanakao,\nNy asan''ny Fanahy koa,\nNo zava-mahamendrika\nAnay hahita Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (414, 1, 'He maharavoravo,\nJeso, ny fitiavanao ;\nIzaho dia mpanota lavo,\nNefa Jeso, nareninao.\nMamin''ny foko loatra,\nNy fanavotana vitanao ;\nHe ! ny foko tsy afa-\nmanoatra\nJeso, hihira ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (414, 2, 'Izaho mpanota adala\nNo nanehoam-pitiavan-tsoa\nNy heloko, Jeso malala,\nNavelanao tokoa tokoa.\nMamin''ny foko .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (414, 3, 'Endrey ny hafaliako :\nNy heloko navelanao,\nHe ! maivana ny fahoriako\nNoho ny fanavotanao.\nMamin''ny foko .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (415, 1, 'Tsaroako dia tsaroako\nRaiko ô, Raiko be fitia !\nFa ny fahadisoako\nNodiovinao satria\nNoho Jeso Mpamonjy soa,\nInty izaho fa afaka,\nHe ! azoko tokoa tokoa\nNy famelana heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (415, 2, 'Ny aizim-pahotàna\nRaiko ô, Raiko be fitia !\nMisolo hazavàna\nMamiratra erý.\nNy lalako mahitsy izao\nMitory olona afaka\nNoho ny hery vaovao \nNy famelanao heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (415, 3, 'Endre, ny hafaliako\nRaiko ô, Raiko be fitia !\nFa maivana ny diako\nAvy any Kalvary.\nNy tahotaho-poko tao\nTsy reko intsony, afaka ;\nAn-tanako tokoa izao \nNy famelana heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (415, 4, 'Deraiko lalandava\nRaiko ô, Raiko be fitia !\nNy vavako hilaza \nNy vitanao tetý.\nFa mbola misy marobe\nManiry te-ho afaka,\nHo azy  ireny koa anie\nNy famelana heloka.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 2, 'Ny ratsy efa tiako,\nTsy tahotro, tsy henatro\nFa zava-bao no hitako :\nTe-hijery izany aho.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 3, 'Ao ny hazo fijaliana\nAo ny Tompo efa maty :\nIzy no nijery ahy\nTsy tezitra, fa niantra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 4, 'Hoy ny fijeriny ahy\n« Hianao mamono Ahy »\nDia  hitako mahantra aho\nNy heloko namono Azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 5, 'Aiza no handosirako\n''Ndrao hidiran-doza aho ?\nIza no hamonjy ahy,\nFeno ota, feno loto ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 6, 'Nijery ahy indray Izy :\n« Avelako ny helokao \nFa ny ràko no manadio\nHahazoanao fiainana ».', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (416, 7, 'Maty Kristy, be ny ota,\nMaty i Jeso ! be fitia.\nFaly malahelo aho,\nSambatra, manana ody aina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (417, 1, 'Nanatona Anao, \nMpanavotra ô !\nNy olo-mahantra fahiny,\nNy rofiny be, ny senton''ny fo\nNaneho fa soa no niriny.\nTsy nety ho sasatra Hianao,\nTsy nisy nolavina akory,\nFa samy nandray ny\nindrafonao,\nKa ravo ny olona ory.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (417, 2, 'Manatona Anao, ry be\nindrafo,\nNy olo-mahantra anio,\nFa be siasia, sy reraka am-po\nKa raisonao ankehitrio !\nNy maty omeonao aina\nvaovao,\nNy sasatra ampiadano,\nNy masonay jamba mba\nhazavao\nNy rofin''ny fonay sitrano.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (417, 3, 'Ka rahatrizay hanatona Anao,\nNy tsy ho farofy intsony,\nFa hisy ny Alahady vaovao\nKa sitrana tsara ny fony.\nIzay tany soa misy Anao\nNo hisy ny olonao koa,\nHahita ny voninahitrao ao,\nRy Zoky malala Tsitoha.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (418, 1, 'Ho vavolombelonao\nN''aiza n''aiza no aleha\nIzay no faniriana vao\nSy ataoko rehareha,\nKa raha asa no atao\nInty tokoa fa vonona\n( ho vonona aho )\nTsy hiala na handao,\nTompo ô, iraho aho !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (418, 2, 'Ho vavolombelonao\nEran-tany raha ilaina\nToy ny loharano vao\nIzay mamelombelona  aina\nHanasoa ny tany hay\nEfa mihamaina taho\n( mihamaina )\nTompo ô, inty indray\nAlefaso, vonona Aho.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (418, 3, 'Ho vavolombelonao\nAo anaty fitsapana,\nKa hijoro ho Anao,\nNa fefena, na rarana,\nTsy hiahotra ny dia :\nFa Hianao naniraka Ahy\n( naniraka Ahy )\nTsy hiahotra aho satria\nAmiko Hianao Mpitahy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (419, 1, 'Reko izao, ry Tompo,\nIzany antsonao\nMiantso izany mpanompo\nMba ho irakao !\nInty ! inty !\ninty aho Jeso !\nInty ! inty !\niraho aho Jeso !\nEkeko re,\nny ho miaramilanao\nHo vonona hanao\nny sitrakao,\nKa iraho !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (419, 2, 'O ! diovy Tompo\nizany molotro !\nKa ny fo madio hanompo\nno atolotro.\nInty ! inty ! …sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (419, 3, 'Tsy handeha irery,\nFa hiaraka  Aminao,\nKa dia mba omeo ny hery\nsy Fanahinao.\nInty ! inty ! …sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (419, 4, 'Reko izao ny toky hoe :\n" Momba ahy Hianao,\nRy Jesosy Tompo\nsy Zoky\nVonona aho " izao.\nInty ! inty ! …sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (420, 1, 'Inty ny olona, Jeso Tompo ô !\nFa nandre ny antsonao\nHe, maniry mba hotarihinao\nHo akaikinao kokoa.\nMba tariho aho, Jeso\nTompo ô !\nEo akaikinao kokoa,\nMba tariho aho, Jeso\nTompo ô !\nMba ho olonao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (420, 2, 'Tsy ny sitrako, fa ny sitrakao\nNo iriko mba hatao,\nFa navotanao ny\nmpanomponao\nKa Manaiky ho Anao.\nMba tariho aho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (420, 3, 'Raha mitokana mba hivavaka,\nKa miresaka Aminao,\nVelombelona ny fanahiko,\nTafakambana Aminao.\nMba tariho aho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (420, 4, 'Raha avy re izay hodiako,\nHody Aminao indray,\nAo ny saiko vao tena afa-po\nTonga ao an-tranon-dRay.\nMba tariho aho, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (421, 1, 'Endrika sariaka\nNo anompoako Anao,\nJehovah Ray\nFa tapaka ny hevitro :\nIzaho dia olonao\nmandrakizay.\nHe ny foko afa-jaly\nTena ravo, sady falifaly,\nKa : endrika sariaka\nNo anompoako Anao\nJehovah Ray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (421, 2, 'Fa tsaroan’izany saiko,\nMandra-maty\nho tadidiko,\nNy nanavotanao\nny aiko,\nIlay fanahy sarobidiko.\nFa nomena fahendrena\nIzay ekena\nfa ny famonjena,\nKa : endrika ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (421, 3, 'Be ny zavatra mihatra\nMampahory\nsy mandrera-po ;\nKanefa ireny\ntsy handratra\nNy arovanao,\nry Ilay Tsitoha.\nFa ny fony tena tony\nTsy mba kivy, \ntsy mba taitra intsony,\nKa : endrika ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (421, 4, 'Misy ve ny mampanahy\nSy ny zava-manahirana ?\nHe, tsy kivy ny fanahy\nFa hijoro, ka ho mirana,\nFa Jehovah,\nRay Mahery,\nMiandany\nsady koa mijery,\nKa : endrika ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 2, 'Na be no mahareraka,\nTsy mety kivy ny mino ;\nNy Tsitoha no heriny\nNy lehibe no tohany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 3, 'Ny hani-mandrakariva\nMofo avy an-danitra\nNy sotroin’isan’andro\nDia rano mahavelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 4, 'Manda afo no arony\nFinoana no ampingany,\nVavaka no fofon’ainy,\nOdiaina no tànany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 5, 'Ho lavorary ny diany,\nHo resy ny fahavalony,\nDia sitraka no hitany,\nVoninahitra no azony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (422, 6, 'Mahereza ilay malala,\nAza ketraka hanompo,\nLasa ny Mpitari-dàlana,\nHanamboatra ho anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (423, 1, 'Mandra-pihavin’ny\nTompo, mivavaha ;\nNy teny tsara nomeny\ntadidio,\nNy adidinao, tontosay\nsy efao,\nMijery ny ataonao\nny Ray.\nRaha mpanompo tsara\nhianao,\nHe, ny Tompo faly\naminao,\nKoa tanteraho ireo\nadidinao,\nMba ho mpanompo\nmahasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (423, 2, 'Mandra-pihavin’ny\nTompo, miasà ;\nAmpitomboy ireo\ntalenta azonao :\nVola, fahaizana, aina,\nhery koa\nNo anompoy ny Tompo\nTsitoha.\nRaha mpanompo ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (423, 3, 'Moa tianao va\nIlay Mpamonjy \nTompo soa ?\nNy rany anie\nnanavotany anao !\nNy hafany tanteraho\nsy hajao\nValio ny fanavotany\nanao.\nRaha mpanompo ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (424, 1, 'Mba taominao ny fo izao,\nRy Jeso be fitia,\nHanarahako Anao,\nKa mba tsy hania.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (424, 2, 'Mba taominao ny fo izao,\nHamoizako ny tany,\nFa izao rehetra izao\nDia mora lany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (424, 3, 'Mba taominao ny fo izao,\nHanaraka ny dia,\nIzay nomban’ny olonao\nTsy naniasia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (424, 4, 'Mba taominao ny fo izao,\nHahaizako hifaly,\nFa atý ny olonao\nOry sy mijaly.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (424, 5, 'Mba taominao ny fo izao,\nHo any amin-dRainao,\nMba ho reko hatrizao\nNy hafin’ny ainao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (425, 1, 'Ny foko, ry Mpamonjy,\nAtolotro Anao ;\nMaloto ka diovy,\nMba ho fonenanao.\nNy ranao izay nalatsaka,\nNo aoka mba hanafaka\nIzao fahotako be izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (425, 2, 'Ny fahoriam-poko\nAtolotro Anao ;\nFa he, mampitoloko,\nManindry mafy ao ;\nO, ry nijaly an-tsitrapo,\nTohàny ny fanahiko\nMba tsy hamoy fo izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (425, 3, 'Ny fifaliam-poko\nAtolotro Anao ;\nArovy tsy haloto,\nDiovy ho Anao,\nAmpio ho tanteraka,\nHo tonga ao an-danitra\nHanatrika ny tavanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (425, 4, 'Ny tenako rehetra\nAtolotro Anao ;\nAnao tsy misy fetra,\nAnao, re, hatrizao,\nKa raiso ho fanananao\nHanompo\nsy hanaja Anao,\nRy Tompo\nsy Mpanjaka ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (426, 1, 'Iza no hanoa\nny Tompo lehibe ?\nIza no hanaja\nAzy tsara re ?\nIza no hitady\nolon-kafa koa,\nMba hanaiky\nsy handray \nNy Tompo soa ?\nIndreto Jeso Tompo !\nizahay izao\nAvy mba hanompo,\navy ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (426, 2, 'Tsy an-tery re\nno ilatsahana\nToy ny olom-bery\nnandevozina ;\nTa-hanompo tsara\nizahay izao\nTa-hamely koa izay\ntoherinao :\nEny, Jeso Tompo !\nta-hanao tokoa\nIzahay mpanompo\nizay nasian-tsoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (426, 3, 'Na dia ta-hanompo\naza izahay,\nBe ny fahavalo iadianay ;\nIzahay malemy\nfeno tahotra,\nMba tsy horeseny,\nmanampia anay,\nKapiteny sahy !\nentonao izao\nMba hitomban-dahy\nny mpiadinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (426, 4, 'Mafy re ny ady,\nmaro be no voa,\nMisy tsy mahandry,\nbe ny maty koa ;\nIzahay mpanompo\nmiandrandra Anao ;\nTompo izay tsy toha !\nmba herezonao :\nTompo manan-kery !\nhatanjahonao\nIzahay, sao very \nka afaka Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (427, 1, 'Ny foko, ry Jehovah ô !\nMba ento ho Anao,\nDiovy ho fonenanao,\nHo tsara sy vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (427, 2, 'Fanahin’Andriamanitra ô !\nMidína ato koa,\nOvay ny saiko mba hadio\nSy masina tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (427, 3, 'Ny aiko no navotanao,\nRy Jeso be fitia !\nMba hanompoako Anao,\nRy Tompoko, avia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (427, 4, 'Ry sofiko, mba mihainoa !\nRy maso, mijere !\nRy tongotra aman-tanako,\nHajao ny Tompo e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (427, 5, 'Ry tena sy fanahy koa !\nAvia hankatò\nNy tenin’ny Mpanavotra,\nIzay masina sy tò.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (428, 1, 'Ry Jeso Kristy Tompo,\nO ! ampio ny olonao !\nAmpianaro mba hanompo\nSy hanao ny sitrakao ;\nMalemy aho re,\nKa omeo ny herinao,\nTsy hivily, tsy hania,\nFa hanara-dia Anao.\nO ! vonjeo, o ! vonjeo,\nKa tantano sy jereo ;\nAza afoy izahay,\nFa Hianao no Avotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (428, 2, 'Ny foko mba diovy,\nRy Mpamonjy lehibe !\nKa ambeno sy arovy,\nSao dia very aho re,\nIriko indrindra koa\nNy hitombo sy hamoa,\nKa ny voka-tsoa rehetra\nHo Anao, ry Jeso ô !\nO ! vonjeo, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (428, 3, 'Mba raisonao, ry Tompo !\nHo Anao tokoa izao\nIzay ananay rehetra,\nKa anarano fo izao,\nFa maty Hianao\nNoho ny fitiavanao,\nKa tsy hainay hadinoina\nNy fitiavanao anay :\nO ! vonjeo, .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (429, 1, 'Avia, Jehovah Tomponay,\nHandray ny vavakay,\nFa mitaraina izahay,\nValio ny hatakay :\n:,: Avia :,: handray ny fonay,\n:,: Avia, :,:\nry Tomponay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (429, 2, 'Savao ny alahelom-po,\nMba hifalianay ;\nNa mafy aza ny manjo,\nMatoky izahay :\n:,: Avia :,: handray .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (429, 3, 'Avia, ka taomy izahay,\nHo tonga zanakao ;\nMba averenonao indray,\nFa te-ho olonao :\n:,: Avia :,: handray .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (429, 4, 'Manolo-tena izahay\nMba ho mpanomponao ;\nNy fonay no atolotray,\nKa raiso ho Anao :\n:,: Avia :,: handray .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (429, 5, 'Raha avy ny hodianay\nHo any Aminao\nMba raiso ny fanahinay\nHo ao an-dapanao :\n:,: Avia :,: handray .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (430, 1, 'Mba taomy aho e,\nRy Tompo ô !\nHo tonga zanakao\nMadio am-po,\nMba taomy aho e,\nHo tia Anao tokoa !\nNy fo atolotro\nMba raisonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (430, 2, 'Tariho aho e,\nRy Jeso ô !\nAza ilaozanao,\nFa be manjo.\nTrotroy an-tratranao\nNy ondrikelinao,\nKa ento mody izao\nIlay nania.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (430, 3, 'Diovy aho e,\nFanahy ô !\nDiniho sy hazavao,\nNy toetro.\nDiovy aho e,\nNy fo ovay ho vao,\nKa ny fanahiko\nOnenonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (431, 1, 'Ry Kristy ô, Mpanjakako !\nNy foko ho Anao ;\nNy feoko hasandratro\nHihira ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (431, 2, 'Mba raiso ny fanahiko\nHanao ny sitrakao ;\nNy tenako, ny heriko,\nDia ento ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (431, 3, 'Ny tongotro, ny tanako,\nHiasa ho Anao ;\nNy androko, ny volako,\nOmeko ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (431, 4, 'Izaho dia tsy mendrika\nAnao, ry Tompo ô !\nKanefa re, mba raisonao\nNy foko ho Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 1, 'Raiso aho, Tompo ô !\nFa te-ho mpanomponao ;\nRaiso koa ny androko,\nKa  anarano fo izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 2, 'Raiso re ny tanako\nMba hanao ny asanao ;\nRaiso koa ny tongotro\nHo fanirakirakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 3, 'Raiso ny fahaizako,\nEnto mba hanaja Anao ;\nRaiso koa ny volako\nHo Anao tokoa izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 4, 'Raiso re ny vavako\nHampiseho ny hafatrao ;\nRaiso koa ny feoko\nHo mpanandratra Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 5, 'Raiso ny fanahiko\nDiovy ho tempolinao ;\nRaiso aho, Tompo ô !\nHo Anao mihitsy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (432, 6, 'Ho Anao manokana,\nRy Mpamonjy,\nTompo soa !\nHo Anao dia  Anao\ntokoa,\nHo Anao mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (433, 1, 'Endre ! ity fahotana,\nMamely ao am-po,\nO! ady mampahana,\nKa mafy ny manjo ;\n:,: Vonjeo, ry Mpiantra !\nIzaho zanakao,\nFa tery sy mahantra\nMiady lava izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (433, 2, 'Ry Antoky ny aiko !\nIzay mahafatra-po,\nRy Toky antenaiko !\nIzay tanjaky ny fo ;\nNy tena sy fanahy\nApetraka  Aminao,\nKa  ataovy lehilahy\nMpandresy toa  Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (433, 3, 'Izaho no Mpamonjy,\nKa matokia  tokoa,\nIzaho mahavonjy\nKoa  antenao ny soa ;\nNy sandriko mahery\nNo iankinanao,\nHianao dia tsy ho very,\nMinoa hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (433, 4, 'Ry Tompo manan-kery !\nNy fitiavanao\nFitia feno hery,\nKa raisiko izao ;\nNy dera aman-daza\nNo mendrika ho Anao\nNy foko hankalaza,\nNy voninahitrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (434, 1, 'Raiso ho Anao ny foko,\nRy Jehovah Raiko ô !\nMitoera atý am-poko\nMba ho tompon-\ntrano ao,\n:,: O, ry Raiko !\nMitoera ao izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (434, 2, 'Raiso ho Anao ny foko,\nRy Jesosy Tompo ô !\nTsy mba vola, fa ny foko\nNo atolotro Anao :\n:,: Raiso, Tompo !\nFa fanati-tsitrapo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (434, 3, 'Raiso ho Anao ny foko,\nRy Fanahy Masina ô !\nHazavao ny ao am-poko,\nDiovy ho tempolinao :\n:,: Indro, raiso !\nItokany ho Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (434, 4, 'Tsy harindriko ny foko,\nFa vohako ho anao ;\nKa midira atý am-poko,\nManjakà irery ao :\n:,: Eny, Tompo !\nRaiso aho ho Anao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (435, 1, 'Ry Raiko feno antra !\nInty ny zanakao,\nNy foko dia mahantra\nRaha  lavitra aminao ;\nFa tany karankaina\nIzao tontolo izao,\nKa reraka ny aina\nMihataka aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (435, 2, 'Nivezivezy aho\nNikaro-java-tsoa,\nIzay rehetra hita\nNandramana avokoa ;\nNandany taona maro\nNanaran-tsitrapo,\nKanefa tsy nahazo\nIzay mahafa-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (435, 3, 'Mamitaka mihitsy\nIzao tontolo izao,\nNy mamy dia  mangidy,\nIzay tena soa tsy ao ;\nKoa  aza mba adalainy\nIntsony re, ry fo !\nAndeha mba ho hendry\nHatramin’ny anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (435, 4, 'Ry Jeso, Mofon’aina\nSy Ranon’aina koa !\nHianao no mahatòny\nIzay filan’ny fo ;\nHianao no mahavoky\nHianao no manome\nIzay irin’ny foko,\nDia  hasambaram-be.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (436, 1, 'Jeso mora fo indrindra,\nTsara sady mahasoa,\nTsy miova, tsy mifindra,\nTompo be fitia tokoa.\nMba iriko ho toa Azy\nTsy mba mety soso-po,\nKa hionona ny foko\nRaha avy ny manjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (436, 2, 'Jeso tia, nanetry tena\nSy nandefitra tokoa,\nNefa Tompo be harena\nSy Mpanjaka foto-tsoa.\nMba iriko ho toa Azy\nIzay ninia  ho ory koa ;\nKa ny sainy no fidiko,\nFa fiainana tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (436, 3, 'He ! manolo-tena aho,\nHo Anao, ry Tompo soa !\nKa ny heloko rehetra\nMba sasaonao avokoa.\nEnga ka homenao ahy\nFo madio sy vaovao,\nMba hahaizako manefa\nIzay rehetra sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (437, 1, 'Atolotro Anao\nny tenako, ry Raiko !\nFa Hianao no tena\nloharanon-tsoa\nIzao tontolo izao\nmanintona ny saiko\nHiataka Aminao\nho babony tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (437, 2, 'Atolotro Anao\nny tenako, ry Raiko !\nHo eo an-tananao\nhosakambininao,\nHohatanjahanao\nny tena sy ny saiko\nHisikina tokoa\nhiady ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (437, 3, 'Atolotro Anao\nny tenako, ry Raiko !\nFa izany indrindra re\nno hetahetako ;\nNy ran’ny Zanakao\nmitory ao an-tsaiko\nMba hanolorako Anao\nny tenako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (438, 1, 'Hianao, ry Kristy\nTompo,\nNo hasiniko izao,\nAvy aho, fa mpanompo,\nHiankohoka Aminao,\nRaiso re ny dera saotra\nTena mendrika\nho Anao ;\nHe ! ny foko faly loatra,\nFaly mba hanaja Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (438, 2, 'Izao no tena faniriako :\nNy ho velona  Aminao,\nKa hitoeranao ny foko,\nFa tsy holalovanao ;\nEny, Tompo ô ! monena,\nMba monena ao tokoa,\nIzay no tena famonjena\nIzay no mahafatra-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (438, 3, 'Tsy ho orin-java-tsoa\nIzay rehetra itoeranao,\nTena sambatra tokoa,\nRaha ny fo no lapanao,\nMandrosoa, fa vohako\nHo Anao ny foko izao ;\nEny, Tompo, fa vohako,\nKoa  midira Hianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (439, 1, 'Ry Tompo ô ! tariho\nNy ondrikelinao,\nTantano sy vimbino\nHanao ny sitrakao ;\nRaha  misy hafaliana,\nDia  taomy mba ho tia ;\nRaha sendra fahoriana,\nSakano tsy hania :\nRy Jeso ô ! tazano\nNy lalanay izao,\nTariho sy tantano\nHo ao am-balanao ;\nAnio dia mikasa\nHanolo-tena koa,\nFa nenina ny lasa\nHiezaka tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (439, 2, 'Anio ho hafaliako,\nNy foko lasanao,\nNy maminao no tiako,\nHatao ny sitrakao,\nNy androko rehetra,\nAtolotro Anao ;\nAnao tsy misy fetra,\nKa raiso, anjakao :\nRy Jeso ô ! tazano .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 1, 'Njay reko, Tompo ô\n‘Njay re ny feonao ;\nMiantso ahy Hianao\nHankany Kalvary,\nTompo lehibe,\nRaiso aho e !\nMba sasao ny ranao soa\nIzay tao Kalvary.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 2, 'Malemy aho e,\nKa mitaraina hoe :\nVonjeo ny aiko,\nTompo ô !\nVonjeo ny olonao :\nTompo lehibe, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 3, 'Ny fiadanam-po\nMandresy ny manjo ;\nEsorinao ny tahotro,\nRy Tompo ô ! vonjeo\nTompo lehibe, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 4, 'Mpanota tsy mandà,\nRy Tompo, mamelà !\nNy fahotako lehibe\nIfonako tokoa ;\nTompo lehibe, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 5, 'Ny foko tsy madio\nMba diovinao anio\nMaloto aho, \nry Tompo ô !\nSasao ho voadio\nTompo lehibe, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (440, 6, 'Ny foko no sasao,\nKa diovinao izao ;\nNy fahotako marobe\nLevòny avokoa\nTompo lehibe, .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (441, 1, 'Jeso Kristy be fiantra !\nMba Jereo izahay\nVery amin’ota.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (441, 2, 'Hianao Mpiandry tsara,\nRaisonao izahay\nVery toy ny ondry.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (441, 3, 'Fombanay ny tia ratsy,\nMba ovay ho vaovao\nNy fonay izay ratsy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (441, 4, 'Zazabodo sy mahantra,\nIzahay raha  atý,\nTsy mba manan-kery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (441, 5, 'Hianao no Vatolampy,\nTanjakay, Avotray,\nHery sy Ampinga.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (442, 1, 'Tompo ô ! tsy takatry ny saiko\nNy fitiavanao tsy nohajaiko ;\nKanjo io no hany antenaiko\nMba hamonjy ahy  izao.\n(Hamonjy izao)\nTompo ô, voababonao\nIzany foko ho Anao\nMamy re !\nIzany fitiavanao, ry Jeso ô !\n\n( Feo II )\nTompo ô ! dia resinao ny\nfoko ilay nandà,\nVoababonao tokoa tokoa ;\nKa nasandratrao ho Zanak’\nAndriamanitra,\nHotarihinao an-danitra.\nMamy dia mamy re, tsaroan’\nny aiko izao\nIzany fitiavanao, ry Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (442, 2, 'Ny fandavan-tenanao tsaroako\nFa fandavan-tena nahitako\nNy fitiavanao izay inoako\nFa mambomba ahy izao.\n(Mambomba izao)\nTompo ô, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (442, 3, 'Ry Jesosy, tsy mba misy haiko\nAfa-tsy manolotra ny aiko\nEo an-tananao izay\nandrandraiko\nHampijoro ahy  izao\n(Hijoro  izao)\nTompo ô, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (443, 1, 'Tompo ô ! ovay ny foko\nMba ho mora anarina\nToy ny any zazakely\nTsotra sady marina,\n:,: Tsy mialona, tsy maditra,\nFa manao ny sitrakao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (443, 2, 'Zaza, ka tsy te-hifidy\nIzay homenao ahy anio ;\nNy ho azoko ampitso\nDia  ankinina  aminao ;\n:,: Koa tsy manahy aho,\nFa efa niantohanao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (443, 3, 'Zaza tsy maharo tena,\nFa miankina amin-dray.\nTsy mahery, tsy mba hendry,\nAry tsy mahay mandeha ;\n:,: Hianao no Raiko tia,\nRaiko be fahari-po :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (444, 1, 'Mihirà, ry mponina ao\nan-danitra\nTonga izao ny very fahizay,\nFa ny Rainy faly sy finaritra\nMba handray ny zanany indray\nMihobia, re, ry lanitra,\nKa derao Andriamanitra.\nEny, mihobia re , ry mino ô,\nKa hirao ny fihiram-baovao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (444, 2, 'Mihirà, ry mponina ao\nan-danitra\nFa ilay very dia hita  izao,\nVoaray ho Zanak’\nAndriamanitra,\nEfa tonga olona vaovao.\nMihobia, re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (444, 3, 'Mihirà, ry mponina ao\nan-danitra\nLehibe ny fifalianay,\nKa derao izao Andriamanitra,\nFa ny maty velona indray !\nMihobia, re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (445, 1, 'Veloma no apetrako,\nFa tapaka ny hevitro,\nHandao ny fahazarako,\nFa Kristy no harahiko.\nVeloma Hianao,\nRy izao tontolo izao !\nIzaho tsy tamana atý,\nVeloma Hianao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (445, 2, 'Ny làlan’ota hitako\nNamono ny fanahiko,\nFa he, erý dia tazako\nIlay tany soa mamiko\nVeloma ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (445, 3, 'Mpanota ô, nahoana\nNo tsy miaraka aminay,\nFa mbola misy toerana\nIzay ho anao  sy ho anay.\nVeloma ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (445, 4, 'Veloma, fa  ao aloha ao\nNo tany izay honenako,\nKa maika aho mba hankao\nHandray ny fitsaharako.\nVeloma ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (445, 5, 'Jerosalema tazako,\nKa velona ny hirako,\nFa any no honenako\nHidera ny Mpanjakako.\nHo any aho izao,\nHo any aho izao,\nIzaho tsy tamana atý,\nHo any aho izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (446, 1, 'Ny androm-pahasoavana\nManeho zava-baovao ;\nMahazo fahasambarana\nIzao tontolo izao.\nRavoravo ny efitra maina\nMbamin’ny tany karankaina ;\nHe, torina ny teny soa,\nKa dia afaka avokoa\nNy olom-bery maniry ho\nvelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (446, 2, 'Ka miandrandra ny lanitra\nNy malahelo izao ;\nMihoby izy fa sambatra\nNahazo aina vaovao.\nHe, vahàna ireo migadra,\nAfaka ny midradradradra,\nMitsambikina ny mandringa,\nFaingana izy sady kinga,\nAry mihoby ny lelan’ny\nmoana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (446, 3, 'Ny very no tadiavina\nMba hody amin’ny Ray ;\nNy maty dia velomina\nKa ravo mandrakizay,\nIzy dia mifady ny ota,\nNoho ny lova tsy ho tonta,\nSady manaraka Azy Tompo,\nIzay voaeso sy voahombo,\nAmabara-podiny any\nan-danitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (447, 1, 'He, sambatra ny navelanao\nNy helony, ry Jehovah !\nNy otany voasaronao,\nKa tsy mba isaina koa ;\nHe, sambatra ny mahitsy fo,\nFa azy ny lovasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (447, 2, 'Ny fo nony nampaginiko,\nNy heriko nihalany ;\nNy ota izay nafeniko\nNo dia nahatonga izany,\nKa ketraka ny fanahiko,\nNisento sy nitomany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (447, 3, 'Ny otako dia nambarako,\nNiantso ny Tompo aho,\nNiaiky ny fahadisoako,\nNivalo sy nitalaho,\nKa dia nomeny indrafo,\nMba ho afa-keloka aho.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (447, 4, 'Jehovah no fialofako\nSy manda miaro ahy ;\nNy rano, na dia tondraka,\nDia tsy hahatratra ahy ;\nNy hobin’ny afa-keloka\nNo ampihirainao ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (447, 5, 'Ry olon’Andriamanitra,\nVonjeo ny andro soa,\nNy otanao izay miafina,\nItsorinao avokoa ;\nRaha mita ilay ony maizina,\nDia  aronao ny Tsitoha.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (448, 1, 'Jeso hany  iriko\nLoharanon’aiko\nSy Sakaiza soa !\nMamiko indrindra\nNy ho tafafindra\nAny Aminao,\nNa dia toa takona,\nHianao ao an-tsaiko,\nSady tohan’aiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (448, 2, 'Loza tsy misava,\nSento lalandava\nNo mitranga izao ;\nNotrotroiny aho,\nKa ny fahavalo\nTsy mahazo ao.\nNa be aza ny manjo,\nTsy ho kivy ny fanahy,\nJeso mbola  ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (448, 3, 'Ny haren’ny tany,\nNa dia tsara ihany,\nTsy ahoako ;\nHianao irery\nLova tsy ho very,\nReharehako !\nKa na fahoriam-be\nNo mihatra amin’ny aiko,\nHianao tsy foiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (448, 4, 'Raha ao am-poko\nHianao, ry Tompo,\nKa manjaka ao,\nIreo ahiahy,\nNahavalaka  ahy,\nDia  afakao,\nNa mavesatra  entana\nHianao no antenaiko\nSady Tohan’aiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (449, 1, 'Tompo tia ô, midina,\nMitoera aminay,\nKa fenoy fifaliana\nAo anatin’ny fonay.\nHianao, ry be fiantra,\nTomponay Mpamindra fo,\nNo mpamangy ny mahantra,\nKa midira ao am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (449, 2, 'Aoka re ny fofon’ainao\nHahavelona anay,\nHanahafanay ny sainao\nHampiadana anay.\nNy fitiavanay ny ota\nMba vonoy, ry Tomponay,\nManafaha anay mpanota,\nRy Andriamanitray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (449, 3, 'Ny mpanomponao ampio\nMba handraisany Anao,\nAza afoy fa mba tahio,\nAoka ho tempolinao,\nHianao no hotompoinay,\nRy Andriamanitra,\nHianao no hoderainay\nHatrarý an-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (449, 4, 'Tanteraho ao am-ponay\nNy fiainana vaovao,\nNy fanahy sy ny sainay\nMba fenoy ny herinao,\nAmboary sy havaozy\nIsan-andro izahay\nMandra-pahatonga any\nAminao mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (450, 1, 'Jeso, Loharanon’aina,\nJeso, Masoandronay !\nIzao ny fonay mitaraina,\nMba velomy izahay !\nO, savao ny zavon-tany\nAo am-pandehananay,\nDia Hianao ihany\nNo ho Masoandronay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (450, 2, 'Jeso, Mofon’aina soa,\nMba vokiso izahay,\nJeso, Làlana tokoa,\nMba tariho izahay !\nAlfa sy Omega tsara\nHianao, Mpamonjinay ;\nTeny soa manambara :\nJeso no fiainanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (451, 1, 'Ario ny tahotrao,\nRy olona  osa fo !\nIzay lazain’ny Tomponao\nTsy hisy tsy ho tò ;\nNy fisentoanao\nDia reny avokoa ;\nNy feon’ny fitarainanao\nValiany tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (451, 2, 'Mpanjaka lehibe\nNo itokianao ;\nNy tanany mahery re\nNo hamonjena anao ;\nTsy misy mahalà\nIzay tiany hatao :\nHandositra amin-tahotra\nNy fahavalonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (451, 3, 'Ny fitondràny soa\nNo iankinonao ;\nKoa  aoka hofidiny koa\nIzay halehanao ;\nNa dia tsy takatrao\nNy sainy lalina\nIzay tendreny ho anao\nEkeo ho marina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (451, 4, 'Ry Tompo tsara ô !\nMba mijere anay,\nNa reraka  aza re ny fo\nTohanonao izahay :\nOvay ny hevitray,\nNy fonay hazavao,\nIzay atoronao anay\nHizoranay izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 1, 'Aoka ho an’Andriamanitra\nNy fanendrena ny lalanao,\nFa hovimbininy  hianao\nAmin’ny lalan-tsarotra ;\nFa toky tsy mamitaka\nNy tenin’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 2, 'Fa inona no azonao\nAmin’ny fitahiana ?\nTsy afaka  ory hianao\nNoho ny fisentoana ;\nNa be herehina hianao,\nHo be ny alahelonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 3, 'Lefero kely, maneke\nIzay sitrak’Andriamanitra,\nNy zavatra tinendriny\nTsy maintsy mahasoa anao ;\nIsika nofinidiny,\nKa tsy ho foiny indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 4, 'Fa fantatr’Andriamanitra\nIzay andro hifaliana\nIsika no hodioviny\nAmin’ny fahoriana ;\nTsy maintsy hofantenana\nVao afa-pahoriana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 5, 'Dia aza atao fa hianao\nAfoin’Andriamanitra ;\nFa Izy dia mananatra\nNy zaza izay tiany,\nKa dia mba manomana azy\nHandova ny an-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (452, 6, 'Misaora ny Ananrany,\nFa aza ketraka hianao\nFa ho tanteraka aminao\nNy soa lazain’ny Teniny ;\nFa toky tsy mamitaka\nNy Tenin’Andriamanitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (453, 1, 'Eo an-tananao, ry Raiko !\nNo apetrako izao\nNy fiainako rehetra,\nMba tahio ny zanakao ;\nMarobe ny fahavalo\nTa-hamely ahy e ;\nRy Jehovah, Ray Mpiaro !\nMba arovanao anie.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (453, 2, 'Raha mbola eto aho,\nZaza be fisiasia,\nTia ratsy noho ny soa,\nKa mandà ny Tompo tia ;\nAza mba manary ahy,      \nRy Mpamonjy tsy mandao !\nAza manalavitra ahy,\nAndrao dia takona Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (453, 3, 'Na ho lava, na ho fohy\nNo hiainako etý,\nTsy mba sitrako akory\nNy handà ‘zay tendrinao ;\nRaha tonga anio ny andro\nIzay handaozako ity,\nEo an-tànanao no tiako,\nRaiso aho ho arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (453, 4, 'Eo an tànanao, ry Raiko !\nNo apetrako izao\nAina, tena sy fanahy,\nMba vonjeo ny Zanakao,\nAmpianaro hahazaka\nFitondrana tianao ;\nIlazao mba hahalala\nFa izao no sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (453, 5, 'Eny Raiko ! mba valio\nIzao fivavako izao,\nE, jereo ka mba tahio\nIzaho miandrandra Anao ;\nMba sasao ny ranao soa,\nDia hadio aho izao ;\nRy Mpamonjy ! mihainoa\nNy mpanomponao izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (454, 1, 'Jehovah no Ampinga\nSy Masoandro koa,\nHiaro amin-doza\nSy hanazava koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (454, 2, 'Ka tsy handresy ahy\nIzay fahavaloko ;\nNa ota na devoly\nHo resy avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (454, 3, 'Ka raha alaim-panahy\nNa misy izay manjo,\nHanatona Azy aho\nHakako hery koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (454, 4, 'Rehefa tapitra andro,\nKa tonga amiko\nIzay hialako aina\nDia tsy ho tahotro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (454, 5, 'Ny lanitra haleha\nSy hitoerako ;\nHidera an’i Jeso\nMandrakizay doria.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (455, 1, 'Ry Tompo manan-kery,\nMiarova anay !\nRy mpitady ondry very,\nMamonje anay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (455, 2, 'Jeso Tompo be fitia,\nMitsinjova anay !\nNy fanahinay mania,\nMiantrà anay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (455, 3, 'Jeso Tompo be fiantra,\nMijere anay !\nNy fanahinay mahantra,\nMitahia anay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (455, 4, 'Jeso Tompo mahalala,\nMananara anay !\nNy fanahinay adala,\nMitariha anay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (456, 1, 'Ry Jeso ô ! ry Jeso !\nNy foko lazanao,\nKa Hianao irery\nNo Tompoko izao ;\nTsy manan-tahotra aho,\nHianao no aroko ;\nNy lalako mazava,\nHianao faniloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (456, 2, 'Mitaona dia mitaona\nNy zava-poana ;\nAnkoso-bolamena\nMamirampiratra ;\nAnefa ry Mpamonjy !\nIzay ampianao\nDia haharesy tsara\nIzao tontolo izao,\nRy Jeso ô ! ry Jeso ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (456, 3, 'Ny nofoko malemy,\nKa be ny sitrapo ;\nBe koa ny fitsapana\nMahazo ahy izao ;\nAnefa Jeso tia !\nRaha re ny feonao,\nMahazo hery aho\nHanao ny sitrakao,\nRy Jeso ô ! ry Jeso ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (456, 4, 'Ry Jeso ô ! ry Jeso !\nMba tovy amiko\nIlay teny maharavo\nHoe “Sakaizako” ;\nIriko lalandava\nHiaraka Aminao,\nMba hahitako tsara\nNy fitiavanao,\nRy Jeso ô ! ry Jeso ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (456, 5, 'Ny dianao jereko\nEtý an-dàlana,\nAmbara-pahatratra\nIlay fitsaharana,\nOmeo hery aho\nHanompo tsara Anao ;\nFa fantatrao, ry Jeso !\nNy foko lazanao.\nRy Jeso ô ! ry Jeso ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (457, 1, 'Ry Jeso ô ! ry Jeso ô ! \nNy fonay tia Anao,\nKa manomàna anay izao,\nHo any aminao :\nFa sambatra avokoa\nIzay tonga ao aminao ;\nIzahay maniry koa\nHo eo ankilanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (457, 2, 'Ry Jeso ô ! ry Jeso ô ! \nAmpionao izahay\nHihevitra ny lanitrao\nIzay hodianay :\nFa sambatra ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (457, 3, 'Ry Jeso ô ! ry Jeso ô ! \nMaloto izahay\nTsy mendrika ny lanitrao,\nDiovinao izahay :\nFa sambatra ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (457, 4, 'Ry Jeso ô ! ry Jeso ô ! \nNy fahavalonay\nMamely ny fanahinay,\nReseo, fa rafinao :\nFa sambatra ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (457, 5, 'Ry Jeso ô ! ry Jeso ô ! \nSao reraka izahay\nMiandry ny hodianay,\nTohanonao ‘zahay :\nFa sambatra ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (458, 1, 'Mba manampia anay,\nRy Jeso Tompo soa !\nKa dia ny fiainanay\nHambinina avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (458, 2, 'Ny tadiavinay\nDia fihavanana,\nTsy hisarahana aminao\nIzay Fiainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (458, 3, 'Ampionao izahay\nMba hahalala Anao,\nKa aoka mba harahinay\nNy nodiavinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (458, 4, 'Dia mankalaza Anao\nIzahay ry Tompo ô !\nFa he, ny fitahianao\nDia tena mahasoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (459, 1, 'Irinay Jeso tia,\nMpanota izahay,\nKa misy alahelo\nAtý anatinay ;\nIrinay ny Mpamonjy\nHiseho ho anay,\nHanafaka ny ota\nMba hifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (459, 2, 'Irinay Jeso tia,\nMba hanazava anay,\nFa maizina ny sainay,\nKa diso izahay ;\nIrinay Jeso Kristy\nHanolo-trsaina anay,\nIrinay Jeso hendry\nMba hianaranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (459, 3, 'Irinay Jeso tia,\nHo tonga aminay,\nHiantra sy hiaro\nSy hanadio anay ;\nNa dia malemy aza\nKa reraka izahay,\nRehefa voavonjy,\nMatanjaka izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (459, 4, 'Ry Tomponay malala !\nRy Jeso tianay !\nRy Havanay mahery !\nRy Rahalahinay !\nVonjeo izahay mpanota\nMiankina aminao,\nTahio izahay mahantra\nMivavaka aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (460, 1, 'Jeso ô, Mpitia anay !\nMaminay ny teninao,\nOsa ny fanahinay,\nFa mahery Hianao ;\nMamy, ry Jeso ! (in-3)\nNy fitiavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (460, 2, 'Jeso ô, Mpitia anay !\nMahadio Hianao ;\nKa ny halotoanay,\nAoka ho voasasanao :\nMamy, ry Jeso ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (460, 3, 'Jeso ô, Mpitia anay !\nTompon’aina Hianao ;\nMaro ny aretinay,\nNefa samy afakao :\nMamy, ry Jeso ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (460, 4, 'Jeso ô, Mpitia anay !\nAoka hotarihinao ;\nAza mba mandao anay,\nMandra-pahafatinay :\nMamy, ry Jeso ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (461, 1, 'Ny foko, ry Mpamonjy,\nMangetaheta Anao,\nManiry lalandava\nHikambana Aminao.\nTsy afaka ao am-poko\nNy faniriana Anao ;\nOneno re ny foko\nHo lapanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (461, 2, 'Ny androm-pahoriana,\nRaha lapanao ny fo,\nManjary fitahiana,\nIzay manadio ny fo :\nTsy afaka ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (461, 3, 'Ny ora fifaliana,\nRaha ao anilanao,\nMamelom-paniriana\nHanao ny sitrakao :\nTsy afaka ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (461, 4, 'Ho lao ny fahotako\nRaha akaikezinao,\nTsy misy hadalako\nIzay tsy afakao :\nTsy afaka ao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (461, 5, 'Finaritra ny saiko\nRaha ipetrahanao,\nFa reraka ny aiko\nRaha halavirinao :\nTsy afaka ao ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (462, 1, 'He, sambatra aho, Jeso,\nraha miaraka Aminao,\nFa ny làlanao dia lavi-\npahoriana ;\nNy ali-mampahory,\nny raho-mitatao\nEo anilanao dia mody\nfitahiana.\nTano aho, Jeso, hiaraka\nAminao\nIzay làlana ombako, e ! mba\nhazavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (462, 2, 'Izao no faniriako, manetsika\nny fo :\nHo sakaizanao mandrakizay\ndoria ;\nNa inona mihatra, na inona\nmanjo,\nHianao no antenaiko hitàntana\nny dia.\nO ! avia Jeso, tano aho e !\nTantanonao ny diako,\nTompo mamonje !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (462, 3, 'Izay sakaizanao, Jeso, no\nampoky ny soa,\nFeno haravoana sy lavitra\nmanjo ;\nIzay rehetra ilaina anananao\ntokoa,\nRaha miaraka Aminao,\nmionona ny fo\nSambatra aho, Jeso,\nTretrika tokoa,\nRavo ny fanahiko, toloranao\nny soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (462, 4, 'Inona no mamiko mihoatra\nnoho Hianao ?\nTompo manan-kery sy\nloharanon-tsoa ?\nIzay zavatra ilaiko azoko\nAminao\nKoa onenanao ny foko, ho\nlapanao tokoa.\nManjakà, Jeso, ato am-poko\nHianao\nIzay fahotako e ! ry Tompo\navelao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (463, 1, 'Iriko e, ry Jeso !\nHitovy Aminao,\nIzay tsy manan-keloka,\nFa masina tokoa ;\nIriko e, ry Jeso !\nHo tonga toa Anao,\nNamonjy sy nanafaka\nFanahy, aina koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (463, 2, 'Iriko, ry Mpamonjy !\nMba ho sakaizanao,\nHiaraka ory Aminao ;\nKa tsy handao Anao ;\nIriko, ry Mpamonjy !\nHanar-dia Anao ;\nNy hazofijaliana\nHo entiko izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (463, 3, 'Iriko, ry Mpandresy !\nHiaraka Aminao,\nHo eo an-toby lehibe,\nHo miaramilanao ;\nIriko, ry Mpandresy !\nHanaraka Anao ;\nMitondra ny fanevanao,\nHandray ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (463, 4, 'Iriko, ry Mpanjaka !\nHo ao an-dapanao\nHiara-paly Aminao,\nFa tonga Zanakao ;\nIriko, ry Mpanjaka !\nHo eo anilanao\nHandray ny lova atolotrao,\nMpanjaka be fitia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 1, 'Mombà anay, ry Jeso \nTompo ô ! \nFa maizina ny alina atý ;\nTsy misy vonjy raha tsy\nAminao ;\nMpamonjy tia ô !\nmombà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 2, 'Mandalo koa ny andronay etý\nKa mihalo ny fifalianay ;\nMiovaova izao rehetra izao\nRy tsy miova ô !\nmombà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 3, 'Aza mba mivahiny aminay,\nAza mihevitra ny helokay ;\nAoka ho be ny famindranao\nfo ;\nRy Jeso Tompo ô !\nmombà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 4, 'Tsy mahaleo tena izahay,\nFa mora resy, mora kivy koa ;\nRy Jeso Tompo ! aoka\nhonenanao\nEtý an-dasin’ny \nmpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 5, 'Ny teninao no fifalianay ;\nNy masonao mitari-dalana ;\nAry ny herinao no tanjakay ;\nSakaiza tsara ô ! mombà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 6, 'Izay ombanao afa-tahotra,\nTsy mety kivy na mamoy fo ;\nTsy misy fahoriana izay\nHaninona ny fo arovanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (464, 7, 'Rehefa tonga ny fotoana\nHitànay ilay ony maizina,\nAoka ny tavanao hazava ao,\nAmin’izany re mombà anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (465, 1, 'Jesosy ô, Hianao no faleha !\nMba hankanesana amin’ny Ray,\nKa mitariha anay mba handeha\nAmin’ny làlana marina indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (465, 2, 'Fa Hianao no Mpamonjy voatendry,\nZanaka mandrakizay Hianao ;\nKa Hianao no Mpitarika hendry,\nLalana amin’ny Ray Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (465, 3, 'Diso ny tsara, ka dia nizotra\nTamin’ny lalana ratsy izahay ;\nKoa mamerena anay mba hizotra\nAmin’ny lalana tsara indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (465, 4, 'Fa Hianao no mitondra ho any\nAmin’ny lanitra fonenanao ;\nAry tsy misy mahazo mankany\nAfa-tsy amin’ny alalanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (465, 5, 'Maro ny zavatra izay mampahory,\nFa Hianao no Mpiaro anay ;\nAoka izahay tsy ho reraka Akory,\nRy Mpanalalana amin’ny Ray !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (466, 1, 'Jeso Mpamonjy, Mpiandry\ntokoa \nAmpivereno hanaraka Anao ;\nOndry mania, manary ny soa,\nAza avela hiala aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (466, 2, 'Taomin’ny ratsy, fitahin-tSatana,\nMila ho lasan-ko babo izahay,\nFa Hianao no mahery mitana\nTsy hahavery ny ondry iray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (466, 3, 'Varivariana, salasalaina,\nBe ahiahy, jereo izahay ;\nAmpianaro ny tsy hary saina,\nAmpitsaharo ny fanianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (466, 4, 'Aina nomenao hisolo ny very,\nAry ny ranao nanvotra anay,\nFa Hianao no nitondra irery\nFampijalian-kanitsy anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (466, 5, 'Fa ny fiainan-tsi-hita resenao\nVelona mandrakizay Hianao ;\nDia fiainana an-danitra omenao\nIzay ondry mety manaraka Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (467, 1, 'Ny eo akaikinao, ry Ray,\nNo mamiko izao,\nHanjary tena tafaray\nNy foko sy ny Anao,\nSoa re, ry Raiko ô !\nRaha eo anilanao ;\nNy hazo fijaliana\nHo tonga maivana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (467, 2, 'Ny eo akaikinao, ry Ray,\nNo herin-janakao ;\nHianao irery no mahay\nManampy ahy izao.\nSoa re, ry Raiko ô !\nRaha eo anilanao ;\nNy ota tsy hanapaka\nNy olona afaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (467, 3, 'Ny eo akaikinao, ry Ray,\nNo mandako tokoa ;\nNy ratsy dia tsy hahay\nHandrombaka ahy koa,\nSoa re, ry Raiko ô !\nRaha eo anilanao ;\nNy ora izay manjombona\nHo fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (467, 4, 'Ny eo akaikinao, ry Ray,\nNo fifaliako izao,\nNy tanako dia hifandray\nAmin’ny tananao.\nSoa re, ry Raiko ô !\nRaha eo anilanao ;\nFenoinao fanambinan-tsoa\nIlay kapoakako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (468, 1, 'Oram-be mivatravatra,\nArotsahy, Tompo ô !\nFa mangetaheta loatra\nIzahay mpanompona.\nO ! valio, O ! valio,\nMitaraina ka valio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (468, 2, 'He ! malazo re ny voly\nIzay maniry tsy mamoa ;\nNefa raha omenao aina,\nDia ho vokatra tokoa.\nO ! vangio, O ! vangio,\nSao dia maty, ka vonjeo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (468, 3, 'Tovy eto, ry Mpamonjy !\nIzay nambara fahizay,\nKa iraho ny Fanahy\nAmin-kery lehibe.\nO ! henoy, O ! henoy,\nKa valio ny vavakay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (468, 4, 'He ! toa kivy ka manahy\nIzahay mpiasanao ;\nAza ela, ry Fanahy !\nAsehoy ny herinao.\nO ! avia, O ! avia,\nMisehoa, Fanahy ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (469, 1, 'Ny hirako izao,\nRay be fitia,\nNy ho akaikinao\nBebe kokoa,\nIzay re no hirako,\nMandra-pahafatiko ;\n:,: Akaikinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (469, 2, 'Raha mba sarotra\nNy diako\nKa zary maizina\nNy làlako,\nRy Ray ! akaikinao\nNo tian’ny zanakao :\n:,: Akaikinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (469, 3, 'Ao no hiseho re\nNy tohatra\nMba hanatonako\nNy lanitra,\nAnjely miantso ahy\nAkaikinao, ry Ray !\n:,: Akaikinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (469, 4, 'Ny fahoriana\nSy loza koa,\nRaha eo anilanao\nDia mody soa,\nKa dia tano aho\nHo ao akaikinao ;\n:,: Akaikinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (469, 5, 'Na dia hilentika\nNy androko,\nAry izao hita izao\nHilaozako,\nMbola ho hirako\nRy Andriamanitro !\n:,: Akaikinao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (470, 1, 'Jeso no anjara soa\nIzay mahafa-po tokoa,\nSoa tsy manam-paharoa,\nAhy Jeso : Ampy   izay.\nAhy Jeso hatretý,\nAhy Jeso hatrarý\nAhy Jeso Zoky tia\nMandrakizay doria, doria.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (470, 2, 'Vola sy harena be\nMaro tia tokoa anie ;\nNefa mamiko ny hoe :\nAhy Jeso : Ampy  izay.\nAhy Jeso hatretý, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (470, 3, 'Fahefana, laza soa\nMahafatifaty koa ;\nNefa izao no tena soa :\nAhy Jeso : Ampy  izay.\nAhy Jeso hatretý, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (470, 4, 'Eny, izao rehetra izao\nTsy hamoizako Anao,\nFa Jeso izay tsy mandao !\nAhy Jeso : Ampy  izay.\nAhy Jeso hatretý, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (470, 5, 'Jeso, aina, hery, zo ;\nJeso, lova tsy mba lo ;\nJeso fiainam-po ;\nAhy Jeso : Ampy  izay.\nAhy Jeso hatretý, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (471, 1, 'Mamin’ny foko, ry Jeso ô !\nNy famonjena izay vitanao,\nNy hadisoako navelanao,\nTena afaka aho mandrakizay.\nJeso malala ô ! Mpamonjy soa !\nIndro ny aiko raiso ho Anao ;\nVelon-kanompo, velon-kanoa\nVonon-kanao izay sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (471, 2, 'Tano ny foko, ry Jeso tia !\nZaza tsy mahasaraka Anao !\nMbola malemy, mora mania,\nTano hifikitra ao Aminao.\nJeso malala ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (471, 3, 'Aoka ny foko sy ny fonao\nHiara-mitepo tena iray ;\nAoka ny saiko sy ny sainao\nHiray safidy mandrakizay.\nJeso malala ô ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (472, 1, 'Ry Tompo be fitia !\nIzaho zanakao\nTantano tsy hania\nHiaraka aminao ;\nFa mamiko tokoa\nNy eo ankaikinao ;\nTsy orin-java-tsoa,\nIzay sakaizanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (472, 2, 'Harena be an-tany\nMamilivily fo,\nFa tiana mora lany\nKa tsy mampionom-po ;\nIzany aniriako\nHifikitra aminao ;\nAtolotro ny foko\nHo eo an-tananao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (472, 3, 'Fa raha Izaho irery\nVoasakan-tsitrapo,\nMandeha manirery,\nKa maro no manjo ;\nIzany no ilàko\nHiaraka aminao,\nVimbino aho, Raiko !\nHo eo an-tananao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (472, 4, 'Tsy hitako mihitsy\nIzay mahafa-po,\nTsy misy etý akory\nIzay tsy mety lo ;\nFa ao am-paradisa\nNo feno zava-tsoa,\nTsy mahadisadisa,\nFa sambatra tokoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (473, 1, 'Jehovah ô ! Hianao nandinika\nahy,\nKa fantatrao ny toe-piainako ;\nNy fo sy saina mbamin’ny\nfanahy\nTsy miafina aminao, ry\nTompoko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (473, 2, 'Ka na mipetraka aho, na\nmandeha\nIzay rehetra ataoko hitanao,\nHe, fantatrao ny lalana haleha,\nNy androko dia eo an-tananao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (473, 3, 'Na velona aho, eny, maty aza,\nNy tenako dia eo imasonao,\nKa toerana aiza, lalana\nmankaiza\nNo hialako tsy ho hitanao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (473, 4, 'Ry Tompo ! tena mahagaga\nloatra\nNy asanao sy ny fisainanao ;\nTsy misy saina mety\nmahatratra\nNy fahendrenao sy ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (473, 5, 'Izao no hany zavatra iriko ;\nVelaro indrindra ny fitiavanao,\nHahaizako hametraka ny foko\nHionona eo am-pelatananao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (474, 1, 'Zanahariko Tsitoha\nTompo, Hery sy Fitia,\nRay hatramin’ny taloha,\nAhy anio, ka ho doria.\nManjakà mandrakizay\nAo am-poko, Tompo soa ;\nDera, laza, Anao ry Ray !\nZanaka, Fanahy koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (474, 2, 'Ny finoakao Anao,\nHatanjaho mba hamoa\nHiasako ny asanao,\nHamafazako ny voa.\nAoka ny Fanahinao\nJiro mba hitarika ahy\nKa apetrako aminao\nNa ny tena na fanahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (474, 3, 'Antenaiko, Hianao, Jeso\nIzay Mpitsara lehibe\nHanamarina ahy ao,\nNa mpanota aza re.\nDia hiezaka aho etý\nMba hanao izay mendrika\nHamindrànao fo arý\nFa voavela heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (474, 4, 'Ray ô, ny fitiavanao\nAfindrao ho toetrako,\nMba hitiavako Anao,\nAry koa ny namako.\nHo mpanompo aho re !\nHo mpitory koa Anao !\nMba hanjaka an-tany e,\nTompo, ny fitiavanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (475, 1, 'Ireo didinao, Jehovah Ray ;\nIzay tianao hankatoavinay\nDia rehareha sy voninahitra,\nSatria filamatry ny ao\nan-danitra.\nIlaina mba hiseho fa tsy hiery,\nHo fisintonana ireo izay very,\nFa antoky ny fiainanay,\nHatramin’izao, mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (475, 2, 'Ny fitsipikao, Tomponay\nIzay nomenao mba\nharahinay,\nDia mampifantina izay anjara\nsoa\nKa manamboatra sy\nmanatsara,\nMampisy endrika itony tena\nSy manazava ny firenena,\nKa antoky ny fiainanay,\nHatramin’izao, mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (475, 3, 'Ireo teninao, Jesosinay\nNo hany zavatra ivelomanay,\nFa tanjaka tokoa, tena hery be\nHahafahanay ny lalana\nmideza re,\nSakafo mahavelona ny aina,\nSy mampiravo tokoa ny saina,\nTena antoky ny fiainanay,\nHatramin’izao, mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (476, 1, 'Moa Jesosy ve tsy ahy ?\nAndriamanitra mahery \nKoa nahoana no hanahy\nIzy miambina  mijery.\nFahavalo na dia maro\nTsy mba mahakasika ahy\nIzy no ampinga miaro\nKoa izaho tsy hanahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (476, 2, 'Moa Jesosy ve tsy ahy ?\nIlay Mpanolotsaina hendry\nKoa tsy kivy fa ho sahy\nAmin-java-tsoa kinendry.\nTsy manaonao foana\nFa tarihin-Kapiteny.\nTsy manahy satria nahoana ?\nAo ny saina izay nomeny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (476, 3, 'Moa Jesosy ve tsy ahy ?\nIlay Jesosy mahagaga,\nMpanasitrana, Mpitahy\nKa ahoako izay hitranga ?\nRanomasina aza tao,\nNampagininy ka tony ;\nHainy mihitsy ny manao\nHahatò ny sitrapony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (476, 4, 'Moa Jesosy ve tsy ahy ?\nRain’ny mandrakizay, eny !\nVelon-toky ny fanahy\nIzay nekeny , novonjeny,\nKoa na ory sy mijaly\nAza aho etý an-tany,\nVetivety dia hifaly :\nMampaninona izany ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (476, 5, 'Moa Jesosy ve tsy ahy ?\nIlay Andriampiadanantsika,\nAndriamanitra Mpiahy,\nFitahiana feno hika.\nFamonjena efa vita\nHofikiriko hatrany,\nFahasoavana hampita,\nAny am-paradisa any.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (477, 1, 'Inona no hahasaraka ahy\nAmin’ny fitiavanao, Jeso ?\nFahoriana ve, sa ahiahy ?\nFahantrana, sa famoizam-po ?\nTsia, ry Jeso Tompo be fitia,\nIzaho tsy mivadika Aminao\nFa mandrakizay doria doria\nIzaho dia hanaraka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (477, 2, 'Inona no hahasaraka ahy\nAmin’ny fitiavanao, Jeso ?\nZo sy laza sy fakam-panahy ?\nSa harena, zava-mora lo ?\nTsia, ry Jeso ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (477, 3, 'Inona no hahasaraka ahy\nAmin’ny fitiavanao, Jeso ?\nSabatra, sa fanenjehana\nmafy ?\nSa mosary, ady be manjo ?\nTsia, ry Jeso ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (477, 4, 'Inona no hahasaraka ahy\nAmin’ny fitiavanao, Jeso ?\nFaty ve no hampivadika ahy,\nLoza, hery, fahefana koa ?\nTsia, ry Jeso ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (477, 5, 'Eny, izao rehetra izao,\nry Tompo\nTsy mba hahasaraka Aminao !\nNa inona hanjo ity mpanompo\nDia hijoro, tsy handao Anao !\nVelona aho, Tompo, eny maty\nDia tsy mivadika Aminao !\nHianao no velona ao anaty,\nKa doria, ny foko ho Anao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (478, 1, 'Fanavaozana izay mahagaga\ntokoa \nNomen’i Jeso ahy re !\nFahazavan’ny fanahy izay\nmahafa-po\nNomen’i Jeso ahy re !\n:,: Hatramin’izao dia\nendrey, endrey :,:\nNy fitempon’ny foko izay\nravo tokoa\nOnenan’ny Tompo Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (478, 2, 'Ny fiainako be siasia taloha\nVoaova mihitsy izao\nEny, ny fahotako, izay\nnahavoa\nVoasasany koa izao,\n:,: Hatramin’izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (478, 3, 'Ny finoako, ny fanantenako\ndia be\nNy tokiko fatratra koa\nNy fisalasalana dia levona re,\nNy saiko mandroso ho soa.\n:,: Hatramin’izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (478, 4, 'Tonga mamirapiratra fatratra\nerý\nNy aloky ny faty re !\nKa dia tsinjoko ilay vavahady\narý\nMankao an-tanàn-dehibe\n:,: Hatramin’izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (478, 5, 'Tsy miato, mizotra mankany tokoa\nNy diako hatramin’izao\nHovantaniko any Ilay Tompo soa\nJesosy malala no ao.\n:,: Hatramin’izao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (479, 1, 'Na inona inona hitranga,\nJeso !\nNa ny lanitra ho manga,\nJeso !\nNa ho maloka mangina,\nJeso !\nDia ho feno fifaliana,\nJeso !\nNy fotoana dia handalo,\nJeso !\nFa tsy hisy hampimalo,\nJeso !\nRaha miorina ao am-poko,\nJeso !\nIlay fitiavanao inoako,\nJeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (479, 2, 'Raha jereko ny andro lasa,\nJeso !\nTao ny tsiky, tao ny ratra,\nJeso !\nNefa Hianao, Ilay \nMpampahery,\nJeso !\nNo nambomba sy nijery,\nJeso !\nNy fotoana ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (479, 3, 'Ny ho avy antenaiko,\nJeso !\nMbola ho fifalian’ny saiko,\nJeso !\nNy hasasarako hisava\nJeso !\nNy harivako hazava,\nJeso !\nNy fotoana ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (480, 1, 'Tena sambatra sy mahate-ho\ntia\nIzay masim-po, madio \nfisainana,\nFa miara-dàlana mifankatia\nAmin’Andriamanitra,\nFiainana.\nMasina tokoa re ry Tompo\nTianay mba ho masina,\nSaina, tena sy fanahy\nKa fiainan-danitra tokoa\nNo santarin’izay mino sy\nmanoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (480, 2, 'Indro, mampahery, mahatoky\nkoa\nRaha mahitsy ny fijery, soa\nny fo ;\nFa ny fo madio sy masina\ntokoa :\nParadisa an-tany, feno\nzava-tsoa\nMasina tokoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (480, 3, 'Koa halaviro ny fitaoman-\ndratsy,\nNy fisakaizana tsy mba\nmasina ;\nFa ny tia ny Tompo, dia\nolon-tsoa mitafy\nFahadiovan’ny Fanahy \nMasina.\nMasina tokoa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (480, 4, 'Miaraha làlana amin’i Jesosy.\nFa ny fahavalo ta-hamotraka,\nMijoroa, reseo ny toetra\nmampihosina\nAo anaty loto sy ny fotaka\nMasina tokoa ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (481, 1, 'Ry kristiana, andrandrao\nNy Tompo sy Mpanjakanao,\nFa Izy no Mpiaro ;\nKa aza taitra hianao,\nNa dia manakora izao\nNy fahavalo maro ;\nOmban’ny Tompo hianao,\nKa aza atahoranao\nNy rahon’ny mpamely.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (481, 2, 'Fa indro tsy rendrehana\nIlay Mpiambi-masina,\nIzay Tokin’Israely ;\nFantaro marina izao,\nIzay ataon’ny rafinao\nAoka ho azy izany ;\nFa andrandrao ny lanitra,\nAnao Andriamanitra,\nAnao ny fanjakany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (482, 1, 'Inoantsika marina\nNy Mpamorona Tsitoha,\nIsika dia natsangana\nMba ho zanany tokoa,\nHanina sahaza omeny,\nAry be ny famonjeny,\nAmin’ireo loza maro\nDia Izy no Mpiaro ;\nInoantsika ao am-po\nFa Izy marina sy to.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (482, 2, 'Jesosy Kristy Zanany\nNo inoantsika koa ;\nIsika dia fananany,\nFa navotany tokoa,\nIzy Zanak’i Maria,\nDia rahalahy tia ;\nMaty ka natao ho very,\nFa nitsangana amin-kery,\nNiakatra ao an-dapany,\nHitsara ao am-parany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (482, 3, 'Ka ny Fanahy Masina\nNo inoantsika koa,\nNy fiangona-masina\nNo naoriny tokoa ;\nAo isika dia mahita\nFamonjena efa vita ;\nAfaka ny helotsika,\nFo vaovao no raisintsika ;\nIsika dia hatsangana\nHo velona ao an-danitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (483, 1, 'Izaho mino ny\nMPAMORONA,\nMpanao ny tany sy ny lanitra ;\nIzaho manam-panorenana,\nFa manan-dRay arý\nan-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (483, 2, 'Izaho mino ny\nMPANAVOTRA,\nNy rany masina no mamiko,\nNy momba Azy dia sambatra,\nNy diany no mba harahiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (483, 3, 'Inoako ny MPANAMASINA,\nMpananatro sy Masoandroko ;\nIzaho omeny afo masina,\nMba ho madio ny fiainako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 8, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 1, 'Ny loharanom-pamonjena,\nHe, hitako tokoa izao,\nDia Jeso Kristy, Ilay nomena\nHamonjy izao tontolo izao,\nNy rany dia fantaro\nFa ampy hiankinako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 2, 'Ny otantsika no nanery\nNy Tompontsika hankatý\nIsika hitany ho very,\nIzany no nidinany\nNitondra fahoriana\nSy hazo-fijaliana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 3, 'Ny nialan’ny Tompo aina\nHanaisotra ny heloka\nDia tsy mba takatry ny saina,\nFa hitan’ny finoana,\nNy rany no miantso hoe :\nHe, famindrampo lehibe !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 4, 'Izay no nampionona ahy,\nKa afaka ny tahotro ;\nNa dia ory ny fanahy,\nJesosy no Mpanavotro\nNa misy mampanahy fo,\nNy Raiko no be indrafo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 5, 'Na very aza ny harena,\nKa be ny fahoriana,\nIzaho mbola manantena\nHahita fifaliana ;\nJesosy, Izay fananako,\nDia Tompo feno indrafo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 6, 'Izaho tsy matoky tena,\nNa ny fahamarinako ;\nJesosy foto-pamonjena\nNo tena itokiako,\nNy Raiko no isaorako,\nFa izy be indrafo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 7, 'Izay sitraky ny Ray malala\nNo aoka mba ho sitrakao\nFa izy tena mahalala\nHametraka izay anjarako ;\nIzay ataony tiako,\nFa hitako ny indrafo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (484, 8, 'Izany fototra izany\nNo toky izay hotanako\nAmbara-pitako ho any\nAn-koatry ny fasako,\nKa any no hisaorako\nIlay fototry ny indrafo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (485, 1, 'O, ry Zanahary,\nIzay nahary ahy,\nBe ny herinao!\nManan-toky aho,\nFa tsy maty taho,\nManana Anao ;\nAfa-po ny zanakao,\nHianao no Ray Mpiahy,\nKa  iza no hanahy !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (485, 2, 'Hianao no Raiko\nTena fototr’aiko,\nSady heriko.\nHianao no aro,\nTsy ho reraka aho,\nTsy ho kivy fo ;\nAo ambany elatrao\nNo ataoko fiereko ;\nAo tsy misy sento.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (485, 3, 'Latsaka isan-andro\nToy ny ranonando\nNy fitianao.\nRaha alaim-panahy,\nKa manafotra  ahy\nRe ny otako,\nDia ho tafatsangana\nHanavao ny herin’aiko\nHianao, ry Raiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (485, 4, 'Ary raha lany\nToy ny zavon-tany\nNy fiainako ;\nMisy onin’aina\nTsy mba mety maina\nAo an-danitrao ;\nAo no hitoerako,\nAo an-tranonao, ry Raiko,\nAo no antenaiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 1, 'Jeso dia tokiko,\nFa Mpamonjiko mahery,\nSady tena zokiko\nTsy hamela ahy irery\nAo amorom-pasako,\nFa hiaraka amiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 2, 'Izy tenany mandre\nNy hangidin’ny fitàna\nIlay ony maintibe\nVokatry ny fahotana ;\nKoa tsy hilaozany\nIrery ny olony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 3, 'Izaho hody vovoka,\nFa ny androko ho lany,\nNefa dia hatsangana\nSy hakarina hankany\nAmim-boninahitra,\nIzay tsy mety tapitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 4, 'Dia ho tafatambatra\nNy fanahy sy ny tena,\nAry samy sambatra,\nFa nahazo famonjena,\nIzay Jesosy, havako,\nVao ho tena hitako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 5, 'Izay manjo atý izao\nTsy mba misy ao akory,\nFa finaritra ny ao.\nTapitra ny mampahory,\nKa ny fahalovana\nHody fifaliana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (486, 6, 'Eto aho reraka,\nAny misy hazon’aina\nVita sy tanteraka\nIzay rehetra nantenaina\nAo no hahatrarako\nNy fahasambarako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 1, 'Jeso iankinako,\nIzy no mpanavotro ;\nKristy itokiako\nAmin-tsaina sy ny fo ;\nKristy ifaliako,\nIzy marina sy to.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 2, 'Nahafoy ny lanitra\nHianao, Mpanavotra ;\nRaha ny vahoakao\nNobaboin’ny rafinao,\nHianao mangoraka,\nRy Kristy Mpanafaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 3, 'Fahoriambe ny erý\nNo nanjo Anao tatý\nFa ny fahavalonao\nDia nanenjika Anao ;\nNefa novalianao\nIndrafo ny rafinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 4, 'He, nijaly Hianao\nNoho ny fitiavanao\nKa ny famindramponao\nNo nahafatesanao ;\nHianao nasandratra,\nFa nijaly fatratra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 5, 'Sarobidy izahay,\nFa ny ranao avotray,\nIzay nomenao ho anay,\nMba hanafaka anay ;\nTonga olom-potsinao\nIzahay navotanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (487, 6, 'Ravoravo izahay,\nRy Mpamonjy maminay,\nKa ny fanavotanao\nAn’izao tontolo izao\nSy ny fitivanao\nNo isaoranay Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (488, 1, 'Ny tany sy ny lanitra tsy\nmanam-petra\nHiaraka amin’ny mino\nrehetra\nManaiky fa Jeso no aina\nhamonjy\nNy olona ory izay mila vonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (488, 2, 'Na dia manenjika ahy ny\nratsy,\nTsy lany ny hery, fa Jeso\nmanampy\nDia  Izy no tanako mafy\ntokoa,\nKa tsy ialako, fa Tompo\nTsitoha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (488, 3, 'Jesosy Sakaizako dia miahy\nNy Azy, ka afaka ny ahiahy,\nNy fo malahelo ovany ho\nfaly,\nKa tretrika indray   ireo olo-\nmijaly.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (488, 4, 'Jesosy fidiko ho Tompo\ntokoa,\nEfaiko ny asa sy hevitra soa,\nIzay halan’ny Tompo tsy\ntiako intsony,\nKa tena iraisako tsara any\nfony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (488, 5, 'Izay tanako mafy sy mamy\nhambara\nRehefa ho faty, ka tonga ny\nanjara :\nJesosy, Jesosy irery ihany\nNo aina, harena tsy mety ho\nlany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (489, 1, 'Ahy va Jesosy Tompon’aina ?\nMoa ahy va ny lanitra ?\nEny, matokia, ry fo sy saina,\nFa eken’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (489, 2, 'Eny, zokiko Jesosy Tompo,\nFahagagana lehibe erý,\nTsy antsoiny hoe mpanompo,\nFa ataony tena havany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (489, 3, 'Toa mila hampisalasala\nIo tena fahagagana io,\nAry lavin’ny tsy mahalala,\nNefa azon’ny finoako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (489, 4, 'O, mba mifalia, ry fanahy,\nFa anao Ilay Mpanavotra !\nKa misaora, aza mba manahy,\nFa ho azonao ny lanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (489, 5, 'Iombonana ny lova any,\nFa Iray ny Ray an-danitra !\nKa harena be dia be izany,\nHasambarana maharitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (490, 1, 'Inona re no hatakaloko\nAn’i Jesosy Izay mpisoloko ?\nNy zavatra izay ho simba va\nsy mora levona ?\nRaha mbola hazoniko ny to,\nKa tsy maty ny jiro ao am-po,\nJesosy tsy soloako na inona\nna inona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (490, 2, 'Indro, Jesosy no fiainako,\nIzy no manda fiarovako ;\nIzaho efa maty, nefa\nnovelominy indray.\nKa na dia mahantra aza atý,\nKa tsy mbola nafindra ho arý,\nJesosy no anjarako, ka ampy\nahy re izay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (490, 3, 'O, ry Jesosy Tompo tsara ô !\nAza mamela ny fanahiko\nHisaraka  aminao, fa tano aho\nho mpanomponao.\nMba velomy ny fitiavako,\nKa tohano ny fandehanako,\nMba hanarahako ny lalana\nizay nalehanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (491, 1, 'He ! manda fiarovana,\nAndriamanitsika,\nAmpinga mahatohana\nIzay manjo antsika.\nMamely fatratra\nSatana sompatra,\nMpandoza hatrizay,\nMpamitaka mahay\nTsy manam-paharoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (491, 2, 'Malemy, tsinontsinona,\nFa mora resy isika ;\nFa misy voahasina\nMiady ho antsika ;\nJesosy re izay,\nIlay iraky ny Ray,\nMpitarika mahay,\nMpamonjy hatrizay\nSy Tompo-pandresena.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (491, 3, 'Na dia ta-handevona\nIreo devoly maro,\nTsy misy atahorana,\nJesosy no Mpiaro ;\nIlay rain’ny maizina\nDia nohelohina,\nKa tsy ahoana,\nFa teny tokana\nDia mahalavo azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (491, 4, 'Kendreny hofongorana\nNy teny tanantsika,\nKanefa tsy ho foana,\nNy Tompo momba antsika.\nNa dia ho fongana\nNy laza ananana,\nHarena, havana\nTsy mampaninona,\nNy lanitra antsika.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (492, 1, 'Namely mafy loatra\nNy henatra aman-tahotra ;\nTsy afaka ny aizim-po,\nFa mbola tao ny heloka,', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (492, 2, 'Nandroso aho nivavaka :\nRy Jeso ô, Mpanavotra !\nIzao no angatahiko ;\nMba mamelà ny heloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (492, 3, 'Izaho tsy hiahotra ;\nNy tenako atolotro,\nIzay rehetra ananako\nAnao, Mpamela  heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (492, 4, 'Ny tahotra dia levona,\nAriako ny henatra, \nSatria mandre ny feonao\nHoe : voavelako ny helokao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (492, 5, 'Endrey ! ny hafaliako\nIzay velona ao anatiko,\nKa sahiko ny hanao hoe :\nVoavelanao ny heloko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (493, 1, 'Finaritra ny vorona ao an-ala\nSy ao an-karam-bato avobe ;\nNa dia betsaka ny \nmpankahala\nMivonon-kahafaty azy re ;\nMatoky izy, tsy misalasala,\nFa manam-piarovan-dehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (493, 2, 'Jesosy Tompoko no\nharambato\nSy ala mafy ikirizako ;\nNa mafy aza sady tsy miato\nIzay ataon’ny fahavaloko,\nMamely ahy ary misahato,\nJesosy re no fiarovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (493, 3, 'Izay rehetra ta-hamely ahy,\nMikendry, nefa sasa-poana ;\nIzaho dia ravo am-panahy,\nTsy azony atao ahoana,\nFa misy manda manodidina\nahy,\nIzay miaro  sy  manohana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (494, 1, 'Ry Jeso Tompo tsara ô !\nTsy azoko valiana\nIzato fitiavanao\nManafa-pahoriana,\nHe, sambatra aho, Tompo ô !\nFa manana Anao izao,\nIzay tsy mba mety sosotra,\nNa faly hampitahotra\nKa Hianao no tokiko\nSy foto-pifaliako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (494, 2, 'He, isam-pahoriana\nIzay niantsoako Anao,\nDia efa novaliana\nNy hataka izay natao,\nNy heloko navelanao,\nNy foko nohavaozinao,\nNy loza nosakananao,\nNy ngidiny nesorinao;\nNy aim-baovao iainako,\nKa velona ny heriko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (494, 3, 'Ka amin’izao sisa izao,\nMikasa aho Tompo ô !\nHanao izay nohafaranao,\nHanaraka Anao am-po.\nSatana dia holaviko,\nHafoiko izao tontolo izao,\nRafesiko ny nofoko,\nFa Hianao, Mpamonjy ô !\nMpanavotra tsy foiko,\nNo sisa hotompoiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (495, 1, '“Ambarao, ry mpivahiny !\nNy alehanao izao !”\n“Fiakarana mideza,\nIhoaranay anio ;\nJesosy Tompo no miantso\n:,: Mba ho ao an-tany soa ;\n:,: Mba ho ao an-tany soa ;\nJeso no alehanay :,:”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (495, 2, '“Lozanao, ry mpivahiny,\nLàlana tsy fantatrao !”\n“Tsia, fa anjely maro\nNo miaraka aminay ;\nJeso Tompo no mitondra.\nTsy hanjo anay ny loza,\n:,: Tsy hanjo anay ny loza ;\nIzy no ampinganay :,:”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (495, 3, '“Inona, ry mpivahiny,\nNo ho azonao ary ?”\n“Jeso Kristy be fitia\nVonon-kanome anay\nLamba fotsy, zava-tsoa,\nRanon’aina tsy ho lany,\n:,: Ranon’aina tsy ho lany !\nJeso no hatoninay :,:”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (495, 4, '“Tianay, ry mpivahiny,\nMba hiaraka aminao !”\n“Mandrosoa ry sakaiza !\nMiaraha aminay;\nFa izay mitady tsara\nTsy mba lavin’i Jesosy,\n:,: Tsy mba lavin’i Jesosy,\nIzy no hidiranay :,:”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (496, 1, '“Mpivahiny ! mba lazao ;\nZovy moa hianao ?”\n“Izaho namanao ity,\nFa miandry Ilay ary.”\n“Mpivahiny ! mba lazao :\nIza no andraisanao ?”\n“Jeso, Havako arý,\nNo andrasako aty.”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (496, 2, '“Mpivahiny ! mba lazao :\nAiza no halehanao ?”\n“Lalana ety avokoa\nSady sarotra tokoa.”\n“Mpivahiny ! mba lazao :\nIza no harahinao ?”\n“Jeso, Zoky be fitia,\nTsy mamela mba hania.”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (496, 3, 'Mpivahiny ! mba lazao :\nAiza no hodianao ?”\n“Ao an-danitra madio\nNo aleha ankehitrio.”\n“Mpivahiny ! mba lazao :\nIza no handray anao ?”\n“Jeso, Ilay Mpanjaka soa,\nTompo, Havako tokoa.”', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (497, 1, 'Tsy misy miadana ka afa-po\nIzay monina eto an-tany ;\nFa izay tia sy mino ny Tompo\nJeso\nNo hahazo ny soa tsy lany.\nMba irio, mba irio\nNy hananana an’i Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (497, 2, 'Raha misy mahory mahazo etý,\nJeso no hanala izany ;\nTsarovy ny avotra tao Kalvary\nFa nalatsany tany ny rany,\nMba irio, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (497, 3, 'Izaho maniry hanaraka Anao,\nRy Tompo Mpanafaka ota !\nFa fantatro tsara hatramin’izao\nNy fitiavanao ahy mpanota\nMba irio, sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (498, 1, 'Ranomasi-mampahory\nNo alehantsika izao ;\nFa ny mino dia hitody,\nKoa maharitra izy izao ;\n:,: O ! andeha isika hody\nMba ho ao\nan-tranon-dRay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (498, 2, 'Jeso Tompo eo am-pita\nKa miantso hoe: Avia !\nIzy koa no mampita\nKa manao hoe: Matokia !\n:,: O ! andeha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (498, 3, 'Maro efa tonga any\nKa mifaly avokoa ;\nMbola be ny fiantrany\nMisy ho antsika koa.\n:,: O ! andeha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (498, 4, 'Kristy koa no sambon’aina\nIzay mitondra ho arý.\nFamonjena azo maina,\nKa ilaozy re ity.\n:,: O ! andeha, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (499, 1, 'Ny sambokelinay etý\nMitety ranobe,\nMisononoka ho ery\nAn-dafin’ony be  (in-3)\nMisononoka ho ery, sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (499, 2, 'Ny rivo-doza tsy hanjo,\nFa Jeso aminay;\nNy alondrano mankato\nIlay maherinay  (in-3)\nNy alondrano mankato, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (499, 3, 'Mamaky onja izahay\nFa tsy ho faty eo ;\nNy fitodian-tsambonay,\nHe ! tazanay izao  (in-3)\nNy fitodian-tsambonay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (499, 4, 'Irinay ny ho tonga ao\nHilaozanay ity ;\nMitsena anay tokoa izao\nNy zokinay ary  (in-3)\nMitsena anay tokoa izao, ..sns', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (500, 1, 'Jeso Tompo ! Vatolampy,\nVato voafantina,\nMba ho fanorena-mafy\nIzay tsy mety levona;\n:,: Vato mafy :,:  (in-2)\nDia itokianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (500, 2, 'Jeso Tompo ! Vatolampy\nVoazaha toetra ;\nVato tsy mba misy tsiny,\nSady tsara endrika.\n:,: Vato soa :,:  (in-2)\nDia ifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (500, 3, 'Jeso Tompo ! Vatolampy,\nVato fehizoronay ;\nEfa nampiray fanahy\nNy fitianao anay\n:,: Fehizoro :,:  (in-2)\nVato fampiraisana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (500, 4, 'Jeso Tompo ! Vatolampy,\nTsy miova Hianao ;\nManan-jara ny matoky\nNy tsy fivadihanao\n:,: Fa ho faly :,: (in-2)\nKa tsy hanan-kenatra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (501, 1, 'Jeso ! ny hamarinanao\nNo lamba hitafianay,\nAmin’ny andro farany,\nIzany hifalianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (501, 2, 'Raha hitsangana izahay\nHandray ny lova aminao,\nDia tsy ho resy lahatra,\nFa Kristy no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (501, 3, 'Io lamba io no mbola vao,\nNa levona izao tany izao,\nHaharitra tsy rovitra,\nHavaozin’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (501, 4, 'Ny hery sy ny antranao\nToroy izao tontolo izao ;\nAmpandrosoy ny teninao,\nAmpanekeo ny rafinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (501, 5, 'Ny maty hahare ny feo\nHiantso azy ho velona ;\nJeso ! ny hamarinanao\nNo lamba hitafianay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 1, 'Jeso no sakaiza tena tia  anay,\nIzy ta-hamonjy olombelona ;\nAoka  ho tiantsika ny\nMpanavotra,\nFa Izy no Sakaiza\nmahateho tia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 2, 'Jeso no Sakaiza ifaliana,\nIzy nahafoy ny voninahitra,\nMba hananantsika fanavotana\nSy handraisantsika\nny fiainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 3, 'Jeso no Sakaiza itokiana,\nIzy mahavaly sy mahanatra ;\nAoka hinoantsika\nny Mpanavotra,\nHahazoantsika fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 4, 'Jeso no Sakaiza ivavahana,\nIzy mahavonjy ny \nMpangataka ;\nAoka hinoantsika ny \nMpanavotra,\nMba handraisantsika\nfanampiana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 5, 'Jeso no sakaiza fiarovana,\nIzy maharesy ny mpanohitra ;\nIza no hanimba izay arovany ?\nAoka hisaorantsika ny\nMpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (502, 6, 'Jeso ô, Sakaiza ! raisonao\nizao\nNy fisaoran-tsara izay\natolotray ;\nIzahay mpanota te-hidera\nAnao,\nRaiso re ny dera izay\naterinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (503, 1, 'He ! matoky aho, Tompo,\nAzoko ny teninao ;\nTeny soa mahafaly,\nTeny avy Aminao.\nHe ! matoky aho, Tompo,\nAzoko ny teninao ;\nTeny soa mahafaly\nNo omenao ahy  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (503, 2, 'Tahotra  amana ahiahy\nDia ariako izao ;\nFa nomenao toky aho\nSady tô ny teninao,\nHe ! matoky, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (503, 3, 'Izay rehetra fahotana\nEntiko hesorinao ;\nHianao no mahadio\nKa diovy aho izao.\nHe ! matoky, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (503, 4, 'Mahasasa, mahadio\nMahavonjy Hianao,\nKa mandray sy mahatondra\nTsara ho ao an-tranonao.\nHe ! matoky, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (503, 5, 'Jeso Tompo ! avy aho,\nAvy hankalaza Anao ;\nRaisonao re izay omeko ;\nVola, tena, aina koa.\nHe! matoky, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (504, 1, 'Jeso Sakaizanay,\nJeso anay ;\nSady tsy mahafoy,\nJeso anay ;\nRaha ny olona\nMety mandao anay,\nJeso no tsy mandao,\nJeso anay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (504, 2, 'Jeso Sakaizanay,\nJeso anay ;\nIzy no Avotray,\nJeso anay ;\nMaty ny Tomponay\nNoho ny helokay ;\nAfaka izahay,\nJeso anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (504, 3, 'Jeso Sakaizanay,\nJeso anay ;\nIzy no aronay,\nJeso anay ;\nReraka izahay\nNoho ny adinay,\nJeso no herinay,\nJeso anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (504, 4, 'Jeso Sakaizanay,\nJeso anay ;\nIzy no lalanay,\nJeso anay ;\nLalana marina \nHahatongavanay\nAny an-danitra,\nJeso anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (504, 5, 'Jeso Sakaizanay,\nJeso anay ;\nManana izahay,\nJeso anay ;\nJeso Sakaizanay\nNo hovantaninay\nAny an-danitra,\nJeso anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (505, 1, 'Ry Jehovah Tomponay !\nOlom-bery izahay,\nNefa tsy mba foinao,\nFa vonjen’ny Zanakao;\nZanahary, Aba Ray,\nJeso Kristy, Havanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (505, 2, 'Lehibe ny indrafo ;\nIza re no nanampo\nFamonjena taminao ?\nKanjo azonay izao ;\nZanahary, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (505, 3, 'Gaga izahay izao !\nBe fitia Hianao;\nAvotr’aina azonay,\nHody aminao izahay,\nZanahary, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (505, 4, 'Jeso ô ! Mpamonjy soa\nSy mpamindra fo tokoa !\nAvy ny navotanao\nKa midera Anao izao;\nZanahary, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (506, 1, 'Tsara lova, ry Jeso ô !\nNy mpanota voavotrao,\nFa fonenana soa\nAmboarinao ao,\nHanefanao ny teninao ;\nHo amin’io tany io tokoa\nNo izoran’ny dianay ;\nJeso Tompo no ao, \nSady efa manao\nTrano soa hipetrahanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (506, 2, 'Tsara lalana, Jeso ô !\nNy vahiny tarihinao ;\nRaha misy manjo,\nTsy mba kivy ny fo\nFa tohanan’ny herinao ;\nHo amin’io, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (506, 3, 'Tsara vatsy  ry Jeso ô !\nNy mpandeha fahananao,\nFa ny teninao soa\nMofon’aina tokoa \nIveloman’ny olonao.\nHo amin’io, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (507, 1, 'An-dafin’ny efitra lava sy\nmaina\nNo misy ilay tany izay\nmamin’ny fo\nIlay tany izay efa\nnampanantenaina\nHo lovan’ny mino izay\nmankatò\nIriko indrindra ny mba\nho tafita,\nTsy mety miadana\nmandra-pahita.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (507, 2, 'Anefa ny lala-mankany \nno mafy,\nNy rafiko maro manotrika ao,\nNy nofo indrindra no tena\nmpandrafy,\nKa tery ny lalan-kaleha izao !\nAnefa tsaroako tsara ny teny\nMilaza fa misy fitsaharana \neny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (507, 3, 'Ka aza mba ketraka,\nry mpivahiny,\nHevero fa Jeso no momba\nanao ;\nIlay nampangina ny loza \nfahiny\nDia mbola mahay\nmampandresy anao !\nAndrasonao kely, ho lany\nny fetra,\nHo ringana ny fahavalo\nrehetra', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (507, 4, 'Izaho hiezaka mafy tokoa\nHahazo ny loka ao\nalohako ao ;\nNy tafio-divotra mba\nmahasoa,\nFa hahatongavako\nfaingana ao ;\nIndray mipi-maso ny eto\nan-tany\nNy any an-danitra tsy\nhita lany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (508, 1, 'Ny alina ho lasa,\nNa ela aza re,\nHipoaka ny andro\nMahafinaritra ;\nNy zavona hisava\nAn-tendrombohitra ao,\nNy vavahady tsara \nHo tsinjoko izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (508, 2, 'Mba sarotra tokoa\nNy fiakarana e !\nMamely mafy koa\nNy rivo-doza be ;\nFa he ny mpivahiny\nIzay efa trotraka\nTantanan’ny Mpamonjy \nTsy ho solafaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (508, 3, 'Ny famonjen’i Kristy,\nNy fanavotany,\nNo loharano soa\nMangarangarana ;\nNandramako sahady\nNy rano velona,\nKa dia nahazo hery\nAm-pandehanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (508, 4, 'Ny rano be hitànay,\nNy fefy tsilo koa,\nNy lalana mahàna\nHo afaka avokoa ;\nKa dera, Haleloia,\nNo mendrika hatao\nAmbara-pahatonga\nAm-pitsaharana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (509, 1, 'Rehefa vita ny asako etý\nKa ny fanahiko raisina erý,\nMiara-mitoetra Aminao,\nTomponay,\nNo hasambarako\nmandrakizay\nMandrakizay, sambatra re,\nAzoko ilay fahasambarana\nbe !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (509, 2, 'Rehefa ataoko veloma ny etý\nKa azoko ‘lay paradisa erý,\nSady mahita ny tavan’ny Ray\nNo fahasambarako \nmandrakizay ;\nMandrakizay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (509, 3, 'Miombom-pahasambarana\narý\nAmin’ireo olon-tiako tatý\nKa tsy misaraka mandrakizay\nNo hasambarako\nmandrakizay ;\nMandrakizay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (510, 1, 'Mba maniry hody aho,\nRy Jesosy Tompo ô !\n:,: Ka ny foko hatanjaho\nMba hankany aminao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (510, 2, 'Tsy tamana eto aho\nMampahory ny atý,\n:,: Koa dia mitalaho,\nMba hafindranao arý :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (510, 3, 'Hody aho, ry Mpiantra,\nAoka mba horaisinao.\n:,: Hianao dia feno antra,\nKa matoky aho izao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (511, 1, 'Ny masoandronay\nMirefarefa izao,\nMadiva hody izahay\nHiverina Aminao ;\nRy Jeso ô ! sasao,\nSasao madio izahay,\nHo mendrika ny lanitra\nIzay honenanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (511, 2, 'Tsy ela intsony koa\nIty ady lava ity,\nHo afa-tsasatra tokoa\nRehefa tonga arý :\nRy Jeso ô ! sasao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (511, 3, 'Madiva ho hitanay\nIlay Tomponay arý,\nMadiva hihaona aminay\nNy namanay tatý :\nRy Jeso ô ! sasao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (511, 4, 'He ! kely sisa izao\nNo hisentoanay,\nDia ho fafan’ny tananao\nNy ranomasonay :\nRy Jeso ô ! sasao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (511, 5, 'Ity hita maso ity,\nIzay tsy mahefa-po,\nHisolo paradisa arý,\nIzay hifalian’ny fo :\nRy Jeso ô ! sasao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (512, 1, 'Ravoravo re ny foko,\nFa Jesosy ao am-poko,\nIzy no tsaroako\nAmin’ny alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (512, 2, 'Andro fohy, mora lany\nNo mba ahy atý an-tany,\nFa ny ao an-danitra\nDia tsara lavitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (512, 3, 'Tsy mba misy ota any,\nKa tsy hisy hitomany,\nTsy mba hisy ny manjo,\nNa ny alahelom-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (512, 4, 'Any aho no hahazo\nZava-tsoa tsy halazo,\nSatroboninahitra,\nHaingon’ny nanafika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (512, 5, 'Dia ao no hahitako\nSady tena hiderako\nAn’i Jeso avotro.\nMifalia re, ry fo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (513, 1, 'He, tanim-pahazavana be,\nTsy misy maizina ao,\nKa any tsy mba hitete\nNy ranomasonao.\nIzay voavonjy hiangona ao,\nHiara-mihira ny hira vaovao,\nHo sambatra any an-danitra,\nAo amin’ny Mpanavotra.\nMoa ao an-danitra ?\nEny, ao an-danitra.\nKa tsy hisaraka ?\nEny, tsy hisaraka !\nHo sambatra any an-danitra\nAo amin’ny Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (513, 2, 'Ry Rainay feno indrafo,\nBe fitiavana,\nOmàny ny fanahiko\nHandray fiainana.\nIzahay, ry Jesosy, maniry\nAnao.\nFa maty nisolo anay Hianao,\nNanavotra anay mba ho afaka.\nKa tsy ho voaheloka.\nMoa dia ho afaka ?\nEny, ho afaka.\nKa tsy ho helohina ?\nEny, tsy ho helohina.\nJesosy Mpamonjy nanafaka,\nKa tsy ho voaheloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (513, 3, 'O, ry Jesosy Tompo ô !\nTsarovy izahay,\nKa mba ambino hatrizao\nNy fandehananay.\nVonjeo ny ondry manaraka\nAnao\nFa otany no namonoana\nAnao,\nHo faly   izahay ao an-danitra\nAo amim-boninahitra.\nMoa ao an-danitra ?\nEny, ao an-danitra.\nKa tsy hisaraka ?\nEny, tsy hisaraka.\nHiara-mifaly  ao an-danitra,\nHo feno voninahitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (514, 1, ':,: Te-hifindra :,:\nAho, ry Jesosy ô !\nBe manjo atý an-tany,\nFifaliam-be no any\nAminao, ry Tompo ô.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (514, 2, ':,: Ny anjely :,:\nSy ny olo-masina\nTafahaona ka mihoby :\nSambatra ny tafatody,\nFaly fa  noraisina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (514, 3, ':,: Faly koa :,:\nAho, ry  Mpamonjiko.\nManantena fa hahita\nNy fonenana ao am-pita,\nKa handray ny lovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (514, 4, ':,: Tsy tamana :,:\nAho, amin’ny manjo,\nFa vahiny atý an-taný\nKa maniry mba ho any\nAminao, ry Tompo ô.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (514, 5, ':,: Ao tsy misy :,:\nOta na aretina !\nTsara raha hodimandry,\nNefa aho te-hiandry\nNy fetr’andro marina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (515, 1, 'Hitsahatra aina, ry foko,\nizao ?\nKa iza ataonao hiaro anao ?\nNy tany va misy hitsaharan’\nny fo,\nNa lova tsy simba, tsy mety\nho lo ?\nTsia, tsia, tsy mba atý izany,\nFa any an-danitra erý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (515, 2, 'Afoizo ny tany izay manindao\nKatsaho ny lanitra ho lovanao\nJerosalema any, tanàna \nvaovao\nSy tsara tokoa, miandry anao,\nEny, eny, ao dia ao \nNo misy tokoa\nfonenam-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (515, 3, 'Ny mponina ao dia tretrika\nam-po.\nFa resy ny ota, mahery ny tò\nKa faly midera ny Tompo\nny ao ;\nNy mino hihira ny hira\nvaovao !\nIndro, indro, be dia be tokoa\nNy soa ao an-danitra ao re !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (516, 1, 'Vahiny eto isika\nKa monina amin-day,\nMitady mba hahita\nNy any amin-dRay ;\nFa ao ny fanjakany\nSy fiadanana,\nKa ao tsy hita lany\nNy fitiavana,\nAo no fodiana,\nMihaona ao ny mino,\nKa tsy hisaraka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (516, 2, 'Izay atý an-tany\nDia mora tapitra,\nFa ny tsy mety lany\nDia ao an-danitra !\nFa ao ny Tompontsika\nIzay nohomboana\nMba hahazoantsika\nNy fifaliana,\nAo no fodiana, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (516, 3, 'Manafotra ny tava\nNy ranomaso be,\nKanefa dia hisava,\nFa hofafana e !\nIsika manantena\nFa ny Mpampakatra\nHo tonga mba hitsena\nNy ampakarina.\nAo no fodiana, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 1, 'Jesosy no Mpiandry tsara maty tia,\nNy rany nampiray antsika olo‑\nFa Izy no mandidy hampifa­nkatia\nIzay te-ho olony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 2, 'Ka manahafa Azy Tomponaa, ry mino,\nTsarovinao ny didy izay nato­loriyy,\nFa tiany hifankatia isika mpino, Fa samy olony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 3, 'Tsarovy fa iray tam-po izao isika ;\nTsarovy re fa samy mpiantafika ; Miraisa hina mba hiaraka tafita Ho ao an-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 4, 'Ka aza mba mijery aaina an‑\ntava ;\nMifanaralra tsara sy mifamelà,\nFa aza tia fotsiny amin'' ny\nvava ;\nDia aza mifandà.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 5, 'Mifankatiava ary mifanaova soa ; Mifandefera tsy hiavona am-po ;\nVonoy re ny lolompo sy t''osa koa, Ka aoka ho tao-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 6, 'Mora. fo, mihavàna, maleme fa­nahy,\nKa aza mety hanaratsy narnana ; Mifankatiava ré, toy ny mpira­halahy\nMiara-dàlana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (517, 7, 'Ka mivavaha re ho an'' ny raha­lahy,\nHiraisantsika tsara, fa iray manjo ;\nNy fo izay tia tsy mba mampia­hiahy,\nHoraisin'' i Jeso.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (518, 1, 'Fitiavana rano velona,\nLoha.rano lalina ;\nKa miafina ao anatiny\nNy fiainan-danitra.\n\nFitiavana no nirahina\nAvy tamin-Jehova;h ;\nO, mba raisonao, dia ho fanta­trao\nNy fahasarnbarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (518, 2, 'Fitiavana didy tokana\nAry didy lehibe ;\nMitoera ao, hitoeranao Arnim-piadanam-be.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (519, 1, 'Ry Jeso loharanon’aina,\nIzay foto-pitiavana !\nNy fonay toy ny tany maina\nFenoy fitiavana !\nAidino ao ny tena aina\nMba hampifankatia  anay,\nFandrao ho tonga very maina\nNy fitiavanao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (519, 2, 'Ny hasomparana levony,\nSoloy fitiavana ;\nNy lolompo ampijanony\nOvay ho fihavanana ;\nNy fo malaky mankahala\nAtaovy tia namana ;\nAmpianaro hahalala\nNy didim-pitiavana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (519, 3, 'Toroy sy ampahafantaro\nNy fitiavanao izahay ;\nMpiandry tsara ô, velaro\nNy fonao mba ho hitanay ;\nMba ho tarafiny tokoa\nNy fonay mbola maizina,\nKa samy hifanisy soa\nSy hifamela heloka.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (520, 1, 'Ry Jeso lohan’ny mpandefitra,\nNilaozanao ny voninahitrao,\nMba hahatonga anay \nmpiavona\nHanetry tena eo imasonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (520, 2, 'Ry tsy miavonavona am-po !\nIzaho te-hianatra aminao,\nMba hitsaharan’ny fanahiko\nFa mampiandam-po ny\nentanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (520, 3, 'Ny fietrenao, ry Voahombo\nô !\nNo iakaram-boninahitra ;\nNy tany sy ny lanitra vaovao,\nHtaonao lovan’ny \nmpandefitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (520, 4, 'Mifandefera sy mifanajà,\nRy samy voavela heloka !\nMiraisa hazo fijaliana\nHiraisantsika voninahitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (521, 1, 'Iray ny Ray Tsitoha,\nMpitia olombelona ;\nIray ny Tompo soa,\nIray ny rano velona,\nIray ny vavahady\nIzay idirana ;\nNy rafy sy ny ady\nDia  iombonana,\nIray ny fahantrana\nIlaozantsika atý,\nIray ny fanjakana\nAlehantsika arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (521, 2, 'Ry zana-Janahary,\nRy voavela heloka,\nMiraisa hina ary,\nKa mifandraisa tanana ;\nMifamelà isika,\nFa samy meloka ;\nMifanajà isika,\nFa samy zanaka.\nRy Jeso be fitia,\nAmpio ny olonao\nMba ho mpifankatia\nManahaka Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (522, 1, 'Misy didy tena tsara\nIzay nomen’ny Tompo hoe :\nMba mifankatiava tsara,\nHianareo izay mino e !\n:,: Ka miraisa :,:\nIzao no didy lehibe. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (522, 2, 'Tonga Jeso mba hanompo\nSy mba hampihavana ;\nTsy mpanaraka ny Tompo\nIzay tsy tia namana.\n:,: Ka tadiavo :,:\nIzay ho fampiraisana.(in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (522, 3, 'Fa iray tokoa ny Loha,\nDia Jesosy Zanaka,\nEndriky ny Ray Tsitoha,\nIzay halaina tahaka.\n:,: Tsy ho resy :,:\nRaha miray ny tafika. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (522, 4, 'Jeso no Ilay foto-kazo,\nTena voaloboka ;\nKa ny sampana halazo,\nRaha tafasaraka.\n:,: Ikambano :,:\nKa  aza mba mihataka. (in-2)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (523, 1, 'Finaritra ny manome\nIreo mahantra maro be,\nKa mahafoy fananana\nHiantra ny tsy manana.\nIzao no asa mahasoa\nIzay manendrika tokoa ;\nNy manome an-tsitrapo\nHitsinjo olona ory fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (523, 2, 'Ry olon’Andriamanitra !\nMba mihetseha fatratra\nHamonjy sy hanafaka,\nHanampy ireo madinika.\nIzay mamonkin-tanana\nMalain-kiantra namana,\nIzay mihirim-belona\nTsy hanam-piadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (523, 3, 'Ny vola izay nafoinao\nDia tsy mba very foana izao,\nFa ny mahantra no mandray,\nKa lasan’i Jehovah Ray,\nIreo kamboty mba vangio,\nMpitondratena mba jereo ;\nIzao no fivavahana\nIzay madio sy masina.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (524, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (524, 2, 'Mifankatiava tsara,\nJeso no mampihavana,\nFa  Izy no alalantsika\nAmin’Andriamanitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (524, 3, 'Mifankatiava tsara,\nMba hahatsara antsika,\nFa  fototry ny didiny\nSady anton’ny lalàny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (524, 4, 'Hitsahatra finoana\nMbamin’ny fanantenana,\nNefa ny fitiavana\nHaharitra mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (524, 5, 'Mifankatiava tsara,\nTsy maharatsy namana,\nAry tsy mahatezitra\nAn’Andriamanitra koa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (525, 1, 'Endrey, ny hamaroan’ny soa\nNomenao Ray ho ahy koa !\nNy aiko, he, navotanao,\nNy heloko vonoinao.\nEny, Raiko,\nEny fa ravo tokoa\nIty zanakao voavonjy re\nKa velombolo erý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (525, 2, 'He, azoko tokoa Jeso,\nIlay perla sao, madio sy vao ;\nNy zava-tsoan’ny tany re\nDia tsy ahoako tokoa.\nEny, Raiko, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (525, 3, 'Tsy haiko ny hilaza izao,\nHamisavisa re, ry Ray ;\nNy fanomezan-dehibe\nDia tsy voatonoko tokoa\nEny, Raiko, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (525, 4, 'Ny foko no hidera izao,\nHisaotra sy hanaja Anao\nHanoa, ho vonona hanao\nNy sitrakao rehetra.\nEny, Raiko, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (526, 1, 'Finaritra ny masoko\nMijery ny Mpanjakako ;\nNy vavako hisokatra\nHidera ny Mpanavotro ;\nAndro soa, andro soa,\nFa Jeso no Mpanavotra ;\nHe ! ravo ny fanahiko\nMihira ao anatiko,\nAndro soa, andro soa,\nFa Jeso no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (526, 2, 'Ho tiako ny Tompoko\nAmbara-pahafatiko,\nFa Izy no Mpanafaka\nManaisotra ny heloko ;\nAndro soa, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (526, 3, 'Faingàna re, ry havako !\nHatòny e ! ny Tomponao ;\nAndeha mba hiaraka\nHihira sy hivavaka ;\nAndro soa, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (527, 1, 'Faly dia faly izahay \nmpanomponao\nIzay mihira eto anatrehanao ;\nFa be fiantra Hianao, ry Jeso\nTompo tianay !\nKa feno haravoana tokoa\nizahay :\nFaly dia faly izahay \nmpanomponao\nIzay mihira eto anatrehanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (527, 2, 'Ela dia ela no nandavanay\nAnao,\nBe ny zava-dratsy efa vitanay,\nNa diso aza izahay, anefa faly\naminao,\nFa afakao ny helokay, ry\nTompo tianay !\nFaly dia faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (527, 3, 'Sarotra tokoa ny alehanay\nizao ;\nBe ny alahelo iaretanay ;\nNa ory aza izahay, dia mbola\nfaly aminao,\nFa levonao ny tahotray, ry\nTompo tianay !\nFaly dia faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (527, 4, 'Manantena tsara izahay\nmpanomponao\nMba handray ny zaza\ntehirizinao ;\nRehefa maty izahay ka entinao\nan-danitra,\nHo azonay ny lova soa\nhatolotrao anay ;\nFaly dia faly ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (528, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (528, 2, 'Hitako ny lalan-tsara,\nKa hizorako izao,\nAzoko ny famonjena,\nKa hotanako tokoa\n:,: Manampia :,:\nRy Fanahy Masina ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (528, 3, 'Ravoravo aho, Jeso !\nHianao no Tompoko ;\nFaly aho, ry Mpamonjy !\nAfakao ny heloko ;\n:,: Gaga aho :,:\nNoho ny fanavotanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (528, 4, 'Fitiavana ihany\nNo natolotrao anay :\nNefa kosa fahotana\nNo navalinay Anao :\n:,: Aza isaina :,:\nRe ny hadisoanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (528, 5, 'Ny mpanota hilazako,\nNy Mpamonjy tànako ;\nHilazako fa ny rany\nNo nataony avotra :\n:,: Matokia :,:\nHe, ny fitiavany !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (529, 1, 'He, izany hira manako re !\nIlay zaza very tonga\namin-dRay\nHe ! faly faly izay mandre :\nIlay efa maty  velona indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (529, 2, 'Indro ny Rainy lasa nankao,\nHe ! fa miteny : « Peratra soa,\nSoloy ny lamba, kapa alao »\nIlay efa maty  velona indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (529, 3, 'He, izany hoby an-tranon-\ndRay !\nFa voavonjy ilay nania ;\nHobio ry tany sy lanitra !\nIlay efa maty  velona indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (529, 4, 'O, ry anjely an-dapa soa !\nMiraisa hina hanandra-peo ;\nAza mangina, fa mba lazao,\nIlay efa maty  velona indray.', 'False');
