INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (531, 1, 'Mihoby ny fanahiko,\nFa voavonjy izao ;\nNy ratran’ny Mpamonjiko\nNo misy aim-baovao.\nNy ran’i Jesosy\nNo tena avotro ;\nKa sambatra aho\nSy tretrika am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (531, 2, 'Ny ratran’ny Mpamonjinao,\nRy foko, mba jereo,\nFa hanasitrana anao,\nKa ho fiainanao,\nNy ran’i Jesosy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (531, 3, 'Mba mibitaha re, ry fo,\nNy gadra tapaka :\nNy hajambana izay nanjo,\nIzao dia  afaka,\nNy ran’i Jesosy, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 2, 'Misaora an’i Jehovah,\nMiantsoa ny Anarany ;\nMitoria ny asany,\nAmin’ny ny olombelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 3, 'Ambarao ny fiantrany,\nLazao ny famindrampony,\nFa mahagaga antsika\nNy fitiavany antsika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 4, 'Mpanota tsy naringana,\nVery mana-Mpanavotra,\nMaty ka nomeny aina,\nHo velona mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 5, 'Ry Andriamanitray ô !\nRy Jesosy Mpamonjy ô !\nRy Fanahy Masina ô !\nVonjeo ny olona ory.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (532, 6, 'Fa zohy no itoerana,\nLava-bato no ierena\nFa indrafo no ifalian’\nNy mpivahiny mandala.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (533, 1, 'Endrey, izany hafaliana\nFa velona  aina izahay\nTanteraka ny faniriana\nKa dera ho Anao, ry Ray !\nAtolotray tokoa ny tena\nMba ho fanati-tsitrakao,\nSatria manaiky sy ekena\nHo tonga olom-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (533, 2, 'Ny Tanindrazanay malala,\nRy Jeso Tompo, anjakao\nHiorina eto, tsy hiala,\nNy fiadanana entinao !\nNy aizin’ota  anie hisava,\nNy fahantrana koa handao\nFa ho tarafin’ny Mazava\nHanjary tany vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (533, 3, 'Ny Fiangonanao angony,\nFanahy ô, ho tena iray,\nKa tsy hizarazara intsony\nFa Hianao no mampiray,\nDia ho fanasina, fanilo,\nHo an’izao tontolo izao ;\nHijoro, tsy handraraka ilo,\nHitafy toetra vaovao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (534, 1, 'Haleloia, haleloia !\nJeso no Mpamonjiko, \nHaleloia, haleloia !\nAfaka ny heloko.\nSambatra  aho, tretrika aho,\nAhy re ny lanitra,\nHaleloia, haleloia ! \nJeso no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (534, 2, 'Haleloia, haleloia !\nRavoravo re ny fo,\nHaleloia, haleloia !\nJeso re no soloko.\nAfaka aho, afaka aho,\nLevona ny otako ;\nHaleloia, haleloia ! \nLanitra no lovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (534, 3, 'Haleloia, haleloia !\nMatokia hianao.\nHaleloia, haleloia !\nMaty mba ho solonao.\nJeso Tompo mahatoky,\nRy mpanota very ô !\nHaleloia, haleloia !\nRaiso ny famindrampo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (535, 1, 'Ry Jeso Masoandronay ô !\nNy aizin’ny fo lasa tokoa,\nFa tonga aty anatinay izao\nMazava lehibe.\nEny, Jeso ô, Zoky be fitia,\nSoa lehibe (no) nentinao tatý\nAvy izahay mba hisaotra\nAnao\nNoho  ny fitiavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (535, 2, 'Vangianao ny tokantrano\nKa menatr’erý, kaody sy lao\nNy fomba ahitana angano\nIzay tsy mba sitrakao.\nEny, Jeso ô, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (535, 3, 'Mpanjaka Fiadananay ô !\nEkena tokoa ny herinao :\nNy sabatry ny firenena\nHatao fangady izao.\nEny, Jeso ô, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (535, 4, 'Ry Jeso Tompo tia anay ô !\nMisaotra Anao izahay izao\nFa henika ny hasoavanao\nIzao tontolo izao.\nEny, Jeso ô, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (536, 1, 'He, Jehovah no Mpiandry ahy,\nTsy mba misy atahorako ;\nRaha Izy no mitondra ahy,\nAhi-maitso no handriako.\nEny, fa ny tany karankaina,\nDia hody saha lonaka,\n:,: Ary ao amoron-dranon’aina\nHialan’ny foko sasatra :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (536, 2, 'He, Jehovah no Mpitarika ahy,\nAo an-dàlam-pahamarinana,\nKoa tsy ho kivy ny fanahy\nAmin’ny alo-pahafatesana ;\nHianao ry Tokiko irery,\nNo hiara-dia amiko,\n:,: Ka ny tsoran-kazonao\nmahery\nNo hanafaka ny tahotro :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (536, 3, 'Amelaranao latabatra aho\nEo imasom-pahavaloko ;\nNotoloranao kapoaka aho,\nKa mahery sady afa-po ;\nEny, indrafo tsy hita lany\nNo hiaraka  amiko izao\n:,: Mandra-pahatongako ho\nany\nHanatrehako ny tavanao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (537, 1, 'Jesosy no asandratro,\nFa Izy no Mpanavotro,\nNy tanany nanafaka\nNy aiko tao an-davaka ;\nNy rafiko ho ketraka,\nIzaho afa-kenatra,\nFa manana ny Tompoko, \nIzay mandako sy aroko :\nRy foko ô ! ankalazao\nJesosy, Izay Mpamonjinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (537, 2, 'Ny tsiron’ny fiainana\nAndramako sahady re,\nFa efa mby an-tanana\nNy famonjena lehibe ;\nNy ranon’aina azoko,\nNy mofon’aina haniko,\nSakafo mahatanjaka,\nMamelona ny reraka ; :\nRy foko ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (537, 3, 'Ny rany no anjarako,\nNy ainy no fananako ;\nNy lanitra ho lovako,\nNy tany dia hilaozako\nNy ota tsy hanaraka\nNy ady dia hitsahatra\nNy aloka ho tapitra,\nFa ao no tena zavatra ;\nRy foko ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (537, 4, 'Ny hanitry ny lanitra\nMamerovero fatratra ;\nNy hiran’ny voavotra\nManatsika ahy hiakatra\nNy tavan’ny Mpamonjiko\nNo tsijon’ny fanahiko,\nNy tavany manafaka\nNy aizina ao anatiko ;\nRy foko ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (537, 5, 'Ny masoko mibanjina\nTahaka ny mpiambina,\nManiry ny Mpamonjiko\nHo tafahaona amiko,\nIzaho hovimbininy\nHipetraka  ao anilany,\nHanao ny hiran-danitra\nSy honina finaritra :\nRy foko ô ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (538, 1, 'Ry Tompo ô, ny tany maina\nNo itoerako ;\nMidina re, ry Andon’aina\nHo ao anatiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (538, 2, 'Tsy aloka malaky lasa\nNo angatahiko,\nFa hery monina hiasa\nSy hanavao ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (538, 3, 'Ny afonao ampandreseo\nNy afo’nofoko ;\nNy sitrakao anie hekeko\nHo tena sitrako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (538, 4, 'He, tena zavatra ilaiko\nNy hohavaozinao ;\nNy ainao asoloy ny aiko\nHo velon-ko Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (539, 1, 'Tsy lany fahoriana\nIty tanintsika ity,\nFa be ny fihafiana\nIzay hitako atý,\nNy Tompo no jereko,\nRaha reraka ny fo,\nKa eo no fiereko,\nRaha  azon’ny  manjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (539, 2, 'Etý alaim-panahy,\nJeso, ny olonao,\nKanefa tsy manahy\nIzay nahita Anao,\nHianao no hojereny\nKa moa ho resy ve ?\nHianao no handreseny\nNy fahavalo be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (539, 3, 'Tsy azo ialana\nNy eo anilanao,\nMahita fanimbana\nIzay mahafoy Anao.\nAsain’ny fahoriana\nJerena Hianao,\nMahazo fifaliana\nNy maso aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (539, 4, 'Rehefa hialana\nIzao tontolo izao,\nNy tany tsy hitana\nIzay mahita Anao.\nKa dia hojereko,\nRy Tompo, Hianao,\nFa Hianao homeko\nIzao aiko kely izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (540, 1, 'Ry foko ô, mba atolory\nNy Rainao ny manjo anao ;\nNa mafy aza ny mahory,\nDia hovonjeny hianao ;\nNy lanitra no andrandrao\nHahafaka ny sentonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (540, 2, 'Andriamanitra mahita\nIzay hahasambatra anao,\nKa Izy no mahay mamita\nIzay zava-tsarotra aminao ;\nTsy hotsitsiny aminao\nIzay mety hahasoa  anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (540, 3, 'Ka aza kivy, ry mahantra,\nNa ory aza hianao,\nFa matokia ny Mpiantra,\nIzay tsy mahafoy anao ;\nNy amboariny izao\nDia  famonjena ho anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (540, 4, 'Dia  aoka hianao hatoky,\nRaha  mafy ny manjo anao,\nNy Rainao dia mahatoky,\nKa ampy hiankinanao ;\nTsy misy tsy ho afany\nRaha  avy ny fotoany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (541, 1, 'Any an-danitra ambony\nMisy mijery anao ;\n:,: He, feno antra ny fony,\nKoa mangina izao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (541, 2, 'Ry rera-po mitomany,\nJeso onena anao ;\n:,: Ampy ho toky izany,\nKoa mangina izao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (541, 3, 'Na dia be ny mahory,\nIzy hiaro anao ;\n:,: Tsy ho tra-doza akory,\nKoa mangina izao ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (541, 4, 'Mba mahareta miandry,\nIzy hanafaka anao ;\n:,: Haka anao hodimandry,\nKoa mangina izao ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (542, 1, 'Ry Tompo mahavonjy ô !\nHenoy ny vavakay,\nKa mamelà ny helokay ;\nFa tena tsy mba fantatray\nIzay tokony hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (542, 2, 'Ry Tompo be fiantra ô !\nMalemy izahay,\nKanefa raha ampianao\nDia hanan-kery toa Anao\nIzahay mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (542, 3, 'Ry Tompo izay Mpandresy ô !\nOmeo ny herinao,\nFa tianay handresena koa\nNy ota mety hahavoa\nNy olon-tianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (542, 4, 'Ry Tompo Ilay Mahery ô !\nAmpionao izahay,\nHijoro, hampiseho izao\nFa aminay tokoa Hianao\nRy Tompo tia ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (543, 1, 'Rahoviana no ho lany\nNy manjo atý an-tany ?\nJeso Vatolampiko\nAmpy hiankinako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (543, 2, 'Mahereza, ry fanahy\nAza manana ahiahy,\nJeso itokiako,\nHampionona ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (543, 3, 'Ny mahory dia mandala,\nRingana ny fahavalo,\nNy atý hilaozako,\nMba handray ny lovako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (543, 4, 'Mino aho, ry Mpamonjy,\nFa ho tena voavonjy :\nAfakao ny heloko,\nHianao isaorako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (543, 5, 'Dia ho lany ny mahory,\nKa ho samy tafavory\nEo anatrehanao,\nIzahay mpanomponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (544, 1, 'Ny Paradisa  an-tany\nTsy misy re izany !\nNy hazon’aina very,\nFa tsilo be no ao !\nRy foko, aza kivy,\nFa ao ny tany soa,\nNy Paradisa hisy,\nRy foko, andrandrao !\nTsy misy ory any,\nFa tretrika  avokoa,\nAfaka ady ka mihoby,\nMisy hazon’aina ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (544, 2, 'Mandrahona ny ady,\nMirehitra sahady,\nFa be ny fahavalo\nMamely ahy izao,\nRy foko, aza kivy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (544, 3, 'Ny eso, latsa, ratra,\nMatetika mihatra\nTsaroako avaly\nNy asa vitako.\nRy foko, aza kivy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (544, 4, 'Miferinaina eto,\nTomany sy misento ;\nIndrisy ! ory aho,\nVonjeo, Tompo ô !\nRy foko, aza kivy, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (545, 1, 'Raha  manahy ny fanahy\nAzom-pahoriam-be,\nKa ny aina mitaraina,\nRanomaso mitete,\nAza ketraka etý.\nFa tazony ny arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (545, 2, 'Ao miharo feo maro,\nFatratra ny hira ao ;\nAo mifaly ny nijaly,\nOry teo, ravo izao ;\nAo Jesosy Zokinao\nFaly hovantaninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (545, 3, 'Hazon’aina tsy mba maina\nNo maniry tsara ao,\nVoninkazo tsy halazo\nNo ho fifalianao ;\nFaly ny nandre izao,\nMainka fa ny tonga ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (545, 4, 'Ry Mpamonjy, ho tra-bonjy\nAnie ny olonao !\nFa maniry ny tahiry\nAo an-danitra  aminao ;\nMba faingàna Hianao\nHaka ny malalanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (546, 1, 'Ity fonenana ity\nTsy manam-pitsaharana ;\nManiry aho ho arý\nAn-koatry ny fasana ;\nNy lanitra no hitodiako,\nFa tena tanindrazako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (546, 2, 'Kanefa mahatahotra\nNy ranobe alehako,\nFa ny tafio-drivotra\nManohitra ny samboko ;\nNy tselatra, ny kotrokorana,\nMamely sy mandrahona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (546, 3, 'Ny sambon’ny fiainako\nMamaky onja sarotra,\nKa amin’ny alehako\nMitopatopa fatratra\nNy alondranom-pahoriana,\nKanefa tsy maninona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (546, 4, 'Fa ny Mpamonjy tokana\nMitarika ny samboko ;\nNy Tompon’ny fiainana\nMiara-mita amiko,\nMba ho tafita aho olony,\nKa honina  any aminy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (547, 1, 'He, vetivety ny atý an-tany,\nFa trano lay no itoeranao ;\nKa aza ketraka na mitomany,\nFa andrandrao ny\ntanindrazanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (547, 2, 'He, vetivety ao an-dalan-tery\nNo mahasarotra ny dianao,\nKa aza kivy, fa Ilay Mahery\nMamboa-trano hitsaharanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (547, 3, 'He, vetivety ao an-tany maina\nNo mahabe ny fijalianao,\nKa dia ho hitanao ny\nranon’aina\nHahafaka ny hetahetanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (547, 4, 'Arahonao ny lalan-toratady,\nMba hahafaingana ny dianao ;\nJesosy Tomponao no\nvavahady,\nNy lanitra no hovantaninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (547, 5, 'He, vetivety ary dia ho hita\nNy tany soa mandry fahizay ;\nJesosy ô, faingàna hahavita\nNy fikasanao tsara aminay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (548, 1, 'Mpivahiny aho eto\nTahaka ny razako,\nKa ny foko feno sento\nAmin’ny alehako.\nEny, tsy etý tokoa\nNo ho fitsaharako,\nFa mandalo avokoa\nIzay rehetra hitako,\nKa izaho tsy miato\nAndro aman-alina,\nNefa aho izay misento\nDia hasoavina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (548, 2, 'Be ny mampahory ahy\nMila tsy ho zakako,\nToa velona  hiahy\nAo am-pandehanako ;\nNefa kosa tsy mba tery\nSady tsy mamoy fo,\nFa izaho tsy irery\nEto amin’ny manjo,\nFa Jesosy momba ahy\nKa manafa-tahotra ;\nRaha ory ny fanahy,\nIzy no mahavotra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 1, 'Raha maizina ny lalana\nJehovah Tompo ô !\nHianao no iankinana,\nHianao no jiroko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 2, 'Tsy hitako izay hanjo,\nFa takona amiko\nNy andro sisa iainako,\nIzay nalahatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 3, 'Na mbola ela velona,\nNa maty haingana,\nNy zarako voatendrinao,\nTsy te-hifidy aho.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 4, 'Fa Kristy ivelomako\nRaha mitoetra ;\nNy faty tombondahiko\nFodiana aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 5, 'Ny fanompoako atý,\nTsy mahavesatra ahy ;\nTsy maika te-hiala etý\nRaha tsy antsoinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 6, 'Raha tapitra ny androko,\nDia tsara lavitra\nNy mody any aminao\nHiala sasatra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (549, 7, 'Ny sitrakao no sitrako,\nJehovah Tompo ô !\nNy teninao andrasako,\nHianao no tokiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (550, 1, 'Miandry ao ambony\nNy fifaliana ;\nAtý tsy mety tony\nNy fahoriana,\nKa tonga sesilany\nNy fitarainana ;\nArý tsy hita lany\nNy hasambarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (550, 2, 'Variana mikoràna\nNy tsy misaina be,\nNy hendry dia mitana\nNy hevitra hoe :\nAleoko mitomany\nNoho ny otako ;\nNy lanitra ihany\nNo fianinako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (550, 3, 'Ny lalan’ny adala\nTsy mba harahiko,\nTsy mampisalasala\nIzay anjarako ;\nNy sasany mikendry\nHo afa-po atý,\nIzaho dia miandry\nNy lovako arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (550, 4, 'Izaho manantena\nAnao, ry Tompo ô !\nKa hobim-pamonjena\nNo hoventesiko ;\nHe, vetivety ihany\nNo hiaretako,\nTsy ela dia hankany\nAn-tanindrazako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (551, 1, 'Ry mana-manjo !\nAkaiky anao\nNy Tompo, Ilay be fiantra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (551, 2, 'Ka Izy anie\nHitahy anao,\nHanafaka ny mampahory.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (551, 3, 'Ny ory atý\nHo faly arý,\nKa aza mba be alahelo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (551, 4, 'Ny Tomponao re\nHanefa tokoa\nIzay hataky ny malahelo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (551, 5, 'Ny lapany ao\nVoavoatra ho anao\nHonenanao mandrakizay re.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (552, 1, 'Tsy hita  izay androko sisa\natý,\nAhiko fandrao tsy ho tonga\narý ;\nRy fanahiko ! ao ny\nMpamonjy anao,\nHe ! Jeso Mpamonjy anao\n(in-3) \nRy fanahiko ! ao ny\nMpamonjy anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (552, 2, 'Izaho anefa tamàna atý ;\nTsy hiomana ve, fa hifindra\narý ?\nFa  efa fantatrao izao fa\nhavela ity\nHe ! lanitra soa arý (in-3)\nFa  efa fantatrao izao fa\nhavela ity\nHe ! lanitra soa arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (552, 3, 'Ilaozy ity, hadinoy, ry fo !\nAfoizo izao, ry fanahiko ô !\nFa  efa reko ny Tompo\nmiantso anao,\nHe ! feo mahafaly izao (in-3)\nFa  efa reko ny Tompo\nmiantso anao,\nHe ! feo mahafaly izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (553, 1, 'Mandrahona ny andronay,\nKa raiki-tahotra izahay ;\nAvia, manampia anay:\nRy Tompo ô ! vonjeo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (553, 2, 'Ny teninao no tokinay,\nNy herinao no tanjakay ;\nAfaho loza izahay :\nRy Tompo ô ! vonjeo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (553, 3, 'Fa misy ifaharanay,\nKa toky be no azonay ;\nHianao no iankinanay :\nRy Tompo ô ! vonjeo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (553, 4, 'Noho ny nahaterahanao,\nNoho ny nahafatesanao,\nSy noho ny fifonanao :\nRy Tompo ô ! vonjeo !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 1, 'Raha mafy ny manjo,\nRaha be ny hovitray,\nRaha malahelo fo,\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 2, 'Endrikay notafinao,\nNentinao ny entanay,\nNitomany Hianao,\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 3, 'Mafy ny nanjo Anao\nNefa tsy mba meloka ;\nHelokay ny nentinao,\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 4, 'Voahombo Hianao,\nVoatsindron-defona,\nNandry tao am-pasana,\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 5, 'Raha ory izahay,\nKa ny fitarainanay\nTonga eo an-tsofinao,\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (554, 6, 'Raha tapitra aminay\nNy fe-taom-boatendrinao,\nAza mba mandao anay,\nMihainoa, Jeso ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (555, 1, 'Aza malahelo,\nAza kivy fo ;\nIzay tia an’i Kristy,\nLoza tsy hanjo.\nJeso, be fiantra !\nMba avia izao,\nAry ny mahantra\nMba tantanonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (555, 2, 'Aza be ahina,\nMpino hianao,\nFa mba miankina\nAmin’ny Rainao.\nJeso, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (555, 3, 'Aza mba misento ;\nKristiana anie !\nFa homen’ny Tompo\nFifaliam-be.\nJeso, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (555, 4, 'Tomponay mahery !\nMba tsinjovinao,\nTano sao ho very\nNy mpanomponao.\nJeso, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (556, 1, 'Ry fanahy ory,\nAzon’ny manjo !\nBe ny mampahory,\nAza rera-po ;\nFeo mamelovelo\nMba henoy izao,\nAza malahelo,\nJeso havanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (556, 2, 'Aza mba misento\nNa mamoy fo,\nRaha mbola eto,\nMisy ny manjo ;\nNefa Jeso tia\nTsy mandao anao,\nKa mba matokia,\nJeso havanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (556, 3, 'Namanao mahantra\n‘Ndeha, ilazao,\nJeso be fiantra\nNo Mpamonjy izao ;\nIzy Tompo tia\nKa mandray anao,\nDia mifalia,\nJeso havanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (557, 1, 'Indrisy ! ry sakaiza ory fo !\nHolazainao aiza ny manjo ?\nJeso no Mpamonjy ho anao ;\nIzy mahavonjy, ambarao\n:,: Azy, ry tra-bonjy !\nny manjo ao am-po :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (557, 2, 'Indrisy ! ry mahantra !\nlozanao ;\nAiza ny Mpiantra ? Indro ao ;\nJeso hanazava aizim-po,\nIzy hampisava ny manjo ;\n:,: Soa lalandava\nNo hatao aminao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (557, 3, 'Harena be an-tany tsy anao ;\nZava-mora lany ka mandao,\nNefa indro Jeso Zaranao ;\nHira no venteso, ka derao\n:,: Izay nataon’i Jeso\nHo anao hatrizao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (557, 4, 'Ny lanitra mivoha rahateo ;\nAndrandrao ny loha ka jereo !\nJeso Tompo soa no arý,\nHavanao tokoa hatretý ;\n:,: Dia mazotoa\nHiditra ao Hianao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (558, 1, '« Mafy ny alon-drano izao,\nSady maria no manindao,\nNefa ny Tompo momba anao ;\nMoa ho kivy va ? »\n« Tsia, Jeso ato aminay,\nIzy hampifaly ny fonay ;\nTsy mba ho kivy re izahay ;\nIzy no tokinay. »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (558, 2, '« Maro no efa tola sy lao\nNoho ny alahelo izao ;\nIsan’ireny va hianao ?\nMoa ho kivy va ? »\n« Tsia, ..sns. »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (558, 3, '« Amin’izao fiainana izao\nTsy mba ilaozan-java-manjo,\nNefa  ao Jesosy, tokin’ny fo ;\nMoa ho kivy va ? »\n« Tsia, ..sns. »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (558, 4, '« Raha ny Tompo aza tetý\nNety ho faty tao Kalvary,\nIza no tsy handefitra atý ?\nMoa ho kivy va ? »\n« Tsia, ..sns. »', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (559, 1, 'Raha misy mampahory,\nIndro, Jeso Tomponao ;\nTsy afoiny tsy akory,\nAza kivy hianao.\nRaha rava ny harena,\nKa ho mafy ny manjo,\nMbola misy famonjena,\nAza mety kivy fo.\nRaha misy mampahory,\nIndro, Jeso Tomponao,\nTsy afoiny tsy akory,\nAza kivy hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (559, 2, 'Raha misy ady mafy,\nIndro, Kristy herinao ;\nRaha avy ny mpandrafy,\nRaiso koa ny sabatrao.\nRaha tamy hanangoly,\nIlay fahavalonao,\nKa mandrobo ny devoly,\nMba aingao ny vavakao.\nRaha misy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (559, 3, 'Na marary mafy aza\nKa mangirifiry ao,\nAry na ho faty aza\nKa handao ny havanao,\nMbola misy antenaina,\nIndro, Jeso avotrao ;\nFamonjena avotr’aina,\nIndro, raiso ho anao.\nRaha misy ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (560, 1, 'Jereo ny fahorianay\nAndriamanitra ô !\nMarary izao ny namanay,\nKa dia mba iantrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (560, 2, 'Raha sitrakao ry Tomponay !\nValio ny vavakay,\nFa mora reraka  izahay,\nTohano sy ampio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (560, 3, 'Ny fitondrana tianao\nNo iankinanay ;\nNy didy avy aminao,\nIzay no sitrakay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (560, 4, 'Sitrano ny fanahinay\nHo tonga zanakao,\nTariho lalana  izahay\nHankany aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (561, 1, 'Mahereza, ry sakaiza\nVotom-pahoriana !\nMatokia, aza kivy,\nKristy mialoha anao ;\n:,: Matokia :,:\nMahereza hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (561, 2, 'Aza mba matoky tena,\nFoana ny herinao ;\nTano mafy ny ampinga\n‘Zay atolony anao ;\n:,: Raiso koa :,:\nNy Fanahy ho sabatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (561, 3, 'Maizina izao ny andro,\nSarotra ny lalanao ;\nAo ambony ny mazava\nAmpy hanazava  anao ;\n:,: Mba jereo :,:\nIlay mazava amboninao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (561, 4, 'Vetivety, afa-doza,\nVita ny alehanao ;\nAo an-danitra ambony\nMisy hifalianao ;\n:,: Miravoa :,:\nTapitra ny adinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (562, 1, 'Aza kivy hianao\nAzom-pahoriana,\nTany no fonenanao,\nMisy fihafiana.\nMatokia hianao\nRaha tratrin’ny manjo,\nJeso Tompo herinao,\nAza mety rera-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (562, 2, 'Aza kivy hianao\nNoho ny manjo izao,\nRaha Jeso aminao,\nAfa-tahotra hianao ;\nMatokia hianao\nFa ny Tompo tia tokoa,\nKa izao rehetra izao\nDia mety mahasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (562, 3, 'Aza kivy hianao\nNa dia  ory aza  etý,\nNy Mpanjaka Tomponao\nMba nihafy koa tetý ;\nMatokia hianao\nNa inona mihatra izao,\nJeso Ilay Sakaizanao\nTsy mba mahafoy anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (563, 1, 'Ry Jeso ô ! Sakaiza\nMpamonjy tia anay !\nRaha mbola mianjera\nNy rian-drano be ;\nMifofofofo mafy\nNy rivo-doza be,\nVimbino izahay re,\nMba tsy ho faty ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (563, 2, 'Arovy izahay re,\nRy Jeso Tomponay !\nAmbara-pahalasan’\nNy loza izay manjo ;\nDia ento izahay re\nHo any aminao,\nKa raiso ny fanahy\nHo faly aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (563, 3, 'Kanefa raha mbola\nHijanona izahay\nAtý ambony tany\nKa malahelo fo,\nNa misy fahavalo\nMiady aminay,\nAvia Jeso Tompo,\nAmpio ny olonao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (563, 4, 'Tsy tambonay isaina\nNy olona aminao ;\nHiadana tokoa\nIzay horaisinao ;\nFa Jeso no Sakaiza\nAnjely maro be\nNo hiaraka  aminay ao\nAn-dapa rahatrizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (564, 1, 'Ry Sakaizan’ny mpanota !\nFantatrao ny zanakao,\nFa malemy, mora lavo,\nKa mangataka aminao ;\n:,: Mba todiho, mba todiho,\nKa henoy ny vavakay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (564, 2, 'Raha mitombo ny harena,\nNoho ny fanambinanao,\nNa matanjaka ny tena\nNoho ny fitahianao ;\n:,: Mba tantano, mba tantano,\nSao tsy mahalala Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (564, 3, 'Raha mamely ny devoly,\nFahavalon-janakao,\nKa mandroso manangoly\nHampisaraka aminao ;\n:,: Mba tazòny, mba tazòny,\nFa tsy mahatana Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (564, 4, 'Raha mangirifiry mafy,\nNoho ny tsorakazonao,\nKa ny havana rehetra\nTsy mahita izay hatao :\n:,: Mba tsidiho, mba tsidiho,\nFa maniry indrindra Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (564, 5, 'Raha tonga ilay fotoana\nHiverenana aminao,\nKa manahinahy foana\nNoho ny ratsy izay natao :\n:,: Mba tohano, mba tohano,\nHahatsiaro tsara Anao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (565, 1, 'Jeso Sakaizanay,\nSakaiza rahateo !\nIndreto izahay, vonjeo,\nFa be ny adinay :\nJeso, Sakaizanay,\nSakaiza rahateo !\nIndreto izahay, vonjeo,\nFa be ny adinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (565, 2, 'Jeso Sakaizanay,\nSakaiza be fitia !\nTariho izahay hanao\nNy zava-tianao :\nJeso, Sakaizanay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (565, 3, 'Jeso Sakaizanay,\nSakaiza tsy mandao !\nNa dia mpanota aza re\nIzahay mpanomponao :\nJeso, Sakaizanay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (565, 4, 'Jeso Sakaizanay,\nSakaiza tsy mamoy,\nNa ory na finaritra\nIzahay ankehitrio :\nJeso, Sakaizanay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (565, 5, 'Jeso Sakaizanay,\nOmaly sy anio,\nKa mbola ho Sakaizanay\nMandrakizay doria :\nJeso, Sakaizanay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (566, 1, 'Lohasahan-dranomaso\nIzao fiainantsika izao,\nKa misento lalandava\nIzay rehetra velona ao ;\nKoa aiza re no misy\nHampionona ny fo ?\nIza no mahay mamonjy\nHampisava ny manjo ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (566, 2, 'Manatòna ilay Mpamonjy,\nRy sakaiza ory ô !\nKa aza mba misalasala,\nMandrosoa hianao ;\nFamonjena mahatoky\nTsy miova no anao,\nRaha manana an’i Kristy\nHo fanala-tahotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (566, 3, 'Vola sy harena an-tany\nMety tsy hanananao ;\nHavana, sakaiza koa\nDia hisaraka aminao ;\nNefa Jeso no anjara\nTsy alaina aminao ;\nIzy no sakaiza tsara\nIzay nanavotra aina anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (567, 1, 'Aza mba mandalo ahy,\nRy Mpamonjy ô !\nAza mba manary ahy,\nJeso Tompo ô !\nRy Mpanjaka, mihainoa re !\nMamonje fa mitaraina\nNy vahoakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (567, 2, 'Fantatrao fa malahelo\nNy mpanomponao ;\nRaha mbola eto aho,\nMba tsinjovinao.\nRy Mpanjaka, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (567, 3, 'Indro ! fa miandry eto\nNy navotanao ;\nAoka tsy mba halahelo,\nMba falio izao.\nRy Mpanjaka, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (567, 4, 'Eny, manomeza hery\nMba ho ahy re,\nKa hahery sy handresy\nNy mpanomponao.\nRy Mpanjaka, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (568, 1, 'Ry be fiantra sy be indrafo !\nAo aminao misy soa ;\nOry izahay noho ny zava-\nmanjo\nAo aminao misy soa ;\nBe siasia an-dalana atý,\nSao tsy ho tratra ny tsara arý ;\nTompo miantra, vonjeo\nizahay,\nAo aminao misy soa ;', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (568, 2, 'Rahona mainty manarona\nanay,\nAo aminao misy soa ;\nMba hazavaonao izay dianay\nAo aminao misy soa ;\nAmpaherezo hanaraka Anao,\nAmpaherezo hatoky Anao ;\nTompo mahery, hazòny\nizahay,\nAo aminao misy soa ;', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (568, 3, 'Any an-danitra izay lapanao,\nAo aminao misy soa ;\nKa dia matoky izahay \nzanakao,\nAo aminao misy soa ;\nAza ilaozana akory izahay,\nSao dia very an-dalana indray\nTompo malala, tariho izahay,\nAo aminao misy soa ;', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (569, 1, 'Aiza re no hahitako\nIzay hampionona ny fo ?\nIza re no handrandraiko\nAndrao dia tolan’ny manjo ?\nO ! ry ory sy mahantra\nManatòna Ahy izao.\nHe, ny foko feno antra,\nHampiadana anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (569, 2, 'Aiza re no hahitako\nIzay hanala tahotra ?\nFa ny lalana ombako\nMahatoran-kovitra !\nAza kivy, ry mahantra ;\nManatòna Ahy izao.\nHe, ny foko feno antra,\nAmpy hiafenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (569, 3, 'Aiza re no hahitako\nIzay hamela heloka ?\nIndrisy re ity fahotako\nTena mahaketraka !\nRy sakaizako mahantra !\nMba minoa Ahy izao ;\nHe, ny foko feno antra,\nHanavotako anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (569, 4, 'Ry Jesosy ô, malala !\nFialofako Hianao ;\nIzaho tsy hisalasala\nHiantoraka Aminao.\nEny, manatòna Ahy\nFeno fitiavana ;\nAza manana ahiahy ;\nIzaho no mpiantoka.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (570, 1, 'Ry Jeso ô ! tahio izahay\nAtý am-pandehananay ;\nNy hadalanay avelao,\nNy fonay koa ovay ho vao,\nArovy re ny olonao,\nNy lalanay mba hazavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (570, 2, 'Ny aizim-be alehanay,\nMamely koa ny rafinay ;\nTsy fantatray izay hanjo\nKa be ny tahotra ao am-po.\nArovy re ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (570, 3, 'Mikasa mafy izahay\nHo tsara, nefa fantatray\nFa raha tsy ambininao,\nTsy misy azonay atao.\nArovy re ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (570, 4, 'Mba tano mafy izahay\nAmbara-pahafatinay,\nDia ento ao an-danitrao\nIzahay ho faly aminao.\nArovy re ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (571, 1, 'Mba tarihonao,\nTompo ô ! izahay,\nFantatray fa mpivahiny ;\nIzahy ankehitriny ;\nAny aminao\nNo honenanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (571, 2, 'Mafy ny manjo,\nReraka ao am-po ;\nNa dia be ny ady aza,\nTsy ho ketraka ny fonay ;\nAny aminao\nNo halehanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (571, 3, 'Alahelo be,\nRanomaso koa,\nAzon’ny fakam-panahy,\nSaiky resin’ny devoly ;\nFa ny Sandrinao\nIankinanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (571, 4, 'Any aminao\nNo honenanay ;\nRy Mpitari-dalan-tsara ;\nMba tarihonao izahay re,\nHahatongavanay\nAny aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (572, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (572, 2, 'Arovy ny fanahinay,\nAndriamanitra !\nTariho ny fanahinay, (in-2)\nHanaraka Anao. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (572, 3, 'Hianao no hiankinanay,\nFa tsara Hianao ;\nKa mamelà ny helokay (in-2)\nFa diso izahay. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (572, 4, 'Izahay hiantso Anao,\nAndriamanitra !\nKa henoy ny fivavaka (in-2)\nAtaonay aminao. (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (572, 5, 'Mitsinjova ny mpanota\nMahantra indrindra,\nFa Jeso, Ilay \nMpanavotra (in-2)\nNo maty nisolo (in-3)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (573, 1, 'Mahantra aho, Tompo ô !\nKa mba tsarovinao\nManiry ho madio fo\nSy mendrika Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (573, 2, 'Manolo-tena aho izao,\nRy Jeso Tompo ô !\nKa aza dia lavinao\nIzay atolotro.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (573, 3, 'Izaho voasoratrao\nAo amim-bokinao\nHo isan’ny mpianatrao\nHianatra aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (573, 4, 'Ataovy izay hahaizako\nHanaraka Anao\nAmin’ny fahadiovam-po\nHo voninahitrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (574, 1, 'Jesosy Kristy Tompo ô !\nHianao no antenaiko ;\nTsy misy Mpamonjy toa\nAnao,\nRy Heriko sy Aiko,\nFa tsy mba misy olona\nMahafaka ny heloko ;\nTsy zakakao ny entako,\nRy Jeso ô !\nHianao anefa tokiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (574, 2, 'Ny otako dia lehibe,\nNy foko dia ory !\nMpamonjy ô, vonjeo re !\nNy heloko esory,\nNy Rainao tsara ilazao,\nFa izaho no navotanao,\nKa mba havela heloka,\nDia azoko\nNy fiadanana ao am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (574, 3, 'Finoa-marina ao am-po\nNo aoka mba homenao,\nMba hanandramako tokoa\nNy tsirom-pamonjenao,\nMba hitiavako Anao\nSy ireo rahalahinao ;\nRaha  avy ny fotoako,\nO, mba vonjeo !\nKa mba reseo ny rafiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (574, 4, 'Ny saotra, dera, ho Anao,\nRy Raiko be fiantra,\nSy ho Anao, ry Kristy ô,\nMpamonjy ny mahantra,\nAnao, Fanahy Masina !\nIzay tena Mpampionona !\nIzaho mba omeonao\nNy aim-baovao,\nHaharitra ho olonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (575, 1, 'Jehovah Tompo no itokiako,\nFa mampitombo ny fifaliako,\nNy fo sy saiko miankina\naminy ;\nIzay ilaiko omen’ny taniny,\nKa tena aiko ny toro-heviny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (575, 2, 'Ampio aho, Jehovah Tompo\nô !\nFa mitalaho izao ny zanakao,\nOmeo hery hitanako ny to,\nMba tsy ho very ny rakitra\nao am-po ;\nHianao irery no iankinako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (575, 3, 'Ry Kristy Tompo, mba \nmamonje anay\nMba ho mpanompo mazoto\nizahay,\nMpiasa tsara ankasitrahanao,\nIzay manambara ny fitiavanao\nKa hiafara eo anatrehanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (576, 1, 'Tompo Mpamindra fo !\nMahery sy Tsitoha\nIzay nidina tatý\nHamonjy ny mania !\nMivavaka Aminao\nMifona e ! izahay,\nNy gadranay tapaho\nHahafaka anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (576, 2, 'Tompo ô ! Mpanjaka,\nAmin''ny olonao ;\nFaingàna, iantrao\nIreo izay narovanao\nMiondreha, mijere,\nIzahay niandrandra Anao ;\nNy herinao anie\nHatafinao ireo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (576, 3, 'Tompo Mpanova fo,\nMpampionona tokoa,\nMidina ka ampio\nIzahay hahery izao.\nToky mamy loatra\nNo omena anay ;\nHatrany an-koatra,\nArovinao izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (577, 1, 'Ry Jesosy Tompoko,\nIzay nanetry tena ô !\nNaka ny tarehinay\nHanavotanao anay\nMba omeo anay izao\nNy Fanahy Masinao,\nHanarahanay Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (577, 2, 'Zanak''i Davida ô !\nMba midira ao am-po,\nRaiso ka diovinao\nIzy ho tempolinao ;\nIzahay manasa Anao\nHonina ao am-po izao\nKoa mba ekeonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (578, 1, 'Kristy Tsitoha, Tomponay\nmahery,\nMba mitodiha re ny olom-\nbery,\nKa mamonje anay izay\nmania ;\nO, mba avia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (578, 2, 'He, Hianao kiadin’ny mivalo,\nKa mba reseonao ny fahavalo,\nNy ta-hanimba ny Anao\nsakano,\nIzahay tantano !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (578, 3, 'Ny fiangonanao ampiadano,\nNy tanindrazanay re mba\ntohano\nNy tena sy fanahinay tahio,\nAmpifalio !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (578, 4, 'Ny asanao, ry Zana-Janahary,\nDia manakalaza Anao\nmiharihary,\nKa izahay midera Anao\nMpamonjy,\nFa voavonjy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 1, 'Ry Jesosy, Izay Mpiahy,\nMba henoy izao ;\nRaha misy mamely ahy,\nMba reseonao !\nRy Jesosy, mba henoy izao :\nRaha misy mahory ahy,\nMba tsarovinao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 2, 'Raha avy ny devoloy\nKa mandrahona,\nRaha ny nofo manangoly\nSy manintona.\nRy Jesosy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 3, 'Be ny fahavalo fetsy\nMampahory izao,\nIzay dia mora resy,\nKa vonjeonao !\nRy Jesosy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 4, 'Raha misalasala aho,\nMba toroy ny to !\nAry raha mitalaho,\nAmindraonao fo !\nRy Jesosy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 5, 'Miangavy mafy aho,\nRy Mpamonjy ô !\nMba vonoy ny fahotako\nAmin’ny ranao.\nRy Jesosy, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (579, 6, 'Raha tapitra ny andro\nIvelomako,\nHazavao, ry Masoandro,\nAo am-pasako,\nRy Jesosy, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (580, 1, 'Be ny mampahory\nAtý, Jesosy ô !\nKely, osa, ory,\nNy mpanomponao.\nRy Mpiandry lehibe,\nAnao ny tenako ;\nHianao irery re,\nNo iankinako,\nTompo ô, ny foko\nManantena  Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (580, 2, 'Miarova ahy,\nJesosy Tompo ô !\nTena sy fanahy\nHasoavinao.\nRy Mpamonjy lehibe,\nAnao ny tenako ;\nHianao irery re,\nNo Vatolampiko,\nTompo ô, ny foko\nManantena  Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (580, 3, 'Miarova ahy,\nJesosy Tompo ô !\nTena sy fanahy\nHasoavinao.\nRy Mpamonjy lehibe,\nAnao ny tenako ;\nHianao irery re,\nNo Vatolampiko,\nTompo ô, ny foko\nManantena  Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (581, 1, 'Ry Jeso ô, Mpanjakanay,\nHadinonao va izahay\nNo tsy omenao faingana\nNy rano mahavelona ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (581, 2, 'He, maina sy mangentana\nSy ivesarana entana,\nKa mampiferinaina anay\nNy efitra onenanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (581, 3, 'Tsy hitanao ve, ry Tompo ô,\nFa valaka sy rera-po,\nMalain-kanaraka Anao\nNy olom-boafidinao ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (581, 4, 'Aidino ny Fanahinao\nSy fifaliam-po vaovao ;\nNy ranon’aina anie\nHatondrakao ny tany re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (581, 5, 'Reseo ny fahavalonay,\nOmeo rariny  izahay\nTapahonao ny gadranay,\nAmpiadano izahay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (582, 1, 'Ry Tompoko malala,\nMpiandry tsara ô !\nHianao no mahalala\nNy hasasarako.\nNy ota an-tapitrisa\nIzao mandreraka !\nKa manomeza ahy\nFiadanana !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (582, 2, 'Ry Tompoko malala,\nHe, zatra Hianao\nMampody ny adala\nNiala taminao ;\nAmpiadano aho,\nTariho lalana,\nKa mahareta Tompo,\nAza sasatra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (582, 3, 'Ry Tompoko malala,\nTe-hody aho izao,\nFa manina ny vala\nNy ondrikelinao ;\nKa mitalaho aho :\nVohay, ry Jeso ô,\nIzay mba efitrano\nHitoerako !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (583, 1, 'Ry Raiko ô ! mba raiso ny\ntanako\nTantano mba handroso ny \ndiako\nHazòny tsy hania ny zanakao,\nRy Raiko ô ! avia, tarihonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (583, 2, 'Ny foko ta-hanoa ny didinao,\nKanefa mora voan’ny ratsy\nizao\nKa dia mba fenoy ny herinao,\nRy Raiko ô ! henoy ny \nzanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (583, 3, 'Rehefa tsy mahita ny lalana\nKa toa tsy ho tafita ny lalina,\nFa ketraka ny aiko, dia \nraisonao\nTrotroy aho, Raiko,\nan-tratranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (584, 1, 'Indreto izahay mitomany,\nFa be alahelo ny fo ;\nNy ota no fototr’izany,\nNy heloko sy ny manjo ;\nFa reraka re, ny fanahy,\nMahantra sy be ahiahy,\nKa tena variana erý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (584, 2, 'Ry Tompo, Izay mofon’ny\naina,\nRy rano mamelona  anay,\nTsinjovy izay mitaraina,\nMahantra izao izahay !\nOnòny ny fonay fa voa,\nOmeo izay mahasoa,\nMonena aminay Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (584, 3, 'Ny fonay dia be hetaheta,\nManiry indrindra Anao ;\nRy Tompo, mba mampahareta\nAnay ho mpanaraka Anao ;\nNy reraka ataovy mahery\nVonjeo izahay olom-bery ;\nMatoky Anao izahay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (585, 1, 'O, ry Jesosy Mpamonjy\nmahery !\nIzaho maniry indrindra Anao,\nFa reraka aho ka mila ho very,\nNy foko saronan’ny ratsy izao.\nRy Tompo, tsilovy\nNy lalan’ny saiko,\nKa mba arovy\nNy fo sy ny saiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (585, 2, 'Efa hanafotra ahy ny rano,\nFaingàna, Jesosy, vonjeonao\nre !\nHo difotra aho, ry Tompo,\nka tano,\nDiovy, afaho ny foko anie !\nRy Tompo, tsilovy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (585, 3, 'Efa akaikin’ny lalina aho,\nKa mila ho voa, hilentika ao ;\nRy Tompo malala, ny foko\ntanjaho\nKa mba hazavao hahalala\nAnao :\nRy Tompo, tsilovy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (585, 4, 'Aiza izao Hianao, ry \nMpamonjy,\nRy Tokin’ny ory sy reraka ô.\nNo tsy mampiseho ny herin’ny\nvonjy\nHanafaka ahy, izay ory\nam-po ?\nRy Tompo, tsilovy ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (585, 5, 'O, ry Jesosy, Izay maharara\nNy onja namely ny sambo tarý\nAnivon’i Genesareta ho tsara\nToneo ny onjan’ny foko atý !\nRy Tompo, tsilovy ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (586, 1, 'Omeo mba handramako\nNy fitiavanao,\nHahaizako, ry Jeso ô,\n:,: Hihavana  Aminao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (586, 2, 'Iriko re ny tavanao,\nRy Ilay malalako,\nFa tiako ny hahita Anao,\n:,: Izay Sakaizako :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (586, 3, 'Hanjavona ny masoko,\nHamoy ny namako,\nFa Hianao Jesosy ô,\n:,: Ho eo anilako :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (586, 4, 'Raha  ao ny ali-maizim-be\nIzay handehanako,\nHo eo an-tananao anie\n:,: No hahafatesako :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (586, 5, 'Ka tsy mba hatahorako\nNy aizim-pasana,\nHatsanganao ny tenako\n:,: Ho tena sitrana :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (587, 1, 'O, ry Jesosy, mifaly ny foko\nRaha tsy lavitra  Anao,\nKa mba mangataka  indrindra,\nre aho\nAry mifona Aminao ;\nTano ny zanakao mba tsy ho\nvery,\nTano akaiky Anao !\nZaza adala ka mety miery,\nRaha dia lavitra Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (587, 2, 'Aza manary izay mitalaho,\nFa mba vonjeo anie ;\nTena mahantra sy ory re aho,\nDiso sy meloka be ;\nNefa maniry hiova ho tsara,\nKa ta-hanoa Anao,\nAry ny zavatra izay voarara,\nTsy mba hataoko izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (587, 3, 'Raiso, ry Tompoko, ny\nfikasako,\nRaiso ny foko anio !\nRaha ny marina no ialako,\nDia ahitsio sy ampio ;\nFa Hianao, ry Mpamonjy\nmahery,\nNo tsy mba mety mandao,\nAry Izaho dia tsy mba irery,\nFa tsy ho lavitra Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (588, 1, ':,: Ry Raiko ô ! :,:\nIzaho atý mpivahiny\nManina ny tranonao,\nKa mba tariho\nNy zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (588, 2, ':,: Ry Raiko ô ! :,:\nIndro, Satana milofo\nMba hanangoly anay,\nKa mba vonjeo\nNy zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (588, 3, ':,: Ry Raiko ô ! :,:\nMaizina sady malama,\nSarotra ny lalanay,\nKa mba tantano\nNy zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (588, 4, ':,: Ry Raiko ô ! :,:\nMaro ny zava-mihatra,\nIzay mampanahy anay,\nKa mba tohano\nNy zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (588, 5, ':,: Ry Raiko ô ! :,:\nAoka  anie ny Fanahy\nNo mba hitarika anay\nHo tonga any\nAn-tranonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (589, 1, 'Misaotra Anao, Jesosy ô,\nNy olom-boavonjy,\nFa be dia  be ny indrafo\nNataonao, ry Mpamonjy ;\nFa tonga ory Hianao,\nNy ainao no natolotrao\nNisolo ny mpanota', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (589, 2, 'Ampibebaho izahay\nHo olonao manoa ;\nNy ota ivesaranay\nEsory avokoa,\nNy fifaliana vaovao\nNo aoka mba hasolonao\nNy alahelom-ponay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (589, 3, 'Ampio izahay izao\nHinoanay ny teny\nIzay nolazain''ny vavanao,\nFa tanjakay ireny :\nIzao no angatahinay,\nMba hatanjaho izahay\nHanaraka izany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (590, 1, 'Ry Andriamanitry ny\nfamonjena,\nSy Kintana antenain''ny\nfirenena,\nHenoy ny fangataham-\npandresena,\nRy Ray Tsitoha !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (590, 2, 'Ilay fitopaton''alondrano\nSy reharehan-drafinao foano !\nNy olonao mahantra \nmba tantano,\nRy Ray Mpiaro !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (590, 3, 'Raha  osa, Hianao mahay \nmitana,\nRaha  reraka\nasiam-pahotana,\nNy herinao\ntsy resin''i Satana,\nRy Ray Mahery !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (590, 4, 'Ampionao izahay\nhandresy rafy,\nAmpionao mba haharitra\nny mafy,\nAmpionao tsy ho ketraka\nmihafy,\nRy Ray Mpanampy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (591, 1, 'He sambatra, Jesosy ô,\nNy ondry vitsinao !\nFa Hianao Mpiandry soa\nMaharo ny Anao ;\nNy ainao no natolotrao\nHo an''ireo ondrinao,\nNy very tadiavinao\nHiaraka  Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (591, 2, 'Izaho voamarika,\nHo isan''ny Anao,\nAnefa mba matahotra\nSao dia lavinao\nKa aoka mba hotairinao\nNy foko, raha hitanao\nFa mila ho sondriana\nSy diso lalana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (591, 3, 'Ny hafanana mahamay\nNo mahatrotraka,\nAnefa mbola tazanay\nNy fitsaharana.\nHo avy faingana anie\nNy andro soa lehibe,\nIzay hamelaranao ny lay\nHandrakotra anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (592, 1, 'Ento aho, ry Jehovah,\nMpivahiny raha etý ;\nTano aho fa malemy\nRaha  tsy hazoninao\n:,: Mofon''aina :,:\nHo fihinako anie !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (592, 2, 'Mba savao ny loharano\nHahazoako ny soa ;\nAoka  hitari-dalana  ahy\nIzay andri-rahonao,  \n:,: Ry Tsitoha :,:\nFiarovana  Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (592, 3, 'Raha hita  an''i Jordana,\nAmpandrio ny tahotro ;\nRy Mpandresy fanimbana,\nEnto any Kanana aho ! \n:,: Fiderana :,:\nNo hatolotro Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (593, 1, 'Jeso, Hery mahavonjy\nTena fialofana,\nFa miantra sy mamonjy\nZanak''olombelona\n:,: Hery soa, hery soa,\nItokianay tokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (593, 2, 'Raha misy ny tafiotra\nIzay mamely mafy anay\nNy fitiany tsy ho ritra\nTokinay mandrakizay\n:,: Tsy mandao, tsy mandao,\nIankinana hatrizay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (593, 3, 'Raha  mambomba sy miaro,\nMampitony tahotra,\nJeso Tompo tia, Mpiaro ;\nResy re ny hovitra,\n:,: Fa matoky, fa matoky,\nIlay ampinga lehibe :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (593, 4, 'Tianay tokoa  tokoa\nHo an-tratranao doria,\nMba hatoky ka ho soa\nRy Mpamonjy be fitia,\n:,: Tompo soa, Tompo soa,\nO ! hazoninay tokoa :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (594, 1, 'O, ry Raiko\nIzay Mpandahatra !\nVatofantsika mafonja !\nAminao no mifahatra\nItony foko safotra  onja,\nRy Tsitoha, ô ! mijere !\n:,: Ry Mpamonjy ô !\nMamonje ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (594, 2, 'He manonja mafy loatra\nItony fiainako voafofo\nToa dia diso tafahoatra\nIreo akaso izay milofo,\nRy Tsitoha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (594, 3, 'Nefa kosa tsy mamoy !\nIzany foko manantena !\nFa ny masonao manjohy,\nIandrandrako famonjena\nRy Tsitoha, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (595, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (595, 2, 'Jeso no Mpiambina antsika\nSy Mpiandry tsara,\nKa natolony ny ainy\nHisolo ny ondry.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (595, 3, 'Matokia hianareo,\nRy ondry vitsy\nFa ny Raiko no mikasa\nHitandrina anareo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (595, 4, 'Izaho no Mpitarika\nSy Mpitondra tsara\nIzay manaraka Ahy\nHandresy ny ratsy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (595, 5, 'Ary amin''ny farany,\nHalaiko ny Ahy\nTsy hisaraka amiko\nEla mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (596, 1, 'He miandrandra  Anao,\nJehovah Tompo ô !\nNy olonao;\nHenoy ny vavakay,\nVonoy ny helokay,\nHahafantaranay\nFa Zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (596, 2, 'Raha  hodiavinay\nNy tany maizina,\nTarihonao ;\nFafao ry Jesosy\nNy ranomasonay !\nKa mba esorinao\nNy tahotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (596, 3, 'Mba manomeza anay\nNy hasoavanao,\nRy Tompo ô !\nMba hahazoanay\nNy hasambarana\nTsy mety levona,\nAn-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (596, 4, 'Tohanonao Izahay,\nRehefa tapitra\nNy andronay,\nRay ô ! mba hamoray\nNy hanalana anay\nHo any Aminao,\nmandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (597, 1, 'Hianao, Jehovah Tompo !\nNo hatoninay izao ;\nAvy izahay hanompo\nSy hanao ny sitrakao ;\n:,: Indro, raiso,\nRaiso ho Anao izahay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (597, 2, 'Mba tantano sy arovy,\nFa malemy izahay ;\nHe ! marary sy farofy\nHianao no herinay ;\n:,: Ody aina\nHitsaboanao anay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (597, 3, 'Mpivahiny ka mahantra,\nEntonao, Fanahy ô !\nAna sy mangetaheta,\nAsehoy ny antranao ;\n:,: Ranon’aina\nHosotrinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (597, 4, 'Tranonao madio no any,\nVorodamba no anay,\nFa hilaozanay ny tany,\nKa soloy ny lambanay ;\n:,: Lamba fotsy\nHitafianay anie :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (598, 1, 'Ny foko, ry Jehovah ô !\nFidio ho lapanao ;\nEkeo ho fonenanao,\nKa ao no anjakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (598, 2, 'Ny foko, ry Jesosy ô !\nSasao ho voadio ;\nIzay eritreri-dratsy ao\nLevoninao anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (598, 3, 'Ny foko, ry Fanahy ô !\nMba hamasinonao ;\nAvia, Fanahy Masina ô !\nOvay ho fo vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (598, 4, 'Ray, Zanaka, Fanahy ô !\nMpamonjy mahasoa,\nMpanafaka ahy Hianao,\nIsaorako tokoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (599, 1, 'Jehovah Ray Malala ô !\nIzay ao an-danitra ao,\nHenoy ny fivavahako\nAtaoko Aminao,\nFa Jeso Kristy Zanakao\nNo tena sorona\nIzay entiko ho Aminao,\nMpamela heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (599, 2, 'Jehovah Ray Malala ô !\nIzay ao an-danitra ao,\nHenoy ny fivavahako\nAtaoko Aminao,\nFa Jeso Kristy Zanakao\nNo tena sorona\nIzay entiko ho Aminao,\nMpamela heloka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (599, 3, 'Ny fikapohana, izay\nAtaonao amiko,\nDia  misy fananarana\nMba hibebahako ;\nNy fahafatesana, izay\nHo tonga koa  izao,\nDia  làlana halehako\nHo any Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (599, 4, 'Mba raisonao ny tanako,\nTantano aho izao,\nFa zazabodo, Raiko ô !\nAmpio ny zanakao\nNa tondraka ny rano re\nKa safononoka  ao,\nNy faty tsy ho tahotro\nHodiako Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 1, 'Ry Ray, Mpiaro lehibe !\nMba mihainoa ahy e,\nKa ampio aho hanao hoe :\nHatao anie ny sitrakao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 2, 'Izay rehetra sitrakao\nNo aoka mba hatao izao\nFa mahasoa ny tianao :\nHatao anie ny sitrakao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 3, 'Na sarotra  aza ny hatao,\nMba manampia ny zanakao\nHanaraka ny teninao :\nHatao anie ny sitrakao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 4, 'Na tonga aza ny manjo\nKa ory sy mivadi-po,\nHo toy izao ny teniko :\nHatao anie ny sitrakao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 5, 'Mba tanterahonao tokoa\nIzay kasainao hahasoa ;\nNy tena sy fanahy koa ;\nHatao anie ny sitrakao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (600, 6, 'He ! Hianao dia tsy mandao\nIzay voaray ho olonao\nKa mankatò ny didinao :\nHatao anie ny sitrakao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (601, 1, 'Mpiandry lehibe !\nVorio ny ondrinao\nHiray fisoko avokoa,\nHiadana  Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (601, 2, 'Sakaiza herinay !\nJereo ny ondrinao\nMitebiteby, ory fo,\nKa miandrandra Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (601, 3, 'Manahy izahay,\nHe ! ala be izao ;\nSao dia ho very foana  atý,\nVonjeo ny ondrinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (601, 4, 'Henoy, ry Tokinay !\nVohay ny tranonao ;\nRaha avy ny hodianay\nHitoetra  aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (602, 1, 'O, Ray, jereo ny zanakao\nMivavaka  Aminao !\nHenoy ny fitarainanay\nIzay atao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (602, 2, 'O, Ray, vonjeo ny zanakao\nFa osa izahay ;\nNy tananao no herinay,\nHandresy izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (602, 3, 'O, Ray, tahio ny Zanakao !\nNy soa Aminao ;\nKa arotsahy aminay\nNy fitahianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (602, 4, 'O, Ray, misaotra  Anao\nizahay !\nKa dera no atao ;\nNy soa azonay atý\nIsaoranay Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (602, 5, 'O, Ray, vahiny izahay !\nTsy taninay ity !\nKa raha hody raisonao\nHo Aminao arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (603, 1, 'Ry Jehovah, Ray Mahery !\nIndreto ny mpanomponao ;\nFa mpanota saiky very,\nNefa tsy mba foinao ;\nFa ny masonao mijery\nNy fanahinay mania ;\nNy fitaomanao mahery\nNo miantso hoe : Avia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (603, 2, 'Ry Jehovah, Ray Mpitahy\nIzay rehetra ory fo !\nNy tsy manana mpiahy\nMora resin’ny manjo ;\nZovy ary no hanahy\nRaha  miankina Aminao ?\nEny, faly ny fanahy\nIzay matoky Aminao.\n\n.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (604, 1, 'Rainay be fitia !\nAvy izahay\nHila zava-tsoa\nAto Aminao ;\nMihainoa anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (604, 2, 'Rainay tsy miova !\nMijere anay,\nZanaka nikomy,\nFa naverinao :\nManeke anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (604, 3, 'Rainay be fiantra !\nMenatra  izahay,\nDia mba mifona\nEo imasonao\nMiantrà anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (604, 4, 'Rainay be harena !\nMpanome ny soa !\nIzahay mahantra,\nMitaraina hoe :\nManomeza  anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (604, 5, 'Rainay be fitia !\nIndreto izahay\nAvy mba hitondra\nSaotra ho Anao :\nAza lavinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (605, 1, 'Aza manadino ahy,\nRy mpihaino vavaka !\nAza mba manary ahy\nHanalavitra Anao ;\nMitodiha, ry Mpanjaka !\nKa jereo ny olonao ;\nAza manadino ahy,\nRy mpihaino vavaka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (605, 2, 'Hianao no fiereko,\nVatolampy, heriko ;\nNa kamboty aza aho,\nFaly manana Anao ;\nAfa-tahotra ny foko\nRaha miandrandra Anao ;\nAza manadino ahy,\nRy mpihaino vavaka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (605, 3, 'Eny, Hianao no Raiko,\nKa hatoniko izao ;\nNefa ny fahadalako\nAza mba heverinao ;\nO ! ampifalio aho,\nKa ombay ny elatrao\nAza manadino ahy,\nRy mpihaino vavaka !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (605, 4, 'Tabataba lalandava\nNo mba hotako izao,\nKa izaho te-ho tonga\nAo am-pitsaharanao ;\nMitenena, ry Jehovah !\nMamalia ahy izao ;\nAza manadino ahy,\nRy mpihaino vavaka !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (606, 1, 'Eny Raiko ! mba tariho\nNy fanahin-janakao,\nKa ny saiko atodiho\nHankatò ny teninao ;\nZaza be fisiasia,\nTano re ny zanakao,\nDia taomy mba ho tia\nSy hanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (606, 2, 'Eny Raiko ! mba tsilovy\nNy alehan-janakao,\nKa tantano sy arovy\nMba hankatý Aminao ;\nMora reraka ny aina\nRaha tsy fahananao,\nKa tohano tsy halaina\nMba hanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (606, 3, 'Eny Raiko ! raha mafy\nIzany tsorakazonao,\nKa ny aiko tsy mahahafy\nMba vonjeo ny zanakao ;\nKa ny foko izay adala\nMba ovay sy hazavao,\nDia taomy hahalala\nMba hanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (606, 4, 'Eny Raiko ! mba tohano\nRaha  mihatra ny manjo,\nKa vimbino sy tantano\nTsy ho kivy re ny fo ;\nRaha tapitra ny aiko,\nRaiso hody Aminao;\nEny, ento aho, Raiko !\nFa izay no sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (607, 1, 'Ry Raiko ô ! tariho ny zanakao,\nTariho re !\nFa maizina izao lalako izao,\nTariho re !\nTsilovy, ka omeo finoana,\nTantano am-pandehanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (607, 2, 'Izay ela izay ny lalan-tsitrapo\nNo tiako ;\nIzay ela izay izao tontolo izao\nNo mamiko ;\nKanefa  izao, tariho ny zanakao,\nRy Raiko ô ! handia ny lalanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (607, 3, 'Tsy mba ahoako ny loza atý\nan-dalana ;\nTsy te-hitsinjo koa ny arý\nan-koatra ;\nFa isan-andro alaharonao,\nRy Raiko ô ! \nny dian’ny zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (607, 4, 'Na mbola lavitra aza re izay\nalehako,\nTarihonao ambara-pahafaka\nny alina,\nDia vao hitsahatra ny zanakao,\nRy Raiko ô ! arý an-danitrao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (608, 1, 'Zanahary, Ray Mahery !\nIzahay tsy mana-kery,\nNefa he ny fahavalo,\nFa masiaka sady maro ;\nO ! ampio re izahay,\nFa ny zavatra mihatra\nTsy mba misy maharatra\nNy arovanao, ry Ray,\nZanahary, Ray Mahery,\nMba arovy izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (608, 2, 'Zanahary, Ray Miantra !\nOlon-diso sy mahantra\nIzahay izay mitaraina,\nHianao no antenaina ;\nIantrao re izahay !\nBe tokoa ny fahotana\nMahatonga fanimbana\nEfa vitanay tatý :\nZanahary, Ray Miantra,\nMamelà ny helokay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (608, 3, 'Zanahary, Ray Malala !\nEnga  anie ka hahalala\nMba ho tia sy hanaiky,\nAry koa  mba hanakaiky,\nNy Mpamonjy izahay ;\nTsy ny tahotray ny loza\nNo manaitra anay hiova,\nFa ny ran’ny Zanakao :\nZanahary, Ray Malala,\nMba hazòny izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (609, 1, 'Ry Jeso be fitia !\nTariho izahay\nMba tsy haniasia,\nFa hiaraka Aminao,\nIndrisy ! ela loatra\nNo nijanonanay\nNanaram-po nihoatra\nNy sitrapon’ny Ray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (609, 2, 'Ry Tompo ô ! miarova\nNy fiainanay etý,\nIzay fohy, mora miova,\nFa tsy toy ny arý,\n‘Zay ianteherana eto\nMandalo avokoa ;\nNy fo re no mba ento\nHikambana Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (609, 3, 'Tariho hahalala\nNy tianao hatao,\nKa tsy hisalasala\nHandà izay halanao ;\nMandatsaka ao an-tsaina\nNy soa vitanao,\nKa dia hikely aina\nHanao ny sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (610, 1, 'Mba tantano, Raiko ô !\nNy fanahin-janakao,\nAoka re ny sandrinao\nNo hiaro ahy izao ;\nIndro ! raiso,\nTano aho, Tompo ô !\nMba hanao ny sitrakao ;\nSy hitoetra ao Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (610, 2, 'Ray malala Hianao,\nSady tia ny zanakao ;\nAoka  re ny tavanao\nHanazava ahy izao !\nIndro ! raiso, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (610, 3, 'Na dia maizina aza atý,\nDia matoky ano izao\nFa Hianao dia mandao\nIzay miaraka Aminao :\nIndro ! raiso, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (611, 1, 'Mba omeo ahy\nFo madio kokoa\nAfaka ahiahy\nHo Anao tokoa\nIzaho dia malaina\nO ! Mba hafanao re,\nMba hikely aina\nHahefako be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (611, 2, 'Mbola manangoly\nNy fakampanahy ;\nAo koa ny devoly\nTa-hamotraka ahy ;\nTompo ô ! ampio\nNy mpanomponao izao,\nAry mba tafio\nHerim-po vaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (611, 3, 'Atolory ahy\nZotom-po sy hery,\nFahendrem-panahy,\nFo mahay mietry.\nAmpianaro aho\nMba hanolo-tena,\nKa mba hatanjaho\nNy finoako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (611, 4, 'Dia hamasino\nNy saiko, ry Tompo !\nKa omeo fo mino,\nFo mahay manompo,\nFo mahay mihafy,\nFo miravoravo,\nNa dia voa mafy,\nFo toy ny Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 1, 'Jeso ô, Mpiandry tsara !\nSambatra ny ondrinao,\nHianao no mialoha\nHanarahanay Anao ;\n:,: Mitariha, mitariha\nNy fanahinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 2, 'Jeso ô, Mpiandry tsara !\nFohy ny fisainanay ;\nHianao mahay manoro\nLalana halehanay :\n:,: Mitariha, mitariha\nNy fanahinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 3, 'Jeso ô, Mpiandry tsara !\nTsinjonao ny dianay ;\nOndry very ka ho faty\nRaha tsy hatoninao ;\n:,: Mitariha, mitariha\nNy fanahinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 4, 'Jeso ô, Mpiandry tsara !\nIzao alehanay izao\nAla be sy làla-mafy,\nMaty re ny ondrinao ;\n:,: Miarova, miarova\nNy fanahinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 5, 'Jeso ô, Mpiandry tsara !\nMafy re ny tahotray ;\nBibidia mamiravira\nMampivadi-po anay :\n:,: Miarova, miarova\nNy fanahinay izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (612, 6, 'Jeso ô, Mpiandry tsara !\nEnto mody izahay ;\nAminao ny valan''ondry\nTsy mba misy tahotra,\n:,: Mitariha, mitariha\nNy fanahinay izao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (613, 1, 'Mpamonjy soa malala ô !\nMba miandrandra Anao\nNy foko sy fanahiko\nAnkehitriny izao ;\nFa Hianao irery\nNo tena Mpanome\nNy fahafinaretana\nSy fitsaharam-be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (613, 2, 'Mpiandry ondry mora ô !\nRy Jeso be fitia !\nHazony aho ondrinao\nMba tsy haniasia ;\nAndraso isan-andro,\nArovinao anie,\nEndrey fa mahatahotra\nNy biby marobe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (613, 3, 'Ry Kristy be fitia !\nRy Tompo tsy mandao !\nNy sandrinao iondanako\nFa mamy Hianao ;\nHianao nanolo-tena\nHo faty fahizay,\nHanangana olo-meloka\nHo zanakao indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (613, 4, 'Ry Tompo Zanahary\nBe faharetam-po !\nIzaho te-ho olonao\nMahitsy sy madio ;\nOvay ny toe-tsaiko,\nNy foko mba sasao,\nMba ho tempoly mendrika\nNy hitoeranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (613, 5, 'Ry Jeso tsy miova !\nRy Vonivatoko !\nRy Zanak''Andriamanitra ô !\nRy Aro lehibe !\nIzaho dia matoky\nNy fitiavanao ;\nFa sambatra ny olonao \nMiankina Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (614, 1, 'Jeso Tompo, mamindrà fo\nAmin''ny mpanomponao,\nFa tsy misy famonjena\nAfa-tsy ao Aminao ;\n:,: O ! vonjeo :,:\nNy mifona Aminao. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (614, 2, 'Fa kamboty sy mahantra,\nOry, osa, reraka\nIzahay atý an-tany,\nRaha tsy vimbininao ;\n:,: O ! ampio :,:\nNy miankina Aminao. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (614, 3, 'Tokinay sy Vatolampy,\nAro sy ampinganay !\nAfa-tahotra, afa-doza,\nTsy ho ketraka izahay ;\n:,: O ! arovy :,:\nNy mialoka Aminao. (in-2)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (615, 1, 'Vimbino tsara izahay\nRy Jeso Kristy Tomponay !\nMba tsy ho diso lalana\nAtý ambony rano be,\n:,: Ka na manonja aza re\nNy ria-dranomasina,\nTazòny mafy izahay\nMba tsy ho rendrika atý :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (615, 2, 'Dia mitsangàna, ka avia,\nRy Jesosy, Mpamonjy tia !\nJereo ny fahorianay ;\nAnaronao ny rivotra\n:,: Izay mampanonja toy  izao\nNy alon-dranomasina,\nIzay manafotra anay\nAtý am-pandehananay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (615, 3, 'Fa Hianao no aronay,\nKa dia sahy izahay \nHamaky sy hanohitra\nNy onjan-drano betsaka ;\n:,; Ka aoka tsy ho fefika\nAtý an-dranomasina,\nFa mba ho tonga aminao\nNy sambonay,\nry Tompo ô ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (616, 1, 'Ry Mpamonjy olom-bery !\nRy Jesosy tia anay !\nIzahay tsy manan-kery\nTsy mba hendry, tsy\nmahay,\n:,: Koa Hianao irery\nNo mba iankinanay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (616, 2, 'Ry Mpamonjy be fitia !\nAza ilaozanao izahay ;\nRy Jesosy ô ! avia,\nHianao no tanjakay !\n:,: Miarova, manampia,\nBe ny fahavalonay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (616, 3, 'Ry Mpamonjy ! mitantàna\nizao mpanomponao izao ;\nBe tokoa ny fikasana\nTa-hanao ny sitrakao\n:,: Nefa be ny fahosana\nMomba ny mpanomponao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (616, 4, 'Ry Mpamonjy, Jeso tia !\nMivimbina, mamonje ;\nRy Mahery, manampia !\nMiambena, mijere ;\nMiarova, mitariha,\nRy Sakaiza lehibe !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (617, 1, 'Ry Jeso Mpamonjy,\nMpanavotra anay !\nTafangona eto  anio izahay\nHidera sy mba\nhankalaza Anao,\nFa Tompo namonjy\nanay Hianao :\nHaleloia ! Haleloia ! Hosana\nan''ny Avo !\nHaleloia ! Haleloia ! Hosana\nho Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (617, 2, 'Ry Jeso, Sakaiza\ntsy mety mandao !\nSakaiza tsy mba mahafoy\nHianao\nMijery anay raha misy manjo,\nHo aron''ny tena\nsy tokin''ny fo :\nHaleloia ! .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (617, 3, 'Ry Jeso Mpanjaka\nMpiaro anay !\nHandroso hiady tokoa izahay,\nKa dia vonjeo handresy izao,\nFa tsy mba ho resy\nizay manana Anao :\nHaleloia ! .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (617, 4, 'Ry Jeso, Mpitarika\nanay ho arý\nTariho izahay tsy hivily atý.\nKa dia hamantana\nao Aminao,\nFa faly miandry anay Hianao\nHaleloia ! .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (618, 1, 'Ry Jesosy mba tantàno,\nHo tafita soa arý\nKa ambeno sy sakano\nIzay rehetra loza atý.\nRano be mangeniheny\nIzao alehako izao,\nKa aoka Hianao hiteny\nMba ho tokiko hatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (618, 2, 'Aza miandry ao am-pita\nSao dia  fefika aho atý ;\nFa avia  hiara-mita,\nDia  ho tody soa arý.\nRaha sendra ka manonja\nIzany alon-drano be,\nAoka Hianao ho tonga\nHampitony azy re.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (618, 3, 'Ao ny rivotra mahery\nMety hampivadi-po,\nFa ny feonao irery\nMampisava ny manjo\nAny koa ny vatolampy,\nSy ny biby maro be.\nFa ny herinao dia ampy,\nKa vonjeo ny zanakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (618, 4, 'Ry Jesosy ! Mihainoa,\nIzao fivavako izao,\nMijoroa, misehoa,\nKa jereo ny diako izao ;\nMbola lavitra ao am-pita\nKa tsy hita izay hanjo,\nToy ilay tao Genesareta,\nMisehoa Hianao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (619, 1, 'Ry Jesosy Tompo tsara,\nRy Mpamonjy ô !\nSambatra aho, manan-jara,\nManana Anao ;\nFa na maro ny mpandrafy\nTa-hamely ahy mafy\nHianao mahay manafy\nHery ny Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (619, 2, 'Be ny manodidina ahy\nEfa lasana ;\nManakaiky, mampanahy,\nRe ny fasana ;\nNefa, ry Mpamonjy soa,\nTena sitrakao tokoa \nNy hitantana ahy koa\nAo an-dalana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (619, 3, 'Eny, izaho manantena,\nRy Mpamonjy ô !\nFa hahazo famonjena \nAny Aminao,\nRaha ilaozako ny tany,\nIzaho tsy mba hitomany,\nFa hiakatra hankany\nAnatrehanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (620, 1, 'Ampianaro aho, Tompo,\nmba hiambina !\nMandalo tahaka ny rivotra \nny androko.\nKa mihalany isan-andro\nisan-alina,\nAmbara-pahatapitry\nny fe-potoako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (620, 2, 'Endrey ny voninkazo,\nraha vao maraina e !\nFa tsara ka mamirampiratra\nny endriny ;\nKanefa maty izy amin''ny\nhainandro be\nKa raha hariva dia simba\nny tarehiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (620, 3, 'Ny tenako no voninkazo\nka hihintsana,\nNy aiko tahaka ny\nzavona sy aloka\nIzay mandalo ka\ntsy manam-pijanonana ;\nNy taonako mandroso\ntahaka ny riaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (620, 4, 'Tsy manam-panorenana\natý ny heriko,\nMihelina ka tsy mba hita\nny alehany ;\nMalaky tahaka ny tselatra\nny androko,\nKa tsy miatoato\nao am-pandehanany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (620, 5, 'Ry Tompo ô, ampio aho\nmba hiomana,\nKa mba hadio fo\nna aiza no alehako,\nAmpaherezo mafy aho\nmba ho malina,\nMba tsy ho tonga tampoka\namiko ny fetrako !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 1, 'Izaho tsy mba mahalala\nIzay hiainako atý ;\nAnio angaha dia hiala\nKa hodimandry ho arý !\nNy ran''i Kristy, Raiko ô,\nNo amelao ny heloko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 2, 'Ry Ray ô, mampianara ahy\nHanisa e, ny androko,\nHiantombenan''ny fanahy\nAo amin''ny Mpamonjiko.\nNy ran''i Kristy, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 3, 'Ny asa sy ny ahiahy,\nIzay maningotra  ahy izao,\nVahao mba tsy hisakana ahy\nHiainga raha antsoinao.\nNy ran''i Kristy, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 4, 'Ny fahotako an-tapitrisa\nSaronanao ny avotra,\nIzay nandraisako batisa\nSy ny sakafo masina.\nNy ran''i Kristy, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 5, 'Tsy misy mahasaraka ahy\nAmin’ny fitiavany ;\nNy teniny no mahasahy\nNy olom-boafidiny.\nNy ran''i Kristy, .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (621, 6, 'Ka dia tsy mba mampanahy\nNy andro hahafatesako,\nJesosy no miantoka ahy,\nNy ainy no anjarako,\nNy ran''i Kristy, .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (622, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (622, 2, 'Ny lalana izay nataon''\nAndriamanitra\nTamin''ny olombelona\n:,: Marina indrindra :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (622, 3, 'Raha manaraka izany,\nDia tsy manan-tsiny\nFa nanota ny olona\n:,: Ka nanan-keloka :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (622, 4, 'Raha tsy mino an''i Kristy\nNy olombelona,\nFa matoky ny tenanay,\n:,: Tsy azo hitsina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (622, 5, 'Fa Jeso no manafaka\nNy ota rehetra,\nNy manaiky ny teniny\n:,: No azo hitsina :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (623, 1, 'Aina no fetra, ry Jesosy\nTompo\nIzany no tenin''ity zanakao\nVonon-kanaraka Anao sy\nhanompo\nAmin''ny mora sy sarotra\nhatao :\nMandrakizay no tokim-\npanompoana\nMamin''ny olona izay\nafakao,\nMandrakizay hekenay\nho faneva\nNy holatrao nanavotana\nanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (623, 2, 'Na ny mangidy heverina ho\nmamy\nRaha miray Aminao izahay,\nIza no tsy hahafoy ny ainy\nMba ho Anao izay nisolo\nanay !\nMandrakizay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (623, 3, 'Hianao, ry Jeso ! no Ilay\nKapiteny\nIzay hampandresy tokoa\nanay ;\nFa raha Hianao, Tompo, no\nmba miteny,\nLasa izay mahakivy anay ;\nMandrakizay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (623, 4, 'Vonon-kandeha na aiza\nna aiza,\nIreto mpanompo matoky\nAnao ;\nNa dia ny fahafatesana aza,\nTsy hahaleo anay\nmpianatrao ;\nMandrakizay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 1, 'Hianao, ry Jeso ô !\nNo mba hatoninay\n:,: Tompon''ny aina :,:\nMba mihainoa ka  avia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 2, 'Mba ampangino re\nNy ao anatinay,\n:,: Tsy hisy renay :,:\nRaha tsy feonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 3, 'Aoka izahay handray\nNy fanavaozanao\n:,: Ka mba hitovy :,:\nToe-tsaina Aminao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 4, 'Mafy ny adinay,\nKa be ny tahotray;\n:,: Nefa tsy resy :,:\nIzay miankina Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 5, 'Dia tafionao\nAmin’ny herinao\n:,: Mba ho mpandresy :,:\nIzahay mpianatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 6, 'Ny fonay hafanao\nMba hirehetanay\n:,: Amin’ny asa :,:\nIzay omenao hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (624, 7, 'Mba ho tanterakay\nIzay tena anjaranay,\n:,: Dia  manomeza :,:\nFo miray aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (625, 1, 'Mahareta, mahareta,\nRy mpihazakazaka ô !\nBe ny zava-mahalavo,\nAza kivy Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (625, 2, 'Mahareta, mahareta,\nRy mpihazakazaka ô !\nMahavesatra ny ota,\nAoka re harianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (625, 3, 'Mahareta, mahareta,\nRy mpihazakazaka ô !\nLavi-dalana haleha,\nAndrao tsy tonga Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (625, 4, 'Mahareta, mahareta,\nRy mpihazakazaka ô !\nIndreo ny maso izay mijery,\nTazany ny dianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (625, 5, 'Mahareta, mahareta,\nRy mpihazakazaka ô !\nAo ny Tomponao miandry,\nIzy no jereonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (626, 1, 'Ry Jeso Mpamonjy malala !\nIndreto ny mpanomponao ;\nRaha misy izay mankahala,\nMatoky, fa manana  Anao.\nJereo, Jereo !\nFa vonon-kanaraka Anao.\n(in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (626, 2, 'Ny tafika ataon’i Satana\nMilofo hamotraka  anay ;\nAvia, ry Jeso mitantàna,\nFa mioman-kandresy izahay.\nJereo, Jereo ! .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (626, 3, 'Na zovy mioman-kandrafy,\nIrinay hanaraka Anao ;\nNa iza hisakana mafy,\nHijoro, fa manana Anao.\nJereo, Jereo ! .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (626, 4, 'Ireo nentinao voatantara\nNo feo mampahery anay ;\nNy tokin’ireo Maritiora\nFaneva mitarika anay ;\nJereo, Jereo ! .. sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (627, 1, 'Ry miaramila ! mandrosoa izao,\nKristy Kapiteny mialoha anao ;\nAo aloha tafika, loza, ratra be,\nFitsaharana tsy misy,\nmisikina re :\nRy miaramila ! mandrosoa\nizao,\nKristy Kapiteny mialoha anao ;', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (627, 2, 'Any lehy ratsy no manotrika,\nAny ny tsipìka no milatsaka;\nTano ny finoana ho\nampinganao,\nEntonao ho fandresena izao\nrehetra  izao :\nRy miaramila ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (627, 3, 'Aza miambohona miherika,\nAo an-tratranao ny fiarovana ;\nAza mba matahotra, fa\nsahia, fandrao\nRaha tsy mandresy, dia ho\nresy hianao ;\nRy miaramila ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (627, 4, 'Dia mandrosoa hianao izao,\nKa miraisa dia aminay ho ao ;\nMilofosa fatratra, aza ketraka\n« Miadia ny ady tsaran’ny\nFinoana » :\nRy miaramila ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (627, 5, 'Kristy Kapiteny tapitra ohatra,\nIzy no mpiaro ny mpanafika ;\nMiankìna Aminy mafy hianao\nKa aza mba ny tenanao no\nitokianao\nRy miaramila ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (628, 1, 'Misy fahavalo maro\nTe-hiady aminay,\nFaly sy miravoravo\nRaha mamotraka anay ;\nIzahay tsy manan-kery\nHandresenay azy ireo ;\nRy Mpamonjy olom-bery !\nJeso Tompo ô ! vonjeo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (628, 2, 'He ! mibaiko ny devoly,\nIlay mpihaza aina soa,\nKa mikendry hanangoly\nSy handatsaka aina koa ;\nTsy mba misy izay hanampy\nHampandositra azy izao ;\nHianao no tsy manary\nJeso Tompo ô ! vonjeo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (628, 3, 'Indreo ny fandrika harato\nIzay mivelatra avokoa,\nTsara sy milantolanto,\nToa fiainana tokoa ;\nKanjo indrisy ny mahazo\nIzay fatoran’izy ireo ;\nTsy mba hainy ny miala ;\nJeso Tompo ô ! vonjeo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (628, 4, 'Miomana ry sakaiza !\nBe ny fahavalonao ;\nMitandrema sy mahaiza,\nSarotra ny lalanao ;\nAza mba matoky tena,\nFa ny Tompo no jereo ;\nIndrisy re ny halemena ;\nJeso Tompo ô ! vonjeo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (629, 1, 'Ry Kapiteny, be ny ady manjo\nMila ho reraka sy kivy ny fo,\nKa hatanjaho mba\nhatoky Anao,\nTompo ô ! avia mamonjy\nahy izao :\n“ Eny, Izaho mamonjy  anao,\nKa mampahery sy miaro\nanao ;\nAza matahotra mihitsy\nhianao,\nFa Izaho momba sy manampy\nanao.”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (629, 2, 'Ny fahavaloko atý mba reseo,\nKa taomy aho mba handray\nrahateo\nNy fiadiana avy ao Aminao ;\nTompo ô ! vimbino aho,\naza mandao ;\n“ Eny, Izaho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (629, 3, 'Loza tokoa re ny tahotra teo,\nKa na mamely ahy aza ireo,\nTsy mba manahy  intsony\naho izao\nFa mamonjy sady\ntsy mandao Hianao.\n“ Eny, Izaho, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (629, 4, 'Fa ny mpiady maharesy atý\nNo hosatroham-boninahitra\narý ;\nMaro no efa tonga ao\nAminao,\nTompo ô! avia hitarika ahy\nhankao\n“ Eny, Izaho, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (630, 1, 'Hitako izao, sakaiza !\nJeso no Mpanavotra :\nTsy mba lany ny fitia,\nJeso no Mpanavotra ;\nNa dia  saiky lasa aza,\nKa ny olona mandao,\nIzao no mampiadana  ahy,\nJeso no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (630, 2, 'Raha miasy mafy aho,\nJeso no Mpanavotra :\nToky sy Ampinga Izy,\nJeso no Mpanavotra ;\nManome ny soa Izy,\nFa ny rany mahadio\nNo mahafa-keloka ahy,\nJeso no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (630, 3, 'He ! ilaozako ny eto,\nJeso no Mpanavotra :\nIndro lanitra no azo,\nJeso no Mpanavotra ;\nIzy no Mpamonjy ahy,\nAina sy fanilo koa,\nFahendrena mahasoa,\nJeso no Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (630, 4, 'Ray ô ! Hianao deraiko,\nJeso no Mpanavotra :\nHianao naniraka Azy,\nJeso no Mpanavotra ;\nHianao Mpiaro ahy,\nMpitahiry, Tokiko,\nNo deraiko lalandava,\nJeso no Mpanavotra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (631, 1, 'O ! mandrosoa mba ho tena\nmpandresy,\nDia mazotoa, miasà,\nmitomboa\n:,: Raiso ny tsara, ka roahy\nny ratsy\nKa matokia an’i Jeso tokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (631, 2, 'O ! mandrese, ny finoana tano,\nNa dia mafana ny ady hatao,\n:,: Ny fandresena homena ny\nmino\nAzy ny herin’ny Tompo izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (631, 3, 'Mba matokia ny hery tsy hita\nIzay voatahirin’ny Ray\nho anao,\n:,: Ka voatolotra tao Gologota\nMba ho antsika rehetra izao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (632, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (632, 2, 'Matahora, mangovita,\nFa, indro ! ny devoly\nNo mihendry hisambotra\nNy saintsika rehetra ;\n:,: Miambena :,:\nKa mivavaha tsara. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (632, 3, 'Mifalia, mivavaha,\nJeso no arontsika,\nManaraha ny teniny,\nFa hovonjeny isika ;\n:,: Tano Izy :,:\nHahazoana aina. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (632, 4, 'Manandrata feo hihira\nHidera an’i Kristy,\nHanome voninahitra\nAn’Andriamanitra\n:,: Tompontsika :,:\nFa marina izany. (in-2)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (633, 1, 'Jesosy Kapiteninay\nIzao miantso izao,\nHiady no ilana anay\nKa zovy no hanao ?\nIzay mahafy hatratý,\nMahery fo tokoa\nKa tsy miraika hatrarý ;\nIreny no hanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (633, 2, 'Ny tany hotafihinay,\nIzao tontolo izao ;\nNy tafika hikatrohanay,\nTsy ady mora atao ;\nNy ota hotoherinay\nHo rava sy hikoa.\nNy tany hitorianay\nNy herinao tsy toha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (633, 3, 'Indreo, ny maritiora soa\nNo loha-lalanay,\nNalatsaka  aina avokoa,\nKa marika aminay ;\nFandavan-tena lehibe\nNo vitan’izy  ireo\nHodian-tsy hita foana ve\nIreo maritiora ireo ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (633, 4, 'Ry Jeso Kapiteninay !\nIzao anio izao,\nNo anavaozanay indray\nNy fanompoana Anao ;\nAtolotray Anao tokoa\nIzao tenanay izao,\nKa hamasino avokoa\nHanao ny sitrakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 1, 'Na aiza no alehako,\nDia  misy izay ahina,\nFa be ny fahavaloko\nMahery sy kirina.\nMikendry fatratra\nIreo mpamitaka,\nKa dia hazoniko ny to,\nNa aiza no alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 2, 'Na aiza no alehako,\nDia  misy ady mafy ;\nNy ota hotoheriko,\nHataoko tena rafy ;\nNy fahoriana,\nIzay fikapohana,\nTsy maintsy hiaretako,\nNa aiza no alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 3, 'Na aiza no alehako,\nNy fasana mangina\nMiandry eo alohako,\nFa aloka ny aina\nSy toy ny zavona\nMalaky levona ;\nIzany no tsaroakao\nNa aiza no alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 4, 'Na aiza no alehako,\nMomba ahy ny anjely,\nKa tsy mba hatahorako\nSatana izay mpamely,\nRy izao tontolo izao,\nHo resy hianao,\nAnjely re ho namako\nNa aiza no alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 5, 'Na aiza no alehako,\nJesosy no kiady ;\nNy ratrany no aroko\nSy herin’ny mpiady.\nNy dian-tongony\nArahiko atý,\nKa fandresena azoko\nNa aiza no alehako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (634, 6, 'Na aiza no alehako,\nNy lanitra andrandraiko,\nKa ravo ny fanahiko,\nFa ao ny Tompon’aiko,\nRy izao tontolo izao,\nNy voninahitrao\nDia  vovoka hitsahiko ;\nNy lanitra  ao alohako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 1, 'He ! sarotra ny lalam-\npamonjena\nHitanana ny vonjy izay\nnomena\nHamelona hatrao am-parany\nNy fitiavana voalohany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 2, 'Ny maro dia mazàna \nmankamamy\nIzao tontolo izao, ka mora lany\nNy zotom-po sy finoana\nDia tonga tia zava-poana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 3, 'Malaina izy ka tsy te-hiady\nNy ady masina izay maro fady,\nNy lalan-tery dia halany,\nFa ny malalaka no tiany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 4, 'Mazàna mety menatra ireny\nRehefa voatsindron-toriteny,\nFa hitany ny ratsy ao am-po,\nKanefa tsy mba te-hiova fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 5, 'Ry Jeso, Hianao no tokin’aina,\nIzay novonoina sy \nnankahalaina,\nOmeo ahy faharetam-po,\nHiaritra izay anjarako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (635, 6, 'Raha reraka aho ka misalasala,\nDia  asehoinao, ry Ray malala,\nNy lovako arý an-danitra\nHihazona  ahy mba haharitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (636, 1, 'Miambena  izao,\nRy fanahiko !\nMisy rafinao misaina\nHanangoly ny malaina ;\nBe ny fandrika\nVoavelatra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (636, 2, 'Miambena  izao,\nRy fanahiko !\nMba tsy ho andevon’ota,\nNa hotaomin’ny mpanota ;\nMivavaha  izao,\nRy fanahiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (636, 3, 'Miambena  izao,\nRy fanahiko !\nKa tohero ny devoly,\nRaha avy manangoly ;\nKristy raisonao\nMba ho aronao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (636, 4, 'Miambena  izao,\nRy fanahiko !\nTsy ho ela tsy akory\nDia ho lasa ny mahory,\nKa ho afaka\nNy mpiambina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (636, 5, 'Miambena  izao,\nRy fanahiko !\nFa ho avy  indray ny Tompo\nMba hampody ny mpanompo\nHo finaritra\nAo an-danitra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (637, 1, 'Ry mino, miambena re,\nFa manam-pahavalo be ;\nJesosy no arahonao\nHitarika anao.\nEzahonao ny dianao,\nNy satroboninahitrao\nMiandry ao aloha ao,\nKa mba tratraronao !\nMba mivavaha, ry havako,\nA miomana ao am-po ;\nAndeha  izao ilaozinao\nNy zavatra ho lo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (637, 2, 'Izao no fiadiana\nOmen’Andriamanitra,\nNy fahamarinana alao\nMba hisikinanao ;\nHazony ny finoana\nAmpinga hanohanana ;\nNy tenin’Andriamanitra\nNo tena sambatra ;\nNy famonjena raisonao\nHo fiarovan-dohanao ;\nIreo no hanoheranao\nNy ratsy ao am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (637, 3, 'Ry Havana malala ô,\nAndeha  isika hiray am-po\nHikatsaka ny lanitra,\nFa ao tsy misy manjo.\nHo faly ao an-danitra\nNy vita fanompoana,\nKa izao isika handao\nNy zava-poana !\nHo lasa faingana indray\nNy androntsika an-trano lay ;\nVonjeonao ilay tany vao\nIzay mandry fahizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (638, 1, 'Miambena, mivavaha, fa tsy\nfantatrao\nNa ny andro, na ny ora\nfe-potoananao\nNy Mpanjaka masina ho\ntonga tampoka\nHampaneno ny trompetra\nao an-drahona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (638, 2, 'Amboaro ny fanilon’ny finoana\nSy ny solika fitaiza voaomana,\nKa Velomy re ny afom-\npitiavana\nMba hidiranao an-dapam-\npifaliana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (639, 1, 'Jeso ampio izahay mba\nhandresy !\nNy fahavalo mamely anay !\nIndro, ny aizina tsy mbola\nresy ;\nAry Satana mamandrika anay,\nKa mba omeo anay ny\nmazava,\nHampahasahy anay lalandava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (639, 2, 'Jeso ampio izahay mba\nhandresy !\nKely finoana re izahay ;\nKivy matetika, mila ho resy ;\nAoka ny herinao homba anay ;\nDia hatoky  izahay,\nry Mpandresy\nFa ny devoly dia  ho voasesy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (639, 3, 'O, ry Mpandresy, arovy\nny ainay !\nAdy mafy hiseho izao ;\nKa mba tohany hatoky\nny fonay !\nDia hiainga hanaraka Anao ;\nNa dia loza ny herin-tSatana,\nSahy izahay, Hianao no\nmitana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (639, 4, 'Ampandreseo izahay,\nry Mpamonjy !\nKa raha vita ny ady atý,\nDia ho isan’izay voavonjy,\nAry ho faly sy tretrika erý ;\nTsy mba hitsahatra intsony\nny hoby\nHankalazana Anao,\nry Mpamonjy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (640, 1, 'O, ry Jesosy, omeo ny hery,\nMba handreseko ny ota izao !\nOsa sy ketraka aho irery,\nRaha tsy avy izao Hianao.\nRaiso ny fo.\nTano tokoa,\nMba handreseko,\nry Tompoko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (640, 2, 'O, ry Jesosy, omeo ny hery,\nKa amboary ny fonay izao ;\nO, mba esory izay mahavery,\nMitomoera aminay Hianao ;\nKa hazavao\nNy fo izao\nAmpandosiro ny aizina ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (640, 3, 'O, ry Jesosy, omeo ny hery,\nKely finoana re izahay,\nAry ny lalanay ety sy tery,\nRy Jeso ô, mitantàna anay !\nTohanonao\nNy olonao,\nAmpandreseo izay manaraka\nAnao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (641, 1, 'Sakaiza, raha tia\nNy Tompo Hianao,\nFaingàna ka avia\nHanaraka Azy izao !\nO, mba tsarovinao\nFa Izy tsy nifaly,\nNaleony nijaly\nMba hamonjena  anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (641, 2, 'Fanahy, mahereza,\nEfao ny adinao,\nFa làlana mideza\nNo hiakaranao ;\nNy zava-tsoa atý\nDia  zavatra mandala ;\nTsy azom-pahavalo\nNy lovanao arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (641, 3, 'Ry Tompo manomeza\nAnay ny herinao ;\nFinoana mateza\nMba atolorinao;\nMba hanarahanay\nNy lalam-pamonjena.\nHahita  Anao mitsena\nAnay rahatrizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (642, 1, 'Mba faingàna re, ry mino,\nAza mba tamana atý ;\nAry aza manadino\nNy anjaranao arý.\nNy Mpamonjy anao\nNo manangana faneva,\nAry mampihevaheva\nAzy eo alohanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (642, 2, 'Marobe no mba mitady\nNy fiainana vaovao,\nNefa kosa tsy miady\nAmin’izao tontolo izao.\nNy Mpamonjy anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (642, 3, 'Koa raiso ny ampinga,\nTano re ny sabatrao,\nFa izany mahakinga\nNy mpandeha toa anao.\nNy Mpamonjy anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (642, 4, 'He, akaiky anao Kanana,\nTany hovantaninao ;\nAry any hy tanàna\nMisy ny anjaranao.\nNy Mpamonjy anao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (643, 1, 'Kristy miantso antsika hiady,\nIzy nandresy ilay ratsy sahady,\nKa mba Henoy, ry sakaiza\nizao :\nKristy miandry indrindra anao !\nMila antsika\nKristy Mpanjaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (643, 2, 'Kristy Mpitarika amin’ny ady,\nKa mampandresy izay\nte-hifady\nNy fahotana, izay maimbo\nsy lo ;\nIzy no manda mafin’ny fo.\nMiaro antsika\nKristy Mpanjaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (643, 3, 'Kristy mampiananatra antsika\nhahery,\nIzy miaro antsika sao very,\nKa mba avia hifahatra ao,\nIndro ny ady mandroso izao\nMiantso antsika\nKristy Mpanjaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (643, 4, 'Kristy mamnjy antsika\nizay voa,\nAry tsaboiny ho entiny koa\nHo ao an-dapany mandrakizay\nHonina ao an-tranon’ny Ray.\nHaka antsika\nKristy Mpamonjy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (643, 5, 'O, ry Jesosy Mpanjaka,\nMpamonjy !\nTompon’ny dera aman-daza\nsy vonjy,\nIreo rafinao mba reseo izao,\nKa tanteraho izay sitrakao.\nO, mba avia,\nKristy Mpanjaka.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (644, 1, 'Maty malala, ka ory ny fo ;\nLasa ny tiana, ka mafy manjo ;\nNefa isika tsy mba mamoy,\nFa manantena hihaona indray :\nAo aminao Tompo ! izy izao,\nTsy mba marary na misy\nmanjo ;\nNentina any ambony madio,\nDia ho sambatra mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (644, 2, 'Mba notandremana fatratra\nkoa,\nNefa tsy tana, fa lasa izao ;\nEny, ry Tompo ! ekena tokoa\nIzay sitrakao, fa izany no soa :\nAo aminao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (644, 3, 'Fantatra koa fa isika izao\nSamy vahiny ka hody indray ;\nIndro ! ny namana lasa izao,\nLasa miandry antsika arý :\nAo aminao ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (644, 4, 'Ary isika, raha hiala atý,\nDia hotsenain’ireo havan-\ntsy foy\nEny, hihaona mifaly indray\nKa tsy hisaraka mandrakizay\nAo aminao ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (645, 1, 'Lasa re ilay nazoto,\nNody ao an-tany soa,\nIzay nanompo tamin-joto\nKa nahefa be tokoa,\n:,: Indro, faly :,:\nFa mijinja voka-tsoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (645, 2, 'Lasa nody fa voantso :\n“Miakara”, hoy ny Ray,\nKa fafao ny ranomaso,\nJeso Tompo no mandray.\n:,: Faly, ravo :,:\nFa tafakatra ao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (645, 3, 'Lasa re ilay nihafy\nOry tena, be nanjo,\nFa nanohitra  ady mafy,\nNefa mba nahery fo.\n:,: Ka nandresy :,:\nFahavalo maro be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (645, 4, 'Afaka  ady ka mifaly,\nFa tsenaina hatratý ;\nAfaka  ota, afa-jaly,\nKa mihoby hatrarý.\n:,: Hoy ny Tompo :,:\n“Mandrosoa, ka mifalia”.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (646, 1, 'Tonga ny fahafatesana,\nAry jinjany ny velona ;\nIzy manapaka ny lehibe\nSady mamira ny kely re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (646, 2, 'Nefa ny fasana maizina\nEfa nidiran’ny Masina ;\nIzy nampody ny fiainana,\nResy ny fahafatesana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (646, 3, 'Amin’ny manam-pinoana,\nDia mazava ny fasana ;\nIzy hitsangana finaritra\nNoho Jesosy Mpanavotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (646, 4, 'Resin’ny Tompo ny fasana,\nKoa, ry fahafatesana,\nAiza izao ny fanindronanao ?\nAiza, ry helo, ny herinao ?', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 1, 'Ry hava-malala misaona\nMangina fa mbola hihaona\nIsika sy izy nalaina,\nRaha  tonga indray ilay \nmaraina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 2, 'Ny naman’ny tany ihany\nNo mody ho amin’ny tany,\nKa dia ny fanahy mifindra\nHo amin’ny Avo indrindra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 3, 'Tsy mainsty afafy aloha\nNy vary vao mety hamoa ;\nNy faty halevina koa\nTsy very fa mbola hifoha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 4, 'Dia miandrandrà,\nry mpisaona !\nHo avy ilay lohataona,\nKa dia haniry ny voa,\nIzay misy ny ain’ny Tsitoha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 5, 'Ho avy ny Tompon’ny aina\nHiantso ny vovoka maina\nHitafy ny fahazavany\nSy hiditra ao am-panjakany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (647, 6, 'Faingàna, ry Kristy Mahery,\nRy Fanantenanay irery,\nNy fasanay iva tsarovy,\nNy toky nomenao mba tovy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (648, 1, 'Sambatra ny nodimandry,\nNiala teto ka tafandry\nAo ampofoan’i Kristy ;\nRavoravo izao ny fony\nKa tsy misasatra intsony,\nMby ao am-pitsaharany.\nNy asany tatý\nManaraka  azy arý,\nHaleloia !\nTafiditra  ao an-dapanao,\nRy Jeso, ny malalanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (648, 2, 'Saotra, dera, laza, hery,\nMba raiso ho Anao irery,\nRy Zanakondry tokinay !\nHianao dia nialoha\nHanolotra anjara soa\nHo an’ilay malalanay.\nNy fahafatesanao\nNo misy aim-baovao,\nHaleloia !\nHahita Anao ny olonao\nHohazavain’ny tavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (648, 3, 'Tsy mba misy alina any,\nJesosy no fahazavany\nSy tena masoandrony.\nAzony izay niriny,\nKa sambatra ny mpivahiny,\nFa ao no tanindrazany,\nFa efa tapitra\nNy ali-maizina,\nHaleloia !\nFa foana ny fasana,\nMaraina no miposaka.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (649, 1, 'He, lasa ny zaza malalan’ny\nfo !\nAtolotra ny tany ny tena ho lo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (649, 2, 'Tsy maty anefa ny zaza, fa he\nJesosy no milaza :\n« Matory izy e ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (649, 3, 'Jesosy hamoha ny zaza indray,\nHiara-paly amin’anjelin’ny \nRay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (649, 4, 'Ka mba mionòna, ry havana\natý !\nHo hitanao ny zaza ao\nan-danitra arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (650, 1, 'O, ry Mpamonjy, avia Hianao,\nIndro misento ny fonay izao ;\nNy alahelo mamely anay,\nKoa mitady Anao izahay !\nIza no aron’izay ory fo ;\nSady mpanampy ny mana-\nmanjo\nFa tsy ny Tompo mahery\nsy to ?\nDia Hianao, Izay be indrafo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (650, 2, 'Jeso Mpamonjy ô, mba\niantrao,\nIzay malahelo miantso Anao !\nNy mitomany tsy hitanao va ?\nFantatray fa Hianao tsy handà\nDia  manatòna ka mba mijere\nNy ranomaso izay mitete !\nFa Hianao no mahay manome\nFampiononana be dia be.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (651, 1, 'He, lasa izao ny malala !\nNifindra  ao an-dapan’ny\nRay ;\nIsika rehefa miala,\nHo faly hahita azy indray,\nIsika izao mitomany,\nIharan’ny mafy manjo ;\n:,: Jesosy mahita izany,\nKa Izy hanony ny fo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (651, 2, 'Matetika re ny mangidy\nAvela hihatra aminao,\nKa aoka mba tsy hanadidy,\nFa feon’ny Tompo izao,\nNy zanaka tian’ny rainy\nTsy maintsy anariny re !\n:,: Izay mahasoa ataony,\nOmbam-pitiavana be :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (651, 3, 'Ny fo mora kivy ario,\nFa tsy mba hanary ny Ray ;\nNy mamy tsy hita anio\nHaposany rahatrizay,\nHofaohiny ny ranomaso,\nSoloam-pifaliana be ;\n:,: Ny toky omeny andraso,\nFa ho tanterahiny re ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (652, 1, 'Nalain’ny Mpahary ity\nzaza ity\nHo sambatra mandrakizay,\nMiandry antsika nilaozany atý\nFa mbola hihaona indray,\nOnony ny fonao,\nRy mana-manjo !\nAo an-danitra izy izao,\nRavo izy izao,\nFa ny lapan’ny Ray\nFonenana mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (652, 2, 'Nafahina amin’ny loza etý\nKa tretrika izy izao,\nNy nofo ho lo no alevina atý\nFa any ny tena vaovao.\nOnony ny fonao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (653, 1, 'Ry mpisaona malahelo,\nMijere ny Tomponao,\nIzay namindra ny malala\nMba ho ao an-danitra ;\nMba fafao ny ranomaso,\nAndrandrao ny lanitra\nMitsahara, ry mpisento,\nKa derao ny Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (653, 2, 'Nofo no anterintsika\nAo am-pasana anio ;\nNy fanahinay anefa\nTsy mba ao anatin’io.\nJeso no namindra azy,\nLasany ny havanao,\nFa navotany ho Azy,\nKa derao ny Tomponao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (653, 3, 'Mangataha fahendrena,\nKa isao ny andronao ;\nMikatsaha famonjena\nSy fiainana vaovao,\nMba horaisiny tokoa\nHonina  any Aminy,\nKa hekeny ho sakaiza,\nMba ho ao an-tranony.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (654, 1, 'Eo an-kavanan-dRay Hianao,\nRy Zanakondry ô !\nKanefa loza mahamay\nNo niaretanao.\nNy henatra aman-kelokay\nNihatra taminao ;\nNivesatra ny otanay\nNo nahafatesanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (654, 2, 'Ry Zanakondry masina\nNijaly an-tsitrapo,\nTeren’ny fitiavana\nHanala otako !\nNy tongotra aman-tananao\nDia  voafantsika ;\nNy gadranay dia  tapakao,\nIzay dia  afaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (654, 3, 'Vahoaka alinalina\nMitafy fotsy erý,\nKa samy miramirana\nMahita an’i Kristy.\nNy hira fihobiana\nManakoako ao,\nManeho fa navotana\nIzao tontolo izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (654, 4, 'Indreo ny vahoakanao\nAn-tapitrisany\nMitondra ny Anaranao\nEo an-kandriny.\nHe ! toy ny rian-drano be\nNy fiderana Anao,\nVoasokatrao ny lanitra\nHo an’ny olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (654, 5, 'Misaotra Anao izahay, ry Ray\nIzay navotanao,\nNatolotrao ho solonay\nNy Lahitokanao.\nRy Zanakondry, Avotray,\nDerainay Hianao,\nDerainay ho mandrakizay\nNy fitiavanao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (655, 1, 'Zavatra iray ihany\nNo hotadiavina,\nFa izay atý an-tany\nDia tsy mampianina\nNy nofo dia zava-mandalo\nihany,\nSatria ho lo ao anatin’ny tany ;\nRaha azoko kosa ny zavatra\niray,\nNy foko ho sambatra\nmandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (655, 2, 'Tadiavinao tokoa va\nny zavatra iray ?\nMba hevero fa ny soa\nMomba ny mandrakizay ;\nKa aza fatoran’ny eto intsony,\nKatsaho tokoa ny any \nambony,\nFa any no misy ny zavatra iray\nIzay hifalianao mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (655, 3, 'Ry Fanahy ô, ampio\nNy finoako izao !\nRy Mpamonjy ô, tahio\nNy mpangataka Aminao !\nTariho ny foko hitady tokoa\nNy any ambony izay \nmahasoa ;\nJesosy ihany no zavatra iray,\nIzay ho anjarako mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 1, 'Jesosy Tompoko no làlana,\nFahamarinana, fiainana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 2, 'Tsy afaka manatona ny Ray\nRaha tsy amin’ny alalany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 3, 'Jesosy Tompon’ny fiainana ô !\nAoka Hianao no ho fiainako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 4, 'Etý an-tany : fiainam-bavaka,\nRahatrizay : fiainan-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 5, 'Iriko mafy ny hiakatra,\nHiaina izany fiainan-\ntsambatra !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (656, 6, 'Jesosy tokana no hanome\nAhy ny fiainana mandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (657, 1, 'He, tazako ny lalan-tery,\nIzay misy fahoriam-be,\nKanefa feno hira, re ;\nFa na miady, tsy ho very.\nNy mino izay mizotra eo,\nNa misy mafy aza eo,\nHahazo lakrona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (657, 2, 'He, tazako Sakaiza mora,\nMpanala fahoriam-be,\nMpiaro soa, Mpanome\nNy toky tsara isan-ora ;\nSakaiza feno herim-po,\nMamonjy amin’ny manjo ;\nSakaiza tsy manova.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (657, 3, 'He, tazako ny lamba soa\nOmena ny voavotra\nHo fanesoran-keloka\nSy fanavaozana  azy koa ;\nFa Jeso efa nanome,\nNy rany sarobidy re,\nHo fanadiovana  azy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (657, 4, 'He, tazako ny andro avy,\nIzay hialako sasatra,\nKa ny ririnin’efitra\nHanjary lohataona mamy ;\nKa any vao hivelatra\nNy vonindraozin-danitra\nAo amin’ny Edena.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (657, 5, 'He, tazako ny voavonjy\nAo amin’ny Mpampakatra,\nDia  ao an-tampon-danitra,\nIzay niakaran’ny Mpamonjy ;\nMiravoravo izy ao,\nMihira fideram-baovao\nHo an’ny Zanakondry.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (658, 1, 'Ny ony tsy miato, fa mitady\nNy ranomasina ihaviany ;\nKa toy izany koa ny fanahy\nMitady ny Andriamaniny,\nAndriananahary\nNo Tompoko nahary\nNy faniriako,\nIzay mitady Azy, ka manary\nIzay misakana ny diako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (658, 2, 'Izay midongy sy mirehareha\nTsy hahatratra ny Edenany ;\nLomorina ny rano tsy\nmandeha,\nKa samba ao am-pijanonany ;\nKa toy izany koa :\nHo samba avokoa\nIzay mijanona,\nKa tsy manatona ny Tompo\nsoa\nIzay mitahy sy mamelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (658, 3, 'Tsy misy zavatra atý an-tany\nMahafa-po ny faniriako ;\nVahiny aho eto ka mankany\nAn-danitra izay fodiako.\nNy Tompoko malala\nHo avy hampiala\nNy olony atý ;\nIzaho dia tsy misalasala\nHanaraka ny Tompo ho arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (659, 1, 'Ao an-danitra\nDia ho sambatra\nIzay tia ny Tompo atý !\nMalahelo izao,\nRavoravo re, ao\nAmin-dRaintsika an-danitra\narý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (659, 2, 'Fahoriana\nAo an-dalana,\nFa iharan’ny zava-manjo ;\nAo an-danitra re,\nFiadanana be ;\nMba hevero, ry havana ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (659, 3, 'Aza kivy ô,\nRy fanahiko,\nNa dia  maizina aza atý ;\nVetivety izao\nDia hody hianao\nAo am-pahazavana arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (659, 4, 'Ry Mpanavotray,\nIndreto izahay\nMpivahiny an-dalana izao ;\nTsy tamana atý,\nTe-hifindra arý\nAo an-danitra ao Aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (660, 1, 'O, mamiratra fatratra erý\nIrony kintana tazana etý,\nKa manainga ny foko hankao\nAmin-tranon’ny Raiko izao,\nRaiko ô, mba vohay\nAho hiditra ao Aminao !\nRaiko ô, mba vohay\nFa iriko ny honina ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (660, 2, 'Feno ota ny tany atý ;\nTsy mba misy izany arý,\nTena masina ireo mponina ao,\nKa mifaly mijery anao.\nRaiko ô, mba vohay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (660, 3, 'O, fonenana mamiko re ;\nFa Jesosy nirina elabe,\nSady aina iainako izao,\nNo miandry hndray ahy ao,\nRaiko ô, mba vohay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (660, 4, 'Mba tsilovy ny lalako izao,\nRy Fanahy Mpitarika ô,\nHahitako ny tranon’ny Ray\nHovantaniko rahatrizay,\nRaiko ô, mba vohay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 1, 'Hevero ny fihaonambe\nArý an-danitra  ;\nVahoaka izany re !\n:,: He, samy sambatra :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 2, 'Ho vory ao ny iraky\nNy Tompom-bokatra,\nMitondra amboarany\n:,: Am-pihobiana :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 3, 'Ny isam-pirenena atý\nDia samy misy ao,\nNy teny izay ninoany\n:,: Tanteraka izao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 4, 'Ny feon’ny fisaorany\nHo toy ny riana,\nNy hira iray iraisany,\n:,: Ka samy mirana :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 5, 'Ny lanitry ny lanitra\nDia feno hobiny,\nNy Zanakondry masina\n:,: No ao anivony :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (661, 6, 'Ry Ray be fitiavana,\nIzahay mba taominao,\nMba samy ho tafangona\n:,: Ao anatrehanao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (662, 1, 'Ry mpivahiny reraka sy ana,\nHerezonao  ny dia, fa irý\nNy lapa soan’ny fanantenana,\nKa aza mba mamoy fo etý,\nFa vetivety Hianao no eto,\nDia  tapitra ny alahelonao,\nKa dia mba ononinao ny sento,\n:,: Fa mihafohy ny alehanao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (662, 2, 'Jesosy no mpitsinjo mpivahiny\nKa Izy dia ta-handray anao ;\nNy fony dia toy ny fahiny\nKa vonona hitantana anao,\nKa miezaha hanatratra eny,\nAntsoiny hianao izao hoe :\nMinoa Ahy araka ny teny,\n:,: Ny tranon-dRaiko dia\nlehibe :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (662, 3, 'Mangoraka anao izao ny fony\nNy tena aman-drany vatsinao,\nKa aza mba mihemotra\nintsony,\nFa ampy ho anao Jesosinao,\nKa raiso ny finoana ho\nampinga,\nNy teny masina ho sambatra,\nNy fahamarinana mba\nhahakinga,\n:,: Ny fitiavana hanandratra :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (663, 1, 'Indreo ao an-danitra ny\nolo-marobe,\nMitsangana manatrika ny \nZanakondry, re ! \nAkanjo fotsy lava no\nanaovany,\nKa sampa-palma fandresena\nno an-tànany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (663, 2, 'Ka iza moa ireo, ary avy\ntaiza re ?\nIreny dia avy tamim-\npahoriam-be,\nNanasa sy namotsy ny\nankanjony\nTao amin-dran’ny Zanakondry,\nIzay Mpanavony.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (663, 3, 'Ny tia ny maloto tsy ho ao\nan-danitra,\nFa masina ny tonga any, sady\nsambatra\nMihira fiderana sy fisaorana\nNo zavatra ataony amim-\npifaliana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (663, 4, 'Endrey, maniry mafy aho mba\nho isany,\nHiaraka hihoby sy hifaly\nAminy ;\nAnefa an’ny Tompo ny \nfotoana,\nKa dia Izy no manendry\nny hodiana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (664, 1, 'Ny voarain’Andriamanitra arý\nKa afaka tatý\nAn-tany be mahory,\nDia lasa nody, fa tsy maty tsy\nakory.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (664, 2, 'Ny tafafindra monina ao\nan-danitra ao,\nNankao an-tany vao\nFonenan’ny mazava,\nDia  tsy mba maty, fa nandao\nny mety rava.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (664, 3, 'Ny voasatro-boninahitra\ntsy lo,\nKa ao manaram-po\nMisotro ranon’aina,\nDia tsy mba maty, fa nandray\nny nantenaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (664, 4, 'Ny ondrinao izay manaraka\nAnao\nHo ao am-balanao,\nRy Ilay Mpiandry Tsara,\nDia tsy mba maty re, fa tsara\nfiafara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (664, 5, 'Ny fo mivantana aminao\nIzay nanome\nNy hasambaram-be\nNandramany sahady,\nDia  tsy mba maty, fa \nmpandresy afaka ady.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (665, 1, 'He ! tazana ao an-danitra\nVahoaka finaritra\nMitafy fotsy, ravo fo,\nKa mirana  avokoa.\nNy vazantany efitra\nNo nielezan-kafatra,\nDia  tonga izao\nNy ory tao,\nKa afaka  entana.\nNy ran’ilay Mpamonjiny\nNahafotsy ny ankanjony ;\nKoa dia  ireo\nManandra-peo\nAm-pifaliana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (665, 2, 'Ho an’Andriamanitra\nAn-tsezam-boninahitra\nNy famonjena anie !\nHobin’ny marobe,\nIlay Zanakondry avotra\nNo nanafahan-tahotra\nIzay voaray\nHo zana-dRay\nHobio mandrakizay !\nKa saotra voninahitra\nHo an’Andriamanitra,\nO, mba derao,\nAnkalazao,\nHaleloia ! Amena !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (666, 1, 'Hajao ny olo-masina\nMpanaraka ny Tomponao,\nMiely sy Manerana\nIzao tontolo izao ;\nIreo dia tonga ohatra,\nKa tokony harahinao,\nNa efa olon-dehibe,\nNa zaza hianao ;\nTanteraka ny sasany,\nKa efa nody amin-dRay ;\nNy sasany dia mbola atý\nMilasy amin-day.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (666, 2, 'Ka betsaka ny isany,\nNa atý, na ao an-danitra ;\nNy Rainy kosa dia iray,\nAndriamanitra ;\nIreo maro namanao\nMiantso koa maka anao\nHiaraka hiezaka\nHankao an-danitra ;\nKa dia miezaha re,\nKa aza kivy hianao ;\nJereo ao aloha ao\nNy loka ho anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (667, 1, '« Moa te-hiara-dia ?\nMoa te-hiara-dia,\nO, ry havana malala,\nHo ao Kanana izao ? »\n« Eny, te-hiara-dia,\nEny, te-hiara-dia,\nFa antsoin’Ilay Mpamonjy\nHo ao Kanana  izao ! »\n:,: Dera, dera, haleloia ! :,:\nAn’Andriamanitsika\nIzay ao ambony ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (667, 2, 'Kristy re no lalantsika,\nVatolampy, arontsika,\nIzy no mitondra antsika\nHo ao Kanana  izao.\nTano izahay ry Tompo,\nHatanjaho ny mpanompo,\nKa tsilovy ny haleha\nHo ao Kanana  izao !\n:,: Dera, dera, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (667, 3, 'Tazana erý sahady\nPerla re ny vavahady,\nVolamena ny tanàna\nErý Kanana  erý.\nE ! hahita ny Mpamonjy,\nKa hifaly fa tra-bonjy,\nSy hidera re ny vonjy\nErý Kanana  erý.\n:,: Dera, dera, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (668, 1, 'Aingao ny masontsika\nHitsinjo tany soa ;\nFa ao ny Tompontsika\nManomana tokoa\nTanàna, trano tsara,\nFianan-tsambatra e !\nIzany no anjara\nHo an’ny mino re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (668, 2, 'Ny andro fifaliana\nHo avy ka  antenao ;\nNy andro nihafiana\nHo foana sy handao ;\nNy taona fihobiana\nHomen’ny Tomponao ;\nHo feno fifaliana\nIzao rehetra  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (668, 3, 'Ny andro maharavo\nTsy ho ela  intsony izao ;\nHiarina ny lavo\nHandray ny tena vao,\nIrio ny Tompo Avo\nHo avy hanavao ;\nHo faly izay mahazo\nIzao fiainan-tsoa izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (669, 1, 'Iza moa ireo mitsangana\nArý an-koatra arý ?\nNy seza fiandrianana\nNo eo anilany,\nHaleloia, mihobia !\nFa Mpandresy izy e !\nKa mitana ny rofia,\nVoninahi-dehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (669, 2, 'Fahorina mafy loatra\nNo nentin’izy ireo,\nKanefa tsy nitsoaka\nNijaly fa tsy leo,\nFamonjena firenena\nAdy masina tokoa,\nNanolorany ny tena\nHo mpitory “Teny Soa”.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (669, 3, 'O ! ry mponina ao an-danitra\nAnjely marobe,\nOmeo voninahitra\nNy maritiora e !\nIlay Kanana soa tokoa\nNo fonenany izao\nHetaheta, sanatria\nTsy haninona azy ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (669, 4, 'Mba avia, ry olom-belona\nAvia, ankalazao\nIlay maty nefa velona,\nJesosy Tomponao.\nDera, laza atolotray\nHaleloia, haleloia ! \nHo Anao, Mpanjakanay,\nHo Anao, doria doria.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (670, 1, 'Hianao, Jehovah Tompo\nNo itokiako\nKa tsy matahotra aho\nFa manana Anao.\nRy Harambato mafy,\nIzay ao mandrakizay\nNy foko tsy hanahy,\nFa miankina Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (670, 2, 'Mitsoka mafy loatra\nNy rivo-doza re,\nKanefa tsy hihoatra\nNy fefy lehibe.\nRy Aron’ny tanora,\nRy Tokin-dehibe\nHo heriko isan’ora\nNy herinao anie.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (670, 3, 'Raha tonga re ny fetra,\nNy andro tapitra,\nHaneno ny trompetra,\nHihovitrovitra.\nKanefa matokia,\nRy foko fa Jeso\nHitantana ny dia\nHo eo anilanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (671, 1, 'Ao am-pitan’ilay lohasaha\nizay\nAlo-pahafatesana koa,\nMoa mba tsinjonao ve ‘rý\ntanàna iray\n‘Zay mamiratra toa vatosoa ?\nNy masoandrony tsy mba\nmilentika koa\n‘Zay rehetra mby ao voadio\navokoa\nKa mamiratra toa vatosoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (671, 2, 'Ny Mpanjakany dia ‘Lay Ray\nbe fitia\nSady aina ho an’izay ao ;\nRy mpanota mibebaka ô,\nmatokia\nHosasany hadio hianao,\nNy masoandrony ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (671, 3, 'Nefa tsinjoko fa ny fidirana\nao\nDia am-pototr’ilay lakoroà !\nHe mideza sy ety ny lala-\nmankao\nO, ry foko, moa ho kivy va ?\nNy masoandrony ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (671, 4, 'Olo-masina maro no tsinjoko\n‘ndreo\nAo mihira sy mitafy vao ;\nIriko indrindra ho isan’ireo\nKa tantano, Jeso aho, ho ao.\nNy masoandrony ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 1, 'Ao ny lanitra tsara tokoa\nIzay fonenana mandrakizay,\nTany be hasambarana koa ;\nAo isika hihaona indray ;\nMifalia, mihobia,\nFa  ao isika  hihaona indray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 2, 'Ao ny lova izay mamin’ny fo\nSy fiainana mandrakizay,\nVoninahitra tsy mba ho lo ;\nAo isika hihaona indray ;\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 3, 'Ao ny Ray, Loharanon’ny soa,\nHovantanina rahatrizay,\nIzay hampiditra hoe : \nmandrosoa\nAo isika hihaona indray ;\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 4, 'Ao ny Zanaka be indrafo\nIzay nijaly tatý fahizay ;\nNefa tsy mba nahita ny lo,\nFa  efa velona mandrakizay ;\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 5, 'Ny fanahin’ny Ray ao tokoa\nIzay mitarika antsika izao\nSy mananatra antsika ho soa,\nNo mitondra antsika hankao\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (672, 6, 'Matokia, miravoa, mifalia,\nFa azontsika ny lapan’ny Ray\nMidérà, mihirà, mihobia,\nFiadanana mandrakizay\nMifalia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (673, 1, 'Matokia, mpivahiny !\nAny an-tanàna arý\nMisy trano soa madio\nMandrosoa hianao :\nMisy fitsaharana any,\nMatokia hianao ;\nMisy fitsaharana any,\nIzay ho azonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (673, 2, 'Ao ny Tomponao miandry\nHanome anao ny soa ;\nMisy fiadanana any\nVoavoatra ho anao :\nMisy fitsaharana ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (673, 3, 'Any tsy mba misy rofy\nAmam-panaintainana ;\nIzay rehetra tonga any\nAfa-pahoriana\nMisy fitsaharana ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (673, 4, 'Tsy mba mamy va ny mody\nMba ho afa-tsatsatra\nSy horaisin’i Jesosy\nAmim-piadanana ?\nMisy fitsaharana ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (674, 1, 'Maniry izahay\nHo any aminao,\nJerosalema tanànanay,\nTanàna masina ;\nNy trano lay atý,\nTsy maintsy rovitra,\nKanefa tazanay erý\nIlay tsara lavitra ;\nTazanay, tazanay\nNy tany masina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (674, 2, 'Mba tanterahonao,\nRy Tompo tia anay !\nNy fikasan’ny Zanakao\nNataony taminay ;\n« Ho any amiko\nIzay nomenao Ahy,\nFa tiako ho hitany\nNy voninahitrao »\nTazanay, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (674, 3, 'Mankany aminao\nIzao ny dianay,\nNa sarotra ny làlana,\nMihira izahay ;\nNy hasasaranay.\nTsy misy intsony izao,\nFa tazanay sahady re,\nNy tany masina :\nTazanay, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (675, 1, 'Ny tranon’ny Raiko, trano\nmadio,\nTsy misy mahory na zava-\nmanjo ;\nAvia, ry malahelo, hankao,\nMizora, fa Izy hampiditra\nanao !\n:,: Sambatra re izay tonga\nao :,:\n:,: Faly tokoa :,:\nIzay tafiditra ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (675, 2, 'Mandalo izao fiainana izao,\nKa aza sondriana atý hianao ;\nNy ratsy izay mitaona reseo,\nNy any an-danitra no mba\nirio !\n:,: Sambatra re, ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (675, 3, 'He, ao no ho hitantsika tokoa\nNy tavan’Ilay Zanakondry\nmadio ;\nNy mpino tetý no efa ao koa,\nManiry hihaona amintsika\ntokoa,\n:,: Sambatra re, ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (675, 4, 'Tsy misy manjo izay afa-\nmankao,\nNa andro mahory hitseran’ny\nfo ;\nFa any tsy misy alina koa,\nTanàna mazava, fonenana\nsoa,\n:,: Sambatra re, ..sns :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (675, 5, 'Ny mino rehetra ory tetý\nTafakatra any ka faly tokoa,\nMihira ny famonjen’i Jeso,\nMidera ny fiandrianan’ny Ray.\n:,: Sambatra re, ..sns :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (676, 1, 'Misy tany mahafaly,\nTany izay alehanay,\nTany misy an’i Jeso,\nJeso Tomponay !\nTany soa maharavo,\nTany tadiavinay !\nAfa-tahotra, afa-doza\nIzay tafiditra ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (676, 2, 'Tany tia mpivahiny,\nTany namboarinao,\nRy Jesosy be fitia,\nJeso Tomponay !\nHo anay atý an-tany\nIzay mba malahelo fo\nNoho ny fomban-tany ratsy\nMahazatra anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (676, 3, 'Maro be ny fahavalo\nIzay miady aminay ;\nMafy ny fakam-panahy\nHitanay atý ;\nSakan-dalana, ady mafy\nNo alehanay izao :\nBe tokoa ny mampahory,\nNefa fantatrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (676, 4, 'Eny, fantatrao, ry Jeso\nFa  izahay mpanomponao\nDia  handresy ny devoly\nNoho ny herinao ;\nKoa  ampionao izahay re\nMba hiankina aminao\nHahatraranay ny tany\nNamboarinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (677, 1, 'Fandalovana atý,\nLanitra honenako ;\nEfitra izao tany izao,\nLanitra honenako ;\nVoahodidin-doza atý,\nReraka ao an-dalana,\nValaka sy sasatra ;\nLanitra honenako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (677, 2, 'Na mandrivotra aza atý,\nLanitra honenako ;\nTany no hilaozako,\nLanitra honenako ;\nAlon-dranomasina\nSy ny rian-drano be\nTsy hisakan-dalana ;\nLanitra honenako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (677, 3, 'Ao an-tratran’i Jeso,\nLanitra honenako ;\nAfa-pahoriana\nLanitra honenako ;\nAo ny olo-masina\nIzay mba tiako tatý,\nFaly raha hitako ;\nLanitra honenako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (677, 4, 'Dia tsy halahelo aho,\nLanitra honenako ;\nMafy ny manjo atý,\nLanitra honenako ;\nAo no hitoerako\nFaly anatrehan-dRay,\nAo homeny toerana ;\nLanitra honenako.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (678, 1, 'He ! misy re fonenana ;\n:,: Tsara e ! :,:\nKanefa tsy mba lavitra ;\n:,: Tsara e ! :,:\nNy trano ao voavoatra ;\nHo an’izay mangataka ;\nFa Jesosy no Tompony,\n:,: Tsara e ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (678, 2, 'Tsy misy fahoriana ao ;\n:,: Tsara e ! :,:\nTsy misy alahelo ao ;\n:,: Tsara e ! :,:\nNy ota tsy tafiditra ao,\nNy devoly tsy monina ao,\nFa ny anjely masina ;\n:,: Tsara e ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (678, 3, 'Ny Tompo dia miantso anao ;\n:,: Tsara e ! :,:\nNy teniny henoinao ;\n:,: Tsara e ! :,:\n“ Avia atý ry zanako !\nKa mibebaha re anio,\nTsy misy ‘zay holaviko ”\n:,: Tsara e ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (678, 4, 'Efa azonay ny santatra ;\n:,: Tsara e ! :,:\nVoavelany ny heloka ;\n:,: Tsara e ! :,:\nHo any faingana izahay,\nFa Kristy manome anay\nFiainana mandrakizay ;\n:,: Tsara e ! :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 1, 'Ny lapan’Andriamanitra\nNo ivorian’olona\nTsy omby alinalina,\nIzay madio sy masina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 2, 'Izao firenen-drehetra  izao\nDia samy afaka hankao ;\nTsy fomban’Andriamanitra\nHizaha tavan’olona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 3, 'Ireny no naharitra\nNy hazo fijaliana,\nNa dia nanao ny henatra\nHo zava-tsinontsinona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 4, 'Endrey, ny hafaliany !\nMazava ny tarehiny ;\nFafàn’Andriamanitra\nNy ranomaso latsaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 5, 'Ataony ao ny hira vao\nHoe : “ Voavono Hianao ;\nNy ranao, Zanakondrinay !\nNo nanavotanao anay ”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (679, 6, 'Ry Jeso ô Mpanavotra !\nIzaho dia milatsaka\nHo isan’ny mpanomponao\nManaraka ny dianao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (680, 1, 'He ! misy tany soa\nIzay tsy atý ;\nMaro ny olona\nFaly arý ;\nTsara fonenana\nSambatra sy tretrika\nSady finaritra\nIzay mponina  ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (680, 2, 'Aza sondriana\nFoana atý ;\nO ! miomàna re\nMba ho arý ;\nBe fiadanana,\nTsara fitoerana,\nBe haravoana\nIzay mponina  ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (680, 3, 'Mafy izay manjo\nHita atý,\nKa misikìna re\nMba ho arý ;\nJeso mitandrina,\nSy mitahy fatratra\nAry mahenina\nIzay mponina  ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (680, 4, 'Aza variana\nFoana atý ;\nO ! miaingà izao\nMba ho arý ;\nJeso Sakaizanao\nNo Mpitari-dalana,\nKa dia hitondra  anao\nHo tonga ao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (681, 1, 'Ao ambony ny lapan’ny Ray,\nAo an-danitra feno ny soa,\nHasambarana mandrakizay,\nKristy soa no iriko tokoa,\n:,: Kristy soa, Kristy soa\nNo iriko ho ahy tokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (681, 2, 'Misy tsara ihany atý,\nMba ho santatra eto izao ;\nFa tsy mety ho toy ny arý,\nKa iriko tokoa mba ho ao,\n:,: Kristy soa, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (681, 3, 'Ny voavotra ravo izao,\nAfaka ota ka faly tokoa,\nAfa-paty izay tonga ao,\nAfa-doza, mahazo ny soa,\n:,: Kristy soa, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (681, 4, 'Tsy ny lanitra be zava-tsoa,\nFa ny Tompo malalaka ao ;\nTsy mba tahotro helo tokoa,\nFa ny Tompo tsy foiko izao.\n:,: Kristy soa, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (682, 1, 'Enga ka hanana elatra soa\n:,: Aho etý :,:\nMba hanidinako avo tokoa\n:,: Hody erý ! :,:\nFo malahelo ny ratsy natao\nNo ela-tsoa hitondra hankao ;\nHanana elatra aho anie,\nMba hanidinako ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (682, 2, 'Trano mamiratra tsara irý,\n:,: Raiko no ao :,:\nNa nahabe ahy aza ity,\n:,: Mamiko ao :,:\nHavako maro no sambatra ao,\nFaly ny foko hankeny izao ;\nEnga ka hanana elatra aho,\nMba hanidinako ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (682, 3, 'Na dia an-danitra aza erý,\n:,: Lavitra va ? :,:\nRaha tarihin’i Jeso etý,\n:,: Ketraka va ? :,:\nTsia, fa hiainga tokoa aho\nizao !\nIndro akaiky ny lala-mankao ;\nEnga ka hanana elatra soa,\nMba hanidinako ao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (682, 4, 'Andriamanitra Ray no erý,\n:,: Tsara tokoa :,:\nJesosy koa ‘Lay Mpamonjy\ntetý,\n:,: Zanaka soa :,:\nAo ny Fanahy Masina re !\nTapitra ao avokoa ny soa ;\nHanana elatra aho anie,\nMba hanidinako ao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (683, 1, 'Arý an-koatra arý\nIlay Paradisa soa\nToa takona atý,\nKanefa ao tokoa,\nMamirapiratra   ao,\nNy endriky ny Ray ;\nJesosy Tompo ao\nMiandry mba handray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (683, 2, 'Tsy misy ny hanjo\nIzay tafiditra ao ;\nNy ranomaso koa\nTsy afaka hankao\nNy hazavana soa\nManelatrelatra ao ;\nNy zava-mahasoa\nTsy misy izay tsy ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (683, 3, 'Anjely marobe\nMihira any koa ;\nIreo olo-masina  e\nMifaly avokoa.\nAndeha re izao,\nSakaiza marobe,\nArio ny tahotrao,\nKa dia mizora re !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (683, 4, 'Raha misy loza etý,\nJereo ny Tomponao ;\nFa izy raha tetý\nNitondra toy izao.\nIreo fahoriana atý\nOdio tsy hita re,\nHandraisantsika  arý\nNy fiadanam-be.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (684, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (684, 2, 'Raha ho faty aho\nKa handao ny havako\nAry dia hisaonany,\nFa handeha  hifaly aho ;\nRaha afaka ny aiko,\nDia ho finaritra aho.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (684, 3, 'He ! antsoiny aho,\nHo any an-danitra,\nDia faly ny foko,\nSy ravo ny kiboko,\nFa  azoko ny iriko\nSy nantenaiko ela.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (684, 4, 'Havelako ny tany,\nFa efa azoko tokoa\nNy fifaliam-be ;\nLovako ny lanitra,\nDia tsy matahotra\nNy hafatesana aho.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (685, 1, 'Any ambony fonenana tsara\nDia tsy mba misy itseran’ny\nfo ;\nEny iriko ho tsara miafara\nHiara-manjaka  aminao ry\nJeso !\nIsaoranay ny Tomponay,\nRaha ‘mby any dia faly\ntokoa ;\nIsaoranay ny Anaranao soa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (685, 2, 'Rehefa tonga ilay andro\nmalaza\nIzay hanatrehana Anao, ry\nJeso !\nIzay no heveriko ho tena\nlaza :\nDia ny hidera Anao,\nTompo ô !\nIsaoranay ny Tomponay,\nRaha ‘mby any tsy mba ory\nkoa ;\nIsaoranay ny Anaranao soa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (685, 3, 'Ao izany havana tena malala\n‘Zay mahafaly fa hita indray,\nNefa izay hampifaly tokoa\nDia ny mihaona aminao ry \nJeso !\nIsaoranay ny Tomponay,\nRaha ‘mby any tsy misy\nmanjo ;\nIsaoranay ny Anaranao soa !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (686, 1, 'Iza re ireto avy,\nVoatafy lamba soa,\nSamy voasatroka avy\nSatro-bolamena koa ?\nAvy taiza izy ireo,\nKa ho aiza izy ireo ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (686, 2, 'Izy   ireo dia voavonjy,\nVoavela heloka ;\nSamy nino an’i Kristy\nFony mbola velona ;\nBe ny ratsy nentiny,\nFa izao dia sambatra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (686, 3, 'Maro be ny fahavalo\nNiadiany tatý,\nFa tsy mba misy izany\nintsony ;\nAfaka ny sarotra,\nAfa-pahoriana,\nHe, ny hafaliany !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (686, 4, 'Miramirana tarehy,\nIndreo, avy izy   ireo\nMba hihoby an’i Kristy,\nTompo sy Mpanjaka koa,\nFa nahazo zava-tsoa,\nKa dia tretrika tokoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 1, 'Midera Anao ny olo-masina\nNoho ny fandresena \nnomenao ;\nRy Jeso ô ! Mpitari-dalana !\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 2, 'Ory sy lao, fadiranovana,\nNatao ho zava-tsinontsinona\nNefa ireo no tsy nivadi-po ;\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 3, 'Mafy ny ady aman-tafika,\nIzay nihatra tao an-dalana,\nFa Hianao no nitokiany ;\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 4, 'Indro ! mpandresy no idirany\nAo afovoan’ny ao an-danitra,\nHandray ny satro-boninahitra ;\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 5, '« Mpanompo tsara sady\nmahasoa,\nMidira am-pifalian’ny\nTomponao »\nHe, sambatra izy, fa efa\nvoaray ;\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 6, 'Aoka ireo no hotahafinay\nAmin’ny ady iadiana,\nHiraisam-boninahitra aminy :\nHaleloia ! Haleloia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (687, 7, 'Ankehitrio, Anao ny fiderana,\nRy Ray sy Zanaka, Fanahy koa\nEran’ny tany aman-danitra :\nHaleloia ! Haleloia !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (688, 1, 'Ao ny andro lehibe\nMisy fifaliam-be,\nDia ny andro hahatongavana\nAmin’ny fonenana\nFeno fiadanana.\nTsy mba misy ota na heloka,\nEny, tena andro malaza tokoa\nizay.\nJeso Ilay nandresy ny fasana,\nIzy no hitantana\nAo am-pahafatesana,\nKa ho mora ny handehanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (688, 2, 'Ao ny andro lehibe\nMisy fifaliam-be,\nIzay hihaonana any an-danitra,\nAfa-pahoriana,\nAfa-pijaliana,\nSambatra tokoa sy tretrika.\nEnga ka ho ao avokoa isika\nizao\nKa tsy hisy very na dia iray !\nTava miramirana,\nEndrika mamiratra\nNo hananantsika mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (688, 3, 'Ao ny andro lehibe\nMisy fifaliam-be,\nHahitana an’Andriamanitra\nSy Jesosy Zanany\nMbamin’ny Fanahiny.\nHoby no hameno ny lanitra.\nSerafima sy arkanjely no\nnamana\nMankalaza ilay feno indrafo.\nNy fitomaniana\nSy ny fisentoana\nTapitra, fa afaka ny manjo.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (689, 1, 'Ny hiran’ny  lanitra\nTsara tokoa\nSy mahafinaritra, re !\nHenoy izany feon’ny\nolona maro\nizay faly tokoa mihira hoe :\n(Feo 1)\nHaleloia, Amen !\nHaleloia, Amen !\nHaleloia, Amen !\nHaleloia, Amen ! \n(Feo 2)\nNy dera aman-daza,\nsy hery anie\nHo Anao, ry Zanak’ondry !\nNy dera aman-daza,\nsy hery anie\nHo Anao, Anao, Amen !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (689, 2, 'Toy ny feon’ny rano\nmahery ny hira ataon’ny\ntra-bonjy arý ;\nManerana ny lanitra\nny fiderany,\nFa vita ny adiny mafy tatý,\nHaleloia, Amen ! ..sns.\nNy dera aman-daza, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (689, 3, 'Tsy mety mangina\nny hiran’ny lanitra\nVelona mandrakizay !\nEndrey ny fihaonana\nany an-danitra\nsy ny fiainana mandrakizay !\nHaleloia, Amen ! ..sns.\nNy dera aman-daza, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 1, 'An-danitra, an-danitra\nTempolin’i Jehovah,\nNo hahitàna Azy, re\nAm-boninahitra !\n:,: Ka masina i Jehovah :,:\nNo hohiraina ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 2, 'An-danitra, an-danitra\nNo mba hamiratra\nNy voninahi-dehibe\nHananantsika e !\n:,: Raha tonga\nny fihaonana :,:\nAo amin’ny Jehovah.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 3, 'An-danitra, an-danitra\nNo ho finaritra\nNy tena izay nijaly be\nAm-pahoriana\n:,: Fa any no hifaly e :,:\nFahasambarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 4, 'Ny fo sy ny fanahy e,\nNo faly anie,\nFa afaka ny entana,\nIzay nivesarany ;\n:,: Ka fitsaharan-dehibe :,:\nOmen’i Jehovah..', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 5, 'Tsy hain’ny lela anie\nHanonona izao\nNy soa antenaina e,\nRaha Anatrehany,\n:,: Manenika ny lanitra :,:\nNy fitiavany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 6, 'Ry Jeso Kristy Tomponay\nTariho izahay,\nKa mamelà ny helokay,\nMba hitokianay,\n:,: Fa tsy ho menatra\nizahay :,:\nFa ao i Jehovah.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (690, 7, 'Raha avy ny hodianay\nHo anatrehany\nTariho izahay,\nMba ento, Jeso, izahay\nHankany Aminy !\n:,: Mba ho anay \nmandrakizay  :,:\nNy fitsaharana !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (691, 1, 'Ry Jehovah, Ray Tsitoha\nVory eto izahay,\nRay hatramin’ny taloha,\nMihainoa ny vavakay,\n:,: Mitsangàna :,:\nEto afovoanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (691, 2, 'Andro fampakaram-bady,\nFifaliam-be izao ;\nHianao no mampivady\nRaha voatendrinao ;\n:,: Vady hendry :,:\nLova avy aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (691, 3, 'Ry Jehovah Ray malala !\nMba tezao ireo ho soa,\nSamy tsy hisalasala,\nFa hifankatia tokoa ;\n:,: Hamasino :,:\nMba ho olonao ireo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (691, 4, 'Ho fanahy mifandanja\nSady tsy ho toy ny roa,\nHo mpivady mifanaja\nSady mifamaly soa ;\n:,: Akambano :,:\nMba ho fo iray tokoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (692, 1, 'Ry olon-kanao fanekena\nmasina,\nMisaora ny Tompo, fa ny\nsitrapony\nNo nampikambana\nAmim-pitiavana ;\nKa dia tsy olona roa intsony,\nNy tany Edena tsahivina\nindray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (692, 2, 'Ny fahamarinan’\nAndriamanitra\nNo haingo manendrika ny\ntokantrano\nHo toy ny lanitra\nMamirapiratra.\nRy mpivady marina, Kristy no\ntano\nHandamina ny tokantrano\nvaovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (692, 3, 'Ny fahasoavan’Andriamanitra\nNo andry manohana ny\ntokantrano\nKa mampisandratra\nNy te ho sambatra,\nIzay ankohonanareo\natsangano\nHidera ny famindrampo\nlehibe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (692, 4, 'Ny fanaky ny tokantrano\nmasina,\nManoatra be noho ny\nvolamena ;\nNy fivavahana\nSy ny finoana,\nFisaorana sy fiderana homena\nMarain-tsy hariva ny\nMpamonjy e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (692, 5, 'Jehovah Mpitana ny\nfanekena ô !\nTahio ny toky nomeny ho\nmafy,\nHo masina sy to,\nHo marina am-po ;\nKa izy handresy izay\nta-handrafy ;\nHo azy ny lova ho\nmandrakizay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (693, 1, 'Tompo Zanaharinay !\nJereo ny fivorianay,\nIzao fampakaram-bady izao\nTahio sy hamasinonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (693, 2, 'Ireo zatovo roa ireo\nManketo anatrehanao,\nFa mifanaiky izy izao\nHivady sy hifanasoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (693, 3, 'Mangataka aminao izahay\nHitahy ireo havanay,\nRaha ory mba ononinao\nRaha faly, aza ilaozanao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (693, 4, 'Arovinao ny tenany,\nTsinjovy ny taranany ;\nHo-tsara fara anie ireo,\nHo ravoravo toy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (693, 5, 'Ny fifalianay anio\nHitohy hatrarý anie !\nIty andro lehibe ity\nHo santatry ny soa arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (694, 1, 'Izao manatrika Anao,\nRy Jesosy, izahay,\nIndro tokantrano vao\nNo hoarina indray.\nIzahay mangataka ;\nNy tsodranonao omeo,\nKa avia hanapaka\nNy fiainan’izy ireo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (694, 2, 'Ho Kristiana velona\nAnie ny zanany,\nHo vavolombelona\nFa Anao ny tenany.\nAoka mba hamiratra\nNy filazantsaranao,\nVelona hanaraka\nNy fonay ka hazavao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (694, 3, 'Ka ny fiangonana\nHanompoanay Anao,\nHanana olom-banona\nFaly ny hiasa ao.\nAntenainay marina,\nFa Hianao hanohana\nIzay firaisa-masina,\nHisy fahasoavana.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (695, 1, 'Ry Jesosy be fitia !\nManatreha, manampia,\nNy mpanomponao izao\nIzay mivavaka aminao,\nFifaliana madio\nNo omena anay anio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (695, 2, 'Indreo ny havanay jereo,\nMba ho soa anie ireo !\nTanan-droa mifandray,\nNofo roa, tonga iray\nMifaniry soa tokoa,\nMifanampy, mifanoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (695, 3, 'Ilay tso-drano hatrizay\nNo omeo ireo havanay ;\nMba ho marofara anie,\nHanam-panambinana,\nFeno fiadanana\nNoho ny fitahianao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (695, 4, 'Raha ory mba vangio,\nMitaraina mba valio ;\nMitoera aminy\nAo an-tokantranony\nHamasino, hafanao\nNy fitiavany Anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (696, 1, 'Ry Jehovah Ray kiady\nSy mpikarakara anay,\nKa nitahy ny mpivady\nTao Edena fahizay,\nIndro misy tonga koa\nEto anatrehanao,\nManam-pikasana soa\nHo mpivady hatr’izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (696, 2, 'Mba tahio, ry Ray mpiahy,\nNy firaisany izao,\nHifanaraka am-panahy\nSy hiankina aminao.\nNy tsodranonao fahiny\nAngatahinay indray,\nMba hatao ankehitriny\nHahatò ny hatakay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (696, 3, 'Raha omenao zaza izy,\nDia mba ampio re,\nHitaizany ny ankizy\nAmim-pahendrena be ;\nAza avela ho irery\nRaha tratry ny manjo,\nFa tafionao ny hery\nMahatanjaka ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (696, 4, 'Raha ao ny andro tony\nMila haharenoka,\nDia taironao ny fony,\nRy Mpamela heloka.\nRaha tapitra ny taona\nEfa voafetranao,\nEnga ka ho tafahaona\nAo an-danitra aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 1, 'Jehovah ô, mba mihainoa !\nIndreo misy olon-droa izao\nIzay manam-pikasana soa\nHanorin-tokantrano vao :\nO ! tahio, O ! tahio,\nMba ho tokantrano soa\nmadio\nO ! tahio, O ! tahio,\nMba ho tokantrano soa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 2, 'Dia angatahinay, ry Ray\nNy fitahianao ho azy ireo\nHo nofo sy fanahy iray\nKa hifanaja hatrizao.\nO ! tahio, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 3, 'Fenoy ny fiadananao\nNy tokantranon’izy ireo\nKa mba hamirapiratra  ao\nNy fivavahana  Aminao.\nO ! tahio, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 4, 'Tahio ireo, ry Tompo soa,\nMba ho mpivady marina,\nHitana sy hanaja koa\nNy fanekena masina.\nO ! tahio, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 5, 'Tahio, hifampitantana\nMandritra ny fiainany,\nKa dia ny fahafatesana\nNo fetra isarahany.\nO ! tahio, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (697, 6, 'Ho maro fara anie ireo\nKa mba hitaiza ho Anao\nIreo solofo ho tia  Anao,\nHanompo sy hanaja  Anao.\nO ! tahio, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (698, 1, 'Nakambanao, ry Tompo ô !\nAdama sy Eva nankatò\nNy fampakaram-bady,\nTahio sy vimbinonao\nIreto anatrehanao\nManaiky ho mpivady,\nHahay handray\nNy anjara izay hihatra\nIzy roa\nAry hifanisy soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (698, 2, 'Dia nifanaiky izy izao ;\nRy Tompo ô ! vimbinonao\nMba ho iray tokoa ;\nNy fony mba tarihonao\nHanaraka ny didinao\nNomenao azy roa ;\nIzao handao\nRay sy reny, ka ny teny\nNolazainy\nEnga anie ka tsy hovany.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (699, 1, 'Tompo ô, endrey ny fitahiana\nIzay natondrakao ny zanakao\nVory eto, feno fifaliana\nAmpoky ny fahasoavanao.\nIzay rehetra nikelezan’aina\nDia nombàn’ny fitantananao\nVonona avokoa izay nilaina\nTsy mba lany ny harenanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (699, 2, 'Indro trano efa novitaina\nHanasoavana taranaka\nHitaizàna, hanolokoloana\nIreo tanora halain-tahaka,\nIzay voafafy, antenaina\nMba hamokatra tsy tapaka ;\nDia ho vanona ny firenena\nKa hitombo, hisandrahaka.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (699, 3, 'Izany re, ry Tompo, no\nfinoana\nTananay, ka mba herezonao,\nIzahay handroso tsy hivoana,\nEo ambany fiarovanao,\nKa ny fangataham-pitahiana\nTompo ô ! akarina Aminao\nNy Anaranao no iaingàna\nHoderaina ny Anaranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (700, 1, 'Raha tsy Jehovah\nNo manao ny trano,\nDia miasa foana\nNy mpanorina.\nRaha tsy Jehovah\nNo miambina àzy,\nMiari-tory foana\nNy mpiambina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (700, 2, 'Nahavita trano\nNy mpianakavy,\nKa mifaly, ravo\nNahatsangana\nNefa koa tsaroana\nFa Jehovah Tompo,\nFeno fitahiana,\nNo nanambina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (700, 3, 'Mankasitraka aho,\nAry koa misaotra\nAn’Ilay Nahary\nBe fitiavana.\nIzay rehetra ahy\nDia nomen’ny Ray ;\nEny, ity trano\nDia avy taminy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (700, 4, 'Tompo ô ! tahio\nIzay miditra ato ;\nDia arovy koa\nIzay mivoaka;\nMba monena ato,\nMiaraka aminay\nAnjakao ny trano,\nHiadananay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (701, 1, 'Miposaka indray ny\nmasoandro,\nNy ali-maizim-be dia tonga\nandro ;\nRy foko, mba derao ny Ray\nTsitoha,\nKa dia andrandrao,\nDerao, ankalazao\nNy Rainao soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (701, 2, 'Ny loza marobe atý an-tany\nDia  niarovanao anay izany,\nKa zava-mahafaly no omeny,\nDia fiarovana\nSy fitiavana\nTsy hita lany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (701, 3, 'Ry Masoandronay, mba\nmitariha\nNy dianay anio mba tsy\nhania ;\nMahery Hianao ka maharara\nNy fahavalonay\nTsy hahataona anay\nHandà ny tsara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (701, 4, 'Ny zavatra manjo tsy\nmampanahy\nNapetraka aminao ny ahiahy,\nKa dia matoky aho, ry\nTsitoha !\nOmeo ny zanakao\nIzay heverinao\nHo mahasoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (702, 1, 'Jehovah Zanahary,\nIzahay miderà Anao ;\nNy alina nanjary\nHo firaketanao.\nMaraina izao ny andro,\nKa lao ny maizina ;\nSavao, ry Masoandro,\nIzay fahoriana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (702, 2, 'Ry Kintan’ ny maraina,\nJesosy Havanay,\nArovinao ny aina,\nTahio izahay ;\nNy asanay anio\nMba hasoavinao,\nNy dianay tariho\nHo any aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (703, 1, 'Tonga ny maraina,\nDimbin’alina,\nKa ny Tompon’aina\nNo isaorana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (703, 2, 'Raha mbola nandry\nAho Tompo ô,\nHianao Mpiandry\nDia aroko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (703, 3, 'Tano sy ampio\nNy madinikao,\nSao hanota anio,\nKa handao Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (703, 4, 'Aoka ny anjara\nFanompoako\nMba ho vita tsara\nKa ho sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (703, 5, 'Ary raha lany\nNy fotoako,\nIzaho hody any\nAnatrehanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (704, 1, 'Ry Tompo ô, tahio\nIzao dianay izao ;\nNy lehibe ampio,\nNy kely mba tezao.\nFa ny Anaranao\nNo iaingana anio ;\nTantano sy vimbino\nAn-dalana izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (705, 1, 'Andeha raha  maraina,\nRaha antoandro koa,\nAndeha raha  hariva\nHivavaka tokoa ;\nFo marina no ento,\nArio ny tahotrao,\nMiondreha ao an-trano,\nKa mivavaha ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (705, 2, 'Tsarovy ny sakaiza,\nNy havan-tianao ;\nRaha misy fahavalo,\nMifona ho azy koa,\nKa dia mangataha\nNy soa ho anao,\nJesosy Ilay Mpamonjy,\nAlao ho tokinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (705, 3, 'Na tsy irery aza,\nFa be ny olona ao,\nKa misy hevi-tsara\nMiposaka ao am-po,\nNy hataka mangina\nVoaeritreritrao\nHo ren’ny Rainao tia,\nJehovah Tomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (705, 4, 'Mba manatòna Azy\nRaha  azon’ny manjo ;\nTsarovy koa raha faly\nHisaotra ao am-po ;\nTsy misy hafaliana\n‘Zay mamy toy izao ;\nNy fahazoantsika\nMivavaka aminy.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (706, 1, 'He torimaso mamy\nNo atolory ahy,\nRy Raiko Mpiandany,\nSy herin’ny fanahy.\nNy sandrinao mahery\nNo hiampofo ahy ;\nNy masonao mijery,\nManala ahiahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (706, 2, 'Ny otako androany\nKoseho, Raiko ô !\nIreo ory mitomany\nAmpaherezonao,\nNy havako rehetra\nSy ny sakaiza koa,\nTahio tsy hanjoretra\nAmpifalio tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (706, 3, 'Ireo sahiran-tsaina\nSy mandry fotsy izao\nNy kivy, mitaraina\nVonjeo, ry Tompo ô !\nIreo marary maro\nSy ny mpitsabo koa,\nTahio, ry Mpiaro,\nArovy tsy ho voa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (706, 4, 'Hianao no tokinay\nKa dia hatory izao\nIreto nofonay\nAm-pelatananao.\nSakano re ny ratsy\nMba tsy hamely   izao\nRy Jeso Mpiambina ahy\nSy voninahitra   ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 1, 'Efa lasan-davitra,\nKa hariva izao ny andro ;\nHianao no Masoandro,\nRy Andriamanitra,\nMba arovinao ny aiko,\nFa atolotro Anao ;\nAmpidiro ao am-poko\nIzao ny fiadananao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 2, 'Raiso ny fisaorako,\nKa tondraho ranon’aina\nNy fanahy aman-tsaina,\nMba ho velona izao,\nDia hamoa voa tsara\nAmiko ny teninao,\nKa ho tiako hambara \nNy fahasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 3, 'Ry Andriamanitra ô !\nHitanao ny fahotako,\nFantatrao ny fahosako ;\nMamindrà fo amiko ;\nIzaho tsy misalasala\nMba hanatona Anao,\nFa ny Zanakao malala\nMaty noho ny otako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 4, 'Izaho ta-hatory izao,\nFa mba sasatra niasa ;\nTsy heveriko ny lasa,\nFa apetraka aminao\nAoka aho mba hahazo\nFitsaharana izao ;\nRaha maraina mba fohazy,\nDia hankalaza  Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 5, 'Kintan’i Jakoba ô,\nSady Masoandron’aina,\nHazavao ny fo sy saina,\nMba tsy hatahorako\nNy mpanimba sy mpandoza,\nLiona mierona,\nIzay minia hampidi-doza\nAndro aman-alina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 6, 'Arosoy ny teninao,\nHazavao ny firenena,\nMba ho samy hanantena\nFamonjena aminao ;\nKa ny manam-pahefana\nAoka hotarihinao,\nMba hitondra fanjakana\nAraka ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (707, 7, 'Fantatrao, Jesosy ô,\nIzay rehetra mitaraina\nKa miandry ny maraina\nSy ny fahasitranana ;\nNy kamboty sy mahantra\nAoka hotsarovanao ;\nRy Sakaizanay Mpiantra,\nIantrao ny olonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (708, 1, 'Lany indray ny andro ;\nHianao ry Ray,\nNo ho masoandro\nSy ampinganay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (708, 2, 'Be ny zava-tsoa\nAzo taminao ;\nSaotra aman-dera\nEntinay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (708, 3, 'Andronay mandalo\nToy ny rivotra ;\nBe ny fahavalo\nMampitahotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (708, 4, 'Jeso ô, Mpiandry,\nIzaho ondrinao,\nAoka re mba handry\nEo an-tratranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (708, 5, 'Mpivahiny ihany\nIzahay atý,\nKa mba te-ho any\nAminao arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (709, 1, 'Ny andro efa lany\nMangina  izao ny tany,\nMiala sasatra ;\nHeveronao, ry saina,\nFa mendri-koderaina\nNy Raiko  ao an-danitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (709, 2, 'Na maty masoandro,\nKa alina ny andro,\nTsy mampaninona.\nFa Jeso, Izay sakaiza,\nDia  ao fa tsy mankaiza,\nHo tena Masoandroko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (709, 3, 'Hatory  izao ny maso,\nRy Tompo mba andraso\nNy ondrikelinao ;\nNy teny sy fanahy \nSy izay rehetra ahy\nAtolotro harovanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (710, 1, 'He alina ny andro,\nKa mba esorinao\nNy fahotako maro,\nMba tsy ho hitanao ;\nReseo ny mpamely\nNy zanakao, ry Ray ;\nIraho ny anjely\nHiambina anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (710, 2, 'Raha  maizina ny tany,\nNy andro tapitra,\nTsaroanay ihany\nNy fasa-maizina ;\nArovy lalandava\nIzahay, ry Tomponay,\nKa aoka ho mazava\nNy hitsangananay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (711, 1, 'He, alina indray izao,\nJesosy Tompo ô ,\nKa aoka ny mpanomponao\n:,: Homban’ny elatrao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (711, 2, 'Fa iza no hahita anay,\nRaha  azon-doza  izao,\nKanefa kosa aronay\n:,: Ireo anjelinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (711, 3, 'Ka raha hatory izahay,\nRy Rainay tsara ô !\nNy tena sy fanahinay\n:,: Dia mba arovinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (711, 4, 'Ka raha  haposakao indray\nNy masoandronao,\nFohazy tsara izahay\n:,: Hisaoranay Anao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 1, 'Mpamonjy, Masoandronay !\nFahazavan’ny alinay\nNy zavon-tany mba savao\nTsy hanakonany Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 2, 'Tontolo andro Hianao\nNo anton’ny iainanay ;\nRaha alina, mombà anay,\nNy faty tsy ho tahotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 3, 'Ny aizina mba hazavao,\nTarihonao ny lalana\nHalehanay an-efitra\nHo tonga any aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 4, 'Izay marary mba tsaboy,\nNy ory aza  afoinao,\nHo toy ny torin-jaza anie\nNy fiadananay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 5, 'Tahio ny hifohazanay\nHanaovanay ny sitrakao\nAmbara-pahatapitra\nNy andro ivelomanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (712, 6, 'Raha mby aminao izahay,\nHahita fiadanana,\nFa ao tsy misy alina,\nHianao no Masoandronay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (713, 1, 'Alina ny andro\nKa  efa mba hatory\nNy masonay ;\nHianao Jehovah ô !\nIzay tsy mety sasatra,\nNo aronay. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (713, 2, 'Lasa ny mazava,\nKa ny masoandro\nTsy hitanay ;\nTsy matahotra izahay,\nHianao an-danitra\nMahita  anay. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (713, 3, 'Fa ny kintana  eny\nEfa manazava\nNy dianay ;\nKoa tsy nafoinao,\nFa nasehonao ireo\nHo tokinay. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (713, 4, 'Alina sy andro\nSamy mba manaiky\nNy tendrinao ;\nTonga re ny maizina,\nNefa tsy ilaozanao\nNy olonao. (in-2)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (713, 5, 'Eto ny mazava\nDia manam-petra ;\nFa ao aminao\nTsy mba misy alina,\nFa mamirapiratra\nNy tavanao. (in-2)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (714, 1, 'Efa hariva, Jeso tia !\nTomoera aminay ;\nNy fanahinay tahio,\nFa hatory izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (714, 2, 'Fa ny helokay ekenay,\nMahavonjy Hianao ;\nNy manjo anay lazainay,\nFa miantra ny fonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (714, 3, 'Alina maharitra ela,\nAizi-mampivadi-po,\nFa ny olona fidinao\nTsy mba takona aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (714, 4, 'Hianao no mitahiry\nNy miankina aminao ;\nVoahodidin’ny anjely\nNy mialoka  aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (714, 5, 'Raha  anio no tapitra andro\nIzahay mpanomponao,\nAoka izahay hifoha\nAo an-dapa soanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (715, 1, 'Andriananahary\nNikarakara   anay,\nKa nampitsiry vary\nHo fivelomanay !\nNy haninay tahio\nHo tonga tanjakay,\nNy hevitray tariho\nMba hiadananay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (715, 2, 'Ry Rainay ao ambony\nMisaotra Anao izahay,\nFa zava-tsoa itony\nIzay nohaninay.\nNy zavatra rehetra\nDia   manantena Anao,\nFa tsy mba misy fetra\nNy fitiavanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (716, 1, 'Atreho ny sakafonay,\nJehovah, mpanome ny soa !\nVelominao ny tenanay,\nNy sainay hatanjaho koa.\nAmen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (717, 1, 'Ry Jehovah, Ray Tsitoha !\nMba avia hialoha\nSy hiaro ny mpandeha ;\nLavi-dalana haleha,\nMba jereo ny olonao ;\nSarak’olona, irery,\nSady tsy mba manan-kery,\n:,: Ka jereo, ry Tomponay ! :,:\nMba tazano ny mpandeha ;\n(in-3)\nHianao no herinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (717, 2, 'Ao ny andro mamanala,\nSakambino ao an’ala ;\nRaha mandeha mita rano,\nMba hazòny sy tantano,\nKa vonjeo ny olonao ;\nBe ny zavatra mihatra\nIzay manimba sy mandratra,\n:,: Ka vonjeo,\nry Tomponay ! :,:\nMba arovy ny mpandeha ;\n(in-3)\nHianao no aronay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (717, 3, 'Raha ory, mba tohano,\nKa vimbino sy tantano\nTsy ho reraka ny fony,\nAza afoy, fa mba hazòny,\nIantrao ny olonao ;\nRaha mahantra izy any,\nOry sady mitomany,\n:,: Dia vangio,\nry Tomponay ! :,:\nMba tsarovy ny mpandeha\n(in-3)\nHianao no tokinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (718, 1, 'O ! tahio ny havanay re\nIzay handeha lavitra ;\nBe tokoa ny alahelo\nNoho ny fisarahana ;\n:,: Manatreha :,:\nIzao veloma atao izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (718, 2, 'Mba sakanonao ny loza\nTsy hamely azy izao,\nRaha misy mampahosa,\nAsehoy ny herinao ;\n:,: Mba tantano :,:\nTsy hisaraka aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (718, 3, 'Aoka mba ho tonga soa,\nTsy ho azon-tsampona ;\nAoka mba hampodina\nEto aminay indray,\n:,: Ka hisaotra :,:\nNoho ny fivimbinanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 1, 'Enga anie ka homba anao\nJeso !\nHanadio sy hanazava\nHampahery lalandava,\nEnga anie ka homba anao\nJeso !\nHomba anao, homba anao\nHomba anao anie Jeso\nHomba anao, homba anao\nEnga anie ka homba anao\nJeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 2, 'Raha mitambatra ny rahona,\nKa mikimpy ny masoandro,\nRaha Jeso momba isan’andro,\nHo tantely koa ny vahona.\nHomba anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 3, 'Mangamanga indray ny\nlanitra\nLoharanom-pitahiana\nSady foto-pifaliana,\nJeso Zanakan’Andriamanitra\nHomba anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 4, 'Raha mitolona ady sarotra\nMatokia fa tsy ho irery,\nHomba anao Jeso mahery\nHomba anao tsy miady\nvarotra\nHomba anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 5, 'Raha hilaozana ny havana,\nSady mainty koa Jordana,\nHo mazava ny fitàna\nAo Jeso hanangana avana\nHomba anao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (719, 6, 'Raha Jesosy no mitantana,\nParadisa koa ny tany,\nHasambarana hatrany,\nAo Kanana no hamantana.\nHomba anao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (720, 1, 'Velona indrindra,\nry havana ô !\nVelona e, mandra-pihaona ;\nRy Tompo, vimbino\nan-dalana ao\nNy havana izay vao handeha ;\nKa ny mifanao\nVeloma izao\nTahio, mba ho tafahaona !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (720, 2, 'Ry Jeso Mpamonjy, tariho\nizahay\nHihaona indray ao ambony,\nHiara-pinaritra mandrakizay,\nRehefa tafita ny ony,\nHiara-manao\nNy hira vaovao,\nFa tsy mba hisaraka intsony !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (721, 1, 'Ny fihaonana an-tany,\nRy Jesosy Tomponay,\nVetivety, mora lany,\nKa mangoraka izahay ;\nNamana nifankazatra\nHifanao veloma anio,\nRy Jesosy be fiantra,\nNy mpandeha mba tahio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (721, 2, 'Ny ho lasa mba tantano\nTsy ho voa an-dalana ;\nNy hitoetra koa tohano,\nDia mba hiadana.\nNa maraina na hariva,\nNa aiza alehany etý,\nMba ombay mandrakariva\nMandra-podiny arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (721, 3, 'Any volana, any taona,\nNy misaraka izao\nEnga anie ho tafahaona\nRaha mbola sitrakao.\nIlay fihaonana ao ambony\nNo irinay fatratra,\nTsy hisaraka intsony\nIzahay ka sambatra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (722, 1, 'Zanahary ô, tahio\nny Tanindrazanay\nHandroso lalandava,\nHandry fahizay.\nEnga anie hanjaka ao\nNy saina mahay miray,\nDia ho tany sambatra,\nIlay Madagasikaranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (722, 2, 'Ireo mpitondra anay\narovy lalandava koa\nTolory saina hendry,\nkinga sy mahay.\nKa ny marina anie no\nmba hanjaka ao tokoa,\nDia ho tany sambatra\nIlay Madagasikaranay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (722, 3, 'Ny taranakay tahio ho\nvanona tokoa ;\nIreo no solofo, dimby,\nrahatrizay\nMba ho tena mahatoky,\nFeno fahendrena koa,\nDia ho tany sambatra\nIlay Madagasikaranay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (723, 1, 'Arovy ry Tsitoha,\nNy Tanindrazanay,\nVimbino avokoa\nIzay mitondra anay ;\nIty Nosy ity tahio\nMba handry fahizay ;\nTantano sy ampio\nHandroso izahay.\nNy tsara mba tohano,\nRy Zanaharinay,\nNy ratsy mba sakano,\nMba hiadananay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (723, 2, 'Tahio ny mpitsara\nNampanapahinao,\nHahay hitondra tsara\nIzao olonao izao;\nNy lehibe rehetra\nVangio sy hafanao,\nMba hanam-po miantra,\nHatahotra Anao,\nNy tsara mba tohano, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (724, 1, 'Mba tahionao, ry Jehovah !\nNy tanindrazanay,\nHo tsara sy hiova ;\nHambininao anie !\nHo tany izay mandroso\nSy sambatra tokoa,\nKa hanjakan’i Jeso\nMpamonjy Tompo soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (724, 2, 'Mba tahio ny firenena\nIzay monina eto izao,\nHandroso hihatsara,\nHatahotra Anao,\nHandao ny fomba ratsy\nIzay manadala re ;\nHanary koa ny sampy\nMampidi-doza è !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (724, 3, 'Mba tahionao ny mpitondra\nNampanapahinao,\nArovy isan’andro\nMba tsy handà Anao,\nTariho lalandava\nHiankina aminao,\nOmeo fahendrena\nHanao ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (724, 4, 'Mba tahio koa, ry Jehovah !\nNy fiangonanao\nHandroso lalandava\nHomban’ny heerinao.\nHanerana ny tany\nItoeranay izao,\nKa isam-bazantany\nHiondrika aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (725, 1, 'Ry Ray, tahionao,\ni Madagasikara,\nIzay nohasoavinao, ka\nvoakarakara,\nKa nandefasanao ny teny\nmasinao;\nDia angatahanay \n:,: ny fitahianao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (725, 2, 'Ny Fiangonanao,\nry Rain’ny fahazavana\nIzay natsanganao hatao\nfahazavana\nKa nodidianao hanao ny\nasanao,\nDia angatahanay\n:,: ny fitahianao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (725, 3, 'Ny asa marobe sy izay\nfanaovan-tsoa\nMiorina eto izao, ka sitrakao\ntokoa\nNy fanjakana koa, izay\nnatsanganao,\nDia angatahanay\n:,: ny fitahianao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (725, 4, 'Ny tokantranonay, ny tena\nsy fanahy,\nNy fivelomanay, ry Ray Izay\nMpiahy,\nIreo rehetra ireo dia iantsoana\nAnao.\nKa angatahanay\n:,: ny fitahianao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (726, 1, 'Havaozy, Tompo ô,\nNy endriky ny tany,\nNy voninahitrao hatao\nFahazavany,\nIzao aizim-pito izao\nLevoninao tokoa\nIzao ratsy be izao\n:,: esory avokoa :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (726, 2, 'Havaozy Tompo ô,\nNy endriky ny tany,\nReseo ny rafinao,\nRavao ny fanjakany,\nAmpanjakao tokoa\nJesosy Zanakao,\nIzao rehetra izao\n:,: hanao ny sitrakao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (726, 3, 'Havaozy, Tompo ô,\nNy endriky ny tany,\nKoa averenonao indray\nNy hatsaràny ;\nFanahy Mpanavao,\nNy famelomanao\nTadiavinao izao\n:,: vitao ny asanao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (726, 4, 'Havaozy, Tompo ô,\nNy endriky ny tany,\nNy olonao izao maniry\nIndrindra izany.\nHenoy ny sentonay\nMiakatra aminao,\nKa hafainganonao\n:,: ny fanavaozanao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 1, 'Ry Jehovah Tompon’aina,\nO, tahio ny taninay !\nMba fohazy, re ny saina\nSy fanahinay hahay\nMankatò sy mankalaza\nNy Anaranao,\nMba ho fantatray mazava\nNy fitiavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 2, 'Iantranao, ry Jesosy,\nHazavao ny Nosinay,\nKa ny fon’ny Malagasy\nDiovy re ka mba ovay,\nTsy hanompo sampy intsony\nFa hazava re,\nKa ho sitraky ny fony\nNy ho masina e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 3, 'O, tahio ny Faritany\nMba handray ny teninao\nNa ambony na ambany\nMba tsarovy, iantrao !\nNy mpitory mba iraho,\nKa hiely anie,\nOmeo hery, hatanjaho\nHahavita be !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 4, 'O, Jehovah, Ray mahery,\nSakambino izahay,\nEny, Hianao irery\nNo mba hany tokinay ;\nKa ny ratsy mba sakano\nTsy hanimba anay,\nFa ny tsara no tohano\nHiadananay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 5, 'Ao koa ny mpanao lalàna\nMba toroy ny marina,\nIreo manam-pahefana\nOmeonao sain-dalina !\nKa ny tany mba ampio\nHandry fahizay !\nO, arovy sy tahio\nNy mpitondra anay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (727, 6, 'Indreo ny isan-tokantrano\nMba tsidiho, hafanao,\nKa ny zanakay tantano\nSy trotroy ho olonao !\nEny, Tompo, mandrosoa,\nAnjakao izahay\nKa onenonao tokoa\nIzao ny Nosinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (728, 1, 'He ! ny tany manontolo\nMba ambinonao, ao izao,\nRy Jeso Mpanolokolo,\nTena Tompo tsy mandao,\nMba vontosy andon’aina\nMba ho lonaka tokoa,\nTsy ho tany karankaina\nFa ho vokatra tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (728, 2, 'Mba tahio ny fanjakana\nIzay mitondra anay izao\nIreo mpitana ny lalàna\nMba omeo ny hevitrao.\nNy mpifehy isan-toko,\nNy mpikarakara izao\nAnehoy ilay vokovoko\nMba hitondra marina ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (728, 3, 'Ny komity sy voamiera\nRaha miady hevitra ao\nTsy hitompo hevi-tena\nFa handray ny sitrakao\nKa ny didinao hatrany\nNo mba hijoroany izao\nFa ny tetiky ny tany\nDia ho laviny tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (728, 4, 'Ry Mpanjakan’ny mpanjaka,\nMiasà mahery izao\nIreo mpitondra samihafa\nMba toroy ny sitrakao.\nIreo mpanjaka an-tany hafa,\nRevon-diadema koa,\nMba sintony re hitafa\nAminao ry Tompo soa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (729, 1, 'Jehovah Tompo ô !\nTahio sy hatsarao\nNy taninay ;\nTahio ny namanay\nHo tia ny teninao,\nDia mba hazavao\nNy nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (729, 2, 'Ovay ny fombanay\nHo foin’ny taninay\nNy sampiny ;\nOmeo fo marina\nIzao rehetra izao,\nKa dia mba iantrao\nNy nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (729, 3, 'Jehovah Tompo ô\nTahio ny olonao\nNy zanakay,\nTahio ho zanakao\nTahio ho marina\nKa dia mba ho taninao\nNy nosinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (729, 4, 'Izay mpitondra anay\nTahio ho tia Anao,\nJehovah ô !\nOmeo sai-marina\nHanao ny sitrakao,\nKa ampanompoy Anao\nNy nosinay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (730, 1, '(Maintimolaly)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (730, 2, 'Zanahary no mahela\nIzay fe-taona iainanay,\nKa miantra sy mamela\nTombon’andro ho anay ;\n:,: Nefa vitsy :,:\nNo ho andronay atý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (730, 3, 'Betsaka ny andro lany\nNanaranam-po izao ;\nMisy feo manontany :\nVita ve ny asanao ?\n:,: Miezaha :,:\nAvy re ny alim-be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (730, 4, 'Mamindrà fo, Zanahary !\nMamelà ny helokay ;\nTompo ! aza mba manary,\nAndrao ho very izahay ;\n:,: Mihainoa :,:\nNy mifona aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (730, 5, 'Ry Jehovah ! manomeza\nIzay ilainay olonao;\nMitahia, mampahereza,\nMba ho tonga ao aminao,\n:,: Ka hahazo :,:\nSatro-boninahitra ao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (731, 1, 'Ny andronay mandalo\nLany foana taminay ;\nNy mandrakizay no hatoninay,\nNefa adala izahay.\nRy mpanota, ifony ny\nhelokao,\nFa Jeso tia anao\nMba tsy ho very mandrakizay,\n:,: Avoty ny ainao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (731, 2, 'Ny fiadanan-dratsy\nNo mba manadala anay\nHijanona foana atý izao\nHanadino ny Rainay,\nMba hevero ry olon-ko\nfaty e !\nTsy ela no etý ;\nTsarovinao fa hody tokoa ;\n:,: Antsoiny hianao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (731, 3, 'Ny taninay havela,\nMpivahiny izahay ;\nTsy maintsy hiainga hiala atý\nAny no hodianay.\nTsara sady mazava\nNy tranonao,\nMiomàna, fa hankao ;\nTazano, fa mivoha izao ;\n:,: Midira hianao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (732, 1, 'He ! ny andro mora lasa,\nO, ry mpivahiny ô !\nTsy ho ela, kely sisa,\nDia hody hianao !\nTsy mba fantatrao akory\nIzay hahafatesanao ;\nKoa aza mba matory,\nSao ho very hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (732, 2, 'Miomàna ry sakaiza,\nAza mba tamàna atý,\nFa ny eto tsy mateza,\nMiaingà mba ho arý !\nAo ny Tomponao Jesosy,\nHampihaingo tsara anao,\nFa ny rany no mamofsy\nNy akanjo ho anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (733, 1, 'Henoy, ry Jeso Tompo !\nNy fitarainanay,\nFa amin’alahelo \nNo ipetrahanay ;\nNy fonay maniry\nHitovy aminao,\nKanefa be ny ratsy \nMasakana anay ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (733, 2, 'Nateraky ny nofo,\nKa nofo izahay ;\nNanompo tompo ratsy,\nKa very hatrizay !\nNy razanay nanota\nKa nodimbiasànay\nRy Tomponay Tsitoha,\nHavaozy izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (733, 3, 'Ny asam-pamonjena\nNatolotra Anao;\nTsy hisy tsy ho vita\nIzay natombokao ;\nRaha resy avokoa\nNy fahavalonao\nHiposaka tokoa\nNy tena taom-baovao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (733, 4, 'Ny tavanao malala,\nRy Jeso Tomponay,\nHo tena masoandro,\nIzay hifalianay.\nRy nosatrohan-tsilo,\nFaingàna Hianao,\nHanangana ny maty,\nHitondra aim-baovao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (734, 1, 'Mifalia, ry sakaiza !\nFa nahazo taom-baovao,\nZanahary no mitaiza,\nKa isaory sy hajao ;\nMihirà sy mihobia,\nJeso Tompo no derao ;\nMiravoa, mifalia,\nVelona aina hianao.\nMifalia ry sakaiza !\nFa nahazo taom-baovao\nZanahary no mitaiza,\nKa isaory sy hajao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (734, 2, 'Izao no andro mampifaly,\nAndro lehibe anio,\nDia hira mahafaly\nNo hatao ankehitrio ;\nZanahary no mitàna,\nMifalia hianao ;\nTonon-kira hoe : Hosana !\nNo hatao atý izao.\nMifalia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (734, 3, 'He ! Hosana, he ! Hosana\nHo Anao, ry Tompo ô !\nFihirana fiderana,\nNo asandratray izao ;\nDera, saotra, hery, laza,\nHo Anao, Mpanjakanay ;\nHo Anao anie ny haja,\nRaiso ho Anao izao.\nMifalia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (735, 1, 'Haleloia ! Sambasamba !\nHitanay ny taom-baovao;\nAvy izahay, Jehovah !\nMba hifaly aminao ;\nFihirana no atao,\nSaotra, vavaka,\nNo atolotray Anao,\nRy Mpamelona !\nHaleloia ! Sambasamba !\nFaly izahay izao :\nHaleloia ! Sambasamba !\nHitanay ny taom-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (735, 2, 'Lasana ny andro maro,\nBe ny zavatra natao,\nNa ny mety, na ny diso,\nSamy efa fantatrao ;\nAoka anie ny tsininay\nHovonoinao,\nFa ny mety vitanay\nMba haharitra :\nHaleloia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (735, 3, 'Ka ny taom-baovao, Jehovah !\nAoka hanomezanao\nFahendrena ny mpitondra,\nHahasoa ny olonao ;\nKa ny Fiangonana\nHamasinonao\nMba handroso marina\nHahalala   Anao:\nHaleloia, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (735, 4, 'Mitsingerina ny taona,\nFohy andro izahay,\nVetivety dia hihaona\nAminao, ry Tomponay !\nEnga ka ho hitanay\nAo an-danitra\nNy Sabata tsara izay\nTsy ho tapitra :\nHaleloia, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (736, 1, 'Havaozy, Tompo ô !\nIzao rehetra izao\nKa aoka mba ho hita ao\nNy voninahitrao.\nRy Tompo Mpanasoa\nLevony avokoa\nHo fongatra tsy hisy re\nNy ratsy marobe.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (736, 2, 'Izao taona vaovao\nDiavinay izao\nHo fanavaozan-dehibe\nNy tenanay anie !\nKa ny fiainanay\nIzay ratsy hatrizay,\nOvay hatramin’ny anio\nHo tsara sy madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (736, 3, 'Havaozy, Tompo ô!\nNy Fiangonanao\nO! akambanao ho iray,\nHiray mandrakizay.\nRy Tompo be fitia,\nTongava, mamangia;\nMba tanteraho aminay\nNy fanavaozanao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (737, 1, 'Ny taom-baovao dia tonga\nindray\nHo fiantsoanao anay\nMba hanoloranay Anao\nNy tenanay voavotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (737, 2, 'Fa maro be, ry Tomponay !\nNy zava-tsoa nomena anay\nTamin’izao taon-dasa izao,\nIzay isaoranay Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (737, 3, 'Ny mafy izay manjo anay,\nTsy tonga foana aminay,\nFa mba ho fitahianao\nIzay miankina aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (737, 4, 'Ka enga anie ny otanay,\nIzay mitambesatra aminay\nNo hibebahanay izao,\nHanaovanay ny sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (737, 5, 'Ny taom-baovao, diavinay,\nNy taona teo, tafitanay,\nKa dia ny fitarihanao\nNo mba irinay aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (738, 1, 'Dera, laza, ry Mpanjaka !\nNo atolotray Anao\nNoho Hianao nitahy ;\nTratranay ny taom-baovao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (738, 2, 'Andro maro izay no lasa,\nBe ny soa hitanay ;\nFitahia-mahagaga\nAzo taminao, ry Ray !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (738, 3, 'Marobe ny ratsy vita,\nLehibe ny helokay,\nKa tsy mendrika hiantso\nNy Anaranao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (738, 4, 'Miantrà, ry Zanahary !\nMamelà ny helokay,\nKa sasao ny ran’i Jeso,\nMba hadio izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (738, 5, 'Aoka re ny andro sisa\nHanompoanay Anao ;\nHatanjaho, ry Jehovah !\nIzahay mpanomponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (739, 1, 'He, vaovao ny taona,\nRy Jesosinay,\n:,: Ka mba te hihaona\nAminao izahay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (739, 2, 'Hianao mitahy\nNy mpanomponao\n:,: Koa tsy manahy\nIzay tarihinao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (739, 3, 'Ary izaho koa\nMba tantanonao\n:,: Dia hahita soa\nAo an-danitrao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 1, 'O, ry Jesosy, ny Anaranao\nManatsika  ahy izao hanatona,\nHanolotra ny foko ho Anao,\nMpamonjy tia sy Mpamelona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 2, 'Manatona aho, ka miondrika\nHanoroka ny tongo-masinao ;\nIzaho olona tsy mendrika,\nKa mba havaozy, Tompo,\nho Anao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 3, 'Antsoiko isan-andro Hianao\nRy Toky tsara mahafatra-po,\nKa amin’ny Anara-masinao\nNo anankinako izay hanjo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 4, 'Raha io no entiko mangataka,\nHisokatra amiko ny lanitra,\nKa dia ho azoko malalaka\nNy zava-tsoa izay maharitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 5, 'Mamirifiry izao tontolo izao\nTsy misy tena fitsaharana ;\nFa fiereko ny Anaranao\nKa ahitako fiadanana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 6, 'O, ry Anaran’i Jesosiko,\nRy Paradisa atý an-tany ô !\nRy voninkazo mampifaly fo !\nRy tohan’aina ao an-dalako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (740, 7, 'Iriko andro aman-alina\nNy hanana Anao ho ao\nam-po,\nHo Tokiko raha mbola\nvelona\nSy amin’ny hahafatesako.\n\nNY FIVORIAN’NY TANORA', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (741, 1, 'Hiaraka Aminao aho, ô ry Jesoa Tompoko\nTsy manana olon-kafa aho,\nFa Hianao no fiainako\nHanara-dia Anao, izay no faniriako\nHifikitra Aminao aho Jesoa !\nTiako ny hiaraka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (741, 0, 'Tiako ny hiaraka Aminao\nJesoa\nNa aiza na aiza hitondranao ahy\nTiako hiaraka Aminao Jesoa\nFa Hianao no miantso ahy. !', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (741, 3, 'Hiaraka Aminao aho, ô ry Jesoa Tompoko\nHitory teny soa amin’ireo havako\nHanara-dia Anao, izay no faniriako\nHifantsika aminao aho Jesoa !\nTiako ny hiaraka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (741, 4, 'Hiaraka Aminao aho, ô ry\nJesoa   Tompoko\nHo zary famonjena anie\nny fahoriako\nHanara-dia anao izay no\nfaniriako\nHifantsika Aminao aho\nJesoa !\nTiako ny hiaraka ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (741, 5, 'Hiaraka Aminao aho, ô ry\nJesoa   Tompoko\nHibanjina ny Ray, Tompo\nAndriamanitro.\nHanara-dia Anao, izay no\nfaniriako,\nIzay misy Anao, no hisy ahy,\nJesoa,\nTiako ny hiaraka ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (742, 1, 'Mitsangàna, mandehana,\nRy zatovo be laolao !\nManatòna ny Mpamonjy,\nAvelao ny sitrapo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (742, 2, 'He, tsarovy fa mahantra\nSady ory hianao,\nKa hatòny ny Mpamonjy,\nDieny mbola hitanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (742, 3, 'Mandehana, mianara,\nMba ho hendry hianao ;\nJeso Tompo no miandry\nHampianatra anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (742, 4, 'Ry zatovo ! mandehana,\nMba vitao ny asanao,\nMahereza, aza osa,\nNy Mahery homba anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (743, 1, 'O ! ry tanora mba tsarovy\nIlay Tompo soa maty noho\nny helokao ;\nIzy miantso anao moramora\nVohay ny fonao hidirany izao.\nNy saina sy ny safidy nomeny.\nKa mba fidio ny ho\nmpanaraka\nAn’i Jesosy, Mpanjaka\nTsitoha,\nNy fonao atolory hanjakany\nao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (743, 2, 'O ! ry tanora miambena,\nFa ny devoly dia mpamitaka\nmahay\nMaro ny fandrika ka\nmitandrema\nTsy ho tra-nenina re\nmandrakizay.\nKa ekeo anatrehan’ny Tompo,\nHandao ny ratsin’izao\ntontonlo izao.\nRaha Jeso no iankinanao\nmafy,\nIzy hiaro mba mba tsy\nhivadihanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (743, 3, 'O ! ry tanora, mivavaha\nFa Jeso Tompo dia Mpihaino\nvavaka\nAzy irery ny hery rehetra,\nDia matokia mba ho\nafa-tahotra.\nKa raha ory sy mila ho kivy,\nDia andrandraonao Ilay\nTompo be fitia ;\nNy fitarainanao dia hohenoiny\nAry ny fonao dia\nhampiadaniny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (743, 4, 'O! ry Jesosy, mba ampio\nIzaho mpianatrao mba ho\nmadio fo\nTantano aho, vimbino ry Jeso,\nFa sarotra ny lalana alehako ;\nIriko ny ho mpandresy ny ratsy,\nIriko hahatanteraka ny soa ;\nOmeo hery sy tanjaka aho.\nEfao ny asa tsara ao anatiko.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (744, 1, 'Avia ry sakaiza ô !\nAntsoin’ny Mpanjaka   izao\nHandray fiadiana\nHoentina hanafaka.\nVelona ny baikonao,\nAndrandrainay Hianao\nKa arahinay tokoa,\nRy Mpandresy ao Ziona !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (744, 2, 'Ny sabatra atolony\nDia ny teny masina,\nAmpinga fiarovana\nMahery ny finoana.\nVelona ny baikonao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (744, 3, 'Ny vatsy izay atolony\nDia ny ra sy tenany\nHo herintsika olony\nIzay miaramilany.\nVelona ny baikonao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (744, 4, 'Jesosy ô, arahinay\nNy fiantsoanao anay,\nKa manampia ny olonao\nHiady sy handresy izao.\nVelona ny baikonao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (745, 1, 'Tsarovy, ry sakaiza,\nNy RAY an-danitra e,\nFa Izy no nitaiza\nSy nahalehibe ;\nFa raha mbola zaza\nNampitomboiny,\nKa na ho antitra aza,\nTsy mba hafoiny.\nNy saina sy safidy\nNomeny ho anao,\nKa tiany hifidy\nNy tsara hianao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (745, 2, 'Tsarovy, ry tanora,\nJESOSY, avotrao,\nMahay mitondra mora,\nMitandrina anao.\nMiaraka mibata\nNy fahorina,\nKa dia mahareta\nNy hatrotrahana,\nFa Izy no miaro\nIzay mpianany\nKa dia mba fantaro\nNy fitiavany !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (745, 3, 'Tsarovy ry zatovo,\nNy MPANAMASINA\nFa Izy no manovo\nNy rano velona,\nKa dia mba mampandro\nNy ta-hadio fo.\nLazaina isan-andro\nNy marina sy to,\nKa ny masirasira\nEsoriny aminao,\nIzay maniratsira\nNo halavironao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (745, 4, 'Tohero, ry zatovo,\nIzay mamitaka,\nSatana ta hamono\nNy tsy miambina\nNy nofo mitsikilo\nNy voninahitrao ;\nMamono ny fanilo\nIzao tontlo izao\nKa raisony ampinga\nAlao ny sabatra,\nIzany mahakinga\nNy te ho sambatra.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (746, 1, 'Tsarovy e ! rankizy\nIlay Mpanao anao !\nKa tadiavo Izy !\nDieny hitanao ;\nDieny mbola kely,\nTsarovy rahateo,\nKa aza manadino\nIlay Mpanao anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (746, 2, 'Tsarovy ry zatovo\nIlay Mpanao anao !\nIlaozy ny mpanota,\nFandrao manimba anao.\nTsarovy ny Jehovah \nNamorona anao,\nMampanantena lova\nSy nahatoy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (746, 3, 'Tsarovy, ry sakaiza,\nJehovah Tomponao !\nFa Izy no mitaiza\nSy manasoa anao ;\nKoa aza manadino\nIlay Mpanao anao ;\nNy teniny araho,\nFa Izy no Rainao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (747, 1, 'Ry Sakaizan’ny tanora !\nZoky be fitia,\nSakambino moramora\nIzahay mpania.\nTompo ô ! arovy\nNy madinikao\n:,: Aza afoy, fa mba tsarovy\nTano mafy ho Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (747, 2, 'Ry Sakaizan’ny tanora !\nTsinjonao arý\nIzany ady tsy mba mora\nHitanay etý.\nTompo ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (747, 3, 'Ry Sakaizan’ny tanora !\nTanonao izahay\nHe ! fa be ny mpanakora\nRaha lavo indray.\nTompo ô ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (747, 4, 'Ry Sakaizan’ny tanora !\nEny, sitrakay,\nIsan’andro, isan’ora\nHo Anao, izahay.\nTompo ô ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (748, 1, 'Aza mba manadino ny ray\nsy reninao ;\nFa izy no nitaiza sy nanasoa\nanao,\nKa na aiza na aiza aleha\nan-tany lavitra,\nAza mba manadino ny ray sy\nreninao :\nTsara izay ! tsara izay !\nEny, tsy ho hadinonay ny\nray sy reninay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (748, 2, 'Aza mba manadino ny\nTanindrazanao,\nTsarovy ny tanàna izay nahabe\nanao.\nFadio izay tsy mety fanaony\nfahizay.\nAza mba manadino ny\nTanindrazanao :\nTsara izay ! Tsara izay !\nEny, tsy ho hadinonay ny\ntanindrazanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (748, 3, 'Aza mba manadino ireo\nmpitondra anao,\nAraho ny lalàna mba\nhiadananao.\nKa na aiza, na aiza aleha \nan-tany lavitra.\nAza mba manadino izay\nmpitondra anao :\nTsara izay ! Tsara izay !\nEny, tsy ho hadinonay izay\nmpitondra anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (748, 4, 'Aza mba manadino ny\nfivavahanao,\nAtaovy isan’andro mba\nhamonjena anao ;\nMpitondra, ray, sy reny,\nny tany, tena koa,\nAza mba manadino ny\nfivavahanao :\nTsara izay ! tsara izay !\nEny, tsy ho hadinonay ny\nfivavahanay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (749, 1, 'Ry Jeso, Zoky be fitia !\nAvia, mitantàna\nFandrao izahay maniasia\nAvilin’i Satana.\nRy Jeso ô ! tahio izahay\nFa mbola kely saina,\nHe ! tsy mba hendry, tsy \nmahay\nKa mora ambakaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (749, 2, 'Maniry izahay, Jeso !\nHo zandry izay manoa,\nKa dia  ampaherezonao\nHo tia Anao tokoa.\nRy Jeso ô ! tahio izahay ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (749, 3, 'Indreto izahay, Jeso !\nTariho hahalala\nHanaraka ny sitrakao\nIzay Zokinao malala.\nRy Jeso ô ! tahio izahay ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (750, 1, 'Modia, modia,\nFandrao very tokoa,\nFa ny efitra be\nNo diavina  izao ;\nRy zanako ô !\nModia  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (750, 2, 'Modia, modia,\nFa miandry ny Ray\nHampandroso anao\nSady hanome soa ;\nRy zanako ô !\nModia  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (750, 3, 'Modia, modia,\nFa nanavotra anao\nJeso ilay be fitia\nIzay nisolo anao ;\nRy zanako ô !\nModia  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (750, 4, 'Modia, modia,\nKa ilaozy izao\nIzay ratsy natao\nNahameloka anao,\nRy zanako ô !\nModia  izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (750, 5, 'Modia, modia,\nHovantaninao ao\nNy tranon’ny Ray\nSy ny lamba madio\nRy zanako ô !\nModia  izao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (751, 1, 'Tanora ô !\nNy Nosin-drazanao\nMiandry   izao\nManasa   anao.\nIngao anio\nIlay fanevanao,\nO ! tafio\nNy herinao.\nO ! mifohaza izao,\nTafio ny herinao\nInjao ny Nosinao,\nManantena anao !\nJereo ny namanao,\nTaninin-doza izao\nO ! vonjeo\nFa mitoreo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (751, 2, 'Avia, jereo\nNy Fiangonanao ;\nO! mba vonjeo\nFa mila anao.\nTanora soa\nNo mba ilaina koa\nO ! avia\nHiara-dia,\nO ! mifohaza ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (751, 3, 'Omeo anio\nNy herinao izao,\nKa dia falio\nIreo tapakao.\nIreo didy soa\nSy baikon’ny Tsitoha,\nTanteraho\nSy hajao.\nO ! mifohaza ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (752, 1, 'Tsy mangataka  aho, Tompo\nZava-py, harena, zo,\nFa ny tena mba iriko\nDia  izao : ny fo madio.\nFo madio izay akàny\nFeno eritreri-tsoa\nFo madio : haren-tsy lany\nSarobidy avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (752, 2, 'Indrisy, ny soan’ny tany\nManodoka, mora lo\nNy haren-tsy mety lany\nDia  izao : ny fo madio.\nFo madio ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (752, 3, 'Na maraina na hariva,\nDia ity no hatako :\nMba ho ahy ilay soa indrindra,\nDia  izao : ny fo madio.\nFo madio ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (753, 1, 'Henoy ny antson’ny Tompo ;\n« Iza no ho irakay,\nVonon-kandeha sy hanompo,\nHiasa an-tany hay ? »\nAdidy sarotra hefaina\nAsa mangetana re,\nMaty handritra ny aina,\nIza no vonona re ?', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (753, 2, 'Tsy didy miendrika baiko\nNefa mahery tokoa ;\nNanindry mafy ny saiko\nNy antsonao, Tompo soa\nSatriko lavina anefa\nManery ahy hanoa,\nAny am-po no miantefa,\nNy ako, onjany koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (753, 3, 'Inty, ry Tompo malala\nIzaho ho irakao,\nNofo ka misalasala\nRaha toa tsy tohananao.\nNy foko  omeo fahendrena\nHanao ny sitrakao,\nNy saiko mora mivena\nFenoy ny hasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (753, 4, 'Vonona ka hamasino\nHanefa ny irakao ;\nEny, ry Tompo, vimbino\nHandia ny lalanao.\nSarotra nefa hataoko\nNy asa atolotrao,\nNy sitrapo dia  hafoiko,\nTohano ny herinao.\n\nNY HIRAN’NY ANKIZY', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (754, 1, 'Jeso Mpanjaka Malaza\nNefa sakaizan’ny ankizy koa,\nMamin’ny foko ny fitiavanao\nSintony e, ny foko ho tia anao,\nJeso malala ô ! Tompo be\nfitia\nTano ny foko tsy haniasia,\nHo zaza hendry,\nhahay hanompo,\nHo tia Anao anie,\nry Tompo ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (754, 2, 'Mamin’ny foko hotantaninao,\nTiako Jeso hiaraka Aminao\nNy foko e, arovy ho madio\nNy faniriako Tompo ô, valio !\nJeso malala ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (754, 3, 'Ny hazazako ry Tompo, tahio,\nEnga ho zaza tia ny madio\nHo zaza hendry, zaza\nmpankatò\nAnao Mpanjaka, Tompo\nmanan-jo,\nJeso malala ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (755, 1, 'Ry Jeso, Zaza masina ô !\nIzay tonga zaza toa anay ;\nNafoinao ny lapanao\nHitondra vonjy ho anay,\nRy Jeso, Zaza masina ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (755, 2, 'Ry Jeso, Zaza masina ô !\nNanetry tena Hianao ;\nNilaozanao ny lanitrao\nNoho ny fitiavanao,\nRy Jeso, Zaza masina ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (755, 3, 'Ry Jeso, Zaza masina ô !\nHianao no fihavanana ;\nIzahay izay nandà ny Ray\nTadiavanao ho Azy indray,\nRy Jeso, Zaza masina ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (755, 4, 'Ry Jeso, Zaza masina ô !\nHihira ho Anao izahay,\nHianao nanondrotra anay ;\nNy lapan-dRay ho azonay,\nRy Jeso, Zaza masina ô !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (756, 1, 'Endrey ! endrey ! Tompo ô !\nHe, ny hasoavanao,\nFa tsy tapaka hatrany\nIzany fitahianao ;\nNotantananao, ry Tompo\nItony ondrikelinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (756, 2, 'Endrey ! endrey ! Tompo ô !\nHe, ny fitiavanao ;\nEfa elabe tokoa no mba\nniheveranao\nIzany zaza Malagasy\nIzay irinao mba ho soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (756, 3, 'Anio izahay, Tompo ô !\nMankasitraka Anao,\nFa ny sandrinao mahery\nNo niaro toy izao\nTratranay mandrakariva\nNy androm-pahasoavanao !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (756, 4, 'Tahio tokoa, Jeso ô !\nMadagasikaranay\nMba handroso ka ho tonga\nMadagasikaranao ;\nIzany re no faniriana\nHo tanteraka anie !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (757, 1, 'Misy toerana voafidy\nManan-daza lehibe,\nDia saha saro-bidy,\nTena mahavokatra e !\nSaha feno fitahiana,\nMisy voka-tsoa erý ;\nAo no mendrika haniriana\nSaha masina izy ity !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (757, 2, 'Ny Sekoly Alahady,\nIo no sahanao, ry Ray\nNy Baiboly no angady\nHanasoavanao anay ;\nMahavatra ny Mpamboatra,\nKa mirobona tokoa ;\nVoakarakara loatra,\nKa mitombo  sy  mamoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (757, 3, 'Midadasika ny tany,\nNefa karakaina koa ;\nFa ny sahanao ihany\nNo tsy manam-paharoa.\nMisy rano tsy mba ritra\nDia Teny Masinao ;\nIzay toriana eran-tany\nMikoriana lava izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (757, 4, 'Ny ankizy sesehena\nNo mivory eto izao ;\nIreo no hazo ambolena\nAo anaty Sahanao,\nRaha mirona mba tohano,\nRaha lavo, ajoroy,\nZaza osa, ka tantano,\nMbola kely ka toroy !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 1, 'Hajain’ny saiko\nFototry ny aiko,\nItsaohan’ny fanahiko,\nTokana iriko,\nMalala tiako :\nHianao, Jeso Mpamonjiko !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 2, 'Mety malazo\nNy voninkazo,\nNy ahi-maintso koa re !\nFa misy lova\nTsy mba miova :\nJesosy Perla soa e !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 3, 'Ao lalandava\nNy andro mazava,\nNy kintana, ny volana ;\nJesosy irery\nTompon’ny hery :\nTsy azo ihoarana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 4, 'He, igagàna\nNy hatanorana,\nFa na mahafinaritra,\nMety mihena\nNy herin-tena :\nJesosy no maharitra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 5, 'Tsy misy fetra\nZavatra rehetra\nHianao Jesosy, Tokiko,\nAkaiky ahy,\nZoky Mpiahy,\nNy tsara indrindra amiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (758, 6, 'Raha ho lasa,\nFa vita ny asa,\nTohano aho, Jeso ô !\nAmpio aho\nSy atanjaho,\nHo sambatra eo an-tratranao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (759, 1, 'Tsinjovinao ny vorona,\nFa tsy mamafy na mijinja,\nNefa ao Ilay Mpamelona\nMiahy sy mikojakoja,\nJereo ny voninkazo soa,\nFa tsy miasa na mamoly ;\nIreo ahin’ny Tompo koa,\nKa velona  ao an-tanimboly.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (759, 2, 'Isika izay malalany\nNo dia andrian’ny fony\nindrindra,\nKa mifalia, ry zanany !\nNy Zanahary no mpitondra,\nRy Tompo, Ray malalanay !\nHatoky tsy hanahy  intsony\nNy tena sy fanahinay,\nFa manana ny Ray ambony.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 10, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 1, 'Vakio Mat. xi 28.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 2, 'Manatòna, manatòna\nAn’i Jeso izao ;\nManatòna an’i Jeso izao.\n\nVakio Jao. iii. 16', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 3, 'Mba minoa, mba minoa\nAn’i Jeso izao,\nMba minoa an’i Jeso izao.\n\nVakio Heb. vii. 25', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 4, 'Mahavonjy Jeso Tompo\nMahavonjy izao\nMahavonjy Jeso Tompo izao.\n\nVakio Jak. v.11.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 5, 'Be fiantra, be fiantra\nJeso Tompo izao,\nBe fiantra Jeso Tompo izao.\n\nVakio Jao. vi. 37', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 6, 'Tsy hanary Jeso Tompo,\nTsy hanary anao,\nJeso Tompo tsy hanary anao.\n\nVakio Mat. x. 47', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 7, 'Miantsoa, miantsoa\nAn’i Jeso izao\nMiantsoa an’i Jeso izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 8, 'Fa mamaly Jeso Tompo\nFa hamaly anao,\nFa hamaly Jeso Tompo izao.\n\nVakio 1 Jao. i. 7', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 9, 'Fo madio, fo madio\nNo homeny anao \nFo madio no homeny anao.\n\nVakio Apok. iii. 5', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (760, 10, 'Lamba fotsy, lamba fotsy\nNo hatafy anao,\nLamba fotsy no hatafy anao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (761, 1, 'Hianao, ry Jehovah no\nRainay tokoa,\nKa manatona Anao izahay,\nAry Jeso no Hava-malalanay\nkoa.\nDia matoky Anao ny fonay,\nMivoria ry ondry madinika ô !\nMba ho ao am-pisokoanio ;\nMihainoa ny feon’ny Mpiandry\nizao\nO, faingàna, modia hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (761, 2, 'Eny, mamy tokoa ny mihaino\nAnao\nRy Mpamonjy mpanavotra\nAnay !\nFa Sakaiza malala tokoa\nHianao,\nTe hihaino Anao izahay,\nO ! andeha, rankizy madinika\ne !\nFa miantso Jesosy erý,\nMahafaly antsika ny teniny\nHoe :\n« Avelao izy mba hankatý ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (761, 3, 'O ! ry Tompo tsy toha !\nTsinjovy ‘zahay,\nFa masiaka ny lion-dozabe,\nKa manenjika mba hahafaty\nanay\nSy mihaza ny ain-dehibe,\nMitsangàna, zaza \nmadinika ô !\nMandosira ny loza etý\nManatòna an’i Jeso\nMpamonjy izao,\nKa modia ho an-danitra  arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (762, 1, 'Fony Jeso nivahiny\nNisy zazakely koa\nTonga tao, ka notahiny\nSy noraisiny tokoa :\n:,: He, rankizy !\nHy fitiavan-dehibe :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (762, 2, 'Manatòna, izao, rankizy !\nAn’i Jeso Tompo soa,\nFa mandray ny zaza Izy\nKa mitahy azy koa :\n:,: He, rankizy ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (762, 3, 'Maro be ny zaza faly\nAo an-dapan-dRainy ao,\nAfaka ota, afa-jaly,\nSambatra izay tonga ao :\n:,: He, rankizy ! ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (762, 4, 'Mihobia izao, rankizy !\nFa isika koa hankao ;\nEny, mifalia, fa Izy\nHo Sakaizantsika ao :\n:,: He, rankizy ! ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (763, 1, 'Ry Jesosy Tomponay !\nMamindrà fo aminay ;\nBe ny hasoavanao,\nBe ny fitiavanao ;\nZazakely izahay,\nMamindrà fo aminay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (763, 2, 'Fony teto aminay\nHianao sakaizanay,\nTia zaza Hianao,\nZaza notrotroinao :\nZazakely ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (763, 3, 'Masina sy marina\nHianao, ry Tomponay !\nHamasino izahay\nMba ho zanakao tokoa :\nZazakely ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (763, 4, 'Be ny fahavalonay\nO ! arovy izahay ;\nManomeza fo madio,\nFo miankina aminao :\nZazakely ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (763, 5, 'Ary raha tapitra\nNy iainanay atý,\nEnto ny fanahinay\nHonina ao an-danitra :\nZazakely ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (764, 1, 'Mba ekeo, ry Jeso Tompo,\nIzahay madinikao,\nFa maniry te-hanompo\nSy hanao ny sitrakao ;\nTsy mba zaza noterena,\nNa nomena zava-tsoa,\nFa minia manolo-tena,\nHo Anao  tokoa  tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (764, 2, 'Tsy ny manan-kery ihany,\nNa ny lalin-tsaina koa\nNo mahazo miandany\nAminao, Mpamonjy soa ;\nFa ny mbola zaza osa,\nKa malemy toa anay,\nIzay mba te-hidodododo\nHomba Anao, Mpanjakanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (764, 3, 'Maminay ny fanompoana\nIzay natolotrao anay,\nKa hataonay sasa-poana\nIzay mamily dia anay,\nFa ny hany mba ilaina,\nDia ny fanohananao ;\nMba ho azonay vitaina\nIzay rehetra sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (764, 4, 'Enga anie ny faniriana,\nIzay anananay izao,\nKa harahim-pitahiana\nSy hotanterahinao\nDia ho feno fifaliana\nNy fanahy anatinay,\nKa tsy hisy hakiviana\nHanan-kery aminay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (765, 1, 'He ! zaza maro be tokoa\nArý an-danitra,\nMifaly amin’i Jeso,\nFinaritra avokoa :\nHe ! mihoby mafy izy,\nKa miderà an’i Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (765, 2, 'Moa nentin’iza izy ireo\nHo ao an-danitra ao\nNo miramirana endrika\nSy faly toy izao ?\nHe ! mihoby ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (765, 3, 'Jesosy Tompo no nandao\nNy lapa fahizay,\nKa nahatonga azy ireo\nHo aminy arý :\nHe ! mihoby ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (765, 4, 'Ny lambany novàny soa,\nDia lamba tena vao,\nNy fony nosasany koa,\nKa faly izy izao :\nHe ! mihoby ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 1, 'Vory izahay izao\nEto anatrehanao,\nRy Jeso Tomponay !\nHe ! faly ny fonay,\nFaly, faly, faly,\nHe ! faly ny fonay,\nAvy mba hidera Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 2, 'Saotra tsara entinay,\nDera koa atolotray\nAnao, ry Tompo ô !\nHe ! faly .. sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 3, 'Zazakely izahay\nFohy ny fisainanay,\nTarihonao izao :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 4, 'Ela no nasianao\nSoa ny mpanomponao,\nRy Tompo be fitia !\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 5, 'Ao ny ray sy reninay,\nAry koa ny havanay,\nAtaovy olonao :\nHe ! faly ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (766, 6, 'Zaza, ray, sy reny koa\nDia hihaona avokoa\nAn-danitrao arý :\nHe ! faly ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (767, 1, 'Jeso Kristy, Tomponay !\nJeso Kristy, Avotray !\nAvy mba hidera Anao\nIzahay mpianatrao,\n:,: Mihainoa, ry Jeso \nTompo ô ! :,:\nFa ny fonay, fa ny fonay\nMidera Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (767, 2, 'Zazakely izahay,\nZava-bitsy fantatray,\nKoa ny mahasoa anay\nDia ankinina aminao.\n:,: Mihainoa, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (767, 3, 'Mamindrà fo aminay,\nMamelà ny helokay,\nMba hiraisana aminao,\nFa izay no maminao.\n:,: Mihainoa, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (767, 4, 'Mba tahionao izahay\nAry koa ny havanay\nAtsangano ho Anao,\nFa izay no sitrakao\n:,: Mihainoa, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (768, 1, 'He, dera, laza, hery,\nAnao, Jeso ô !\nNy zaza no hilaza\nNy voninahitrao\nHianao Mpanjaka tia,\nHianao no Tomponay\nIzay tonga ho Mpamonjy\nSy ho Mpisolonay.\nHe, dera, laza, hery,\nAnao, ry Jeso ô !\nNy zaza no hilaza\nNy voninahitrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (768, 2, 'Midera Anao, ry Jeso !\nAnjely marobe\nKa izahay an-tany\nTa hankalaza Anao,\nToy  izay nataon’ny zaza\nIzay tao Jerosalema,\nNitondra ny rofia\nHo fanajana Anao.\nHe, dera, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (768, 3, 'Raha mbola tsy nijaly\nHianao, ry Tompo ô !\nNidera Anao ny zaza,\nKa faly Hianao ;\nKoa mbola hoderaina\nHianao ankehitrio,\nRy Jeso Tompo tia !\nMandrakizay doria.\nHe, dera, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (769, 1, 'Mijere, ry ray sy reny !\nIreto sombinainareo ;\nAoka tsy hirenireny,\nTano toy ny ainareo ;\nAza avela ho adala\nRay sy reny ô ! jereo,\nAza mahafoy malala,\nFa tezao ho anareo.\nIndro ô ! indro ô !\nIndro ny zanakareo, jereo !\nJeso ô ! Jeso ô !\nIndro ny zanakay mba vonjeo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (769, 2, 'Ray sy reny ! mananara,\nAza mampanaram-po ;\nManomeza teny tsara,\nFa ny zaza hankatò :\nAza avela mba hanompa,\nAoka ho tezainareo ;\nMampahory raha manota,\nAoka tsy hafoinareo.\nIndro ô, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (770, 1, '« Lazao, lazao rankizy !\nIzay nandaozan’i\nJeso ny fanjakany\nMba honina tetý »\n« Ry mpampianatray ô !\nHamonjy sy handio\nAntsika olom-bery\nNo nihaviany »\n:,: Mifalia izao :,:\nMisy avotra sy vonjy\nHo antsika atý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (770, 2, '« Rankizy ô, rankizy !\nMpitrosa hianao,\nKa inona no homenao\nHanefa trosanao ? »\n« Ry mpampianatray ô !\nNy saina amam-po\nHomenay mba ho Azy\nRaha velona izahay »\n:,: Mifalia izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (770, 3, '« Rankizy ô, rankizy !\nHamonjy sy handray\nAnao ve Jeso Tompo \nRaha mino hianao ? »\n« Ry mpampianatray ô !\nMatoky izahay\nFa efa maty Izy\nHo avotray tokoa »\n:,: Mifalia izao, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (770, 4, '« Rankizy ! angataho\nNy indrafony izao »\n« Ry Jeso ô, Mpamonjy !\nMba iantrao izahay »\nRy Tompo ô ! ry Tompo !\nHenoy, henoy izahay,\nNa kely na vaventy,\nIzay mitaraina izao ;\n:,: Mifalia izao, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (771, 1, 'He, maharavoravo !\nMiantso zaza e\nNy Andriamanitra avo,\nMpanjaka lehibe ;\nKa andeha hanatona Azy,\nFa hoy ny Tomponao :\n“Mba avelao ny zaza\nHanatona Ahy izao.”', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (771, 2, 'Isika mbola zaza\nKanefa tian’ny Ray,\nNa dia tsy hendry aza,\nNa dia tsy mba mahay !\nKa andeha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (771, 3, 'Ny tahotra esory,\nNy henatra ario ;\nTsy laviny akory,\nKa manatòna anio :\nKa andeha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (771, 4, 'Manetry tena izy,\nKa saotra no atao;\nIsika mbola ankizy\nMatoky Azy izao,\nKa andeha, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (771, 5, 'He maharavoravo !\nHe, mahafaly fo !\nFa zanaky ny Avo\nIsika atý anio :\nKa andeha, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 1, 'Jeso, be fitia !\nZaza izahay,\nAvy mitaraina ;\nMihainoa anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 2, 'Bodo sy adala,\nIndreo ny zanakao ;\nJeso ô, malala !\nAza lavinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 3, 'Mpiandry ondry mora !\nMba tarihonao ;\nTano tsy hania\nNy ondrikelinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 4, 'Aza avela hitady\nLaza aman-jo !\nFa Hianao irery\nNo mahafa-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 5, 'Tianao ny zaza,\nJeso ! fantatray\nKa manolo-tena\nHo Anao  izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (772, 6, 'Jeso be fitia !\nZaza izahay,\nAvy mba hidera :\nMihainoa anay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 1, 'Izahay ankizy\nTe hidera Anao,\nRy Jeso Mpamonjy !\nKa henoy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 2, 'Zanaky ny Avo,\nJeso Tomponay,\nFaly sady ravo\nRaha hatoninay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 3, 'Te hifalifaly\nIzahay izao ;\nHira mifamaly,\nTianay hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 4, 'Te hiravoravo\nMiaraka aminao\nNefa mavomavo\nKa antsoy izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 5, 'Te hisoka-bava\nHankalaza Anao ;\nHira lalandava\nSatrinay hatao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (773, 6, 'Sambatra ny zaza !\nIzay trotroinao ;\nNa kamboty aza,\nTonga zanakao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (774, 1, 'Ny teninao, Jehovah !\nIzay ianaranay,\nDia tsara tsy miova,\nKa ifalianay,\nHe, tena manan-jara\nIzahay ankehitrio,\nFa lova tsara fara\nNo azo amin’io.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (774, 2, 'Ireo zaza teo aloha,\nDia tsy nahita izao,\nTsy nisy hampifoha\nNatory foana tao ;\nNahantra sady ory,\nNiferina aina koa,\nTsy fantany akory\nIzay tena foto-tsoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (774, 3, 'Mamiratra indrindra\nNy masoandronay,\nMilanto sy mirindra\nNy antom-piainanay ;\nNy teninao ihany\nNo nahatonga izao,\nHe ! sambatra ny tany,\nFa mahalala Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (774, 4, 'Irinay mba hihombo\nIzay ianaranay,\nAmpio koa mba hitombo\nNy saina anananay ;\nHo avo zato heny\nNy voan’ny teninao,\nHiely eran-tany\nNy voninahitrao !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (775, 1, 'Izay mahakolokolo\nNo manana ny soa ;\nZahao ny tanimboly,\nIzay fatratra tokoa,\nFa he ireo voninkazo\nMamerovero ao,\nToa nofy izay handazo,\nAndeha mba jereo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (775, 2, 'Ireo izay mahavatra,\nHandray ny voka-tsoa,\nKa tena tsy hahantra\nTsy reraka tokoa ;\nJereo ireo taninketsa\nMampanantena voa,\nFa soa tsy manam-petra\nSy hanim-be tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (775, 3, 'Izahay, ry ray sy reny,\nNo voninkazo koa,\nHamerovero any\nAn-tokantrano soa ;\nTezao mba hihatsara\nNy fofo-manitray ;\nMba tsy harahin-tsento\nNy toetra  anananay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (775, 4, 'Izahay no toy ny ketsa\nHo vanona tokoa,\nHamoa tsy misy fetra,\nSy tena hahasoa;\nNy fiangonantsika\nHahita valiny,\nNy fivavahantsika\nHandroso hery koa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (776, 1, 'Ry Jeso Sakaizan’ny zaza,\nKanefa Mpanjaka Malaza;\nMidera ny Anaranao re\nIzahay vory eto izao.\n:,: Ny saotra sy dera aman-\ndaza\nno atolotray ho Anao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (776, 2, 'Ry Jeso, malemy fanahy !\nManatona Anao tsy manahy\nNy zaza mivory atý izao,\nEkeo mba ho mponina arý.\n:,: Ny saotra sy dera, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (776, 3, 'Fahiny Hianao tonga teto\nNanala ny rofy sy sento,\nAvia mitahy anay re,\nMatoky Anao izahay.\n:,: Ny saotra sy dera, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (776, 4, 'Ry Jeso, Mpamonjy sy Zoky\nIzay ampy tokoa mba ho toky,\nMifaly ny manana Anao izao,\nArovy ka aza mandao.\n:,: Ny saotra sy dera, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (777, 1, 'Zaza izahay, ry Jeso ô !\nNefa Hianao dia tia anay;\nInjany re ny feonao\nMiantso hoe : “ Avia ! Avia !”\nMba raisonao re, mba\nraisonao re, ry Tompo ô !\nMba raisonao re, mba\nraisonao re izahay izao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (777, 2, 'Zaza izahay, ry Jeso ô !\nHianao tatý mba zaza koa ;\nTarihonao ny hevitray\nHanahaka ny hevitrao !\nMba raisonao re, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (777, 3, 'Zaza izahay, ry Jeso ô !\nNy tenanay malemy koa;\nKanefa he, ny herinao\nMiaro ny fanahinay !\nMba raisonao re, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (778, 1, 'Jeso ô ! Sakaiza tsara,\nBe ny fitiavanao;\nTsy mba misy voarara\nTsy hihavana aminao ;\n:,: Mahagaga :,:\nNy haben’ny antranao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (778, 2, 'Jeso ô ! mba manampia\nIzao madinikao izao\nTsy hivily, tsy hania,\nFa hanara-dia Anao,\n:,: He, malemy :,:\nNefa be ny herinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (778, 3, 'Jeso ô ! Mpamonjy mora !\nManampia, mamonje,\nFa isan-andro, isan-ora\nMisy fahavalo be\n:,: ’Zay mamely :,:\nKa harovanao anie.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (779, 1, 'Tonga ny hariva,\nKa ny aizina\nEfa mba madiva\nHo mpanarona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (779, 2, 'Jeso ô, tsinjovy\nKa tahio izahay,\nEny, mba arovy\nHandry fahizay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (779, 3, 'Maniraha anjely\nMba hiambina\nAhy zazakely\nRaha maizina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (779, 4, 'Ary raha maraina\nMba fohazy indray\nHo madio sy tsara\nEo imasonao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (780, 1, 'Misy zaza marobe\nFaly, sambatra ao ambony ;\nNy devoly lozabe\nTsy mamely azy intsony,\nIzy ireo dia zazakely\nTonga naman’ny anjely.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (780, 2, 'Mba irionao tokoa\nNy ho naman’izy ireny,\nIndro, misy zava-tsoa\nHo anao raha tonga eny,\nDia afoizo sy ario\nIzay rehetra tsy madio.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (780, 3, 'Tompo, raiso izahay\nMba ho zanakao handova\nLapa soa rahatrizay\nLova tsy miovaova ;\nManan-jara aoka izany\nIzay rehetra tonga any.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 1, 'Mba tsarovy re, rankizy\nAo am-piangonana,\n:,: Tsy hatory fa haniry\nMba ho zaza maotina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 2, 'Tonga ao Jesosy Tompo,\nKa mihaino vavaka ;\n:,: Ny manatona hanompo\nRaisiny ho zanaka :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 3, 'Mba hirao ny fiderana,\nKa ezaho mafy hoa :\n:,: “ Arahaba re, hosana !\nRy Mpanjaka lehibe ” :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 4, 'Eny, Tompontsika Izy,\nTompo tsara, mora fo,\n:,: Ka hatoninao, rankizy,\nO ! hatòny eram-po :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 5, 'Aza mba misalasala,\nAtolory izao ny fo,\n:,: Fa ny Tomponao malala\nFaly ta-hamindra fo :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (781, 6, 'Ry Mpamonjiko mahery,\nRaiso aho ho Anao,\n:,: Raiso ho Anao irery,\nRaiso re, ka anjakao :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (782, 1, 'Ry Jesosy tsara\nAo an-danitra\nIzahay dia zaza\nSaro-tahotra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (782, 2, 'Koa ny anjely\nMba irahonao\nHo anay izay kely,\nRaha sitrakao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (782, 3, 'Dia mba tantano,\nJeso, izahay,\nAry mba fahano\nNy fanahinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (782, 4, 'Hianao, Mpamonjy,\nDia tianay;\nIzahay voavonjy\nTia Anao indray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (783, 1, 'Jeso ô, Sakaizanay,\nAo an-danitra aminao\nMisy zaza namanay\nMifaly ravo ao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (783, 2, 'O, ry tia zaza ô !\nMba ampio izahay\nMba hifaly aminao\nAtý an-dasinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (783, 3, 'Raha mbola ho atý,\nMba tantano izahay,\nKa tariho ho arý,\nRy Zoky tia anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (783, 4, 'Ary raha tapitra\nNy anjaranay atý\nRaisonao mba hiditra\nAo aminao arý.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (784, 1, 'Henoy izany hira vao\nErý an-danitra,\nFa teraka Jesosinao,\nKa mahafinaritra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (784, 2, 'Ianaronao matetika\nNy fitiavany,\nFa sambatra sy tretrika\nIzay sakaizany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (784, 3, 'Atero zava-manitra\nSy volamenam-po\nIlay Mpanjakan-danitra,\nMpanjakantsika koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (784, 4, 'Valio fitiavana\nIlay Zoky tia  anao,\nAteronao fisaorana,\nDerao fa Tomponao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (785, 1, 'Ry Jeso Zaza masina,\nMitsaoka Anao izahay,\nAtolotray ho hasina\nNy tena kelinay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (785, 2, 'Mihoby ny fanahinay\nMihaona aminao ;\nRy Zanak’Andriamanitray,\nTsy misy toa Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (785, 3, 'Ny zavatra anananay\nDia aoka ho Anao,\nNy tena sy fanahinay\nMba hasoavinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (785, 4, 'Ny lanitra nafoinao,\nNilaozanao ny Ray\nMba hanavao ny olonao\nHo velona indray.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (786, 1, 'Izahay zazakely manatona\nAnao,\nRy Jeso izay teraka androany\nFa tonga hanavotra anay\nHianao,\nKa faly izahay izany ;\nMitehafa tànana\nDera no asandratra,\nMihobia, mifalia,\nFa Kristy Tompo teraka\nHo antsika !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (786, 2, 'Izahay zazakely maniry izao\nHo tena sakaiza tokoa,\nKa avy izahay mba hanaraka\nAnao\nRy Jeso Mpanjaka Tsitoha ;\nMitehafa ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (786, 3, 'Izahay zazakely mangataka\nAnao\nHamory ny zaza rehetra,\nFa be ny ankizy miandry Anao\nHo Tompon’ny tany rehetra.\nMitehafa ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (787, 1, 'Zaza sambatra tokoa,\nRy Jesosy, izahay,\nFa ny Tompo Zanahary\nNo mamelona anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (787, 2, 'Zaza sambatra tokoa,\nRy Jesosy, izahay,\nHianao nijaly mafy\nHanavotanao anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (787, 3, 'Zaza sambatra tokoa,\nRy Jesosy, izahay,\nNy Fanahy Mpanadio\nManamasina anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (787, 4, 'Zaza sambatra tokoa,\nRy Jesosy, izahay,\nFiarovanay tsy toha\nHianao, ry Tomponay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (788, 1, 'Haleloia, asandratray\nHo Anao, ry Jeso ô !\nResinao ny rafinay,\nFa nitsangana Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (788, 2, 'Foana ny fasana\nFa tsy nahatana Anao;\nNy fahafatesana\nDia nofolahinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (788, 3, 'Ka mihira izahay,\nFa nandresy Hianao,\nKa ho velona indray\nIzahay madinikao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (788, 4, 'Hianao hisaoranay\nRaha hitsangana izahay\nKa handao ny fasanay,\nMba handray ny lovanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (788, 5, 'Dera no ventesinay\nHo Anao, ry Zokinay ;\nHoby no asandratray,\nRy Mpanjaka maminay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (789, 1, 'Izaho dia zaza\nMalemy, osa fo,\nFa na izany aza,\nMba ondrin’ny Jeso.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (789, 2, 'Arý an-danitra  Izy\nMitsinjo ahy koa,\nSatria ny ankizy\nDia tiany tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (789, 3, 'Anefa ko akaiky\nJesosy mamiko,\nFa Izy dia manaiky\nHo eto amiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (789, 4, 'Manafa-tahotra ahy\nNy tsorakazony,\nManaisotra ahiahy\nNy fitiavany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (789, 5, 'Izao tsy mbola hita\nNy endrik’Izy izay,\nFa raha arý am-pita\nHiahona izahay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (790, 1, 'Misy ny fonenana\nVita re, ho ahy,\nTany ifaliana,\nMamin’ny fanahy ;\nAo, tsy misy tahotra,\nAo ny Tompon’avotra,\nHira mahafinaritra\nNo ataon’ny any.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (790, 2, 'Saotra no hataoko ao,\nFaly fa nandresy ;\nHiakanjo fotsy vao,\nHaingon’ny mpandresy.\nNy anjely masina\nHiaraha-monina,\nJeso Tompo marina\nHiampofo ahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (790, 3, 'Izaho voavidinao,\nRy Mpamonjy tsara,\nSady namboaranao\nLova tsara fara.\nKoa mba arovinao\nToy ny ondrikelinao\nIzaho izay fanananao\nRy Mpiandry Tsara !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (791, 1, 'Avia, ry Fanahy ô !\nHo eto aminay,\nNy fanahinay mba fenoinao\nNy hasoavanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (791, 2, 'Maloto ny fanahinay,\nKa mba diovinao ;\nTsy mahomby re ny\nfihevitray,\nMba amboarinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (791, 3, 'He, zaza osa izahay,\nKa mba tantanonao,\nBe ny ratsy izay te-hitaona\nanay,\nAmpio ny olonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (791, 4, 'O, ry Fanahy hendry ô,\nMiandry izahay,\nManantena Anao hanadio\nny fo\nHe, zaza osa izahay,\nKa mba tantanonao,\nBe ny ratsy izay te-hitaona\nanay,\nHenoy ny hatakay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (792, 1, 'O, ry Fanahy Masina !\nIzahay ampionao,\nFa izahay madinika\nManiry ho Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (792, 2, 'Ny fonay dia maizina,\nKa tsy mba fantatray\nNy tena lala-marina :\nTariho izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (792, 3, 'Koa amoròny fo vaovao\nIzahay mpanomponao ;\nNy ratsy mba esorinao\nHitiavanay Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (792, 4, 'O, ry Fanahy Masina !\nMangataka izahay :\nMombà anay madinika,\nHenoy ny hatakay.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (793, 1, 'Izao rehetra izao dia\nmankalaza Anao,\nRy Ray Mahery, Tomponay\ntokoa,\nNy zava-boahary no asan-\ntananao\nKa tsara  indrindra  sady  soa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (793, 2, 'Miondrika aminao izahay\nmadinikao\nMidera Anao, ry Rainay be\nfitia !\nAmpio izahay, ary mba\narovinao\nNy fonay mba tsy haniasia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (793, 3, 'Ry Rainay be fiantra, tariho\nizahay\nHo tonga zaza tia ny mazava,\nHifaly ao an-danitra ao\nrahatrizay\nHo sambatra any lalandava.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (794, 1, 'Avia ry zaza, avia izao,\nFa indro ny Tompo miandry\nanao ;\nLazao an’i Jeso ny zava-\nmanjo,\nFa Izy onena ny ory am-po.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (794, 2, 'Jesosy Mpamonjy nanavotra\nanao,\nKa zaza malalany koa hianao,\nKa dia matokia tokoa \neram-po\nFa Izy Mpanjaka mahery\nsy to.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (794, 3, 'Jesosy no tsy manadino anao,\nTsarovy izany ka mba\nandrandrao\nNy Tompo malala Izay be\nindrafo ;\nAnao re ny lova tsy mety\nho lo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (794, 4, 'Sasaonao ny foko,\nMpanavotra ô !\nHo tonga madio sy mendrika\nAnao\nHo sambatra aho hahita Anao,\nMpamonjiko tsara ao\nan-danitra ao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (795, 1, 'Manatòna re, rankizy,\nNy Mpamonjy avotrao,\nFa henoy, miantso Izy,\nIzao fotoana anio izao :\n« Zaza be faniasia,\nMiverena hianao ;\nOndry kely, malakia,\nManaraka Ahy izao ! »', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (795, 2, 'Ny devoly andosiro,\nKa ny ota ialao,\nFa ny tsara no fikiro,\nTiavo re Jesosinao ;\nMiovà ka manenena\nNoho ny ota izay natao,\nRaisonao ny famonjena\nKa hazony  hatrizao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (795, 3, 'Miaingà ka manompoa\nAn’i Jeso Tomponao,\nTanteraho avokoa\nNy anjara asanao ;\nHalaviro re ny laina,\nMiasà ka mazotoa,\nHatanjaho re ny saina\nMahareta, mandrosoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (796, 1, 'Eo an-tratranao, Jesosy,\nDia mahafinaritra\nFa ny hafa mampahory\nKa tsy  mahasambatra.\nRaha Hianao no aro,\nAfaka ny tahotro,\nRaiso ho Anao ny foko,\nRy Jesosy tia ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (796, 2, 'Mampifaly ahy koa,\nJeso, ny fitianao ;\nZara mamiko tokoa\nIzany ampofoinao ;\nNa matory na mifoha ;\nDia eo an-tratranao ;\nRy Mpamonjy Tompo soa,\nHoderaiko Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (796, 3, 'Izaho tena tsy tamana,\nRaha lavitra Anao,\nFa mitebiteby lava\nIzay tsy eo akaikinao.\nEo an-tratranao, Jesosy,\nTsara hitoerako ;\nEo ihany e no misy\nZava-tsoa  mahafa-po.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 7, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 1, 'Jeso Sakaizan’ny zaza,\nTompo koa,\nJeso Sakaiza Malaza !\nMihainoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 2, 'Zaza izahay mitaraina\nAminao ;\nMbola marefo ny aina,\nTanonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 3, 'Jeso Sakaiza malala,\nTokinay ;\nAoka mba tsy ho adala\nIzahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 4, 'Jeso ô ! ampianaro\nIzahay,\nJeso ! avia hiaro\nNy dianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 5, 'Zaza izahay,ka sakano\nTsy hania ;\nKely izahay ka tantano\nJeso tia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 6, 'Ary ny ray aman-dreny\nRaisonao,\nAoka ny fony homeny\nHo Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (797, 7, 'Jeso Sakaizan’ny zaza,\nTompo koa !\nJeso Sakaiza malala\nMihainoa.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (798, 1, 'Tompo manampia ny\nmpianatrao,\nAry mitahia ny madinikao,\nMba ho tonga zaza sitrakao\nizahay,\nZaza mankalaza ny \nMpanavotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (798, 2, 'Maminay tokoa ny Anaranao,\nSambatra avokoa re ny\nhavanao ;\nManana anjara any aminao,\nRy Mpamonjy tsara ny\nmadinikao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (799, 1, 'O, rankizy izay mivory,\nMba derao ny Tompo soa,\nKa ny fonao atolory,\nNy fanahy, tena koa\n:,: Mba venteso re ny hira,\nKa derao ny Tomponao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (799, 2, 'O, ry Tompo be fitia,\nMba henoy ny zanakao ;\nFa miantso hoe : Avia !\nManatreha anay izao !\n:,: Izay hataonay re tahio\nHo madio sy masina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (799, 3, 'Mba tolory fahendrena\nIzahay madinikao,\nKa omeo ny famonjena\nHifikirana aminao ;\n:,: O, tarihonao ny dia\nHahitana Anao, ry Ray ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (799, 4, 'He, ny fonay manontolo,\nIzay mangetaheta re,\nDia atolotray daholo,\nAza dia lavina e !\n:,: Mba onenonao ny fonay\nHo Anao mandrakizay :,:', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (800, 1, 'Ry Jeso Zokinay ô !\nJeso malalanay,\nHenoy ny vavakay izao,\nFa zandrinao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (800, 2, 'Ny batisa no nandio\nAnay ho zandrinao,\nKa dia mba tahio\nHanaraka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (800, 3, 'Ry Zokinay malala,\nArovy izahay,\nFa be ny manadala,\nManimba ny fonay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (800, 4, 'Ry Zokinay malala,\nTrotroy ny zandrinao,\nRehefa mba hiala\nHo any aminao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 1, 'Tia zaza\nTia zaza\nNy Jesosinay !\nLanitra nafoiny,\nZaza notrotroiny,\nTia zaza \nTia zaza \nNy Jesosinay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 2, 'Trano tsara\nTany tsara\nNamboariny ;\nTrano be anjely,\nHo anay izay kely,\nTrano tsara, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 3, 'Voankazo,\nVoninkazo \nTsara fofona,\nTsy halazo intsony\nAo Edena ambony,\nVoankazo, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 4, 'Zazakely\nSy anjely\nSamy sambatra ;\nHira no hirainy\nAo an-tranon-dRainy.\nZazakely, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 5, 'Seza tsara,\nLamba tsara\nHo antsika ao\nHiakanjo lava \nFotsy lalandava\nSeza tsara, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (801, 6, 'Ento mora,\nEnto mody\nJeso, izahay,\nHo an-tranon-dRainao,\nFa ao no tranonay ô !\nEnto mora, ..sns.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (802, 1, 'Tsara re, ny lanitra\nSady mahafinaritra !\nAo ny Rainay be fitia,\nAo ny zaza tsy mania,\n:,: Ao ny fifaliam-be :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (802, 2, 'Ondry kely izahay,\nAry tsy mba fantatray\nNy hanjo etý an-tany,\nKa maniry mba ho any\n:,: Aminao, ry Tomponay :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (802, 3, 'Ry Mpiandry Tsara ô !\nAoka mba hamindra fo\nAminay izay mbola kely,\nMba iraho ny anjely\n:,: Ho mpiambina anay :,:\n\nLITANIA', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (803, 1, 'Ry Andriamanitra Rainay,\nMihainoa ! (in-3)\nMiondrika eto anatrehanao\nizahay,\nFa Hianao no Andriamanitray,\nKa henoy izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (803, 2, 'Ry Jeso Kristy, Zanak’\nAndriamanitray,\nMamonje ! (in-3)\nFa Hianao no Mpamonjy\nizao tontolo izao,\nAry ny Ranao no nanavotanao\nanay,\nKa vonjeo izahay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (803, 3, 'Ry Fanahy Masina,\nMpananatra anay !\nDiovinao ! (in-3)\nFa Hianao no mahay manadio\nny fonay\nMba ho tempoly mendrika\nny hitoeranao,\nDiovinao izahay !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (804, 1, 'Ry Zanakondry ô, Mpisolo ny\nmpanota !\nMamindrà fo aminay !\nRy Zanakondry ô, Mpisolo ny\nmpanota !\nMamindrà fo aminay !\nRy Zanakondry ô Mpisolo ny\nmpanota !\nOmeo anay ny fiadananao !\nTompo ô, mba miantrà !\nMamelà re, ny helokay\nNiara nanota izahay tamin-\ndrazanay\nKa tsy madio, fa ratsy ny\ntoetray !\nRy Tompo ô, aza ataonao\naminay\nAraka ny fahotanay\nKa aza averina aminay ny\nhelokay !\nHenoy ny hatakay,\nKa raisonao fifonanay !\nAmen !', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (1, '', 14, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 1, 'Fizarana I', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 2, 'Andriamanitra Tompo ô !\nRay, Fanahy, Zanaka,\nMihainoa anay izao\nRy Telo Izay Iray !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 3, 'Jeso Zanak’i Maria,\nNaka endrik’olona,\nTonga zaza toa anay :\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 4, 'Notafian-dreninao\nLamban-jaza Hianao\nTao anaty vilona :\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 5, 'Novangian’olona\n‘Zay niandry ondriny\nTao an-tsaham-Betlehema :\nMihainoa, Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 6, 'Notezaina Hianao,\nNotrotroin’i Simeona,\nNotompoin’i Ana koa,\nMihainoa, Jeso ô !\n\nFizarana II', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 7, 'Amin’ny avonavona,\nAmin’ny fahatezerana,\nAmin’ny fialonana :\nMamonje, ry Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 8, 'Amin’ny hevi-tsy madio,\nAmin’ny teny ratsy koa,\nAmin’ny fahasarotam-po :\nMamonje, ry Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 9, 'Hankatò ny didinao,\nHankahala heloka,\nMba ho zanakao tokoa :\nMitariha, Jeso !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 10, 'Mba hazoto izahay,\nTia fivavahana,\nTsy ho resin’i Satana :\nMitariha, Jeso !\n\nFizarana III', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 11, 'Noho ny nahaterahanao\nNoho ny ranomasonao,\nNoho ny fahorianao :\nMamonje, ry Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 12, 'Noho ny satro-tsilonao,\nNoho ny ra nalatsakao,\nNoho ny nitsangananao\nMamonje, ry Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 13, 'Noho ilay Anaranao\nIondrehon’olona\nSy ny ao an-danitra :\nMamonje, ry Jeso ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (805, 14, 'Noho ny herinao tsy toha\nNoho ny voninahitrao,\nNoho ny famindramponao\nMamonje, ry Jeso ô !', 'False');
