INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 3, 'Be ny heloka natao,\nBe ny famindramponao,\nMamelà anay izao :\nMihainoa ry Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 4, 'Malahelo izahay,\nFa maditra hatrizay,\nAza mba mandao anay :\nMihainoa ry Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 5, 'Mba diovy ny fonay,\nHatanjaho ny sainay,\nAhitsio ny dianay :\nMihainoa, ry Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 6, 'Be tokoa ny indrafo\nIzay naseho ho anay,\nTena latsaka ao am-po :\nMihainoa, ry Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 7, 'Eny, famonjena re,\nHahitanay aim-be,\nNo efa vitanao tetý :\nMihainoa, ry Tompo !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (807, 8, 'Aina, hery, andro koa\nNo atolotray tokoa,\nMba hahaizanay ny soa :\nMihainoa, ry Tompo !', 'False');
