INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (808, 1, 'Eo an-tratranao, Jesosy,\nDia mahafinaritra\nFa ny hafa mampahory\nKa tsy  mahasambatra.\nRaha Hianao no aro,\nAfaka ny tahotro,\nRaiso ho Anao ny foko,\nRy Jesosy tia ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (808, 2, 'Mampifaly ahy koa,\nJeso, ny fitianao ;\nZara mamiko tokoa\nIzany ampofoinao ;\nNa matory na mifoha ;\nDia eo an-tratranao ;\nRy Mpamonjy Tompo soa,\nHoderaiko Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (808, 3, 'Izaho tena tsy tamana,\nRaha lavitra Anao,\nFa mitebiteby lava\nIzay tsy eo akaikinao.\nEo an-tratranao, Jesosy,\nTsara hitoerako ;\nEo ihany e no misy\nZava-tsoa  mahafa-po.', 'False');
