INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 1, 'Jeso Sakaizan’ny zaza,\nTompo koa,\nJeso Sakaiza Malaza !\nMihainoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 2, 'Zaza izahay mitaraina\nAminao ;\nMbola marefo ny aina,\nTanonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 3, 'Jeso Sakaiza malala,\nTokinay ;\nAoka mba tsy ho adala\nIzahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 4, 'Jeso ô ! ampianaro\nIzahay,\nJeso ! avia hiaro\nNy dianay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 5, 'Zaza izahay,ka sakano\nTsy hania ;\nKely izahay ka tantano\nJeso tia !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 6, 'Ary ny ray aman-dreny\nRaisonao,\nAoka ny fony homeny\nHo Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (809, 7, 'Jeso Sakaizan’ny zaza,\nTompo koa !\nJeso Sakaiza malala\nMihainoa.', 'False');
