INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (810, 1, 'Tompo manampia ny\nmpianatrao,\nAry mitahia ny madinikao,\nMba ho tonga zaza sitrakao\nizahay,\nZaza mankalaza ny \nMpanavotray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (810, 2, 'Maminay tokoa ny Anaranao,\nSambatra avokoa re ny\nhavanao ;\nManana anjara any aminao,\nRy Mpamonjy tsara ny\nmadinikao.', 'False');
