INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (811, 1, 'O, rankizy izay mivory,\nMba derao ny Tompo soa,\nKa ny fonao atolory,\nNy fanahy, tena koa\n:,: Mba venteso re ny hira,\nKa derao ny Tomponao :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (811, 2, 'O, ry Tompo be fitia,\nMba henoy ny zanakao ;\nFa miantso hoe : Avia !\nManatreha anay izao !\n:,: Izay hataonay re tahio\nHo madio sy masina :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (811, 3, 'Mba tolory fahendrena\nIzahay madinikao,\nKa omeo ny famonjena\nHifikirana aminao ;\n:,: O, tarihonao ny dia\nHahitana Anao, ry Ray ! :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (811, 4, 'He, ny fonay manontolo,\nIzay mangetaheta re,\nDia atolotray daholo,\nAza dia lavina e !\n:,: Mba onenonao ny fonay\nHo Anao mandrakizay :,:', 'False');
