INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (812, 1, 'Ry Jeso Zokinay ô !\nJeso malalanay,\nHenoy ny vavakay izao,\nFa zandrinao izahay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (812, 2, 'Ny batisa no nandio\nAnay ho zandrinao,\nKa dia mba tahio\nHanaraka Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (812, 3, 'Ry Zokinay malala,\nArovy izahay,\nFa be ny manadala,\nManimba ny fonay.', 'False');
