INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (813, 2, 'Trano tsara\nTany tsara\nNamboariny ;\nTrano be anjely,\nHo anay izay kely,\nTrano tsara, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (813, 3, 'Voankazo,\nVoninkazo \nTsara fofona,\nTsy halazo intsony\nAo Edena ambony,\nVoankazo, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (813, 4, 'Zazakely\nSy anjely\nSamy sambatra ;\nHira no hirainy\nAo an-tranon-dRainy.\nZazakely, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (813, 5, 'Seza tsara,\nLamba tsara\nHo antsika ao\nHiakanjo lava \nFotsy lalandava\nSeza tsara, ..sns.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (813, 6, 'Ento mora,\nEnto mody\nJeso, izahay,\nHo an-tranon-dRainao,\nFa ao no tranonay ô !\nEnto mora, ..sns.', 'False');
