INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (814, 1, 'Tsara re, ny lanitra\nSady mahafinaritra !\nAo ny Rainay be fitia,\nAo ny zaza tsy mania,\n:,: Ao ny fifaliam-be :,:', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (814, 2, 'Ondry kely izahay,\nAry tsy mba fantatray\nNy hanjo etý an-tany,\nKa maniry mba ho any\n:,: Aminao, ry Tomponay :,:', 'False');
