INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (815, 2, 'Ry Jeso Kristy, Zanak’\nAndriamanitray,\nMamonje ! (in-3)\nFa Hianao no Mpamonjy\nizao tontolo izao,\nAry ny Ranao no nanavotanao\nanay,\nKa vonjeo izahay !', 'False');
