INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Hira Faneva Faha-30 Taonan''ny Fjkm', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 1, 'Mitsangàna ianao ry mino\nFa ilain''ny taninao\nNy finoana ifikiro,\nMivonona ianao !\nNy ady natrehanao tsy mora\nTena sarotra tokoa\nNefa toy ''lay tao Edoma\n''Lay Mahery momba anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 0, 'Mbola ho avy ny maraina\n(''lay maraina)\n''Zay mitondra hery vao.\nRy mpiambina miarena\nKa ingao ny zovinao.\nAmbarao fa ny aizina\nIndro efa lasa re\nEfa tonga any maraina\nHisy hazavana be', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 3, 'Raha sendra ady mafy\nMila handreraka ny fo\nDia tsarovy fa mihafy\nVao hahita soa\nRaha tafita ianao\nHisy hazavana be\nNy haizin-kitroka ho lao\nTapi-dàlana haleha', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (1, 4, 'Fahafahana doria\nTsisy tambiny aminao\nNo omen''ny Tompo tia\nRaha tahiana ianao\nHo fanilo mandra-maty\nHo mpitantana ny dia\nHomba anao doria doria', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'He! Manolo Tena', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 1, 'He, manolo-tena ho Anao\nFa mandre ny feo sy antsonao\n''Zato tenako izay mania\nKa raiso Tompo, raiso ry Jesoa tia', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 0, 'Hanoa ny sitrakao, hanao ''zay tianao\nKa ho mpanompo vonona sy sahy koa\nHitory hatrany ny fitiavanao\nAmbaram-pialam-pofon''aiko Jesoa', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 3, 'Eny, Tompo, tsy mba mendrika aho\nFeno loto ao am-poko ao\nNefa re ilay fitiavanao\nNo inoako handray ahy ho mpanomponao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 4, 'Eo anilanao no fifaliako\nHo Anao mandrakizay no tiako\nNy ho vavolombelonao Jesoa\nNo mamiko sy tena iriko tokoa', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (2, 5, 'Fantatro fa sarotra ny diako\nMaro be ny zavatra ho sedraiko\nNefa raha Ianao no momba ahy\nDia tsy hanan-tahotra ny fo sy fanahy', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Jesoa Vato Fehizoro', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 1, 'Jesoa vato fehizoro\nAfa-tahotra ny fo\nRaha Ianao no mampijoro\nSy iankinana tokoa\nVato fiorenanay\nJesoa Zanaky ny Avo\nVato fialofanay\nAmin-dratsy te-handavo', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 0, 'Vato azo itokiana\nSady tsy mba mivena\nAfaka ny fahoriana\nRaha Ianao no ekena\nJesoa Tompo (irery) in-2\nNo ''lay vato tsy mifindra\nJesoa Tompo (irery) in-2\nNo ''lay fehizoro indrindra', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (3, 3, 'Jesoa vato fehizoro\nMampisinda ahiahy\nFa mandresy horohoro\nManatanjaka fanahy\nO, ry vato tsara paika\n''Zay mandray Anao am-po\nDia mijoro tsy miraika\nN''inon''inona manjo', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Ry Fanahy O, Midina', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 1, 'Ry Fanahy ô, midina\nManatreha anay anio Ianao\nMba hanoloranay ny vavakay mangina\nVetsovetsom-po ''zay atolotray\nVola sy ny harena samy tsy anananay\nFa indro vavaka mangina\nFanoloran-tena ho Anao tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 2, 'Ry Fanahy ô, monena\nAza mba mandao anay Ianao\nKolokoloy ''zahay mba hitombo fahendrena\nKa tsy hahafoy hiaraka Aminao\nOta sy rendrarendra\nSamy te-handavo anay\nSoa ihany, soa fa eo Ianao Fanahy\nVonona hitahy ny fiainanay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (4, 3, 'Ry Fanahy ô, meteza\nMba handray anay ho mpianatrao\nAvia Ianao, avia mampahereza\nHahatonga anahy mba ho irakao\nEso sy fitsapana\nSamy manakivy anay\nKa taomy, taomy hanam-po mafana\nHerim-po vaovao hiasa ho Anao', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Izaho No Fananganana Ny Maty', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 1, 'Hoy Jesosy Tompo : ''Zaho no fananganana\nFananganana ny maty sy fiainana\n''Zay rehetra mino Ahy na dia maty aza\nDia ho velona indray (in-3)\nSatria velona Aho \nVelona ianareo\nAza matahotra fa\nHo velona indray (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 2, 'Maharavo fo ny teniny, henoy indray koa\n''Zay rehetra velona ka mino Ahy tokoa\nDia tsy ho faty mandrakizay, hatsangako\nKa ho velona indray (in-3)\nSatria velona Aho \nVelona ianareo\nAza matahotra fa\nHo velona indray (in-3)', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (5, 3, 'O, Jesosy Tompoko, indro re ampitomboy\nNy finoako Anao, ny herinao asehoy\nTompo iantrao ny foko, tsy finoako vonoy\nMba ho velona indray (in-3)\nMino Anao tokoa\nNy foko Jesoa\nFeno toky aho\nFa ho velona indray (in-3)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Ny Teninao', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 1, 'Ny teninao, Jehovah Ray mahery,\nIzay nomenao anay\nDia teny tsara sady mampahery\nKa mampifaly ny fonay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 0, 'Ny teninao, mofon''aina, sakafo tokoa\nNy teninao, ranon''aina satria foto-tsoa\nNy teninao no teny tsy mba maty\nMaharitra mandrakizay.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (6, 3, 'Soraty re ho velona ao anaty\nHitondra aina vaovao\nHitana ny finoana tsy ho faty\nKa hankato ny didinao.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Tsy Hainay Ny Hangina', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 1, 'Tsy hainay ny hangina\nKa tsy hankalaza ny Avo\nNameno fitahiana\nKa tsy mba namela ho lavo', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 0, 'Ka dera ry Ray sy saotra\nNo atolotray ho Anao\nRaiso (fo sy fanahy) fo faly loatra\nNo entina midera Anao\nNy sainay tsy ampy hahalala\nNy fetran''ny fitiavanao\nKa raiso ry Tompo malala\nNy fankasitrahana Anao, ry Ray', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (7, 3, 'Tsy hainay hadinoina\n''Lay fara-fitiavanao Ray ô,\nFa maty novonoina\nNy Zanakao hamonjy anay', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Masoko manganohano', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (8, 1, 'Masoko manganohano,\nNitomany feno rano\nNoho ny helok’efa vita,\nDia mitony raha nahita\nMasonao be fiantra\nRy Mpamonjy ny mahantra\nO Voavotrao ‘lay very\nRy fitiavana mahery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (8, 2, 'Tanako mangadihady\nNisarangotra nitady\nFanavotana sy Vonjy\nF’efa resy ka nilonjy.\nDia noraisinao ry Tompo\nNa dia tànana maloto\nO voavotrao ‘lay very\nRy Fitiavana mahery.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (8, 3, 'Foko tena fahavalo\nNefa resy ka mimalo\nRaha nifona fa naditra\nHitanao nipitrapitra.\nBabo ka tsy afaka ho aiza\n’Zay noraisinao ho sakaiza\nO voavotrao ‘lay very\nRy Fitiavana mahery.', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Isaorana anie Jehovah', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (9, 1, 'Isaorana anie Jehovah\nAndriamanitra anie\nAndriamanitry ny Israely\nIsaorana anie Jehovah\nAndriamanitra\nIzy irery ihany no nanao fahagagana\nIsaorana, mandrakizay\nny anarany Malaza\nAry aoka ny tany rehetra,\nHo henika ny famirapiratry\nNy voninahitr''i Jehovah Ray Tsitoha\nAmen!', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Mivavaka aho satria', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 1, 'Miloatra avy ao am-poko\nNy tonom-bavako\nManeho ny fanajako\nSy fankatoavako\nNy Tompoko Tsitoha\nNitahy hatrizay\nHatramin’ny taloha\nSy ho mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 0, 'Mivavaka aho satria tena mino\nMino sy mankato\nManaja sady tia an’Andriamanitro\nMivavaka aho satria tena faly\nFaly sy manantena ny avotrao\nOmena mandrakizay doria', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (10, 3, 'Mibahana ato am-poko\nFanahin’I Kristy\nMitaiza tsy miato\nKa mahafaly ery\nManesika ahy hivoatra\nHiasa bebe kokoa\nKa ataony tafahoatra\nNy hasoavako.\nMivavaka aho satria tena mino sns...', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'FALY AHO', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (11, 1, 'Faly aho raha hoy izy ireo tamiko :\nHandeha isika ho any an-tranon''i Jehovah\nNy tongotray efa mijoro\nAo anatin''ny vavahadinao ry Jerosalema\nDia ianao ry Jerosalema, izay efa vita\nTahaka ny tanàna efa voalamina tsara\nAny no hiakaran''ny firenen''i Jehovah\nMba hisaotra ny anaran''i Jehovah.\n(H. RATSIMBAZAFY)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'AM-PELATANANAO RY RAIKO', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 1, 'Am-pelatananao ry Raiko\nNy momba ahy rehetra\nIriko Ianao no ho mpibaiko\nIanao anatiko ao mitoetra\nHo andrianiko tokoa\nHitarika ahy hatrany hatrany\nAndriafô tsy mandao Ianao\nJesoa Ianao no hazavana tsy ho lany\nAm-pelatananao ihany\nMandrakizay doria doria\nNa ny fiainako eto an-tany\nNa hatrarý ankoatra arý.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (12, 2, 'Ny saiko am-pelatananao\nNy foko efa tsy ahy intsony\nFa voaova ho vaovao\nDia lasanao ry Ray ambony\nNy hasambarako ombieny\nMifaly manana anao\nIlay fiainako mpanjeninjeny\nNo voafehinao anatiko ao\nAm–pelatananao ihany\nMandrakizay doria doria\nNa ny fiainako eto an-tany\nNa hatrarý ankoatra arý.\n(ANDRIAMIHADAMAHATRARA M.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MIHIRA FIHIRAM-BAOVAO', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 1, 'Mihirà fihiram-baovao\nHo an''i Jehovah ry tany\nMihirà fihirana ho an''i Jehovah\nDia ambarao amin''ny jentilisa\nNy voninahiny.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (13, 2, 'Mihirà fihirana, fihiram-baovao\nMihirà fihirana ho an''i Jehovah ry tany\nDia ambarao amin''ny jentilisa\nNy voninahiny\nAmbarao amin''ny jentilisa\nFa Jehovah no mendrika\nIzy no mendrika ny fiderana.\n(RAMAROLAHY A.N.J.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'JESOA APETRAKA AMINAO', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 1, 'Jesoa, apetraka Aminao\nNy fanahiako rehetra\nNy asa vita hatrizay\nAnao tokoa tsy misy fetra\nJesoa apetraka aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 2, 'Jesoa, apetraka Aminao\nNy asa fikelazan''aina\nTsy misy miafina aminao\nNy hetaheta tsy hay lazaina\nJesoa apetraka Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (14, 3, 'Jesoa, apetraka Aminao\nNy toky sy fanantenana\nNy sitrakao no aoka hatao\nNa aiza lalana hombana\nJesoa apetraka Aminao.\n(RALAIARIJAONA)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'RAIKO Ô !', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 1, 'Tompo malala tsitoha,inty aho\nIzao vao nandre ilay feo niantso ahy\nTsapako anatiko ao ny herinao\nMitaona ahy mba hiverina aminao.\n\nRaiko ô ! Raiko ô ! Inty aho\nRaiso mba ho isan''ireo zanakao\nFa izaho izay nania dia hiova\nHanaraka ny dianao Raiko ô !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (15, 2, 'Jesosy Zokiko ô ! Raiso aho\nHo isan''ireo zandrinao tianao\nNeniko ireny taloha,fa hiova\nRaiso aho ! Raiso aho Jesoa ! Ho anao.\n\nTompo ô ! Tompo ô ! Inty aho\nRaiso mba ho isan''ireo zandrinao\nFa izaho izay nanda dia hiova\nHanaraka ny dianao Jesoa.\n(J. RAMIANDRISOA)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Tsy vitam-po onena', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 1, 'Tsy vitam-po onena\nTsy vitan-dranomaso\nNy Asam-pamonjena\nTsy azo atao mifampiandry andraso\nTsy azo ifanerena\nMila fandavan-tena\nSy hery tsy mba maty\nHerim-po, herin-tsaina koa\n\nFiv : Na onena ny fonao\nNa tomany ny maso\nTsy izany no ilaina\nFa fifankatiavana', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (16, 2, 'Ny mpianatry ny Tompo\nAsainy mifanompo\nNa mamy na mangidy\nMiara-dia , f’izany no Didy\nMamonjy ny mahantra\nManarina ny lavo\nMitsabo ny maratra\nAry koa tsy mba miavom-po\nNa onena ny fonao, sns…', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'HO AVY NY MARAINA', 0, '');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'AFAFAZO', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 1, 'Raha tonga ny andro\nIzay hitsarana anao\nAmin''izay fotoana izay\nHadinina ianao\nKa hitarika ilay Mpanjakanao\nEfa vonona ve ianao ? (Ary koa) \nEfa ampy ve izay anananao\nHiatrehanao ny Tomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 0, 'Fihatsarambelatsihy\nAok''izay dieny ety\nFahafaham-baraka\nHo anao, ka ialao\nRaha toa tianao\nHo voavonjy ianao\nAfafazo ''zay voa mendrika\n(Mendrika).', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 3, 'Raha tonga ny andro\nIzay itsaràna anao\nAmin''izay fotoana izay\nHadinina ianao\nEfa ampy ve izay nafafinao\nMba ho voa mendrika tokoa ?(Ary ko) \nEfa tontosa ve ny anjaranao\nTsy hahameloka anao rahatrizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (18, 4, 'Raha ho avy ny fotoana hodianao\nAmim-boninahitra\nHo ao an-tranon-dray\nTsy hatahotra, na hangovitra\nIanao raha madio fo (Ary koa)\nSatroboninahitra tsy mba lo\nNo an''ny mpanomponao mahasoa.\n(RAZAFINTSEHENO M.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'RAHA JEHOVAH NO MPIANDRY', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 1, 'Raha Jehovah no Mpiandry,\nTsy mba hisy mampahory ;\nAhimaintso no handriana,\nNy fanahy velombolo ;\nEny, tsy hanahy akory\nLala-marina, lala-tsambatra\n(Sambatra ifaliana).', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 0, 'Raha Jehovah no mpiandry\n(Tsy mba ... hisy)\nTsy mba hisy izay mampahory,\nAhimaintso no handriako\n(Ranon''aina ho sotroiko)\nNy fanahiko ho velominy\nMiasa Izy tsy mba matory,\nLala-marina itondrany\ntsy ho foiko.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 3, 'Ny aloky ny fasana aza\nTsy ho tazantsika intsony ;\nTsy hatahotra ny loza aho\n(Izy mihazona ahy)\nTsorak''azo nitondrany\nSy ny tehiny any ambony,\nNo manolotra ny toky\nNy fanahiko\n(Toky ny fanahiko).', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 4, 'Eo imasom-pahavalo\nNy latabatro velariny ;\nDiloilo feno hanitra\n(Efa namotosany ahy)\nSafononoka tantely\nNy kapoakako namboariny\nFahasoavana no sisa\nAfaka ahiahy\n(Afaka ahiahy).', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (19, 5, 'Ao an-tranonao ry Tompo\nNo iriko hialofana ;\nKa tsy afa-miditra any\n(Ny hetraketraky ny tany)\nRaha ny fonao feno antra\nNo ho lamba irakofana,\nAry ny androm-pifaliana\nTsy ho lany (Tsy ho lany).\n(ANDRIAMANJATO R.\nRAJOELISOLO R.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'JESOA FITIAVANA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 1, 'Efa trotraky ny adin-tsaina\nVao mba nahatsiaro Anao ry Tompo\nMenatr''aho tsy sahy mitaraina\nRy Jesoa fitiavana\nKanjo tambitamby safosafo\nNo nasetrinao ny ditrako\nEny tsapako fa novidianao lafo\nTamin''ny ranao ny tenako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 2, 'Jeso malalako tafio ny herinao\nHahaizako mandresy ny fankam-panahy\nHe! Tsinotsinona izao tontonlo izao\nFa fanimbana tena sy fanahy.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (20, 3, 'Ho reharehako ilay tanambokovoko\nHihamina eo anilanao anie ny foko ;\nFa eo anilanao tsy misy ny tolokoloko,\nEo no mahatsara ahy, Jesoa.\n(ANDRIAMIADAMAHATRATRA M.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MAHATOKY KA TOKONY EKENA', 1, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (21, 1, 'Mahatoky ka tokony hekena\nAmin''ny fankasitrahana\nrehetra\nIzao teny izao (2)\nTonga teny ambonin''ny tany\nKristy Jesoa ;\nHamonjy ny mpanota,\nAry amin''ireny izaho\n(Izaho) 3\nAmin''ireny, izaho no lohany\nKanefa izao no namindrana fo\ntamiko (2)\nDia ny mba hisehoan''i\nJesoa Kristy eo amiko,\nizay lohany (2)\nNy fahari-po rehetra\nno fianarana\nHo an''izay hino Azy\nHahazoana fiainana\nMandrakizay ;\nMahatoky ..sns.\n(RAJAOFETRA John)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'TSY MENDRIKA AHY', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 1, 'Tsy mendrika ahy\nmpanomponao\nNy atao hoe zanakao\nKanefa Ianao ry Tompo\nNo niantso ahy izao\nIzay manatona ahy\nTsy ho laviko hoy Ianao\nDia tamy aho\nKa sahy nanatona Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 2, 'Ianao no madio\n(mba diovy aho re)\nIanao no marina\n(mba hamarino aho re)\nIanao no masina \n(mba hamasino aho re)\nTsy mendrika hitsimpona\nNy latsaka amin''ny\nlatabatrao aho\nKanefa hoy Ianao\n"Izay manatona Ahy" \nNoho ny teninao (2)\nDia sahiko ny manatona.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (22, 3, 'Ianao no madio\n(mba diovy aho re)\nIanao no marina \n(mba hamarino aho re)\nIanao no masina \n(mba hamasino aho re)\nMba diovy aho re,\nhamarino aho re\nMba diovy aho re\nMba hamarino re\n(hamarino re) \nMba hamasino re\n(hamasino re)\nMba diovy, hamarino,\nHamasino.\n(RABEMANAHAKA\nHenri RATSIMBAZAFY)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'ANDRIAMANITRA O ! RY TOMPO ZANAHARY (A.T.Z)
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 1, 'Andriamanitra ô !\nRy Tompo Zanahary\nBe ny fitiavana\nMamonjy sy mamelona\nVatolampy, toky tsy mifindra\nFa maharitra mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 0, 'Raha eto an-tany\nDia mandala ihany\nMiovaova tsy azo\nIanteherana,\nFa izany no fomban''ny tany\nToa fiainana raha jerena\nKanjo indrisy fa mahonena\nHasambarana miserana ihany.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 3, 'O! ry velona tsarovy,\nny andronao fa mandalo\ntsy mba manam-pijanonana\nKa tandremo sao variana\nianao\n''Zao tontolo izao\nho levona avokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (23, 4, 'Mbola ho entin''\nAndriamanitra hotsaraina\n''Zaho sy ianao, ka tsy ho\nafa-maina ;\nFa Jesoa Mpanjaka no hitsara\nKa hanavaka ny ratsy\nsy ny tsara.\n(RAMANOELINA\nANDRIANASOLO R.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MAHEREZA ! NY TOMPO MOMBA ANAO', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 1, 'Miainga, mazotoa\nMikeleza aina koa, \nmivavaha (2)\nHo an''izao tontolo izao\nMijoroa, mitoria\nMamafaza teny soa\nFa ny Tompo be fitia\nNo manohana anao\nSarotra ny lalanao\nKanefa matokia tokoa\nAndrandrao ny masonao\nFa mbola ao ny Tompo soa\nIzy no aminao\nKa hampandresy izao\nO ! mahereza\nfa ny Tompo momba anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (24, 2, 'Mandrosoa, miasà\nTano ny Finoanao\nAza mba matahotra,\nna manahinahy izao\nMiadia, mitafia\nNy firaisan-tsaina soa,\nFa ny fahavalonao\nDia handositra tokoa\nAza kivikivy foana\nFa ny Tomponao dia ao\nMampahery lalandava\nAry koa tsy mba mandao\nIzy no aminao\nka hampandresy anao\nO ! mahereza\nfa ny Tompo momba anao.\n(RAMASITERA T.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'NY VOADIKO', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 1, 'Ny ondry mania no maneho\nAnao\n''Zao fanakena sy voady izao\nNandinika ny zava-bita Jesoa\nTsy mendrika hanatona Anao\nKanefa tsaroako ny\nfitiavanao\nHamonjy ity mpanomponao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (25, 2, '''Lay teny Mahery mananatra\nNampiova ny foko torovana\n''Lay Fanahy Masina inoako\nHampianatra sy hanadio\nNy ho aviko apetraka eo\nAminao\nIzany no voadiko izao.\n(RASOLOJAONA M.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'JESOSY REHAREHAKO', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 1, 'Tsy misy reharehako\nAfa tsy Ianao Jesosy o !\nAnao ny hery, anao ny haja\nsy voninahitra\nNy fasana sy ny fiainan-\ntsy hita\nIndro efa resinao\nIzao tontolo izao dia nahita\nFa efa nitsangana Ianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 0, 'Sambatra aho Jeso ô ! \nManana Anao ''zay\nTompon''ny hery \nSambatra aho Jeso ô !\nMiankina eo an-tratranao\nAmpy ahy ny fitiavanao\nAmpy ahy ny finoako Anao\nRaketiko ao am-po\nlalandava\nFa reharehako Ianao.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 3, 'Tsy nisy nahitam-pamonjena\nAfa tsy taminao\nNanaiky Ianao\ntsy mba noterena\nHitondra aina vao\nNy tananao Mahery\nnatsotranao mba hitantana\nHahaizako mandeha \nsy mizotra ny lala-marinao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (26, 4, 'Nasehonao ilay fahefana\nmanda sy aroko\nKa niala maina teo ''lay satana\n''zay fahavaloko\nTsy misy maharombaka\nakory\n''Zay eo an-tananao\nIzany indrindra no ilazako\nFa reharehako Ianao.\n(RAJAONARIVELO G.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, '''LAY ALIM-PAMONJENA', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 1, '''Lay alim-pamonjena (famonjena) \nTsarovana eto izao \nFa Tompo be harena \nno teraka tamin''io \nHe ! Mahagaga ilay fitia \nNasehon'' Andriamanitra', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 0, 'He ! Ravo ny fo \nHe!mirana koa \nMifaly (falifaly), sambatra doria \nMiventy ny hiran''ny lanitra \nHALELOIA !', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 3, '''Lay alim-pamonjena (famonjena) \nTsarovana eto izao \nFa vita fanekena \nIzaho sy ny Tompo soa \nNy tenako atolotro \nNy tenany ho avotro \nHe ! Ravo ny fo, sns...', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 4, '''Lay alim-pamonjena \nTsarovana eto izao \nMpanota sesehena \nMandova fiainan-tsoa \n''Lay alim-pihavanana \nNitondra fiadanana \nHe ! Ravo ny fo, sns...', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (27, 5, '''Lay alim-pamonjena \nTsarovana eto izao \nNitondra fietrena \nTsy mba manam-paharoa \nFitiava-mampitovy zo \nNy miadana sy ory fo', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'EFA VOADINIKAO ANDRIAMANITRA Ô', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 1, 'Efa voadinikao Andriamanitra ô! \nNy fiainanay zanak''olombelona \nTsy misy ''zay miafina amiko tsy hitanao \nIanao no manendry izany Andriamanitra ô!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 2, 'Indro ny fikasako voalahatrao \nNy fiainako rehetra dia Aminao \nNy fahotana vitako taminao \nMangataka famelana Andriamanitra ô!', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 3, 'Aza avela aho hangaihay \nFa ampioreno mafy ny finoako \nKa na mamely ahy ny fakam-panahy \nDia mahatoky Anao ny fanahiko.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 4, 'Ny nofoko anie malemy no sady kely \nTorovana tsy ampy hery ka manahy \nMeteza Ianao anio Andriamanitra o! \nHo hery sy toky ho ahy mandrakizay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (28, 5, 'Ianao no vatolampy sy fiarovako \nNy herinao no indro hizorako \nNy foko ao anatiko ao efa fantatrao \nDia raiso aho ho zanakao Andriamanitra ô!', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'TSY MBA HISY REHAREHAKO', 2, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 1, 'Tsy mba hisy reharehako \nToy ny hazo nijalianao Jeso (Jesosy ô !) \nEo amin''izay rehetra halehako \nIzany no voadiko Aminao \nHe! izao rehetra izao ekeko (ry Jeso) \nMora lo sy tsy maharitra tokoa (kanefa Jeso) \nFa ny fitiavanao jereko \nRy Jeso tsy niala tamiko', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (29, 2, 'Tsy mba hisy fanorenako \nRaha ny eto an-tany eto Raiko ô \nFa ny lanitra no ho fonenako \nIzany no mba faniriako \nHe! izao tontolo izao tazaniko (tazaniko) \nRendrarendr''izay mamitaka avokoa (kanefa Ray ô !) \nFa ny eo anilanao no mamiko ry Raiko \nRaiso aho ho Anao .', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Fo mitambatra ho iray', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 1, 'Fo mitambatra ho iray\nNo entina eto izao\nMitrotro ny fitia\nHohamasinin’ Andriamanitra\nHitombo hery sy\nHamerovero koa\nKa hifanaiky ireo olon-droa\nFa hiombom-piainana\nNa inona miseho eto an-tany\nDia vonona hifampitantana', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 0, 'Fitiavana toy ny an’Ilay Jesoa\nFitiavana manolotra ny aina\nFitiavana mandresy fahotana\nFitiavana ho mandrakizay doria', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (30, 3, 'Toy ‘zao androany izao\n‘Lay nofinofy izay\nNiainanay taloha\nKa indro fa miara-belona,\nMiara-tsambatra\nAnivon’ ny fitia\n‘Lay dina izay\nNiarahana nikolo\nNo hamafisina ho doria\nHotehirizina ho vaovao\nHandrobona, hitombo hasina\nFitiavana toy ny an’Ilay Jesoa, sns…', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MIFALIA RY MANAM-POFONAINA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 1, 'Mihirà ry manam-pofonaina\nMikaloa sy mitsikia\nTapaka ny gadra novonjena\nAfak''i Jesosy be fitia\n''Lay Edena nihidy tany aloha\n(nihidy aloha)\nNoho ny ota ratsin-\ndrazantsika\nHe, midanadana sy mivoha\nKa idiro hitondray mozika.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 2, '''Lay satana tena fahavalo\nResy teo ka voakodia\nKoa herezo aza mimalo\nTokinao anio ilay Mesia\nRy voahosotra hitondrana\nfamonjena\n(Ry voahosotra famonjena)\nNdreto ento ho Anao ''zahay\nDia izahay izay miferin''aina\nNoho ny ota izay mpahazo\nanay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (31, 3, 'Afaka izahay ankehitriny\nTonga ny Mpanavotra anay \nKa ilay gehenanay fahiny\nVoasolo paradisa indray\nKoa isaoranay tokoa tokoa\n(Koa isaoranay tokoa)\nIanao ry Rainay be fitia\nDia Ianao Jehovah Ray\nTsitoha\nNanome anay Ilay Mesia.\n(SOLOARIVELO R.\nSOLOARIVELO A)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'AZA AVELA HAHALALA', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 1, 'Aza avela hahalala\nNy antony nanaovanao\nNy hariva mamanala\nNa ireo maraina vao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 0, 'Tsy ho takatry ny saiko\nIzay rehetra alahatrao\nNy angatahako ry Raiko\nAoka aho hatoky Anao.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 3, 'Aza avela hanontany\nNy hafaliana atolotrao\nNa ny anton''ny tomany\nEfa haiko hatrizao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (32, 4, 'Aza avelanao hamefy\nNy ampitso izay Anao\nAza avelanao handrefy\nIzay ho fifalianao.\n(ANDRIAMANANTENA\nGeorges)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'INTY AHO JESO', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 1, 'Inty aho Jesoa !\nMiantso ahy Hianao\nNa dia tsy mendrika,\ne manolo-tena izao\nIanao no nifidy,\ntsy haiko ny nandà\nTy antso sarobidy \nry Tompo, mamelà\nNy heloka rehetra,\ndiovy aho, havaozy\nHo olom-baovao\nhanao ny sitrakao\nHo mendrika tokoa\nny antsonao Jesoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 2, 'Inty aho Jesoa,\nmaintso ahy Hianao\nNy fahalemeko anie,\nry Tompo fantatrao\nHianao no nifidy\nsy maniraka ahy\nAvy aminao Jeso,\nny herin''ny fanahy\nHo entiko manoa\nny asa atolotrao\nFa sarotra e ny dia,\nry Tompo\nManampia,\nho mendrika tokoa\nNy antsonao Jesoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (33, 3, 'Inty aho Jesoa,\nmiantso ahy Hianao\nMba ho fahazavan''izao\ntontolo izao\nHianao no nifidy ahy\nho fanasina\nHanefa ny adidy lehibe\nsy masina\nKoa hamasino Tompo\nFa vonona aho\nHanompo,\nhombay ny herinao\nHitondra aim-baovao\nHo mendrika tokoa\nNy antsonao Jesoa.\n(RATSIMBA M.\nNDRIAMAMPIANINA L)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'TOMPO MALALA O !', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 1, 'Tompo malala o !\nMpanjakako Ianao\nTamy aho fa resy lahatra\nNoho ny famonjena vitanao\nEny ry Tompo,\ntsy ho haiko adinoina\nIntsony re ny fitiavana\nnasehonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 0, 'Ka anio anavaozako\nny fanompoako anao\nFa tsy ho voavaliko\nizay natolotrao,\nNy fitiavana natolotrao\nKa raiso Tompo,\nekeo re sy anjakao\nNy fiainako ho voninahitrao.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 3, 'Tompo malala o !\n''Lay hazo fijalianao\nNiaretanao tao Kalvary\nNiaretanao Mpamonjiko\nNo nanehoanao fa tianao\nity mpanota izay\nTsy mendrika Anao akory ity.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (34, 4, 'Tompo malala o !\ntsy ho hadinoiko intsony\nNy fitiavana natolotrao\n(Ny fitiavana natolotrao)\nEny ry Tompo, ity izaho\nizay voababonao\nFa vonona hanao ny sitrakao.\n(J.C. ANDRIAMANAN-\nTOANINA)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'RY TOMPONAY O AVIA !', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 1, 'Ry Mpamonjiko\nRay malala o ! o ! o !\nMifona aminao ''ty Zanakao\nmitalaho Tompo o !\nFa diso taminao izahay\nka mangataka o ! o ! o !\nFamelana sy famindram-po\navy aminao Ray o !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 0, 'Ry Tomponay ô ! avia,\nomeo anay izao\nAnio, ny fo madio\nsy masina anananao\nDia tsy ho very na hoe\nMbola ho diso ihany indray\nNy tena sy fanahy\nizay anananay.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (35, 3, 'Ry Mpamonjiko\nRay malala o ! o ! o !\nBetsaka ireo ota efa vita\nsy nambabo anay\nKa angatahanay Aminao\nsy irinay koa o ! o !\nMba ho foana\ntsy hisy intsony re\nFa reraka izahay.\n(RANDRIANJAFIHARISON A.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MIFALIA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 1, 'Vahoaka sesehena\nNo miara-paly izao\nMiventy ny Antema\nDia hira vaovao\nInjao fiderana\nManakoako ery\nManeho fanajana\nHo an''ilay Mesia.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 0, 'Iza ary no tsy hifaly\n(Mifalia, mihobia)\nHisaotra ao am-po\n(Misaotra ao am-po)\nFa afa-doza, afa-jaly,\nafaka ianao\nHe, famonjena tokoa\nO! mihirà, mihobia\nafaka ianao\nTeraka ny Tompo tia,\nresy ny rafinao.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (36, 3, 'Tsy ireo Anjely ihany\nNo faly ao am-po\nNy mpino eto an-tany\nMiravo avokoa\nFa indro ny Mpamonjy\nMpanjaka be fitia\nNietry an-tranon''omby\nHamonjy ny mania.\n(RAKOTOSON Pascal)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'Mifalia', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 1, 'Fa tsy misy fahoriana\n''Zay natolotrao anay\nKa nitarika hakiviana\nSatria efa zakanay\nNoho''lay vesatra indrindra Mafy,\nsy manonja koa\nlzay nentinao namindra\nRaha novonoina Ianao Jesoa !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 0, 'Ny faniriam-panahy\nHiaraka Aminao\nHamelona ahy ho sahy\nHiasa ho Anao\nNy Famonjena soa\n''Zay nomenao ry Ray\nNo anton''ny fiainanay', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 3, 'Fa tsy misy hafaliana\nTena mampifaly anay\nRaha tsy ilay fitahiana\nAvy Aminao ry Ray\nFa tsy misy koa fitia\nHahasambatra anay\nRaha tsy Ianao ry Tompo tia\nNo mitoetra anatinay', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (37, 4, 'Ny fisainanay ''zay kely\nTsy mahatakatr''indraindray\nFa Ianao, Ianao irery\nNo toky iankinanay\nNy finoana mila hivaha\nTsy mahasedra sarotra\nAmpijoroy tsy hanahy\nHo finoana matotra', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MBA TODIHONAO NY LASA', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (38, 1, 'Mba todihonao ny lasa\nFa ho hitanao ny asa\nNandaniana ny fotoana\nFa dia vita mora foana.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (38, 0, 'Mitsangàna sahia ! sahia\nMijoroa, mitoria, mitoria\nAza kivy sanatria\nFa aminao Kristy.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (38, 3, 'Eny tsara ho tsaroana\nIreo adidy nokoloina\nMaro koa no tsy tontosa\nNampitsotra ny kanosa', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (38, 4, 'Aoka hiezaka hatrany\nMba hanamarina izany\nFa hipetra ho tantara\nHo an''ny zanak''aman-para.\n(RAKOTOARITSIMBA\nGildas)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'FANIRIAKO', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 1, 'Ray ô ! mba faniriako\nRaha toa ka sitrakao\nNy handova lanitra\nDieny ety an-tany\nKanefa tsy izaho irery\nF''ireo ''zay mino Anao\nHiaraka amiko hatrany\nhatrany.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 0, 'Endrey ny fiainana\nanilan-dRay, \nNy Tompo iray\nFanahy iray,\niraisana avokoa\n''Reo asa soa, iray ny fo\nIray ny loha, Ray ô !\nNahoana moa e !\nRaha atomboka anio ?', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 3, 'Ray ô ! mba faniriako\nRaha toa ka sitrakao\nNy ho zanaka manoa\nFeno fahendrena \nHilofo hatrany koa hamita\nIreo asanao\nKa tsy hirehareha\nFa hanetry tena.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (39, 0, 'Endrey ny fiainanay\n(RAHAGALAHY\nANDRIAMIADANARIVO)', 'True');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'LA NUIT DU SALUT', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 1, 'C''était la nuit du salut\nOn s''en souvient à présent\nCar le seigneur très riche\nNaquit en ce moment-là\nC''est un amour fort étonnant\nQue Dieu a bel et bien\nmontré', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 0, 'Le cœur est rempli\nd'' immense gaieté\nDe joie, du bonheur éternel\nOn chante tout haut\nla chanson du Ciel\nHaleluia !', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 3, 'C''était la nuit du salut\nOn s''en souvient à présent\nJ''ai conclu avec le Seigneur\nLe pacte de la rédemption\nÀ lui j''ai donné tout\nmon corps\nPour moi, son sang est\nmon rachat.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 4, 'C''était la nuit du salut\nOn s''en souvient à présent\nDes pécheurs innombrables\nHéritent la belle vie\nCette nuit réconciliant\nÉtait porteuse de la paix.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (40, 5, 'C''était la nuit du salut\nOn s''en souvient à présent\nMon Jésus s''est rabaissé\nPar sa suprême pitié\nO ! bel amour rendant égaux\nLes riches et les malheureux.\n(RAJOELISON)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'JESOA NO MPANAVOTRA', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 1, 'O ! iza iza ?\nIza no hamonjy ahy\nIza, iza no hamonjy ahy re ?\nFa rerak''aho e !\nFa dia reraka\nMilonjy entam-pahotana\nFahotana be.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 2, 'O ! manatona\nManatona an''i Jesoa\nFa izy ihany \nIzy no hamonjy anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 3, 'Fa noreseny\nEfa noreseny na ny\nFasan''aza dia ny\nFahafatesana !\nNy fasan''aza !\nEfa noreseny izao\nNy fahafatesana\nIlay fahavalonao\nKa mba mifalia !\nMifalia ianao\nFa Jesoa Tompo no\nMpanavotra anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 4, 'Ry Jesoa Tompo !\nRy Jesoa Tompo ô !\nMba raiso re ny foko\nHo anao izao !\nFa famonjena\nFanavotan-dehibe\nSy avotr''aina koa\nNo he atolotrao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 5, 'Fa novonoina !\nNovonoina Ianao\nHisolo voina\nMba hisolo voina anay.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (41, 6, 'Fa noresenao\nEfa noresenao na ny\nFasan''aza dia \nNy fahafatesana !\nNy fasan''aza !\nEfa noreseny izao\nNy fahafatesana\nIlay fahavalonao\nKa mifalia re !\nKa mifaly izahay\nFa Jesoa Tompo no\nMpanavotra anay.\n(BESSA)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MOA MISY VA ?', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 1, 'Moa misy va ety,\niriko toa Anao ?\nRy Tompo be fitia,\nMpanao izao rehetra izao\nNy foko lazanao\nry Ray malala ô\nJereo ''ty Zanakao\nmiantoraka Aminao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 2, 'Moa misy va ety,\niriko toa Anao ?\nJesoa Sakaiza, avia Hianao\nhiara-dia izao\nNy foko tia Anao,\noneno, anjakao\nTsy hatakaloko Hianao,\n"Ry fiainako vaovao"', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 3, 'Moa misy va ety,\niriko toa Anao ?\nFanahy hendry ery,\nMpanoro izay rehetra atao\nNy foko he ! Anao,\nFanahy Masina ô !\nNy fiainako ovay ho vao,\nka hamasinonao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (42, 4, 'Moa misy va ety,\niriko noho ilay\nNesoina tany Kalvary ? \n''lay indrafon''ny Ray\nJeso malala ô !\nEndrey ''ty vitanao\nHitanao ny hetahetako\nsy hasambarako.\n(RAKOTONIRAINY J.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'AIZA MOA IANAO ?', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 1, 'Jesosy Tompo miantso anao\nManontany izao\n"Aiza moa ianao ?"\nNy ondrikelinao noana indreo\nMiandry anao re ireo\n"Aiza moa ianao ?"', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 0, '''Ty aho Jeso, ekeo \n(ekeo mba) ho irakao\nRaiso hanao ny sitrakao\ntokoa\nJesosy ô ! (omeko)\nmba ho Anao\nNy foko, tenako,\nny fanahiko.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 3, 'E ! lalan-tsarotra ilàna anao\nAdy mafy no ao,\n"Aiza moa ianao ?"\nReseonao\n''zao tontolo izao\nMba ho ahy re izao,\n"Aiza moa ianao ?"', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (43, 4, 'Ny hery, aina sy fanananao\nNo ilaiko izao\n"Aiza moa ianao ?" \nNy teninao sy ny fanahinao\nNo takiako izao\n"Aiza moa ianao ?"\nNy sandriko no hiaro anao\nHisakambina anao\n"Aiza moa ianao ?"\nRaha ny Fanahy \nno hitantana anao\nVonona ve ianao ?\n"Aiza moa ianao ?"\n(HERITSIMBA A.R.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'RY MADAGASIKARA SOA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 1, 'Ry Madagasikara soa\nNatolo-janahary anay\nNosim-boahangy manirery\nFa nahasoa ny razanay\nHe ! sarobidy tsara loatra\nTsy sandana, tsy atakalo\nFeno ny fo ka toa mangina\nHeriny mivavaka hoe :\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 2, 'Ry firenena Malagasy\nAntsoin''ny Nosy hianao\nAndeha malaky, mitsangàna\nAlao hery, mandrosoa !\n''Zay mahaolona anao re\nAtolory, asorony\nAza mandevina talenta\nFa anondroty Firenena e !\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (44, 3, 'Andriamanitra ambony\nFenoy ny fiahianao\nNy mponina rehetra eto\nAry izay aza mahasoa\nIreo nantsoinao hitondra\nMba tsidiho, mba tariho\nHo sambatra tokoa ny Nosy\nRaha Hianao no azy e !\nAndriamanitra ô !\nTondrahy andon''ny lanitra\nVontosy ny tsiron''ny tany\nI Madagasikara.\n(RAKOTONJANAHARY A.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'HIRA FIDERANA', 4, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 1, 'Hira fiderana ho anao ry Ray\nTsy afaka ato am-bavako\nmandrakizay\nHira mampifaly, \nmampitony jaly\nMampiravoravo fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 2, 'Ny talenta izay efa nomenao\nahy\nHiderako Anao\nn''aiza misy ahy\nMba hamelon''aina,\nhampitony saina\nHampiray ny miaina.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 3, 'Hira tsotra ihany,\nnefa na dia izany\nHo Anao tokoa\nno hikaloako\nRaiso re, Tompo,\nF''io no porofom-pitiavako\nAnao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (45, 4, 'Hira avy ato am-poko,\nindro fa vetsoiko\nHo Anao ry Raiko\nizay antsoiko\nHira tena mamy,\nmanambara hatrany\nFa tena tiako Ianao.\n(M. RAKOTOMAMONJY)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MBA TANTANONAO, RY RAIKO !', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 1, 'Mba tantanonao ry Raiko\nIzato lalan-janakao\nNy mifidy toa tsy haiko\nFa maniry indrindra Anao\nHitanao ny adi-tsaina\nMitambesatra ao an-doha\n''Zany tsikin''ny maraina\nToa ela vao hifoha.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 2, 'Moa diso ny safidiko\nRaha mandroso ka mankao\nSa ''ty lalà-nofinidiko\nDia tsy ilay voasoritrao\nTanteraho ny faniriako\nO ! ry Tompo tena tia\nFa ity zavatra iriko\nMasina amiko doria.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (46, 3, 'Nefa raha tsy izay no didy\nNamboarinao ho ahy\nAmboary ny safidy\nSy ny ratrako am-panahy\nAtolorinao ny hery\nHampijoro izay nikoa\nKa sitranonao ny fery\nSy ny ratrako ao am-po.\n(G. ANDRIAMANANTENA)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'FIADANANA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 1, 'Ianao ''zay mitady\nny fiadanantsoa\nAmin'' izao fiainana izao\nKa mandeha mankatsy\narý sy aroa\nMikorana ary koa milalao\nKanefa tsy mahita\nizay mahasoa\nFa vizana foana ny nofo\nInty misy fiainana\natoroko anao\nAndramo dia ho hitanao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 0, 'Jesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo fiainana atoroko\nanao (Jeso)\nJesosy, Jesosy\n(Jesosy Tompo\nsy Mpamonjy tia)\nNo hany hampiadana anao\nKoa raiso Jesosy\nHo Tompo Mpamonjy\nHanapaka ny fiainanao\nDia ho hitanao ''lay fiadanana\nHaharitra mandrakizay.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (47, 3, 'Ianao ianjadiam-pahoria-\nmarobe\nAmin''izao fiainana izao\nTsy misy hazavana \nfa toa haizim-be\nNo mandrakotra\nny fiainanao\nKa mila ho kivy\nsy hamoy fo\nNoho ireo adim-piainana maro\nInty misy fiainana\natoroko anao\nAndramo dia ho hitanao.\n(ANDRIANAIVONIAINA N.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'ANDRIAMANITRA FITIAVANA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 1, 'Andriamanitra Fitiavana Ianao\nKa mihevitra ny momba\nanay etý\nRaha mifoha maraina\nizahay\nDia tsy afaka am-bavanay\nhoe: Ampy ho anay\nny fahasoavanao', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 0, 'Raha maraina mibaliaka\nny andronay etý\nIanao no masoandro\nniaviany\nRaha ny alin''ny ririnina\nno handalo etý\nIanao no kintana\nhitantana ny dia\nKa ny vavaka atao\nIzay mipololotra\nam-ponay ao hoe :\nIzahay anie hatoky Anao.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (48, 3, 'Andriamanitra Fitiavana Ianao\nKa mitantana ny dianay etý\nRaha mandimby ny andro\nny alina\nMbola atokana hideranay\nhoe: Ampy ho anay\nny fahasoavanao.\n(Ndriana RAMAMONJY)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'MIFONA RE RAY !', 6, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 1, 'Mifona re ry Ray\nIreto Zanakao\nFa diso izahay\nTsy mety ny natao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 2, 'Ny ota sesilany\nNandramana avokoa\nKanefa re izahay\nIfonana tokoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 3, 'Izay rehetra hita\nNatao fa tsy ho to\nIzao ny ratsy vita\nNandrotika ny fo.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 4, 'Ry Jeso ô ! anio\nIndreto izahay\nManaiky ka fidio\nHo zanakao ry Ray.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 5, 'Tsy tsapanay tokoa\nNy antranao ry Ray\nFa hay, ny zava-tsoa\nDia takona aminay !', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (49, 6, 'Maniry fahafahana\nIzao ''ty fo madio\nEkeo ny fibebahana\nAtolotray anio.\n(RAKOTOMALALA J.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'IANAO IZAY MIASA FATRATRA', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 1, 'Ianao izay misa fatratra\nKa mikendry mba hanefa\nzava-tsarotra\nMoa tsy tsapanao ho\nmahasambatra\nNy asa izay atao\nam-pitiavana !\nMihamaivana mora entina\nNy fahasahiranana toa\nmitambesatra\nRaha ao ny Manaiky\nmarina\nHo tia sy handala\nny asa rehetra.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 2, 'Hoy ny hafatry ny Tompo\naminao\nManatona ahy raha toa\nsahirana\nFa izaho tsy manary \nna mandao\nIzay miezaka \nka hita reraka\nNefa moa ve, vonon''izao\nHandà ny tenanao,\nhamoy izao tontolo izao\nDia manaiky koa mba\nho tarihina\nFa sarotra ny làlana\nsy asa atao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (50, 3, 'Mitsanganà,\nmandrosoa hiasa e !\nFa malalaka ny saha\nizay omena anao\nDia meteza koa hanaiky\nhanome\nHarena herim-po\nilaina aminao\nAza ketraka,\nmatokia izao\n''Lay hery ''zay manohana\nNy fihezahanao\nKa mitondra soa,\nmanova mba ho vao\nNy vokatry ny asa\nizay nanasatr''anao.\n(RANDRIANANDRAINA S.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'RY MPAMONJY OLOM-BERY', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 1, 'Ry Mpamonjy olom-bery\nSarobidy sy mahery\nNy fitiavanao ahy\n''Zay mambabo ny fanahy\nNoho ilay fitiavanao\nBabonao ny foko izao\nKa izao rehetra izao\nTsy hamoizako Anao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 2, 'Ny fitiavanao, ry Tompo\nNo mambabo ahy hanompo\nTia ahy Hianao\nKa mba tiako tsotra izao\nEny, fantatrao fa tiako\nIzao fiainana eto izao\nTiako izao rehetra izao\nNefa mbola tiako Hianao.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (51, 3, 'Na dia sarotra sy mafy\nNy hanaraka Anao\nIzaho vonona hihafy\nF''efa tiako Hianao\n''Zao fiainana izao\nTsy atakaloko Anao\nNa dia mamiko aza eto\nMbola tiako Hianao.\n( RAJAONAHSON J.P)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'AVIA ! TORIO KRISTY', 5, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 1, '', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 0, 'Ry tanora ô ! avia !\nMitsangàna, mitoria\nAmbarao ''lay famonjena\nka sahia\nAvia, avia, torio Kristy.', 'True');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 3, 'Fa vory amintsika izao ny hery\nKa mendrika hanaovan-tsoa\nMeteza ka ampio ''reo\nmbola very\nHo mpino toa antsika koa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 4, 'Anio miandry antsika\nny tontolo\nHitondra hazavana soa\nIsika izay voataiza \nsy voakolo\nNo indro irahan''i Jesoa.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (52, 5, 'Ny herin''ny fanahy\nno momba antsika\nHahatomombana ny dia\nAndeha amin''ny endrika\nsariaka\nNo hitoriana an''i Kristy.\n(RANDRIANDRAINY F.)', 'False');
INSERT INTO hira (sokajy_id, lohateny, isa_andininy, mpanoratra)VALUES (2, 'HAVAOZIKO ILAY VOADY', 3, '');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (53, 1, 'Tsy ho toy izao,\nity mpanomponao\nRaha toa tsy tao tokoa\nny fahasoavanao\n''Zay nanohana \n''lay hery kely\ntao am-poko\nNahazoako mba nanao\nasam-panompoako.', 'False');
INSERT INTO tononkira (hira_id, andininy, tononkira, fiverenany)VALUES (53, 0, 'Indro fa havaoziko,\nry Jesoa\n''Lay voady\nmiorin''ao am-po\nNy andro ivelomako\n''Zay omenao iainako\nDia atokako sy mbola\nhitoriako\nIlay fitiavanao !\nNy asa izay\nnataon''i Jesoa\nDia tsapako ao am-po\nFa tsy dia toy inona\nsy bitika tokoa\nNefa na izany aza\ndia inoako koa\nFa ''zay kely vitako :\nhitera-bokatsoa.', 'True');
